hebsuperobject.biblemap.put("b52_3_13", new hebsuperobject(52,3,13,"ויכונן את לבבכם להיות תמים בקדשה לפני אלהינו אבינו בבוא ישוע המשיח אדנינו עם כל קדשיו׃"));
hebsuperobject.biblemap.put("b52_4_1", new hebsuperobject(52,4,1,"ועוד נבקשה מכם אחי ונזהיר אתכם באדנינו ישוע כי כאשר קבלתם מאתנו איך לכם להתהלך ולמצא חן בעיני האלהים כן תוסיפו וכן תרבו עוד׃"));
hebsuperobject.biblemap.put("b52_4_2", new hebsuperobject(52,4,2,"כי ידעתם את המצות אשר נתנו לכם בשם האדון ישוע׃"));
