hebsuperobject.biblemap.put("b52_5_8", new hebsuperobject(52,5,8,"ואנחנו בני היום ננזרה נא ונלבש את שריון האמונה והאהבה וככובע את תקות הישועה׃"));
hebsuperobject.biblemap.put("b52_5_9", new hebsuperobject(52,5,9,"יען אשר לא יעדנו האלהים לחרון כי אם לנחל את הישועה על ידי אדנינו ישוע המשיח׃"));
hebsuperobject.biblemap.put("b52_5_10", new hebsuperobject(52,5,10,"אשר מת בעדנו למען אם נשקד ואם נישן חיה נחיה עמו יחד׃"));
