hebsuperobject.biblemap.put("b52_5_12", new hebsuperobject(52,5,12,"והננו מבקשים מכם אחי להכיר את העמלים בכם ואשר הם נצבים עליכם באדון ומוכיחים אתכם׃"));
hebsuperobject.biblemap.put("b52_5_13", new hebsuperobject(52,5,13,"אשר תנהגו בם כבוד עד למעלה באהבה למען פעלתם ויהי שלום ביניכם׃"));
