hebsuperobject.biblemap.put("b53_2_4", new hebsuperobject(53,2,4,"המתקומם והמתרומם על כל הנקרא אל או עבודה עד כי גם ישב בהיכל האלהים כאלהים ומראה את עצמו כי אלהים הוא׃"));
hebsuperobject.biblemap.put("b53_2_5", new hebsuperobject(53,2,5,"הלא תזכרו כי בעוד היותי אתכם דברתי אלה אליכם׃"));
