hebsuperobject.biblemap.put("b53_2_17", new hebsuperobject(53,2,17,"הוא ינחם את לבבכם ויכונן אתכם בכל דבר ומעשה טוב׃"));
hebsuperobject.biblemap.put("b53_3_1", new hebsuperobject(53,3,1,"ועוד אחי התפללו בעדנו אשר ירוץ דבר יהוה ויכבד כמו גם בקרבכם׃"));
hebsuperobject.biblemap.put("b53_3_2", new hebsuperobject(53,3,2,"ואשר ננצל מן האנשים התעים והרעים כי לא לכל אדם האמונה׃"));
