hebsuperobject.biblemap.put("b53_3_8", new hebsuperobject(53,3,8,"גם לא אכלנו לחם איש חנם כי ביגיעה ותלאה לילה ויומם היינו עמלים לבלתי היות לאיש מכם למשא׃"));
hebsuperobject.biblemap.put("b53_3_9", new hebsuperobject(53,3,9,"לא בעבור שאין לנו הרשות לזאת כי אם לתת אתנו לכם למופת ללכת בעקבותינו׃"));
hebsuperobject.biblemap.put("b53_3_10", new hebsuperobject(53,3,10,"כי גם בהיותנו אצלכם צוינו אתכם לאמר מי שלא ירצה לעבד גם אכול לא יאכל׃"));
hebsuperobject.biblemap.put("b53_3_11", new hebsuperobject(53,3,11,"כי שמענו שיש בכם אנשים המעקשים את דרכיהם ואינם עבדים מאומה ומבלים ימיהם בהבלים׃"));
hebsuperobject.biblemap.put("b53_3_12", new hebsuperobject(53,3,12,"ואנשים כאלה נצוה אותם ונבקש מהם באדנינו ישוע המשיח לעבד בנחת למען יאכלו את לחמם׃"));
hebsuperobject.biblemap.put("b53_3_13", new hebsuperobject(53,3,13,"ואתם אחי אל תלאו בעשות הטוב׃"));
hebsuperobject.biblemap.put("b53_3_14", new hebsuperobject(53,3,14,"ואם לא ישמע איש אל דברנו באגרת הזאת אתו תרשמו לכם ואל תתערבו עמו למען יבוש׃"));
hebsuperobject.biblemap.put("b53_3_15", new hebsuperobject(53,3,15,"אך לא כאיב תחשבהו כי אם תוכיחהו כאח׃"));
