hebsuperobject.biblemap.put("b54_1_2", new hebsuperobject(54,1,2,"אל טימותיוס בנו האמתי באמונה חסד ורחמים ושלום מאת אלהים אבינו והמשיח ישוע אדנינו׃"));
hebsuperobject.biblemap.put("b54_1_3", new hebsuperobject(54,1,3,"הנה בלכתי למקדוניא בקשתי ממך לשבת באפסוס למען תזהיר מקצת אנשים שלא יורו תורה זרה׃"));
