hebsuperobject.biblemap.put("b56_3_10", new hebsuperobject(56,3,10,"ואיש החולק על האמונה אם הוכח פעם ושתים שטה מעליו׃"));
hebsuperobject.biblemap.put("b56_3_11", new hebsuperobject(56,3,11,"ודע כי איש כזה איש תהפכות הוא וחוטא בהרשיע את נפשו׃"));
hebsuperobject.biblemap.put("b56_3_12", new hebsuperobject(56,3,12,"כאשר אשלח אליך את ארטמס או את טוכיקוס תמהר לבוא אלי לניקפליס כי גמרתי בלבי לשבת שם בימי הסתיו׃"));
