hebsuperobject.biblemap.put("b59_4_12", new hebsuperobject(59,4,12,"אחד הוא המחקק אשר יכול להושיע ולאבד ומי אתה כי תדין את עמיתך׃"));
hebsuperobject.biblemap.put("b59_4_13", new hebsuperobject(59,4,13,"הוי האמרים נלכה היום ומחר לעיר פלונית אלמונית ונעשה שם שנה אחת לסחר בה ולהרבות רוח׃"));
