hebsuperobject.biblemap.put("b61_1_2", new hebsuperobject(61,1,2,"חסד ושלום יהיו לכם למכביר בדעת האלהים וישוע אדנינו׃"));
hebsuperobject.biblemap.put("b61_1_3", new hebsuperobject(61,1,3,"באשר גבורתו האלהית נתנה לנו את כל אשר לחיים ולחסידות על ידי דעת הקורא אתנו בכבודו וחילו׃"));
hebsuperobject.biblemap.put("b61_1_4", new hebsuperobject(61,1,4,"אשר בהם נתן לנו הבטחות גדלות מאד ויקרות למען תקחו על ידן חלק בטבע אלהים בהמלטכם מכליון התאוה אשר בעולם׃"));
hebsuperobject.biblemap.put("b61_1_5", new hebsuperobject(61,1,5,"בעבור זאת השתדלו להעמיד באמונתכם את הצדקה ובצדקה את הדעת׃"));
hebsuperobject.biblemap.put("b61_1_6", new hebsuperobject(61,1,6,"ובדעת את הפרישות ובפרישות את הסבלנות ובסבלנות את החסידות׃"));
hebsuperobject.biblemap.put("b61_1_7", new hebsuperobject(61,1,7,"ובחסידות את האחוה ובאחוה את האהבה׃"));
