hebsuperobject.biblemap.put("b61_1_9", new hebsuperobject(61,1,9,"כי האיש אשר אין אלה לו עור הוא קצר הראות ושכח את טהרתו מחטאתיו הראשנות׃"));
hebsuperobject.biblemap.put("b61_1_10", new hebsuperobject(61,1,10,"לכן אחי הוסיפו והשתדלו לחזק את קריאתכם ובחירתכם כי בעשותכם זאת כשול לא תכשלו׃"));
hebsuperobject.biblemap.put("b61_1_11", new hebsuperobject(61,1,11,"כי כן יפתח לפניכם לרוחה המבוא אל מלכות עולם אשר לאדנינו ומושיענו ישוע המשיח׃"));
hebsuperobject.biblemap.put("b61_1_12", new hebsuperobject(61,1,12,"על כן לא אחדל להזכירכם על אלה בכל עת גם כי ידעתם והתכוננתם באמת אשר היא לפנינו׃"));
hebsuperobject.biblemap.put("b61_1_13", new hebsuperobject(61,1,13,"ואחשב כי נכון להזכיר ולהעיר אתכם כל ימי היותי במשכן הזה׃"));
hebsuperobject.biblemap.put("b61_1_14", new hebsuperobject(61,1,14,"באשר ידעתי כי מהר יעתק משכני כאשר גם גלה לי אדנינו ישוע המשיח׃"));
hebsuperobject.biblemap.put("b61_1_15", new hebsuperobject(61,1,15,"ואשתדל שיהיה לכם תמיד גם אחרי פטירתי זכרון הדברים האלה׃"));
hebsuperobject.biblemap.put("b61_1_16", new hebsuperobject(61,1,16,"כי הודענו אתכם גבורת אדנינו ישוע המשיח ובאו לא בלכתנו אחרי הגדות מחכמות כי אם ראות היו עינינו את גדלתו׃"));
hebsuperobject.biblemap.put("b61_1_17", new hebsuperobject(61,1,17,"כי לקח מאת אלהים האב יקר וכבוד בבא אליו קול מתוך הדרת כבודו לאמר זה בני ידידי רצתה נפשי בו׃"));
hebsuperobject.biblemap.put("b61_1_18", new hebsuperobject(61,1,18,"ואת הקול הזה שמענו באזנינו יצא משמים בהיותנו עמו בהר הקדש׃"));
hebsuperobject.biblemap.put("b61_1_19", new hebsuperobject(61,1,19,"ועתה דבר הנבואה יותר קים אתנו והטיבתם עשות אשר שתם לבכם אליו כמו אל נר מאיר במקום אפל עד כי יבקע אור היום וזרח כוכב הנגה בלבבכם׃"));
