hebsuperobject.biblemap.put("b61_1_21", new hebsuperobject(61,1,21,"כי מעולם לא יצאה נבואה ברצון האדם כי אם ברוח הקדש אשר נשאם דברו אנשי אלהים הקדושים׃"));
hebsuperobject.biblemap.put("b61_2_1", new hebsuperobject(61,2,1,"וגם נביאי שקר היו בעם כאשר יהיו גם בכם מורי שקר אשר יכניסו כתות משחיתות ובכחשם במשל אשר קנם יביאו על נפשם כליון פתאם׃"));
hebsuperobject.biblemap.put("b61_2_2", new hebsuperobject(61,2,2,"ורבים ילכו אחרי תועבותם ובעבורם יתן דרך האמת לגדופים׃"));
