hebsuperobject.biblemap.put("b61_2_4", new hebsuperobject(61,2,4,"כי לא חס אלהים על המלאכים אשר חטאו כי אם הורידם לקצבי הרים ויסגירם בכבלי אפל לשמרם למשפט׃"));
hebsuperobject.biblemap.put("b61_2_5", new hebsuperobject(61,2,5,"וגם על דורות קדם לא חס וישמר רק את נח השמיני קרא הצדק בהביאו את המבול על דור הרשעים׃"));
hebsuperobject.biblemap.put("b61_2_6", new hebsuperobject(61,2,6,"ואת ערי סדום ועמרה הפך לאפר והאשימם במהפכה וישימם למשל לאשר עתידים לעשות זמה׃"));
hebsuperobject.biblemap.put("b61_2_7", new hebsuperobject(61,2,7,"ויצל את לוט הצדיק אשר הלאוהו אנשי בליעל ההם בדרך זמתם׃"));
