hebsuperobject.biblemap.put("b61_2_9", new hebsuperobject(61,2,9,"כי יודע יהוה להציל את חסידיו מנסיון ולחשך את הרשעים ליום המשפט להשיב גמולם להם׃"));
hebsuperobject.biblemap.put("b61_2_10", new hebsuperobject(61,2,10,"וביותר את ההלכים אחרי הבשר בתאות תבל ובזים את הממשלה עזי פנים הלכים בשרירות לבם ולא יחרדו מחרף את השררות׃"));
hebsuperobject.biblemap.put("b61_2_11", new hebsuperobject(61,2,11,"אשר אף המלאכים הגדולים מהם בעז וכח לא יגדפום לפני יהוה במשפטם׃"));
hebsuperobject.biblemap.put("b61_2_12", new hebsuperobject(61,2,12,"והמה כבהמות הסכלות הנולדות כחק טבעם ללכד ולשחת יאבדו באבוד נפשם יען חרפו את אשר לא הבינו וישאו גמול עולתם׃"));
hebsuperobject.biblemap.put("b61_2_13", new hebsuperobject(61,2,13,"אשר עדנת יומם לענג יחשבו מטנפים ומום בם המתפנקים במדוחי נפשם ואכלים ושתים עמכם׃"));
hebsuperobject.biblemap.put("b61_2_14", new hebsuperobject(61,2,14,"עינים להם מלאות נאפים אשר לא תחדלות מחטוא ואת נפשות הפתאים יצודדו ולב מלמד בצע להם בני המארה׃"));
hebsuperobject.biblemap.put("b61_2_15", new hebsuperobject(61,2,15,"את הדרך הישר עזבו ויתעו וילכו בדרך בלעם בן בעור אשר אהב שכר העולה׃"));
