hebsuperobject.biblemap.put("b61_2_17", new hebsuperobject(61,2,17,"בארות בלי מים המה עבים נדפים בסערה אשר שמור להם חשך אפלה לעולם׃"));
hebsuperobject.biblemap.put("b61_2_18", new hebsuperobject(61,2,18,"כי בדברם בגאות דברי שוא יצודו בתאות הבשר על ידי זמתם את אשר אך נמלטו מידי ההלכים בדרך תועה׃"));
hebsuperobject.biblemap.put("b61_2_19", new hebsuperobject(61,2,19,"חפשה יבטיחו להם והם בעצמם עבדים לשחת כי האיש עבד לאשר נכבש ממנו׃"));
hebsuperobject.biblemap.put("b61_2_20", new hebsuperobject(61,2,20,"כי אחרי המלטם מטמאת העולם בדעת אדנינו ומושיענו ישוע המשיח אם שבו והטבעו בתוכן ונכבשו אחריתם תהיה רעה מראשיתם׃"));
hebsuperobject.biblemap.put("b61_2_21", new hebsuperobject(61,2,21,"טוב היה להם לא לדעת את דרך הצדקה מאשר ידעהו ונסגו אחור מן המצוה הקדושה המסורה להם׃"));
hebsuperobject.biblemap.put("b61_2_22", new hebsuperobject(61,2,22,"ויקר להם כאשר יאמר משל האמת הכלב שב על קאו והחזיר עלה מן הרחצה להתגלל ברפש׃"));
hebsuperobject.biblemap.put("b61_3_1", new hebsuperobject(61,3,1,"זאת היא האגרת השנית אשר אני כתב אליכם אהובים ובשתיהן אעיר בדרך הזכרה את תמת לבבכם׃"));
hebsuperobject.biblemap.put("b61_3_2", new hebsuperobject(61,3,2,"לזכר את הדברים הנאמרים מקדם בידי הנביאים הקדשים ואת מצות אדנינו ומושיענו אשר נתנה בידנו השליחים׃"));
hebsuperobject.biblemap.put("b61_3_3", new hebsuperobject(61,3,3,"ודעו זאת לכם ראשנה כי באחרית הימים יבאו לצים ההלכים אחרי תאות נפשם ויתלוצצו לאמר׃"));
hebsuperobject.biblemap.put("b61_3_4", new hebsuperobject(61,3,4,"איה הבטחת באו כי מאז שכבו האבות הכל עמד כמו מראשית הבריאה׃"));
