hebsuperobject.biblemap.put("b61_3_7", new hebsuperobject(61,3,7,"גם השמים והארץ אשר לפנינו נצפנו בדברו והם שמורים לאש ליום הדין ואבד אנשי הרשע׃"));
hebsuperobject.biblemap.put("b61_3_8", new hebsuperobject(61,3,8,"וזאת האחת אל תעלם מכם אהובים כי יום אחד כאלף שנים בעיני יהוה ואלף שנים כיום אחד׃"));
hebsuperobject.biblemap.put("b61_3_9", new hebsuperobject(61,3,9,"ולא יאחר יהוה את אשר הבטיח כאשר יש חשבים אתה לאחור כי אך האריך אפו בעבורנו ולא יחפץ באבד איש כי אם בפנות כלם לתשובה׃"));
hebsuperobject.biblemap.put("b61_3_10", new hebsuperobject(61,3,10,"בא יבא יום יהוה כגנב בלילה אז השמים בשאון יחלפו והיסדות יבערו והתמגגו והארץ והמעשים אשר עליה ישרפו׃"));
hebsuperobject.biblemap.put("b61_3_11", new hebsuperobject(61,3,11,"ועתה אם כל אלה ימוגו מה מאד חיבים אתם להתהלך בקדשה ובחסידות׃"));
hebsuperobject.biblemap.put("b61_3_12", new hebsuperobject(61,3,12,"ולחכות לבא יום יהוה ולהחיש אתו אשר בגללו השמים ימוגו באש והיסדות יבערו ונמסו׃"));
hebsuperobject.biblemap.put("b61_3_13", new hebsuperobject(61,3,13,"ואנחנו כפי הבטחתו מחכים לשמים חדשים ולארץ חדשה אשר צדק ילין בם׃"));
hebsuperobject.biblemap.put("b61_3_14", new hebsuperobject(61,3,14,"על כן אהובים אשר לאלה חכיתם שקדו להמצא לפניו בשלום נקים וזכים מפשע׃"));
