hebsuperobject.biblemap.put("b61_3_16", new hebsuperobject(61,3,16,"וכן בכל אגרותיו בדברו שם על אלה ובהם יש דברים קשי ההבנה והבערים והפתאים יהפכו אתם כאשר גם יעשו לכתבים האחרים לאבדן נפשם׃"));
hebsuperobject.biblemap.put("b61_3_17", new hebsuperobject(61,3,17,"ואתם אהובים אשר ידעתם זאת השמרו לנפשתיכם פן תמשכו אחרי טעות אנשי בליעל ונפלתם ממעזכם׃"));
hebsuperobject.biblemap.put("b61_3_18", new hebsuperobject(61,3,18,"ורבו בחסד ובדעת אדנינו ומושיענו ישוע המשיח אשר לו הכבוד גם היום וגם ליום העולם אמן׃"));

