hebsuperobject.biblemap.put("b62_4_20", new hebsuperobject(62,4,20,"כי יאמר איש אהב אני את האלהים והוא שנא את אחיו כזב הוא כי אשר לא יאהב את אחיו אשר הוא ראה איככה יוכל לאהב את האלהים אשר איננו ראה אתו׃"));
hebsuperobject.biblemap.put("b62_4_21", new hebsuperobject(62,4,21,"וזאת המצוה יש לנו מאתו כי האהב את האלהים יאהב גם את אחיו׃"));
hebsuperobject.biblemap.put("b62_5_1", new hebsuperobject(62,5,1,"כל המאמין כי ישוע הוא המשיח הנה זה ילד מאלהים וכל האהב את מולידו יאהב גם את הנולד מאתו׃"));
hebsuperobject.biblemap.put("b62_5_2", new hebsuperobject(62,5,2,"בזאת נדע כי נאהב את בני האלהים באהבתנו את האלהים ובשמרנו את מצותיו׃"));
hebsuperobject.biblemap.put("b62_5_3", new hebsuperobject(62,5,3,"כי זאת אהבת אלהים היא אשר נשמר את מצותיו ומצותיו אינן כבדות׃"));
hebsuperobject.biblemap.put("b62_5_4", new hebsuperobject(62,5,4,"כי כל הנולד מאת האלהים מנצח את העולם ואמונתנו הנצחון המנצח את העולם׃"));
hebsuperobject.biblemap.put("b62_5_5", new hebsuperobject(62,5,5,"מי הוא זה המנצח את העולם אם לא המאמין בישוע שהוא בן האלהים׃"));
hebsuperobject.biblemap.put("b62_5_6", new hebsuperobject(62,5,6,"זה הוא אשר בא במים ובדם ישוע המשיח לא במים לבד כי אם במים ובדם והרוח הוא המעיד כי הרוח הוא האמת׃"));
hebsuperobject.biblemap.put("b62_5_7", new hebsuperobject(62,5,7,"כי שלשה המה המעידים בשמים האב הדבר ורוח הקדש ושלשתם אחד המה׃"));
hebsuperobject.biblemap.put("b62_5_8", new hebsuperobject(62,5,8,"ושלשה המה המעידים בארץ הרוח המים והדם ושלשתם לאחת המה׃"));
hebsuperobject.biblemap.put("b62_5_9", new hebsuperobject(62,5,9,"אם נקבל עדות בני אדם הנה עדות האלהים גדולה ממנה כי זאת היא עדות האלהים אשר העיד על בנו׃"));
hebsuperobject.biblemap.put("b62_5_10", new hebsuperobject(62,5,10,"המאמין בבן האלהים יש לו העדות בנפשו ואשר לא יאמין לאלהים לכזב שמהו יען לא האמין בעדות אשר העיד האלהים על בנו׃"));
hebsuperobject.biblemap.put("b62_5_11", new hebsuperobject(62,5,11,"וזאת היא העדות כי חיי עולמים נתן לנו האלהים והחיים האלה בבנו המה׃"));
hebsuperobject.biblemap.put("b62_5_12", new hebsuperobject(62,5,12,"אשר יש לו הבן יש לו החיים ואשר אין לו בן האלהים אין לו החיים׃"));
hebsuperobject.biblemap.put("b62_5_13", new hebsuperobject(62,5,13,"זאת כתבתי אליכם המאמינים בשם בן האלהים למען תדעון שיש לכם חיי עולם ולמען תאמינו בשם בן האלהים׃"));
hebsuperobject.biblemap.put("b62_5_14", new hebsuperobject(62,5,14,"וזה הוא בטחוננו אליו אשר אם נשאל דבר כפי רצונו ישמענו׃"));
hebsuperobject.biblemap.put("b62_5_15", new hebsuperobject(62,5,15,"ומדעתנו כי ישמענו לכל אשר נשאל נדע גם זאת כי נשיג את המשאלות אשר שאלנו ממנו׃"));
hebsuperobject.biblemap.put("b62_5_16", new hebsuperobject(62,5,16,"איש כי יראה את אחיו חוטא חטאת אשר לא למות שאל ישאל בעדו ויתן לו חיים לכל אשר חטאו לא למות הן יש חטא למות על זה לא אמר לשאל בעדו׃"));
hebsuperobject.biblemap.put("b62_5_17", new hebsuperobject(62,5,17,"כל מעשה שלא כמשפט חטא היא ויש חטא שלא למות׃"));
hebsuperobject.biblemap.put("b62_5_18", new hebsuperobject(62,5,18,"ידענו כי כל הנולד מאת האלהים לא יחטא כי אשר ילד מאת האלהים ישמר את נפשו והרע לא יגע בו׃"));
hebsuperobject.biblemap.put("b62_5_19", new hebsuperobject(62,5,19,"ידענו כי מאלהים אנחנו וכל העולם ברע הוא׃"));
hebsuperobject.biblemap.put("b62_5_20", new hebsuperobject(62,5,20,"וידענו כי בא בן אלהים ויתן לנו בינה לדעת את האמתי ובאמתי אנחנו בבנו ישוע המשיח זה הוא האל האמתי וחיי העולם׃"));
hebsuperobject.biblemap.put("b62_5_21", new hebsuperobject(62,5,21,"בני השמרו לכם מן האלילים אמן׃"));

