hebsuperobject.biblemap.put("b66_4_8", new hebsuperobject(66,4,8,"ולכל אחת מארבע החיות שש כנפים מסביב ולפנימה הנה מלאות עינים ואין דמי להן יומם ולילה ואמרות קדוש קדוש קדוש יהוה אלהים צבאות היה והוה ויבוא׃"));
hebsuperobject.biblemap.put("b66_4_9", new hebsuperobject(66,4,9,"ומדי תת החיות כבוד והדר ותודה לישב על הכסא אשר הוא חי לעולמי עולמים׃"));
hebsuperobject.biblemap.put("b66_4_10", new hebsuperobject(66,4,10,"אז יפלו עשרים וארבעה הזקנים על פניהם לפני הישב על הכסא והשתחוו לחי עולמי העולמים ושמו את עטרותיהם לפני הכסא לאמר׃"));
hebsuperobject.biblemap.put("b66_4_11", new hebsuperobject(66,4,11,"לך נאה אדנינו לקחת כבוד והדר ועז כי אתה בראת הכל והכל ברצונך היו ונבראו׃"));
hebsuperobject.biblemap.put("b66_5_1", new hebsuperobject(66,5,1,"וארא בימין הישב על הכסא ספר כתוב פנים ואחור וחתום בשבעה חתמות׃"));
hebsuperobject.biblemap.put("b66_5_2", new hebsuperobject(66,5,2,"וארא מלאך אביר קורא בקול גדול מי הוא הזכה לפתח הספר ולהתיר את חותמיו׃"));
