hebsuperobject.biblemap.put("b66_5_4", new hebsuperobject(66,5,4,"ואבך בכי גדול על אשר לא נמצא איש זכה לפתח את הספר ולקרא בו או להביט אליו׃"));
hebsuperobject.biblemap.put("b66_5_5", new hebsuperobject(66,5,5,"ויאמר אלי אחד מן הזקנים אל תבכה הנה נצח האריה אשר הוא משבט יהודה שרש דוד לפתח את הספר ולהתיר שבעת חותמיו׃"));
hebsuperobject.biblemap.put("b66_5_6", new hebsuperobject(66,5,6,"וארא והנה בין הכסא וארבע החיות ובין הזקנים שה עמד כמו טבוח ולו שבע קרנים ושבעה עינים אשר הם שבעה רוחות האלהים השלוחים אל כל הארץ׃"));
hebsuperobject.biblemap.put("b66_5_7", new hebsuperobject(66,5,7,"ויבא ויקח את הספר מימין הישב על הכסא׃"));
hebsuperobject.biblemap.put("b66_5_8", new hebsuperobject(66,5,8,"ויהי בקחתו את הספר ויפלו לפני השה ארבע החיות ועשרים וארבעה הזקנים ואיש איש כנור בידו וקערות זהב מלאת קטרת אשר הנה תפלות הקדושים׃"));
hebsuperobject.biblemap.put("b66_5_9", new hebsuperobject(66,5,9,"וישירו שיר חדש לאמר לך נאה לקחת את הספר ולפתח את חותמיו כי אתה נשחטת ובדמך קניתנו לאלהים מכל משפחה ולשון וכל עם וגוי׃"));
hebsuperobject.biblemap.put("b66_5_10", new hebsuperobject(66,5,10,"ותעש אתם מלכים וכהנים לאלהינו וימלכו על הארץ׃"));
hebsuperobject.biblemap.put("b66_5_11", new hebsuperobject(66,5,11,"וארא ואשמע קול מלאכים רבים סביב לכסא ולחיות ולזקנים מספרם רבו רבבות ואלפי אלפים׃"));
