hebsuperobject.biblemap.put("b66_5_13", new hebsuperobject(66,5,13,"וכל בריה אשר בשמים ובארץ ומתחת לארץ ואשר על הים וכל אשר בהם את כלם שמעתי אמרים לאמר לישב על הכסא ולשה הברכה וההדר והכבוד והעז לעולמי עולמים׃"));
hebsuperobject.biblemap.put("b66_5_14", new hebsuperobject(66,5,14,"ותאמרנה ארבע החיות אמן ועשרים וארבעה הזקנים נפלו על פניהם וישתחוו לחי עולמי העולמים׃"));
hebsuperobject.biblemap.put("b66_6_1", new hebsuperobject(66,6,1,"וארא כאשר פתח השה אחד מן שבעה החתמות ואשמע אחת מארבע החיות מדברת כקול רעם לאמר בא וראה׃"));
hebsuperobject.biblemap.put("b66_6_2", new hebsuperobject(66,6,2,"ואביט והנה סוס לבן והרכב עליו קשת בידו ותנתן לו עטרה ויצא מנצח ולמען ינצח׃"));
hebsuperobject.biblemap.put("b66_6_3", new hebsuperobject(66,6,3,"וכפתחו את החותם השני ואשמע את החיה השנית אמרת בא וראה׃"));
