hebsuperobject.biblemap.put("b66_6_7", new hebsuperobject(66,6,7,"וכפתחו את החותם הרביעי ואשמע את החיה הרביעית אמרת בא וראה׃"));
hebsuperobject.biblemap.put("b66_6_8", new hebsuperobject(66,6,8,"וארא והנה סוס ירקרק והרכב עליו שמו המות ושאול יוצאת לרגליו וינתן להם שלטן על רביעית הארץ להמית בחרב וברעב ובדבר ובחית הארץ׃"));
hebsuperobject.biblemap.put("b66_6_9", new hebsuperobject(66,6,9,"וכפתחו החותם החמישי וארא מתחת למזבח את נפשות הטבוחים על דבר האלהים ועל העדות אשר היתה להם׃"));
hebsuperobject.biblemap.put("b66_6_10", new hebsuperobject(66,6,10,"ויזעקו בקול גדול ויאמרו עד מתי אדני הקדוש והאמתי לא תשפט ולא תקום את דמינו מישבי הארץ׃"));
hebsuperobject.biblemap.put("b66_6_11", new hebsuperobject(66,6,11,"ויתן לאיש איש מהם שמלות לבנות ויאמר אליהם לנוח עוד זמן מעט עד מלאת מספר העבדים חברים ואחיהם העתידים להרג כמוהם׃"));
