hebsuperobject.biblemap.put("b66_7_9", new hebsuperobject(66,7,9,"אחרי כן ראיתי והנה המון רב אשר לא יכל איש למנותו מכל הגוים והמשפחות והעמים והלשנות ויעמדו לפני הכסא ולפני השה מלבשים שמלות לבנות וכפות תמרים בידיהם׃"));
hebsuperobject.biblemap.put("b66_7_10", new hebsuperobject(66,7,10,"ויקראו בקול גדול לאמר הישועה לאלהינו הישב על הכסא ולשה׃"));
