hebsuperobject.biblemap.put("b66_8_13", new hebsuperobject(66,8,13,"וארא ואשמע מלאך אחד מעופף במרום הרקיע הקורא בקול גדול אוי אוי אוי לישבי הארץ משאר קלות שופר שלשת המלאכים העתידים לתקע׃"));
hebsuperobject.biblemap.put("b66_9_1", new hebsuperobject(66,9,1,"והמלאך החמישי תקע בשופר וארא כוכב נפל מן השמים לארץ וינתן לו מפתח באר התהום׃"));
hebsuperobject.biblemap.put("b66_9_2", new hebsuperobject(66,9,2,"ויפתח את באר התהום ויעל עשן מן הבאר כעשן כבשן גדול ויחשך השמש והרקיע מקיטר הבאר׃"));
hebsuperobject.biblemap.put("b66_9_3", new hebsuperobject(66,9,3,"ומן הקיטר יצא ארבה על הארץ וינתן להם שלטן כשלטן עקרבי הארץ׃"));
hebsuperobject.biblemap.put("b66_9_4", new hebsuperobject(66,9,4,"ויאמר אליהם אשר לא ישחיתו את עשב הארץ ולא כל ירק ולא כל עץ כי אם את בני האדם אשר אין להם חותם אלהים במצחותם׃"));
