hebsuperobject.biblemap.put("b66_9_8", new hebsuperobject(66,9,8,"ושער להם כשער נשים ושניהם שני אריה׃"));
hebsuperobject.biblemap.put("b66_9_9", new hebsuperobject(66,9,9,"ושרינים להם כשריני ברזל וקול כנפיהם כקול מרכבות סוסים רבים הרצים למלחמה׃"));
hebsuperobject.biblemap.put("b66_9_10", new hebsuperobject(66,9,10,"וזנבות להם כזנבות עקרבים ועקצים בזנבותם והשלטו לענות את בני האדם חמשה חדשים׃"));
hebsuperobject.biblemap.put("b66_9_11", new hebsuperobject(66,9,11,"ומלאך התהום הוא מלך עליהם ושמו אבדון בעברית והוא אפוליון בלשון יון׃"));
hebsuperobject.biblemap.put("b66_9_12", new hebsuperobject(66,9,12,"הצרה האחת חלפה הלכה לה והנה באות אחריה עוד צרות שתים׃"));
hebsuperobject.biblemap.put("b66_9_13", new hebsuperobject(66,9,13,"והמלאך הששי תקע בשופר ואשמע קול אחד מארבע קרנות מזבח הזהב אשר לפני אלהים׃"));
hebsuperobject.biblemap.put("b66_9_14", new hebsuperobject(66,9,14,"ויאמר למלאך הששי אשר השופר בידו התר את ארבעה המלאכים ההם האסורים על הנהר הגדול נהר פרת׃"));
hebsuperobject.biblemap.put("b66_9_15", new hebsuperobject(66,9,15,"ויתרו ארבעה המלאכים אשר היו נכונים לשעה וליום ולחדש ולשנה להמית שלישית בני האדם׃"));
hebsuperobject.biblemap.put("b66_9_16", new hebsuperobject(66,9,16,"ויהי מספר צבאות הפרשים שתי רבוא רבבות ואני שמעתי מספרם׃"));
hebsuperobject.biblemap.put("b66_9_17", new hebsuperobject(66,9,17,"וכן ראיתי במראה את הסוסים ורכביהם אשר שרינותיהם כעין אש ותכלת וגפרית וראשי הסוסים כראשי אריות ותצא מפיהם אש וקיטור וגפרית׃"));
hebsuperobject.biblemap.put("b66_9_18", new hebsuperobject(66,9,18,"ותומת שלישית בני אדם בשלש האלה באש ובקיטור ובגפרית היצאות מפיהם׃"));
hebsuperobject.biblemap.put("b66_9_19", new hebsuperobject(66,9,19,"כי כח הסוסים בפיהם כי זנבותם דומים לנחשים ויש להם ראשים ובהם ישחיתו׃"));
hebsuperobject.biblemap.put("b66_9_20", new hebsuperobject(66,9,20,"ושאר בני אדם אשר לא נהרגו במגפות האלה בכל זאת לא שבו ממעשי ידיהם מהשתחות עוד לשדים ולעצבי זהב וכסף ונחשת ואבן ועץ אשר לא יראו ולא ישמעו ולא יהלכו׃"));
hebsuperobject.biblemap.put("b66_9_21", new hebsuperobject(66,9,21,"ולא שבו מדרכם לרצח ולכשף ולזנות ולגנב׃"));
hebsuperobject.biblemap.put("b66_10_1", new hebsuperobject(66,10,1,"וארא מלאך אחר אביר יורד מן השמים והוא עטה ענן ועל ראשו כמראה קשת הענן ופניו כשמש ורגליו כעמודי אש׃"));
hebsuperobject.biblemap.put("b66_10_2", new hebsuperobject(66,10,2,"ובידו ספר קטן פתוח וישם את רגל ימינו על הים ואת שמאלו על הארץ׃"));
hebsuperobject.biblemap.put("b66_10_3", new hebsuperobject(66,10,3,"ויקרא בקול גדול כאשר ישאג האריה ובקראו דברו שבעת הרעמים בקולתיהם׃"));
hebsuperobject.biblemap.put("b66_10_4", new hebsuperobject(66,10,4,"וכדבר שבעת הרעמים בקולתיהם חפצתי לכתב ואשמע קול מן השמים לאמר חתום את אשר דברו שבעת הרעמים ואל תכתב זאת׃"));
hebsuperobject.biblemap.put("b66_10_5", new hebsuperobject(66,10,5,"והמלאך אשר ראיתיו עמד על הים ועל הארץ הרים ידו אל השמים׃"));
hebsuperobject.biblemap.put("b66_10_6", new hebsuperobject(66,10,6,"וישבע בחי עולמי העולמים אשר ברא את השמים וכל אשר בהם והארץ וכל אשר בה והים וכל אשר בו כי לא יהיה עוד זמן׃"));
hebsuperobject.biblemap.put("b66_10_7", new hebsuperobject(66,10,7,"אך בימי קול המלאך השביעי בעת עמדו לתקוע ונשלם סוד האלהים כאשר בשר את עבדיו הנביאים׃"));
hebsuperobject.biblemap.put("b66_10_8", new hebsuperobject(66,10,8,"והקול אשר שמעתי מן השמים שב לדבר אלי לאמר לך וקח את הספר הקטן הפתוח אשר ביד המלאך העמד על הים ועל הארץ׃"));
hebsuperobject.biblemap.put("b66_10_9", new hebsuperobject(66,10,9,"ואבא אל המלאך ואמר לו תנה לי את הספר ויאמר אלי קח ואכל אתו וימר לבטנך אבל בפיך יהיה מתוק כדבש׃"));
hebsuperobject.biblemap.put("b66_10_10", new hebsuperobject(66,10,10,"ואקח את הספר מיד המלאך ואכלהו ויהי בפי כדבש למתוק ואחרי אכלי אתו וימלא בטני מרורים׃"));
hebsuperobject.biblemap.put("b66_10_11", new hebsuperobject(66,10,11,"ויאמר אלי עליך לשוב להנבא עוד על עמים וגוים ולשנות ומלכים רבים׃"));
hebsuperobject.biblemap.put("b66_11_1", new hebsuperobject(66,11,1,"וינתן לי קנה דומה למטה ויעמד המלאך ויאמר קום ומד את היכל יהוה ואת המזבח ואת המשתחוים בו׃"));
hebsuperobject.biblemap.put("b66_11_2", new hebsuperobject(66,11,2,"ואת החצר אשר לפנימה להיכל השלך חוצה ואל תמדנה כי נתנה לגוים ורמסו את העיר הקדשה ארבעים ושנים חדשים׃"));
hebsuperobject.biblemap.put("b66_11_3", new hebsuperobject(66,11,3,"ואתן לשני עדי ונבאו ימים אלף ומאתים וששים מתכסים בשקים׃"));
hebsuperobject.biblemap.put("b66_11_4", new hebsuperobject(66,11,4,"אלה הם שני הזיתים ושתי המנרות העמדים לפני אלהי הארץ׃"));
hebsuperobject.biblemap.put("b66_11_5", new hebsuperobject(66,11,5,"וכי יבקש איש להרע להם תצא אש מפיהם ואכלה את איביהם וכן כל המבקש להרע להם מות יומת׃"));
hebsuperobject.biblemap.put("b66_11_6", new hebsuperobject(66,11,6,"ולהם השלטן לעצר את השמים ולא יהיה מטר בימי נבואתם וישלטו על המים להפכם לדם ועל הארץ להכתה בכל נגע מדי יחפצו׃"));
hebsuperobject.biblemap.put("b66_11_7", new hebsuperobject(66,11,7,"ואחרי השלימם עדותם החיה העלה מן התהום תעשה עמהם מלחמה ותוכל להם והרגתם׃"));
hebsuperobject.biblemap.put("b66_11_8", new hebsuperobject(66,11,8,"והיתה נבלתם ברהוב העיר הגדולה הנקראת כפי הרוח בשם סדום ומצרים אשר שם נצלב גם אדנינו׃"));
hebsuperobject.biblemap.put("b66_11_9", new hebsuperobject(66,11,9,"ורבים מן העמים והמשפחות והלשנות והגוים יראו את נבלתם ימים שלשה וחצי ולא יתנו את גויתם לשום בקברים׃"));
hebsuperobject.biblemap.put("b66_11_10", new hebsuperobject(66,11,10,"וישבי הארץ ישמחו עליהם ויעלזו וישלחו מנות זה לזה כי שני הנביאים האלה הכאיבו את ישבי הארץ׃"));
hebsuperobject.biblemap.put("b66_11_11", new hebsuperobject(66,11,11,"ויהי אחרי ימים שלשה וחצי ותבא בהם רוח חיים מאת האלהים ויעמדו על רגליהם ואימה גדולה נפלה על כל ראיהם׃"));
hebsuperobject.biblemap.put("b66_11_12", new hebsuperobject(66,11,12,"וישמעו קול גדול מן השמים מדבר אליהם לאמר עלו הנה ויעלו בענן השמימה ושנאיהם ראים אתם׃"));
hebsuperobject.biblemap.put("b66_11_13", new hebsuperobject(66,11,13,"ובשעה ההיא היה רעש גדול ותפל עשירית העיר ושבעת אלפים שמות בני אדם נהרגו ברעש והנשארים רעדה אחזתם ויתנו כבוד לאלהי השמים׃"));
hebsuperobject.biblemap.put("b66_11_14", new hebsuperobject(66,11,14,"הצרה השנית חלפה הלכה לה והנה הצרה השלישית מהרה תבוא׃"));
hebsuperobject.biblemap.put("b66_11_15", new hebsuperobject(66,11,15,"והמלאך השביעי תקע בשופר ויהי קלות גדולים בשמים ויאמרו הנה ממלכת העולם היתה לאדיניו ולמשיחו והוא ימלך לעולמי עולמים׃"));
hebsuperobject.biblemap.put("b66_11_16", new hebsuperobject(66,11,16,"ועשרים וארבעה הזקנים הישבים לפני האלהים על כסאותם נפלו על פניהם וישתחוו לאלהים׃"));
hebsuperobject.biblemap.put("b66_11_17", new hebsuperobject(66,11,17,"ויאמרו מודים אנחנו לך יהוה אלהים צבאות ההוה והיה ויבוא כי לבשת עזך הגדול ותמלך׃"));
hebsuperobject.biblemap.put("b66_11_18", new hebsuperobject(66,11,18,"והגוים קצפו ויבא קצפך ועת המתים להשפט ולתת שכר לעבדיך הנביאים ולקדשים וליראי שמך למקטנם ועד גדולם ולהשחית את משחיתי הארץ׃"));
hebsuperobject.biblemap.put("b66_11_19", new hebsuperobject(66,11,19,"ויפתח היכל יהוה בשמים וירא ארון בריתו בהיכל ויהי ברקים וקלות ורעמים ורעש וברד כבד׃"));
hebsuperobject.biblemap.put("b66_12_1", new hebsuperobject(66,12,1,"ואות גדולה נראתה בשמים אשה אשר השמש לבושה והירח תחת רגליה ועל ראשה עטרת שנים עשר כוכבים׃"));
hebsuperobject.biblemap.put("b66_12_2", new hebsuperobject(66,12,2,"והיא הרה ותזעק בחבליה ותקש בלדתה׃"));
hebsuperobject.biblemap.put("b66_12_3", new hebsuperobject(66,12,3,"ותרא אות אחרת בשמים והנה תנין גדול אדם כאש ולו שבעה ראשים ועשר קרנים ועל ראשיו שבעה כתרים׃"));
hebsuperobject.biblemap.put("b66_12_4", new hebsuperobject(66,12,4,"וזנבו סחב מן השמים שלישית הכוכבים וישליכם ארצה ויתיצב התנין לפני האשה החלה ללדת למען בלע את בנה בלדתה׃"));
hebsuperobject.biblemap.put("b66_12_5", new hebsuperobject(66,12,5,"ותלד בן זכר העתיד לרעות כל הגוים בשבט ברזל וילקח בנה אל האלהים ואל כסאו׃"));
hebsuperobject.biblemap.put("b66_12_6", new hebsuperobject(66,12,6,"והאשה ברחה המדברה אשר שם הוכן לה מקום מאת אלהים למען יכלכלוה שם ימים אלף ומאתים וששים׃"));
hebsuperobject.biblemap.put("b66_12_7", new hebsuperobject(66,12,7,"ותהי מלחמה בשמים מיכאל ומלאכיו נלחמים בתנין והתנין נלחם ומלאכיו׃"));
hebsuperobject.biblemap.put("b66_12_8", new hebsuperobject(66,12,8,"ולא התחזקו וגם מקומם לא נמצא עוד בשמים׃"));
hebsuperobject.biblemap.put("b66_12_9", new hebsuperobject(66,12,9,"ויוטל התנין הגדול הנחש הקדמוני אשר נקרא שמו מלשין ושטן המדיח תבל כלה הוא הוטל ארצה ומלאכיו עמו הוטלו׃"));
hebsuperobject.biblemap.put("b66_12_10", new hebsuperobject(66,12,10,"ואשמע קול גדול בשמים ויאמר עתה באה ישועת אלהינו ועזו ומלכותו וממשלת משיחו כי הורד שוטן אחינו העמד לשטנם לפני אלהינו יומם ולילה׃"));
hebsuperobject.biblemap.put("b66_12_11", new hebsuperobject(66,12,11,"והם נצחהו למען דם השה ולמען דבר עדותם ולא אהבו את נפשם עד למות׃"));
hebsuperobject.biblemap.put("b66_12_12", new hebsuperobject(66,12,12,"רנו על זאת שמים ושכניהם אוי לישבי ארץ וים כי ירד אליכם המלשין בחמה גדולה מדעתו כי קצרה עתו׃"));
hebsuperobject.biblemap.put("b66_12_13", new hebsuperobject(66,12,13,"ויהי כאשר ראה התנין כי הוטל ארצה וירדף את האשה אשר ילדה את הזכר׃"));
hebsuperobject.biblemap.put("b66_12_14", new hebsuperobject(66,12,14,"ויתנו לאשה שתי כנפי הנשר הגדול לעוף המדברה אל מקומה אשר תכלכל שם מועד מועדים וחצי מפני הנחש׃"));
hebsuperobject.biblemap.put("b66_12_15", new hebsuperobject(66,12,15,"וישלח הנחש נהר מים מפיו אחרי האשה לשטפה בנהר׃"));
hebsuperobject.biblemap.put("b66_12_16", new hebsuperobject(66,12,16,"ותעזר הארץ את האשה ותפתח הארץ את פיה ותבלע את הנהר אשר שלח התנין מפיהו׃"));
hebsuperobject.biblemap.put("b66_12_17", new hebsuperobject(66,12,17,"ויקצף התנין על האשה וילך לעשות מלחמה עם יתר זרעה השמרים פקודי אלהים ואשר להם עדות ישוע׃"));
hebsuperobject.biblemap.put("b66_13_1", new hebsuperobject(66,13,1,"ואהי עמד על חול הים וארא חיה עלה מן הים ולה שבעה ראשים ועשר קרנים ועל קרניה עשרה כתרים ועל ראשיה שם גדופים׃"));
hebsuperobject.biblemap.put("b66_13_2", new hebsuperobject(66,13,2,"והחיה אשר ראיתי מראה כנמר ורגליה כרגלי דב ופיה כפי אריה ויתן לה התנין את כחו ואת כסאו וממשל רב׃"));
hebsuperobject.biblemap.put("b66_13_3", new hebsuperobject(66,13,3,"וארא והנה אחד מראשיה כפצוע עד מות ומכת מות אשר לו נרפאה ותשתומם כל הארץ אחרי החיה׃"));
hebsuperobject.biblemap.put("b66_13_4", new hebsuperobject(66,13,4,"וישתחוו לתנין אשר נתן ממשלה לחיה וישתחוו לחיה ויאמרו מי ידמה לחיה ומי יוכל להלחם אתה׃"));
hebsuperobject.biblemap.put("b66_13_5", new hebsuperobject(66,13,5,"וינתן לה פה ממלל גדלות ונאצות ושלטן נתן לה להלחם ארבעים ושנים חדשים׃"));
hebsuperobject.biblemap.put("b66_13_6", new hebsuperobject(66,13,6,"ותפתח את פיה לנאצה אל האלהים ותנאץ את שמו ואת משכנו ואת החנים בשמים׃"));
hebsuperobject.biblemap.put("b66_13_7", new hebsuperobject(66,13,7,"וינתן לה לעשות מלחמה עם הקדשים ולנצחם ותנתן לה ממשלה על כל משפחה ועם ולשון וגוי׃"));
hebsuperobject.biblemap.put("b66_13_8", new hebsuperobject(66,13,8,"וישתחוו לה כל ישבי הארץ אשר לא נכתבו שמותם בספר החיים אשר לשה הטבוח מיום הוסד תבל׃"));
hebsuperobject.biblemap.put("b66_13_9", new hebsuperobject(66,13,9,"כל אשר אזן לו ישמע׃"));
hebsuperobject.biblemap.put("b66_13_10", new hebsuperobject(66,13,10,"כל המוליך לשבי ילך בשבי וכל ההרג בחרב הרג יהרג בחרב בזה סבלנות ואמונת הקדשים׃"));
hebsuperobject.biblemap.put("b66_13_11", new hebsuperobject(66,13,11,"וארא חיה אחרת עלה מן האדמה ולה קרנים כקרני שה ומדברת כתנין׃"));
hebsuperobject.biblemap.put("b66_13_12", new hebsuperobject(66,13,12,"והיא עשה כל פקודי החיה הראשונה בפניה ומביאה את הארץ וישביה להשתחות לחיה הראשונה אשר נרפאה מכת מות אשר לה׃"));
hebsuperobject.biblemap.put("b66_13_13", new hebsuperobject(66,13,13,"ונתנת אותות גדלות וגם אש מן השמים תוריד ארצה לעיני בני אדם׃"));
