hebsuperobject.biblemap.put("b66_13_15", new hebsuperobject(66,13,15,"וינתן לה לתת רוח בצלם החיה למען דבר ידבר צלם החיה ועשתה כי כל אשר אימן משתחוים לצלם החיה מות יומתו׃"));
hebsuperobject.biblemap.put("b66_13_16", new hebsuperobject(66,13,16,"ותעש כי כלם למקטן ועד גדול אם אביון ואם עשיר גם בני החרים גם העבדים יתוו תו על די ימינם או על מצחתם׃"));
hebsuperobject.biblemap.put("b66_13_17", new hebsuperobject(66,13,17,"וכי לא יוכל איש לקנות או למכר כי אם בהיות עליו תו החיה או שמה או מספר שמה׃"));
