hebsuperobject.biblemap.put("b66_14_1", new hebsuperobject(66,14,1,"וארא והנה שה עמד על הר ציון ועמו מאת אלף וארבעים וארבעה אלפים הנשאים שם אביו כתוב על מצחותם׃"));
hebsuperobject.biblemap.put("b66_14_2", new hebsuperobject(66,14,2,"ואשמע קול מן השמים כקול מים רבים וכקול רעם גדול ואשמע קול תפשי כנור המנגנים בכנורותיהם׃"));
hebsuperobject.biblemap.put("b66_14_3", new hebsuperobject(66,14,3,"וישירו כשיר חדש לפני הכסא ולפני ארבע החיות ולפני הזקנים ואין איש אשר יכל ללמד את השיר זולתי מאת האלף וארבעים וארבעת האלפים ההם אשר נקנו מן הארץ׃"));
hebsuperobject.biblemap.put("b66_14_4", new hebsuperobject(66,14,4,"אלה הם אשר לא נגאלו בנשים כי בתולות המה אלה הם ההלכים אחרי השה אל כל אשר ילך אלה נקנו מתוך בני האדם לראשית בכורים לאלהים ולשה׃"));
hebsuperobject.biblemap.put("b66_14_5", new hebsuperobject(66,14,5,"ותרמית לא נמצאה בפיהם כי תמימים המה לפני כסא האלהים׃"));
hebsuperobject.biblemap.put("b66_14_6", new hebsuperobject(66,14,6,"וארא מלאך אחר מעופף במרום הרקיע אשר היה לו בשורת עולם לבשר את ישבי הארץ ואת כל גוי ונשפחה ולשון ועם׃"));
hebsuperobject.biblemap.put("b66_14_7", new hebsuperobject(66,14,7,"ויאמר בקול גדול יראו את האלהים והבו לו כבוד כי באה עת משפטו והשתחוו לעשה שמים וארץ את הים ומעינות המים׃"));
hebsuperobject.biblemap.put("b66_14_8", new hebsuperobject(66,14,8,"ומלאך אחר בא אחריו ויאמר נפלה נפלה בבל העיר הגדולה כי השקתה כל הגוים מיין חמת תזנתה׃"));
hebsuperobject.biblemap.put("b66_14_9", new hebsuperobject(66,14,9,"ומלאך שלישי בא אחריהם ויאמר בקול גדול כל אשר ישתחוה לפני החיה ולפני צלמה ונשא את תוה על מצחו או על ידו׃"));
hebsuperobject.biblemap.put("b66_14_10", new hebsuperobject(66,14,10,"גם הוא שתה ישתה מיין חמת אלהים יין בלתי מהול הנמסך בכוס זעמו ויענה באש וגפרית לפני המלאכים הקדשים ולפני השה׃"));
hebsuperobject.biblemap.put("b66_14_11", new hebsuperobject(66,14,11,"ועשן ענוים יעלה לעולמי עד ולא ימצאו מנוחה יומם ולילה המשתחוים לחיה ולצלמה ואשר ישא את תו שמה׃"));
hebsuperobject.biblemap.put("b66_14_12", new hebsuperobject(66,14,12,"בזה סבלנות הקדשים בזה השמרים את מצות האלהים ואת אמונת ישוע׃"));
hebsuperobject.biblemap.put("b66_14_13", new hebsuperobject(66,14,13,"ואשמע קול מן השמים מדבר אלי כתב אשרי המתים אשר ימותו באדון מעתה אמנם כן אמר הרוח למען ינוחו מעמלם ומעשיהם הלך ילכו אחריהם׃"));
