hebsuperobject.biblemap.put("b66_14_19", new hebsuperobject(66,14,19,"וינף המלאך את מגלו על הארץ ויבצר את אשכלת גפן הארץ וישליכם בגת חמת אלהים הגדולה׃"));
hebsuperobject.biblemap.put("b66_14_20", new hebsuperobject(66,14,20,"ותדרך הגת מחוץ לעיר ויצא דם מן הגת עד רסני הסוסים דרך אלף ושש מאות ריס׃"));
hebsuperobject.biblemap.put("b66_15_1", new hebsuperobject(66,15,1,"וארא אות אחרת בשמים גדולה ונפלאה שבעה מלאכים הנשאים את שבע המכות האחרנות כי בהן כלה זעם אלהים׃"));
hebsuperobject.biblemap.put("b66_15_2", new hebsuperobject(66,15,2,"וארא כים זכוכית בלול באש ואת המתגברים על החיה ועל צלמה ועל תוה ועל מספר שמה עמדים על ים הזכוכית וכנרות אלהים בידיהם׃"));
hebsuperobject.biblemap.put("b66_15_3", new hebsuperobject(66,15,3,"וישירו את שירת משה עבד אלהים ושירת השה לאמר גדולים מעשיך ונפלאים יהוה אלהים צבאות צדק ואמת דרכיך מלך הגוים׃"));
hebsuperobject.biblemap.put("b66_15_4", new hebsuperobject(66,15,4,"מי לא ייראך יהוה ולא יכבד את שמך כי קדוש אתה לבדך כי כל הגוים יבאו וישתחוו לפניך כי נגלו משפטי צדקך׃"));
hebsuperobject.biblemap.put("b66_15_5", new hebsuperobject(66,15,5,"ואחרי כן ראיתי והנה נפתח היכל משכן העדות בשמים׃"));
hebsuperobject.biblemap.put("b66_15_6", new hebsuperobject(66,15,6,"ויצאו מן ההיכל שבעה המלאכים הנשאים את שבע המכות מלבשים בד טהור וצח וחגורים אזורי זהב על לבביהם׃"));
hebsuperobject.biblemap.put("b66_15_7", new hebsuperobject(66,15,7,"ואחת מארבע החיות נתנה אל שבעת המלאכים שבע קערת זהב מלאות חמת האלהים החי לעולמי העולמים׃"));
