hebsuperobject.biblemap.put("b66_16_18", new hebsuperobject(66,16,18,"ויהיו קלות ורעמים וברקים ויהי רעש גדול אשר לא היה כמהו למן היות אדם על הארץ רעש כזה גדול עד מאד׃"));
hebsuperobject.biblemap.put("b66_16_19", new hebsuperobject(66,16,19,"והעיר הגדולה נחלקה לשלשה חלקים ותפלנה ערי הגוים ותזכר בבל הגדולה לפני אלהים לתת לה כוס יין חמת אפו׃"));
hebsuperobject.biblemap.put("b66_16_20", new hebsuperobject(66,16,20,"וינס כל אי וההרים לא נמצאו׃"));
