hebsuperobject.biblemap.put("b66_17_1", new hebsuperobject(66,17,1,"ויבא אחד מן שבעה המלאכים הנשאים שבע הקערות וידבר אלי לאמר בא ואראך את משפט הזונה הגדולה הישבת על מים רבים׃"));
hebsuperobject.biblemap.put("b66_17_2", new hebsuperobject(66,17,2,"אשר זנו אתה מלכי האדמה וישכרו שכני תבל מיין תזנותה׃"));
