hebsuperobject.biblemap.put("b66_17_7", new hebsuperobject(66,17,7,"ויאמר אלי המלאך למה זה השתוממת אני אמר לך את סוד האשה והחיה הנשאת אתה בעלת שבעת הראשים ועשרת הקרנים׃"));
hebsuperobject.biblemap.put("b66_17_8", new hebsuperobject(66,17,8,"החיה אשר ראיתה היתה ואיננה ועתידה לעלות מן התהום וללכת לאבדון וישבי הארץ אשר שמם איננו נכתב בספר החיים מיום הוסד תבל ישתוממו בראתם את החיה אשר היתה ואיננה ותבוא׃"));
hebsuperobject.biblemap.put("b66_17_9", new hebsuperobject(66,17,9,"בזה להבין לאשר לו חכמה שבעת הראשים שבעת הרים המה אשר האשה ישבת עליהם׃"));
