hebsuperobject.biblemap.put("b66_17_15", new hebsuperobject(66,17,15,"ויאמר אלי המים ההם אשר ראית אשר הזונה ישבת עליהם עמים והמנים המה וגוים ולשנות׃"));
hebsuperobject.biblemap.put("b66_17_16", new hebsuperobject(66,17,16,"ועשר הקרנים אשר ראית והחיה המה ישנאו את הזונה ועשוה גלמודה וערמה ואכלו את בשרה ואתה ישרפו באש׃"));
hebsuperobject.biblemap.put("b66_17_17", new hebsuperobject(66,17,17,"כי האלהים נתן בלבם לעשות את עצתו ולעשות עצה אחת ולתת את ממשלתם לחיה עד כי ישלמו דברי האלהים׃"));
hebsuperobject.biblemap.put("b66_17_18", new hebsuperobject(66,17,18,"והאשה אשר ראית היא העיר הגדולה אשר היא נברת ממלכות הארץ׃"));
hebsuperobject.biblemap.put("b66_18_1", new hebsuperobject(66,18,1,"אחרי כן ראיתי מלאך אחר יורד מן השמים אשר לו שלטן גדול והארץ האירה מכבדו׃"));
hebsuperobject.biblemap.put("b66_18_2", new hebsuperobject(66,18,2,"ויקרא בקול עז לאמר נפלה נפלה בבל הגדולה ותהי נוה שערים ומשמר לכל רוח טמא ומשמר לכל עוף טמא ונמאס׃"));
hebsuperobject.biblemap.put("b66_18_3", new hebsuperobject(66,18,3,"כי מיין חמת זנותה שתו כל הגוים ומלכי ארץ זנו עמה וסחרי ארץ משפעת תענגיה העשירו׃"));
hebsuperobject.biblemap.put("b66_18_4", new hebsuperobject(66,18,4,"ואשמע קול אחר מן השמים האמר צאו ממנה עמי פן תתחברו אל חטאתיה ופן תקחו ממכותיה׃"));
