hebsuperobject.biblemap.put("b66_21_19", new hebsuperobject(66,21,19,"ומוסדות חומת העיר מרבצות בכל אבני חפץ המוסד הראשון ישפה השני ספיר השלישי שבו הרביעי ברקת׃"));
hebsuperobject.biblemap.put("b66_21_20", new hebsuperobject(66,21,20,"החמישי יהלם הששי אדם השביעי תרשיש השמיני שהם התשיעי פטדה העשירי נפך אחד העשר לשם שנים העשר אחלמה׃"));
hebsuperobject.biblemap.put("b66_21_21", new hebsuperobject(66,21,21,"ושנים עשר השערים הם שתים עשרה מרגליות כל שער ושער מרגלית אחת ורחוב העיר זהב מופז כזכוכית בהירה׃"));
hebsuperobject.biblemap.put("b66_21_22", new hebsuperobject(66,21,22,"והיכל לא ראיתי בה כי יהוה אלהים צבאות היכלה הוא והשה׃"));
hebsuperobject.biblemap.put("b66_21_23", new hebsuperobject(66,21,23,"והעיר איננה צריכה לאור השמש ולנגה הירח כי כבוד אלהים האיר לה ונרה הוא השה׃"));
hebsuperobject.biblemap.put("b66_21_24", new hebsuperobject(66,21,24,"והגוים ילכו לאורה ומלכי ארץ מביאים כבודם ותפארתם אליה׃"));
hebsuperobject.biblemap.put("b66_21_25", new hebsuperobject(66,21,25,"ושעריה יומם לא יסגרו כי לילה לא יהיה שם׃"));
hebsuperobject.biblemap.put("b66_21_26", new hebsuperobject(66,21,26,"והביאו בה כבוד הגוים ותפארתם׃"));
hebsuperobject.biblemap.put("b66_21_27", new hebsuperobject(66,21,27,"ולא יבוא בה כל טמא ועשה תועבה ושקר כי אם הכתובים בספר החיים של השה׃"));
hebsuperobject.biblemap.put("b66_22_1", new hebsuperobject(66,22,1,"ויראני נחל של מים חיים זך מבהיק כעין הקרח יצא מכסא האלהים והשה׃"));
hebsuperobject.biblemap.put("b66_22_2", new hebsuperobject(66,22,2,"ובתוך רחוב העיר ואל שפת הנחל מזה ומזה עץ חיים עשה פרי שנים עשר כי מדי חדש בחדשו יתן את פריו ועלה העץ לתרופת הגוים׃"));
hebsuperobject.biblemap.put("b66_22_3", new hebsuperobject(66,22,3,"וכל חרם לא יהיה עוד וכסא האלהים והשה יהיה בה ועבדיו ישרתהו׃"));
hebsuperobject.biblemap.put("b66_22_4", new hebsuperobject(66,22,4,"והמה יראו את פניו ושמו על מצחותם׃"));
hebsuperobject.biblemap.put("b66_22_5", new hebsuperobject(66,22,5,"ולילה לא יהיה עוד ולא יצטרכו עוד לאור נר ולאור שמש כי יהוה אלהים הוא יאיר להם וימלכו עד עולמי עולמים׃"));
hebsuperobject.biblemap.put("b66_22_6", new hebsuperobject(66,22,6,"ויאמר אלי הדברים האלה אמתים ונאמנים הם ויהוה אלהי הנביאים הקדשים שלח את מלאכו להראות את עבדיו את אשר היה יהיה במהרה׃"));
hebsuperobject.biblemap.put("b66_22_7", new hebsuperobject(66,22,7,"הנני בא מהר אשרי השמר את דברי נבואת הספר הזה׃"));
hebsuperobject.biblemap.put("b66_22_8", new hebsuperobject(66,22,8,"ואני יוחנן הוא הראה אלה ושמעם ויהי כשמעי וכראותי ואפל לפני רגלי המלאך אשר הראני את אלה להשתחות לו׃"));
hebsuperobject.biblemap.put("b66_22_9", new hebsuperobject(66,22,9,"ויאמר אלי ראה אל תעשה זאת כי עבד כמוך אנכי וחבר לך ולאחיך הנביאים ולשמרים את דברי הספר הזה לאלהים השתחוה׃"));
hebsuperobject.biblemap.put("b66_22_10", new hebsuperobject(66,22,10,"ויאמר אלי אל תחתם את דברי נבואת הספר הזה כי קרוב המועד׃"));
hebsuperobject.biblemap.put("b66_22_11", new hebsuperobject(66,22,11,"החומס יוסיף לחמס והטמא יוסיף להטמא והצדיק יוסיף להצדק והקדוש יוסיף להתקדש׃"));
hebsuperobject.biblemap.put("b66_22_12", new hebsuperobject(66,22,12,"והנני בא מהר ושכרי אתי לשלם לכל איש כמעשהו׃"));
hebsuperobject.biblemap.put("b66_22_13", new hebsuperobject(66,22,13,"אני האלף והתו הראש והסוף הראשון והאחרון׃"));
hebsuperobject.biblemap.put("b66_22_14", new hebsuperobject(66,22,14,"אשרי העשים את מצותיו למען תהיה ממשלתם בעץ החיים ובאו העירה דרך השערים׃"));
hebsuperobject.biblemap.put("b66_22_15", new hebsuperobject(66,22,15,"ומחוץ לה הכלבים והמכשפים והזנים והמרצחים ועבדי האלילים וכל אהב שקר ועשהו׃"));
hebsuperobject.biblemap.put("b66_22_16", new hebsuperobject(66,22,16,"אני ישוע שלחתי את מלאכי להעיד לכם את אלה בפני הקהלות אנכי שרש דוד ותולדתו כוכב נגה השחר׃"));
hebsuperobject.biblemap.put("b66_22_17", new hebsuperobject(66,22,17,"והרוח והכלה אמרים בא והשמע יאמר בא והצמא יבוא והחפץ יקח מים חיים חנם׃"));
hebsuperobject.biblemap.put("b66_22_18", new hebsuperobject(66,22,18,"מעיד אני בכל השמע דברי נבואת הספר הזה אם יוסיף איש עליהם יוסיף עליו האלהים את המכות הכתובות בספר הזה׃"));
hebsuperobject.biblemap.put("b66_22_19", new hebsuperobject(66,22,19,"ואם יגרע איש מדברי ספר הנבואה הזאת יגרע האלהים את לחקו מעץ החיים ומעיר הקדש הכתובים בספר הזה׃"));
hebsuperobject.biblemap.put("b66_22_20", new hebsuperobject(66,22,20,"המעיד את אלה אמר אמנם כן אני בא מהר אמן באה נא האדון ישוע׃"));
hebsuperobject.biblemap.put("b66_22_21", new hebsuperobject(66,22,21,"חסד אדנינו ישוע המשיח עם כלכם כל הקדושים אמן׃"));

