hebsuperobject.biblemap.put("b7_10_16", new hebsuperobject(7,10,16,"ויסירו את אלהי הנכר מקרבם ויעבדו את יהוה ותקצר נפשו בעמל ישראל׃"));
hebsuperobject.biblemap.put("b7_10_17", new hebsuperobject(7,10,17,"ויצעקו בני עמון ויחנו בגלעד ויאספו בני ישראל ויחנו במצפה׃"));
hebsuperobject.biblemap.put("b7_10_18", new hebsuperobject(7,10,18,"ויאמרו העם שרי גלעד איש אל רעהו מי האיש אשר יחל להלחם בבני עמון יהיה לראש לכל ישבי גלעד׃"));
hebsuperobject.biblemap.put("b7_11_1", new hebsuperobject(7,11,1,"ויפתח הגלעדי היה גבור חיל והוא בן אשה זונה ויולד גלעד את יפתח׃"));
hebsuperobject.biblemap.put("b7_11_2", new hebsuperobject(7,11,2,"ותלד אשת גלעד לו בנים ויגדלו בני האשה ויגרשו את יפתח ויאמרו לו לא תנחל בבית אבינו כי בן אשה אחרת אתה׃"));
hebsuperobject.biblemap.put("b7_11_3", new hebsuperobject(7,11,3,"ויברח יפתח מפני אחיו וישב בארץ טוב ויתלקטו אל יפתח אנשים ריקים ויצאו עמו׃"));
hebsuperobject.biblemap.put("b7_11_4", new hebsuperobject(7,11,4,"ויהי מימים וילחמו בני עמון עם ישראל׃"));
