hebsuperobject.biblemap.put("b7_11_8", new hebsuperobject(7,11,8,"ויאמרו זקני גלעד אל יפתח לכן עתה שבנו אליך והלכת עמנו ונלחמת בבני עמון והיית לנו לראש לכל ישבי גלעד׃"));
hebsuperobject.biblemap.put("b7_11_9", new hebsuperobject(7,11,9,"ויאמר יפתח אל זקני גלעד אם משיבים אתם אותי להלחם בבני עמון ונתן יהוה אותם לפני אנכי אהיה לכם לראש׃"));
