hebsuperobject.biblemap.put("b7_11_18", new hebsuperobject(7,11,18,"וילך במדבר ויסב את ארץ אדום ואת ארץ מואב ויבא ממזרח שמש לארץ מואב ויחנון בעבר ארנון ולא באו בגבול מואב כי ארנון גבול מואב׃"));
hebsuperobject.biblemap.put("b7_11_19", new hebsuperobject(7,11,19,"וישלח ישראל מלאכים אל סיחון מלך האמרי מלך חשבון ויאמר לו ישראל נעברה נא בארצך עד מקומי׃"));
hebsuperobject.biblemap.put("b7_11_20", new hebsuperobject(7,11,20,"ולא האמין סיחון את ישראל עבר בגבלו ויאסף סיחון את כל עמו ויחנו ביהצה וילחם עם ישראל׃"));
hebsuperobject.biblemap.put("b7_11_21", new hebsuperobject(7,11,21,"ויתן יהוה אלהי ישראל את סיחון ואת כל עמו ביד ישראל ויכום ויירש ישראל את כל ארץ האמרי יושב הארץ ההיא׃"));
hebsuperobject.biblemap.put("b7_11_22", new hebsuperobject(7,11,22,"ויירשו את כל גבול האמרי מארנון ועד היבק ומן המדבר ועד הירדן׃"));
