hebsuperobject.biblemap.put("b7_12_3", new hebsuperobject(7,12,3,"ואראה כי אינך מושיע ואשימה נפשי בכפי ואעברה אל בני עמון ויתנם יהוה בידי ולמה עליתם אלי היום הזה להלחם בי׃"));
hebsuperobject.biblemap.put("b7_12_4", new hebsuperobject(7,12,4,"ויקבץ יפתח את כל אנשי גלעד וילחם את אפרים ויכו אנשי גלעד את אפרים כי אמרו פליטי אפרים אתם גלעד בתוך אפרים בתוך מנשה׃"));
