hebsuperobject.biblemap.put("b7_13_6", new hebsuperobject(7,13,6,"ותבא האשה ותאמר לאישה לאמר איש האלהים בא אלי ומראהו כמראה מלאך האלהים נורא מאד ולא שאלתיהו אי מזה הוא ואת שמו לא הגיד לי׃"));
hebsuperobject.biblemap.put("b7_13_7", new hebsuperobject(7,13,7,"ויאמר לי הנך הרה וילדת בן ועתה אל תשתי יין ושכר ואל תאכלי כל טמאה כי נזיר אלהים יהיה הנער מן הבטן עד יום מותו׃"));
hebsuperobject.biblemap.put("b7_13_8", new hebsuperobject(7,13,8,"ויעתר מנוח אל יהוה ויאמר בי אדוני איש האלהים אשר שלחת יבוא נא עוד אלינו ויורנו מה נעשה לנער היולד׃"));
hebsuperobject.biblemap.put("b7_13_9", new hebsuperobject(7,13,9,"וישמע האלהים בקול מנוח ויבא מלאך האלהים עוד אל האשה והיא יושבת בשדה ומנוח אישה אין עמה׃"));
hebsuperobject.biblemap.put("b7_13_10", new hebsuperobject(7,13,10,"ותמהר האשה ותרץ ותגד לאישה ותאמר אליו הנה נראה אלי האיש אשר בא ביום אלי׃"));
