hebsuperobject.biblemap.put("b7_20_13", new hebsuperobject(7,20,13,"ועתה תנו את האנשים בני בליעל אשר בגבעה ונמיתם ונבערה רעה מישראל ולא אבו בנימן לשמע בקול אחיהם בני ישראל׃"));
hebsuperobject.biblemap.put("b7_20_14", new hebsuperobject(7,20,14,"ויאספו בני בנימן מן הערים הגבעתה לצאת למלחמה עם בני ישראל׃"));
