hebsuperobject.biblemap.put("b7_21_19", new hebsuperobject(7,21,19,"ויאמרו הנה חג יהוה בשלו מימים ימימה אשר מצפונה לבית אל מזרחה השמש למסלה העלה מבית אל שכמה ומנגב ללבונה׃"));
hebsuperobject.biblemap.put("b7_21_20", new hebsuperobject(7,21,20,"ויצו את בני בנימן לאמר לכו וארבתם בכרמים׃"));
