hebsuperobject.biblemap.put("b9_4_8", new hebsuperobject(9,4,8,"אוי לנו מי יצילנו מיד האלהים האדירים האלה אלה הם האלהים המכים את מצרים בכל מכה במדבר׃"));
hebsuperobject.biblemap.put("b9_4_9", new hebsuperobject(9,4,9,"התחזקו והיו לאנשים פלשתים פן תעבדו לעברים כאשר עבדו לכם והייתם לאנשים ונלחמתם׃"));
hebsuperobject.biblemap.put("b9_4_10", new hebsuperobject(9,4,10,"וילחמו פלשתים וינגף ישראל וינסו איש לאהליו ותהי המכה גדולה מאד ויפל מישראל שלשים אלף רגלי׃"));
hebsuperobject.biblemap.put("b9_4_11", new hebsuperobject(9,4,11,"וארון אלהים נלקח ושני בני עלי מתו חפני ופינחס׃"));
