hebsuperobject.biblemap.put("b9_4_17", new hebsuperobject(9,4,17,"ויען המבשר ויאמר נס ישראל לפני פלשתים וגם מגפה גדולה היתה בעם וגם שני בניך מתו חפני ופינחס וארון האלהים נלקחה׃"));
hebsuperobject.biblemap.put("b9_4_18", new hebsuperobject(9,4,18,"ויהי כהזכירו את ארון האלהים ויפל מעל הכסא אחרנית בעד יד השער ותשבר מפרקתו וימת כי זקן האיש וכבד והוא שפט את ישראל ארבעים שנה׃"));
hebsuperobject.biblemap.put("b9_4_19", new hebsuperobject(9,4,19,"וכלתו אשת פינחס הרה ללת ותשמע את השמעה אל הלקח ארון האלהים ומת חמיה ואישה ותכרע ותלד כי נהפכו עליה צריה׃"));
hebsuperobject.biblemap.put("b9_4_20", new hebsuperobject(9,4,20,"וכעת מותה ותדברנה הנצבות עליה אל תיראי כי בן ילדת ולא ענתה ולא שתה לבה׃"));
hebsuperobject.biblemap.put("b9_4_21", new hebsuperobject(9,4,21,"ותקרא לנער אי כבוד לאמר גלה כבוד מישראל אל הלקח ארון האלהים ואל חמיה ואישה׃"));
