hebsuperobject.biblemap.put("b9_6_9", new hebsuperobject(9,6,9,"וראיתם אם דרך גבולו יעלה בית שמש הוא עשה לנו את הרעה הגדולה הזאת ואם לא וידענו כי לא ידו נגעה בנו מקרה הוא היה לנו׃"));
hebsuperobject.biblemap.put("b9_6_10", new hebsuperobject(9,6,10,"ויעשו האנשים כן ויקחו שתי פרות עלות ויאסרום בעגלה ואת בניהם כלו בבית׃"));
hebsuperobject.biblemap.put("b9_6_11", new hebsuperobject(9,6,11,"וישמו את ארון יהוה אל העגלה ואת הארגז ואת עכברי הזהב ואת צלמי טחריהם׃"));
hebsuperobject.biblemap.put("b9_6_12", new hebsuperobject(9,6,12,"וישרנה הפרות בדרך על דרך בית שמש במסלה אחת הלכו הלך וגעו ולא סרו ימין ושמאול וסרני פלשתים הלכים אחריהם עד גבול בית שמש׃"));
