hebsuperobject.biblemap.put("b9_7_3", new hebsuperobject(9,7,3,"ויאמר שמואל אל כל בית ישראל לאמר אם בכל לבבכם אתם שבים אל יהוה הסירו את אלהי הנכר מתוככם והעשתרות והכינו לבבכם אל יהוה ועבדהו לבדו ויצל אתכם מיד פלשתים׃"));
hebsuperobject.biblemap.put("b9_7_4", new hebsuperobject(9,7,4,"ויסירו בני ישראל את הבעלים ואת העשתרת ויעבדו את יהוה לבדו׃"));
