hebsuperobject.biblemap.put("b9_7_7", new hebsuperobject(9,7,7,"וישמעו פלשתים כי התקבצו בני ישראל המצפתה ויעלו סרני פלשתים אל ישראל וישמעו בני ישראל ויראו מפני פלשתים׃"));
hebsuperobject.biblemap.put("b9_7_8", new hebsuperobject(9,7,8,"ויאמרו בני ישראל אל שמואל אל תחרש ממנו מזעק אל יהוה אלהינו וישענו מיד פלשתים׃"));
hebsuperobject.biblemap.put("b9_7_9", new hebsuperobject(9,7,9,"ויקח שמואל טלה חלב אחד ויעלה עולה כליל ליהוה ויזעק שמואל אל יהוה בעד ישראל ויענהו יהוה׃"));
hebsuperobject.biblemap.put("b9_7_10", new hebsuperobject(9,7,10,"ויהי שמואל מעלה העולה ופלשתים נגשו למלחמה בישראל וירעם יהוה בקול גדול ביום ההוא על פלשתים ויהמם וינגפו לפני ישראל׃"));
