hebsuperobject.biblemap.put("b9_10_26", new hebsuperobject(9,10,26,"וגם שאול הלך לביתו גבעתה וילכו עמו החיל אשר נגע אלהים בלבם׃"));
hebsuperobject.biblemap.put("b9_10_27", new hebsuperobject(9,10,27,"ובני בליעל אמרו מה ישענו זה ויבזהו ולא הביאו לו מנחה ויהי כמחריש׃"));
hebsuperobject.biblemap.put("b9_11_1", new hebsuperobject(9,11,1,"ויעל נחש העמוני ויחן על יבש גלעד ויאמרו כל אנשי יביש אל נחש כרת לנו ברית ונעבדך׃"));
hebsuperobject.biblemap.put("b9_11_2", new hebsuperobject(9,11,2,"ויאמר אליהם נחש העמוני בזאת אכרת לכם בנקור לכם כל עין ימין ושמתיה חרפה על כל ישראל׃"));
hebsuperobject.biblemap.put("b9_11_3", new hebsuperobject(9,11,3,"ויאמרו אליו זקני יביש הרף לנו שבעת ימים ונשלחה מלאכים בכל גבול ישראל ואם אין מושיע אתנו ויצאנו אליך׃"));
hebsuperobject.biblemap.put("b9_11_4", new hebsuperobject(9,11,4,"ויבאו המלאכים גבעת שאול וידברו הדברים באזני העם וישאו כל העם את קולם ויבכו׃"));
hebsuperobject.biblemap.put("b9_11_5", new hebsuperobject(9,11,5,"והנה שאול בא אחרי הבקר מן השדה ויאמר שאול מה לעם כי יבכו ויספרו לו את דברי אנשי יביש׃"));
hebsuperobject.biblemap.put("b9_11_6", new hebsuperobject(9,11,6,"ותצלח רוח אלהים על שאול בשמעו את הדברים האלה ויחר אפו מאד׃"));
hebsuperobject.biblemap.put("b9_11_7", new hebsuperobject(9,11,7,"ויקח צמד בקר וינתחהו וישלח בכל גבול ישראל ביד המלאכים לאמר אשר איננו יצא אחרי שאול ואחר שמואל כה יעשה לבקרו ויפל פחד יהוה על העם ויצאו כאיש אחד׃"));
hebsuperobject.biblemap.put("b9_11_8", new hebsuperobject(9,11,8,"ויפקדם בבזק ויהיו בני ישראל שלש מאות אלף ואיש יהודה שלשים אלף׃"));
hebsuperobject.biblemap.put("b9_11_9", new hebsuperobject(9,11,9,"ויאמרו למלאכים הבאים כה תאמרון לאיש יביש גלעד מחר תהיה לכם תשועה בחם השמש ויבאו המלאכים ויגידו לאנשי יביש וישמחו׃"));
hebsuperobject.biblemap.put("b9_11_10", new hebsuperobject(9,11,10,"ויאמרו אנשי יביש מחר נצא אליכם ועשיתם לנו ככל הטוב בעיניכם׃"));
hebsuperobject.biblemap.put("b9_11_11", new hebsuperobject(9,11,11,"ויהי ממחרת וישם שאול את העם שלשה ראשים ויבאו בתוך המחנה באשמרת הבקר ויכו את עמון עד חם היום ויהי הנשארים ויפצו ולא נשארו בם שנים יחד׃"));
hebsuperobject.biblemap.put("b9_11_12", new hebsuperobject(9,11,12,"ויאמר העם אל שמואל מי האמר שאול ימלך עלינו תנו האנשים ונמיתם׃"));
hebsuperobject.biblemap.put("b9_11_13", new hebsuperobject(9,11,13,"ויאמר שאול לא יומת איש ביום הזה כי היום עשה יהוה תשועה בישראל׃"));
hebsuperobject.biblemap.put("b9_11_14", new hebsuperobject(9,11,14,"ויאמר שמואל אל העם לכו ונלכה הגלגל ונחדש שם המלוכה׃"));
hebsuperobject.biblemap.put("b9_11_15", new hebsuperobject(9,11,15,"וילכו כל העם הגלגל וימלכו שם את שאול לפני יהוה בגלגל ויזבחו שם זבחים שלמים לפני יהוה וישמח שם שאול וכל אנשי ישראל עד מאד׃"));
hebsuperobject.biblemap.put("b9_12_1", new hebsuperobject(9,12,1,"ויאמר שמואל אל כל ישראל הנה שמעתי בקלכם לכל אשר אמרתם לי ואמליך עליכם מלך׃"));
hebsuperobject.biblemap.put("b9_12_2", new hebsuperobject(9,12,2,"ועתה הנה המלך מתהלך לפניכם ואני זקנתי ושבתי ובני הנם אתכם ואני התהלכתי לפניכם מנערי עד היום הזה׃"));
hebsuperobject.biblemap.put("b9_12_3", new hebsuperobject(9,12,3,"הנני ענו בי נגד יהוה ונגד משיחו את שור מי לקחתי וחמור מי לקחתי ואת מי עשקתי את מי רצותי ומיד מי לקחתי כפר ואעלים עיני בו ואשיב לכם׃"));
hebsuperobject.biblemap.put("b9_12_4", new hebsuperobject(9,12,4,"ויאמרו לא עשקתנו ולא רצותנו ולא לקחת מיד איש מאומה׃"));
hebsuperobject.biblemap.put("b9_12_5", new hebsuperobject(9,12,5,"ויאמר אליהם עד יהוה בכם ועד משיחו היום הזה כי לא מצאתם בידי מאומה ויאמר עד׃"));
hebsuperobject.biblemap.put("b9_12_6", new hebsuperobject(9,12,6,"ויאמר שמואל אל העם יהוה אשר עשה את משה ואת אהרן ואשר העלה את אבתיכם מארץ מצרים׃"));
hebsuperobject.biblemap.put("b9_12_7", new hebsuperobject(9,12,7,"ועתה התיצבו ואשפטה אתכם לפני יהוה את כל צדקות יהוה אשר עשה אתכם ואת אבותיכם׃"));
hebsuperobject.biblemap.put("b9_12_8", new hebsuperobject(9,12,8,"כאשר בא יעקב מצרים ויזעקו אבותיכם אל יהוה וישלח יהוה את משה ואת אהרן ויוציאו את אבתיכם ממצרים וישבום במקום הזה׃"));
hebsuperobject.biblemap.put("b9_12_9", new hebsuperobject(9,12,9,"וישכחו את יהוה אלהיהם וימכר אתם ביד סיסרא שר צבא חצור וביד פלשתים וביד מלך מואב וילחמו בם׃"));
hebsuperobject.biblemap.put("b9_12_10", new hebsuperobject(9,12,10,"ויזעקו אל יהוה ויאמר חטאנו כי עזבנו את יהוה ונעבד את הבעלים ואת העשתרות ועתה הצילנו מיד איבינו ונעבדך׃"));
hebsuperobject.biblemap.put("b9_12_11", new hebsuperobject(9,12,11,"וישלח יהוה את ירבעל ואת בדן ואת יפתח ואת שמואל ויצל אתכם מיד איביכם מסביב ותשבו בטח׃"));
hebsuperobject.biblemap.put("b9_12_12", new hebsuperobject(9,12,12,"ותראו כי נחש מלך בני עמון בא עליכם ותאמרו לי לא כי מלך ימלך עלינו ויהוה אלהיכם מלככם׃"));
hebsuperobject.biblemap.put("b9_12_13", new hebsuperobject(9,12,13,"ועתה הנה המלך אשר בחרתם אשר שאלתם והנה נתן יהוה עליכם מלך׃"));
hebsuperobject.biblemap.put("b9_12_14", new hebsuperobject(9,12,14,"אם תיראו את יהוה ועבדתם אתו ושמעתם בקלו ולא תמרו את פי יהוה והיתם גם אתם וגם המלך אשר מלך עליכם אחר יהוה אלהיכם׃"));
hebsuperobject.biblemap.put("b9_12_15", new hebsuperobject(9,12,15,"ואם לא תשמעו בקול יהוה ומריתם את פי יהוה והיתה יד יהוה בכם ובאבתיכם׃"));
hebsuperobject.biblemap.put("b9_12_16", new hebsuperobject(9,12,16,"גם עתה התיצבו וראו את הדבר הגדול הזה אשר יהוה עשה לעיניכם׃"));
hebsuperobject.biblemap.put("b9_12_17", new hebsuperobject(9,12,17,"הלוא קציר חטים היום אקרא אל יהוה ויתן קלות ומטר ודעו וראו כי רעתכם רבה אשר עשיתם בעיני יהוה לשאול לכם מלך׃"));
hebsuperobject.biblemap.put("b9_12_18", new hebsuperobject(9,12,18,"ויקרא שמואל אל יהוה ויתן יהוה קלת ומטר ביום ההוא ויירא כל העם מאד את יהוה ואת שמואל׃"));
hebsuperobject.biblemap.put("b9_12_19", new hebsuperobject(9,12,19,"ויאמרו כל העם אל שמואל התפלל בעד עבדיך אל יהוה אלהיך ואל נמות כי יספנו על כל חטאתינו רעה לשאל לנו מלך׃"));
hebsuperobject.biblemap.put("b9_12_20", new hebsuperobject(9,12,20,"ויאמר שמואל אל העם אל תיראו אתם עשיתם את כל הרעה הזאת אך אל תסורו מאחרי יהוה ועבדתם את יהוה בכל לבבכם׃"));
hebsuperobject.biblemap.put("b9_12_21", new hebsuperobject(9,12,21,"ולא תסורו כי אחרי התהו אשר לא יועילו ולא יצילו כי תהו המה׃"));
hebsuperobject.biblemap.put("b9_12_22", new hebsuperobject(9,12,22,"כי לא יטש יהוה את עמו בעבור שמו הגדול כי הואיל יהוה לעשות אתכם לו לעם׃"));
hebsuperobject.biblemap.put("b9_12_23", new hebsuperobject(9,12,23,"גם אנכי חלילה לי מחטא ליהוה מחדל להתפלל בעדכם והוריתי אתכם בדרך הטובה והישרה׃"));
hebsuperobject.biblemap.put("b9_12_24", new hebsuperobject(9,12,24,"אך יראו את יהוה ועבדתם אתו באמת בכל לבבכם כי ראו את אשר הגדל עמכם׃"));
hebsuperobject.biblemap.put("b9_12_25", new hebsuperobject(9,12,25,"ואם הרע תרעו גם אתם גם מלככם תספו׃"));
hebsuperobject.biblemap.put("b9_13_1", new hebsuperobject(9,13,1,"בן שנה שאול במלכו ושתי שנים מלך על ישראל׃"));
hebsuperobject.biblemap.put("b9_13_2", new hebsuperobject(9,13,2,"ויבחר לו שאול שלשת אלפים מישראל ויהיו עם שאול אלפים במכמש ובהר בית אל ואלף היו עם יונתן בגבעת בנימין ויתר העם שלח איש לאהליו׃"));
hebsuperobject.biblemap.put("b9_13_3", new hebsuperobject(9,13,3,"ויך יונתן את נציב פלשתים אשר בגבע וישמעו פלשתים ושאול תקע בשופר בכל הארץ לאמר ישמעו העברים׃"));
hebsuperobject.biblemap.put("b9_13_4", new hebsuperobject(9,13,4,"וכל ישראל שמעו לאמר הכה שאול את נציב פלשתים וגם נבאש ישראל בפלשתים ויצעקו העם אחרי שאול הגלגל׃"));
hebsuperobject.biblemap.put("b9_13_5", new hebsuperobject(9,13,5,"ופלשתים נאספו להלחם עם ישראל שלשים אלף רכב וששת אלפים פרשים ועם כחול אשר על שפת הים לרב ויעלו ויחנו במכמש קדמת בית און׃"));
hebsuperobject.biblemap.put("b9_13_6", new hebsuperobject(9,13,6,"ואיש ישראל ראו כי צר לו כי נגש העם ויתחבאו העם במערות ובחוחים ובסלעים ובצרחים ובברות׃"));
hebsuperobject.biblemap.put("b9_13_7", new hebsuperobject(9,13,7,"ועברים עברו את הירדן ארץ גד וגלעד ושאול עודנו בגלגל וכל העם חרדו אחריו׃"));
hebsuperobject.biblemap.put("b9_13_8", new hebsuperobject(9,13,8,"וייחל שבעת ימים למועד אשר שמואל ולא בא שמואל הגלגל ויפץ העם מעליו׃"));
hebsuperobject.biblemap.put("b9_13_9", new hebsuperobject(9,13,9,"ויאמר שאול הגשו אלי העלה והשלמים ויעל העלה׃"));
hebsuperobject.biblemap.put("b9_13_10", new hebsuperobject(9,13,10,"ויהי ככלתו להעלות העלה והנה שמואל בא ויצא שאול לקראתו לברכו׃"));
hebsuperobject.biblemap.put("b9_13_11", new hebsuperobject(9,13,11,"ויאמר שמואל מה עשית ויאמר שאול כי ראיתי כי נפץ העם מעלי ואתה לא באת למועד הימים ופלשתים נאספים מכמש׃"));
hebsuperobject.biblemap.put("b9_13_12", new hebsuperobject(9,13,12,"ואמר עתה ירדו פלשתים אלי הגלגל ופני יהוה לא חליתי ואתאפק ואעלה העלה׃"));
hebsuperobject.biblemap.put("b9_13_13", new hebsuperobject(9,13,13,"ויאמר שמואל אל שאול נסכלת לא שמרת את מצות יהוה אלהיך אשר צוך כי עתה הכין יהוה את ממלכתך אל ישראל עד עולם׃"));
hebsuperobject.biblemap.put("b9_13_14", new hebsuperobject(9,13,14,"ועתה ממלכתך לא תקום בקש יהוה לו איש כלבבו ויצוהו יהוה לנגיד על עמו כי לא שמרת את אשר צוך יהוה׃"));
hebsuperobject.biblemap.put("b9_13_15", new hebsuperobject(9,13,15,"ויקם שמואל ויעל מן הגלגל גבעת בנימן ויפקד שאול את העם הנמצאים עמו כשש מאות איש׃"));
hebsuperobject.biblemap.put("b9_13_16", new hebsuperobject(9,13,16,"ושאול ויונתן בנו והעם הנמצא עמם ישבים בגבע בנימן ופלשתים חנו במכמש׃"));
hebsuperobject.biblemap.put("b9_13_17", new hebsuperobject(9,13,17,"ויצא המשחית ממחנה פלשתים שלשה ראשים הראש אחד יפנה אל דרך עפרה אל ארץ שועל׃"));
hebsuperobject.biblemap.put("b9_13_18", new hebsuperobject(9,13,18,"והראש אחד יפנה דרך בית חרון והראש אחד יפנה דרך הגבול הנשקף על גי הצבעים המדברה׃"));
hebsuperobject.biblemap.put("b9_13_19", new hebsuperobject(9,13,19,"וחרש לא ימצא בכל ארץ ישראל כי אמר פלשתים פן יעשו העברים חרב או חנית׃"));
hebsuperobject.biblemap.put("b9_13_20", new hebsuperobject(9,13,20,"וירדו כל ישראל הפלשתים ללטוש איש את מחרשתו ואת אתו ואת קרדמו ואת מחרשתו׃"));
hebsuperobject.biblemap.put("b9_13_21", new hebsuperobject(9,13,21,"והיתה הפצירה פים למחרשת ולאתים ולשלש קלשון ולהקרדמים ולהציב הדרבן׃"));
hebsuperobject.biblemap.put("b9_13_22", new hebsuperobject(9,13,22,"והיה ביום מלחמת ולא נמצא חרב וחנית ביד כל העם אשר את שאול ואת יונתן ותמצא לשאול וליונתן בנו׃"));
hebsuperobject.biblemap.put("b9_13_23", new hebsuperobject(9,13,23,"ויצא מצב פלשתים אל מעבר מכמש׃"));
hebsuperobject.biblemap.put("b9_14_1", new hebsuperobject(9,14,1,"ויהי היום ויאמר יונתן בן שאול אל הנער נשא כליו לכה ונעברה אל מצב פלשתים אשר מעבר הלז ולאביו לא הגיד׃"));
hebsuperobject.biblemap.put("b9_14_2", new hebsuperobject(9,14,2,"ושאול יושב בקצה הגבעה תחת הרמון אשר במגרון והעם אשר עמו כשש מאות איש׃"));
hebsuperobject.biblemap.put("b9_14_3", new hebsuperobject(9,14,3,"ואחיה בן אחטוב אחי איכבוד בן פינחס בן עלי כהן יהוה בשלו נשא אפוד והעם לא ידע כי הלך יונתן׃"));
hebsuperobject.biblemap.put("b9_14_4", new hebsuperobject(9,14,4,"ובין המעברות אשר בקש יונתן לעבר על מצב פלשתים שן הסלע מהעבר מזה ושן הסלע מהעבר מזה ושם האחד בוצץ ושם האחד סנה׃"));
hebsuperobject.biblemap.put("b9_14_5", new hebsuperobject(9,14,5,"השן האחד מצוק מצפון מול מכמש והאחד מנגב מול גבע׃"));
hebsuperobject.biblemap.put("b9_14_6", new hebsuperobject(9,14,6,"ויאמר יהונתן אל הנער נשא כליו לכה ונעברה אל מצב הערלים האלה אולי יעשה יהוה לנו כי אין ליהוה מעצור להושיע ברב או במעט׃"));
hebsuperobject.biblemap.put("b9_14_7", new hebsuperobject(9,14,7,"ויאמר לו נשא כליו עשה כל אשר בלבבך נטה לך הנני עמך כלבבך׃"));
hebsuperobject.biblemap.put("b9_14_8", new hebsuperobject(9,14,8,"ויאמר יהונתן הנה אנחנו עברים אל האנשים ונגלינו אליהם׃"));
hebsuperobject.biblemap.put("b9_14_9", new hebsuperobject(9,14,9,"אם כה יאמרו אלינו דמו עד הגיענו אליכם ועמדנו תחתינו ולא נעלה אליהם׃"));
hebsuperobject.biblemap.put("b9_14_10", new hebsuperobject(9,14,10,"ואם כה יאמרו עלו עלינו ועלינו כי נתנם יהוה בידנו וזה לנו האות׃"));
