hebsuperobject.biblemap.put("b9_23_17", new hebsuperobject(9,23,17,"ויאמר אליו אל תירא כי לא תמצאך יד שאול אבי ואתה תמלך על ישראל ואנכי אהיה לך למשנה וגם שאול אבי ידע כן׃"));
hebsuperobject.biblemap.put("b9_23_18", new hebsuperobject(9,23,18,"ויכרתו שניהם ברית לפני יהוה וישב דוד בחרשה ויהונתן הלך לביתו׃"));
hebsuperobject.biblemap.put("b9_23_19", new hebsuperobject(9,23,19,"ויעלו זפים אל שאול הגבעתה לאמר הלוא דוד מסתתר עמנו במצדות בחרשה בגבעת החכילה אשר מימין הישימון׃"));
hebsuperobject.biblemap.put("b9_23_20", new hebsuperobject(9,23,20,"ועתה לכל אות נפשך המלך לרדת רד ולנו הסגירו ביד המלך׃"));
hebsuperobject.biblemap.put("b9_23_21", new hebsuperobject(9,23,21,"ויאמר שאול ברוכים אתם ליהוה כי חמלתם עלי׃"));
hebsuperobject.biblemap.put("b9_23_22", new hebsuperobject(9,23,22,"לכו נא הכינו עוד ודעו וראו את מקומו אשר תהיה רגלו מי ראהו שם כי אמר אלי ערום יערם הוא׃"));
hebsuperobject.biblemap.put("b9_23_23", new hebsuperobject(9,23,23,"וראו ודעו מכל המחבאים אשר יתחבא שם ושבתם אלי אל נכון והלכתי אתכם והיה אם ישנו בארץ וחפשתי אתו בכל אלפי יהודה׃"));
hebsuperobject.biblemap.put("b9_23_24", new hebsuperobject(9,23,24,"ויקומו וילכו זיפה לפני שאול ודוד ואנשיו במדבר מעון בערבה אל ימין הישימון׃"));
hebsuperobject.biblemap.put("b9_23_25", new hebsuperobject(9,23,25,"וילך שאול ואנשיו לבקש ויגדו לדוד וירד הסלע וישב במדבר מעון וישמע שאול וירדף אחרי דוד מדבר מעון׃"));
hebsuperobject.biblemap.put("b9_23_26", new hebsuperobject(9,23,26,"וילך שאול מצד ההר מזה ודוד ואנשיו מצד ההר מזה ויהי דוד נחפז ללכת מפני שאול ושאול ואנשיו עטרים אל דוד ואל אנשיו לתפשם׃"));
hebsuperobject.biblemap.put("b9_23_27", new hebsuperobject(9,23,27,"ומלאך בא אל שאול לאמר מהרה ולכה כי פשטו פלשתים על הארץ׃"));
hebsuperobject.biblemap.put("b9_23_28", new hebsuperobject(9,23,28,"וישב שאול מרדף אחרי דוד וילך לקראת פלשתים על כן קראו למקום ההוא סלע המחלקות׃"));
hebsuperobject.biblemap.put("b9_23_29", new hebsuperobject(9,23,29,"ויעל דוד משם וישב במצדות עין גדי׃"));
hebsuperobject.biblemap.put("b9_24_1", new hebsuperobject(9,24,1,"ויהי כאשר שב שאול מאחרי פלשתים ויגדו לו לאמר הנה דוד במדבר עין גדי׃"));
hebsuperobject.biblemap.put("b9_24_2", new hebsuperobject(9,24,2,"ויקח שאול שלשת אלפים איש בחור מכל ישראל וילך לבקש את דוד ואנשיו על פני צורי היעלים׃"));
hebsuperobject.biblemap.put("b9_24_3", new hebsuperobject(9,24,3,"ויבא אל גדרות הצאן על הדרך ושם מערה ויבא שאול להסך את רגליו ודוד ואנשיו בירכתי המערה ישבים׃"));
hebsuperobject.biblemap.put("b9_24_4", new hebsuperobject(9,24,4,"ויאמרו אנשי דוד אליו הנה היום אשר אמר יהוה אליך הנה אנכי נתן את איביך בידך ועשית לו כאשר יטב בעיניך ויקם דוד ויכרת את כנף המעיל אשר לשאול בלט׃"));
hebsuperobject.biblemap.put("b9_24_5", new hebsuperobject(9,24,5,"ויהי אחרי כן ויך לב דוד אתו על אשר כרת את כנף אשר לשאול׃"));
hebsuperobject.biblemap.put("b9_24_6", new hebsuperobject(9,24,6,"ויאמר לאנשיו חלילה לי מיהוה אם אעשה את הדבר הזה לאדני למשיח יהוה לשלח ידי בו כי משיח יהוה הוא׃"));
hebsuperobject.biblemap.put("b9_24_7", new hebsuperobject(9,24,7,"וישסע דוד את אנשיו בדברים ולא נתנם לקום אל שאול ושאול קם מהמערה וילך בדרך׃"));
hebsuperobject.biblemap.put("b9_24_8", new hebsuperobject(9,24,8,"ויקם דוד אחרי כן ויצא מן המערה ויקרא אחרי שאול לאמר אדני המלך ויבט שאול אחריו ויקד דוד אפים ארצה וישתחו׃"));
hebsuperobject.biblemap.put("b9_24_9", new hebsuperobject(9,24,9,"ויאמר דוד לשאול למה תשמע את דברי אדם לאמר הנה דוד מבקש רעתך׃"));
hebsuperobject.biblemap.put("b9_24_10", new hebsuperobject(9,24,10,"הנה היום הזה ראו עיניך את אשר נתנך יהוה היום בידי במערה ואמר להרגך ותחס עליך ואמר לא אשלח ידי באדני כי משיח יהוה הוא׃"));
hebsuperobject.biblemap.put("b9_24_11", new hebsuperobject(9,24,11,"ואבי ראה גם ראה את כנף מעילך בידי כי בכרתי את כנף מעילך ולא הרגתיך דע וראה כי אין בידי רעה ופשע ולא חטאתי לך ואתה צדה את נפשי לקחתה׃"));
hebsuperobject.biblemap.put("b9_24_12", new hebsuperobject(9,24,12,"ישפט יהוה ביני ובינך ונקמני יהוה ממך וידי לא תהיה בך׃"));
hebsuperobject.biblemap.put("b9_24_13", new hebsuperobject(9,24,13,"כאשר יאמר משל הקדמני מרשעים יצא רשע וידי לא תהיה בך׃"));
hebsuperobject.biblemap.put("b9_24_14", new hebsuperobject(9,24,14,"אחרי מי יצא מלך ישראל אחרי מי אתה רדף אחרי כלב מת אחרי פרעש אחד׃"));
hebsuperobject.biblemap.put("b9_24_15", new hebsuperobject(9,24,15,"והיה יהוה לדין ושפט ביני ובינך וירא וירב את ריבי וישפטני מידך׃"));
hebsuperobject.biblemap.put("b9_24_16", new hebsuperobject(9,24,16,"ויהי ככלות דוד לדבר את הדברים האלה אל שאול ויאמר שאול הקלך זה בני דוד וישא שאול קלו ויבך׃"));
hebsuperobject.biblemap.put("b9_24_17", new hebsuperobject(9,24,17,"ויאמר אל דוד צדיק אתה ממני כי אתה גמלתני הטובה ואני גמלתיך הרעה׃"));
hebsuperobject.biblemap.put("b9_24_18", new hebsuperobject(9,24,18,"ואת הגדת היום את אשר עשיתה אתי טובה את אשר סגרני יהוה בידך ולא הרגתני׃"));
hebsuperobject.biblemap.put("b9_24_19", new hebsuperobject(9,24,19,"וכי ימצא איש את איבו ושלחו בדרך טובה ויהוה ישלמך טובה תחת היום הזה אשר עשיתה לי׃"));
hebsuperobject.biblemap.put("b9_24_20", new hebsuperobject(9,24,20,"ועתה הנה ידעתי כי מלך תמלוך וקמה בידך ממלכת ישראל׃"));
hebsuperobject.biblemap.put("b9_24_21", new hebsuperobject(9,24,21,"ועתה השבעה לי ביהוה אם תכרית את זרעי אחרי ואם תשמיד את שמי מבית אבי׃"));
hebsuperobject.biblemap.put("b9_24_22", new hebsuperobject(9,24,22,"וישבע דוד לשאול וילך שאול אל ביתו ודוד ואנשיו עלו על המצודה׃"));
hebsuperobject.biblemap.put("b9_25_1", new hebsuperobject(9,25,1,"וימת שמואל ויקבצו כל ישראל ויספדו לו ויקברהו בביתו ברמה ויקם דוד וירד אל מדבר פארן׃"));
hebsuperobject.biblemap.put("b9_25_2", new hebsuperobject(9,25,2,"ואיש במעון ומעשהו בכרמל והאיש גדול מאד ולו צאן שלשת אלפים ואלף עזים ויהי בגזז את צאנו בכרמל׃"));
hebsuperobject.biblemap.put("b9_25_3", new hebsuperobject(9,25,3,"ושם האיש נבל ושם אשתו אבגיל והאשה טובת שכל ויפת תאר והאיש קשה ורע מעללים והוא כלבו׃"));
hebsuperobject.biblemap.put("b9_25_4", new hebsuperobject(9,25,4,"וישמע דוד במדבר כי גזז נבל את צאנו׃"));
hebsuperobject.biblemap.put("b9_25_5", new hebsuperobject(9,25,5,"וישלח דוד עשרה נערים ויאמר דוד לנערים עלו כרמלה ובאתם אל נבל ושאלתם לו בשמי לשלום׃"));
hebsuperobject.biblemap.put("b9_25_6", new hebsuperobject(9,25,6,"ואמרתם כה לחי ואתה שלום וביתך שלום וכל אשר לך שלום׃"));
hebsuperobject.biblemap.put("b9_25_7", new hebsuperobject(9,25,7,"ועתה שמעתי כי גזזים לך עתה הרעים אשר לך היו עמנו לא הכלמנום ולא נפקד להם מאומה כל ימי היותם בכרמל׃"));
hebsuperobject.biblemap.put("b9_25_8", new hebsuperobject(9,25,8,"שאל את נעריך ויגידו לך וימצאו הנערים חן בעיניך כי על יום טוב בנו תנה נא את אשר תמצא ידך לעבדיך ולבנך לדוד׃"));
hebsuperobject.biblemap.put("b9_25_9", new hebsuperobject(9,25,9,"ויבאו נערי דוד וידברו אל נבל ככל הדברים האלה בשם דוד וינוחו׃"));
hebsuperobject.biblemap.put("b9_25_10", new hebsuperobject(9,25,10,"ויען נבל את עבדי דוד ויאמר מי דוד ומי בן ישי היום רבו עבדים המתפרצים איש מפני אדניו׃"));
hebsuperobject.biblemap.put("b9_25_11", new hebsuperobject(9,25,11,"ולקחתי את לחמי ואת מימי ואת טבחתי אשר טבחתי לגזזי ונתתי לאנשים אשר לא ידעתי אי מזה המה׃"));
hebsuperobject.biblemap.put("b9_25_12", new hebsuperobject(9,25,12,"ויהפכו נערי דוד לדרכם וישבו ויבאו ויגדו לו ככל הדברים האלה׃"));
hebsuperobject.biblemap.put("b9_25_13", new hebsuperobject(9,25,13,"ויאמר דוד לאנשיו חגרו איש את חרבו ויחגרו איש את חרבו ויחגר גם דוד את חרבו ויעלו אחרי דוד כארבע מאות איש ומאתים ישבו על הכלים׃"));
hebsuperobject.biblemap.put("b9_25_14", new hebsuperobject(9,25,14,"ולאביגיל אשת נבל הגיד נער אחד מהנערים לאמר הנה שלח דוד מלאכים מהמדבר לברך את אדנינו ויעט בהם׃"));
hebsuperobject.biblemap.put("b9_25_15", new hebsuperobject(9,25,15,"והאנשים טבים לנו מאד ולא הכלמנו ולא פקדנו מאומה כל ימי התהלכנו אתם בהיותנו בשדה׃"));
hebsuperobject.biblemap.put("b9_25_16", new hebsuperobject(9,25,16,"חומה היו עלינו גם לילה גם יומם כל ימי היותנו עמם רעים הצאן׃"));
hebsuperobject.biblemap.put("b9_25_17", new hebsuperobject(9,25,17,"ועתה דעי וראי מה תעשי כי כלתה הרעה אל אדנינו ועל כל ביתו והוא בן בליעל מדבר אליו׃"));
hebsuperobject.biblemap.put("b9_25_18", new hebsuperobject(9,25,18,"ותמהר אבוגיל ותקח מאתים לחם ושנים נבלי יין וחמש צאן עשוות וחמש סאים קלי ומאה צמקים ומאתים דבלים ותשם על החמרים׃"));
hebsuperobject.biblemap.put("b9_25_19", new hebsuperobject(9,25,19,"ותאמר לנעריה עברו לפני הנני אחריכם באה ולאישה נבל לא הגידה׃"));
hebsuperobject.biblemap.put("b9_25_20", new hebsuperobject(9,25,20,"והיה היא רכבת על החמור וירדת בסתר ההר והנה דוד ואנשיו ירדים לקראתה ותפגש אתם׃"));
hebsuperobject.biblemap.put("b9_25_21", new hebsuperobject(9,25,21,"ודוד אמר אך לשקר שמרתי את כל אשר לזה במדבר ולא נפקד מכל אשר לו מאומה וישב לי רעה תחת טובה׃"));
hebsuperobject.biblemap.put("b9_25_22", new hebsuperobject(9,25,22,"כה יעשה אלהים לאיבי דוד וכה יסיף אם אשאיר מכל אשר לו עד הבקר משתין בקיר׃"));
hebsuperobject.biblemap.put("b9_25_23", new hebsuperobject(9,25,23,"ותרא אביגיל את דוד ותמהר ותרד מעל החמור ותפל לאפי דוד על פניה ותשתחו ארץ׃"));
hebsuperobject.biblemap.put("b9_25_24", new hebsuperobject(9,25,24,"ותפל על רגליו ותאמר בי אני אדני העון ותדבר נא אמתך באזניך ושמע את דברי אמתך׃"));
hebsuperobject.biblemap.put("b9_25_25", new hebsuperobject(9,25,25,"אל נא ישים אדני את לבו אל איש הבליעל הזה על נבל כי כשמו כן הוא נבל שמו ונבלה עמו ואני אמתך לא ראיתי את נערי אדני אשר שלחת׃"));
hebsuperobject.biblemap.put("b9_25_26", new hebsuperobject(9,25,26,"ועתה אדני חי יהוה וחי נפשך אשר מנעך יהוה מבוא בדמים והושע ידך לך ועתה יהיו כנבל איביך והמבקשים אל אדני רעה׃"));
hebsuperobject.biblemap.put("b9_25_27", new hebsuperobject(9,25,27,"ועתה הברכה הזאת אשר הביא שפחתך לאדני ונתנה לנערים המתהלכים ברגלי אדני׃"));
hebsuperobject.biblemap.put("b9_25_28", new hebsuperobject(9,25,28,"שא נא לפשע אמתך כי עשה יעשה יהוה לאדני בית נאמן כי מלחמות יהוה אדני נלחם ורעה לא תמצא בך מימיך׃"));
hebsuperobject.biblemap.put("b9_25_29", new hebsuperobject(9,25,29,"ויקם אדם לרדפך ולבקש את נפשך והיתה נפש אדני צרורה בצרור החיים את יהוה אלהיך ואת נפש איביך יקלענה בתוך כף הקלע׃"));
hebsuperobject.biblemap.put("b9_25_30", new hebsuperobject(9,25,30,"והיה כי יעשה יהוה לאדני ככל אשר דבר את הטובה עליך וצוך לנגיד על ישראל׃"));
hebsuperobject.biblemap.put("b9_25_31", new hebsuperobject(9,25,31,"ולא תהיה זאת לך לפוקה ולמכשול לב לאדני ולשפך דם חנם ולהושיע אדני לו והיטב יהוה לאדני וזכרת את אמתך׃"));
hebsuperobject.biblemap.put("b9_25_32", new hebsuperobject(9,25,32,"ויאמר דוד לאביגל ברוך יהוה אלהי ישראל אשר שלחך היום הזה לקראתי׃"));
hebsuperobject.biblemap.put("b9_25_33", new hebsuperobject(9,25,33,"וברוך טעמך וברוכה את אשר כלתני היום הזה מבוא בדמים והשע ידי לי׃"));
hebsuperobject.biblemap.put("b9_25_34", new hebsuperobject(9,25,34,"ואולם חי יהוה אלהי ישראל אשר מנעני מהרע אתך כי לולי מהרת ותבאתי לקראתי כי אם נותר לנבל עד אור הבקר משתין בקיר׃"));
hebsuperobject.biblemap.put("b9_25_35", new hebsuperobject(9,25,35,"ויקח דוד מידה את אשר הביאה לו ולה אמר עלי לשלום לביתך ראי שמעתי בקולך ואשא פניך׃"));
hebsuperobject.biblemap.put("b9_25_36", new hebsuperobject(9,25,36,"ותבא אביגיל אל נבל והנה לו משתה בביתו כמשתה המלך ולב נבל טוב עליו והוא שכר עד מאד ולא הגידה לו דבר קטן וגדול עד אור הבקר׃"));
hebsuperobject.biblemap.put("b9_25_37", new hebsuperobject(9,25,37,"ויהי בבקר בצאת היין מנבל ותגד לו אשתו את הדברים האלה וימת לבו בקרבו והוא היה לאבן׃"));
hebsuperobject.biblemap.put("b9_25_38", new hebsuperobject(9,25,38,"ויהי כעשרת הימים ויגף יהוה את נבל וימת׃"));
hebsuperobject.biblemap.put("b9_25_39", new hebsuperobject(9,25,39,"וישמע דוד כי מת נבל ויאמר ברוך יהוה אשר רב את ריב חרפתי מיד נבל ואת עבדו חשך מרעה ואת רעת נבל השיב יהוה בראשו וישלח דוד וידבר באביגיל לקחתה לו לאשה׃"));
hebsuperobject.biblemap.put("b9_25_40", new hebsuperobject(9,25,40,"ויבאו עבדי דוד אל אביגיל הכרמלה וידברו אליה לאמר דוד שלחנו אליך לקחתך לו לאשה׃"));
hebsuperobject.biblemap.put("b9_25_41", new hebsuperobject(9,25,41,"ותקם ותשתחו אפים ארצה ותאמר הנה אמתך לשפחה לרחץ רגלי עבדי אדני׃"));
hebsuperobject.biblemap.put("b9_25_42", new hebsuperobject(9,25,42,"ותמהר ותקם אביגיל ותרכב על החמור וחמש נערתיה ההלכות לרגלה ותלך אחרי מלאכי דוד ותהי לו לאשה׃"));
hebsuperobject.biblemap.put("b9_25_43", new hebsuperobject(9,25,43,"ואת אחינעם לקח דוד מיזרעאל ותהיין גם שתיהן לו לנשים׃"));
hebsuperobject.biblemap.put("b9_25_44", new hebsuperobject(9,25,44,"ושאול נתן את מיכל בתו אשת דוד לפלטי בן ליש אשר מגלים׃"));
hebsuperobject.biblemap.put("b9_26_1", new hebsuperobject(9,26,1,"ויבאו הזפים אל שאול הגבעתה לאמר הלוא דוד מסתתר בגבעת החכילה על פני הישימן׃"));
hebsuperobject.biblemap.put("b9_26_2", new hebsuperobject(9,26,2,"ויקם שאול וירד אל מדבר זיף ואתו שלשת אלפים איש בחורי ישראל לבקש את דוד במדבר זיף׃"));
hebsuperobject.biblemap.put("b9_26_3", new hebsuperobject(9,26,3,"ויחן שאול בגבעת החכילה אשר על פני הישימן על הדרך ודוד ישב במדבר וירא כי בא שאול אחריו המדברה׃"));
hebsuperobject.biblemap.put("b9_26_4", new hebsuperobject(9,26,4,"וישלח דוד מרגלים וידע כי בא שאול אל נכון׃"));
hebsuperobject.biblemap.put("b9_26_5", new hebsuperobject(9,26,5,"ויקם דוד ויבא אל המקום אשר חנה שם שאול וירא דוד את המקום אשר שכב שם שאול ואבנר בן נר שר צבאו ושאול שכב במעגל והעם חנים סביבתו׃"));
hebsuperobject.biblemap.put("b9_26_6", new hebsuperobject(9,26,6,"ויען דוד ויאמר אל אחימלך החתי ואל אבישי בן צרויה אחי יואב לאמר מי ירד אתי אל שאול אל המחנה ויאמר אבישי אני ארד עמך׃"));
hebsuperobject.biblemap.put("b9_26_7", new hebsuperobject(9,26,7,"ויבא דוד ואבישי אל העם לילה והנה שאול שכב ישן במעגל וחניתו מעוכה בארץ מראשתו ואבנר והעם שכבים סביבתו׃"));
hebsuperobject.biblemap.put("b9_26_8", new hebsuperobject(9,26,8,"ויאמר אבישי אל דוד סגר אלהים היום את אויבך בידך ועתה אכנו נא בחנית ובארץ פעם אחת ולא אשנה לו׃"));
hebsuperobject.biblemap.put("b9_26_9", new hebsuperobject(9,26,9,"ויאמר דוד אל אבישי אל תשחיתהו כי מי שלח ידו במשיח יהוה ונקה׃"));
hebsuperobject.biblemap.put("b9_26_10", new hebsuperobject(9,26,10,"ויאמר דוד חי יהוה כי אם יהוה יגפנו או יומו יבוא ומת או במלחמה ירד ונספה׃"));
hebsuperobject.biblemap.put("b9_26_11", new hebsuperobject(9,26,11,"חלילה לי מיהוה משלח ידי במשיח יהוה ועתה קח נא את החנית אשר מראשתו ואת צפחת המים ונלכה לנו׃"));
hebsuperobject.biblemap.put("b9_26_12", new hebsuperobject(9,26,12,"ויקח דוד את החנית ואת צפחת המים מראשתי שאול וילכו להם ואין ראה ואין יודע ואין מקיץ כי כלם ישנים כי תרדמת יהוה נפלה עליהם׃"));
hebsuperobject.biblemap.put("b9_26_13", new hebsuperobject(9,26,13,"ויעבר דוד העבר ויעמד על ראש ההר מרחק רב המקום ביניהם׃"));
hebsuperobject.biblemap.put("b9_26_14", new hebsuperobject(9,26,14,"ויקרא דוד אל העם ואל אבנר בן נר לאמר הלוא תענה אבנר ויען אבנר ויאמר מי אתה קראת אל המלך׃"));
hebsuperobject.biblemap.put("b9_26_15", new hebsuperobject(9,26,15,"ויאמר דוד אל אבנר הלוא איש אתה ומי כמוך בישראל ולמה לא שמרת אל אדניך המלך כי בא אחד העם להשחית את המלך אדניך׃"));
hebsuperobject.biblemap.put("b9_26_16", new hebsuperobject(9,26,16,"לא טוב הדבר הזה אשר עשית חי יהוה כי בני מות אתם אשר לא שמרתם על אדניכם על משיח יהוה ועתה ראה אי חנית המלך ואת צפחת המים אשר מראשתו׃"));
hebsuperobject.biblemap.put("b9_26_17", new hebsuperobject(9,26,17,"ויכר שאול את קול דוד ויאמר הקולך זה בני דוד ויאמר דוד קולי אדני המלך׃"));
hebsuperobject.biblemap.put("b9_26_18", new hebsuperobject(9,26,18,"ויאמר למה זה אדני רדף אחרי עבדו כי מה עשיתי ומה בידי רעה׃"));
hebsuperobject.biblemap.put("b9_26_19", new hebsuperobject(9,26,19,"ועתה ישמע נא אדני המלך את דברי עבדו אם יהוה הסיתך בי ירח מנחה ואם בני האדם ארורים הם לפני יהוה כי גרשוני היום מהסתפח בנחלת יהוה לאמר לך עבד אלהים אחרים׃"));
hebsuperobject.biblemap.put("b9_26_20", new hebsuperobject(9,26,20,"ועתה אל יפל דמי ארצה מנגד פני יהוה כי יצא מלך ישראל לבקש את פרעש אחד כאשר ירדף הקרא בהרים׃"));
hebsuperobject.biblemap.put("b9_26_21", new hebsuperobject(9,26,21,"ויאמר שאול חטאתי שוב בני דוד כי לא ארע לך עוד תחת אשר יקרה נפשי בעיניך היום הזה הנה הסכלתי ואשגה הרבה מאד׃"));
hebsuperobject.biblemap.put("b9_26_22", new hebsuperobject(9,26,22,"ויען דוד ויאמר הנה החנית המלך ויעבר אחד מהנערים ויקחה׃"));
hebsuperobject.biblemap.put("b9_26_23", new hebsuperobject(9,26,23,"ויהוה ישיב לאיש את צדקתו ואת אמנתו אשר נתנך יהוה היום ביד ולא אביתי לשלח ידי במשיח יהוה׃"));
hebsuperobject.biblemap.put("b9_26_24", new hebsuperobject(9,26,24,"והנה כאשר גדלה נפשך היום הזה בעיני כן תגדל נפשי בעיני יהוה ויצלני מכל צרה׃"));
hebsuperobject.biblemap.put("b9_26_25", new hebsuperobject(9,26,25,"ויאמר שאול אל דוד ברוך אתה בני דוד גם עשה תעשה וגם יכל תוכל וילך דוד לדרכו ושאול שב למקומו׃"));
hebsuperobject.biblemap.put("b9_27_1", new hebsuperobject(9,27,1,"ויאמר דוד אל לבו עתה אספה יום אחד ביד שאול אין לי טוב כי המלט אמלט אל ארץ פלשתים ונואש ממני שאול לבקשני עוד בכל גבול ישראל ונמלטתי מידו׃"));
hebsuperobject.biblemap.put("b9_27_2", new hebsuperobject(9,27,2,"ויקם דוד ויעבר הוא ושש מאות איש אשר עמו אל אכיש בן מעוך מלך גת׃"));
