hebsuperobject.biblemap.put("b9_27_4", new hebsuperobject(9,27,4,"ויגד לשאול כי ברח דוד גת ולא יוסף עוד לבקשו׃"));
hebsuperobject.biblemap.put("b9_27_5", new hebsuperobject(9,27,5,"ויאמר דוד אל אכיש אם נא מצאתי חן בעיניך יתנו לי מקום באחת ערי השדה ואשבה שם ולמה ישב עבדך בעיר הממלכה עמך׃"));
hebsuperobject.biblemap.put("b9_27_6", new hebsuperobject(9,27,6,"ויתן לו אכיש ביום ההוא את צקלג לכן היתה צקלג למלכי יהודה עד היום הזה׃"));
hebsuperobject.biblemap.put("b9_27_7", new hebsuperobject(9,27,7,"ויהי מספר הימים אשר ישב דוד בשדה פלשתים ימים וארבעה חדשים׃"));
hebsuperobject.biblemap.put("b9_27_8", new hebsuperobject(9,27,8,"ויעל דוד ואנשיו ויפשטו אל הגשורי והגרזי והעמלקי כי הנה ישבות הארץ אשר מעולם בואך שורה ועד ארץ מצרים׃"));
hebsuperobject.biblemap.put("b9_27_9", new hebsuperobject(9,27,9,"והכה דוד את הארץ ולא יחיה איש ואשה ולקח צאן ובקר וחמרים וגמלים ובגדים וישב ויבא אל אכיש׃"));
hebsuperobject.biblemap.put("b9_27_10", new hebsuperobject(9,27,10,"ויאמר אכיש אל פשטתם היום ויאמר דוד על נגב יהודה ועל נגב הירחמאלי ואל נגב הקיני׃"));
hebsuperobject.biblemap.put("b9_27_11", new hebsuperobject(9,27,11,"ואיש ואשה לא יחיה דוד להביא גת לאמר פן יגדו עלינו לאמר כה עשה דוד וכה משפטו כל הימים אשר ישב בשדה פלשתים׃"));
hebsuperobject.biblemap.put("b9_27_12", new hebsuperobject(9,27,12,"ויאמן אכיש בדוד לאמר הבאש הבאיש בעמו בישראל והיה לי לעבד עולם׃"));
hebsuperobject.biblemap.put("b9_28_1", new hebsuperobject(9,28,1,"ויהי בימים ההם ויקבצו פלשתים את מחניהם לצבא להלחם בישראל ויאמר אכיש אל דוד ידע תדע כי אתי תצא במחנה אתה ואנשיך׃"));
hebsuperobject.biblemap.put("b9_28_2", new hebsuperobject(9,28,2,"ויאמר דוד אל אכיש לכן אתה תדע את אשר יעשה עבדך ויאמר אכיש אל דוד לכן שמר לראשי אשימך כל הימים׃"));
hebsuperobject.biblemap.put("b9_28_3", new hebsuperobject(9,28,3,"ושמואל מת ויספדו לו כל ישראל ויקברהו ברמה ובעירו ושאול הסיר האבות ואת הידענים מהארץ׃"));
hebsuperobject.biblemap.put("b9_28_4", new hebsuperobject(9,28,4,"ויקבצו פלשתים ויבאו ויחנו בשונם ויקבץ שאול את כל ישראל ויחנו בגלבע׃"));
hebsuperobject.biblemap.put("b9_28_5", new hebsuperobject(9,28,5,"וירא שאול את מחנה פלשתים וירא ויחרד לבו מאד׃"));
hebsuperobject.biblemap.put("b9_28_6", new hebsuperobject(9,28,6,"וישאל שאול ביהוה ולא ענהו יהוה גם בחלמות גם באורים גם בנביאם׃"));
hebsuperobject.biblemap.put("b9_28_7", new hebsuperobject(9,28,7,"ויאמר שאול לעבדיו בקשו לי אשת בעלת אוב ואלכה אליה ואדרשה בה ויאמרו עבדיו אליו הנה אשת בעלת אוב בעין דור׃"));
hebsuperobject.biblemap.put("b9_28_8", new hebsuperobject(9,28,8,"ויתחפש שאול וילבש בגדים אחרים וילך הוא ושני אנשים עמו ויבאו אל האשה לילה ויאמר קסומי נא לי באוב והעלי לי את אשר אמר אליך׃"));
hebsuperobject.biblemap.put("b9_28_9", new hebsuperobject(9,28,9,"ותאמר האשה אליו הנה אתה ידעת את אשר עשה שאול אשר הכרית את האבות ואת הידעני מן הארץ ולמה אתה מתנקש בנפשי להמיתני׃"));
hebsuperobject.biblemap.put("b9_28_10", new hebsuperobject(9,28,10,"וישבע לה שאול ביהוה לאמר חי יהוה אם יקרך עון בדבר הזה׃"));
hebsuperobject.biblemap.put("b9_28_11", new hebsuperobject(9,28,11,"ותאמר האשה את מי אעלה לך ויאמר את שמואל העלי לי׃"));
hebsuperobject.biblemap.put("b9_28_12", new hebsuperobject(9,28,12,"ותרא האשה את שמואל ותזעק בקול גדול ותאמר האשה אל שאול לאמר למה רמיתני ואתה שאול׃"));
hebsuperobject.biblemap.put("b9_28_13", new hebsuperobject(9,28,13,"ויאמר לה המלך אל תיראי כי מה ראית ותאמר האשה אל שאול אלהים ראיתי עלים מן הארץ׃"));
hebsuperobject.biblemap.put("b9_28_14", new hebsuperobject(9,28,14,"ויאמר לה מה תארו ותאמר איש זקן עלה והוא עטה מעיל וידע שאול כי שמואל הוא ויקד אפים ארצה וישתחו׃"));
hebsuperobject.biblemap.put("b9_28_15", new hebsuperobject(9,28,15,"ויאמר שמואל אל שאול למה הרגזתני להעלות אתי ויאמר שאול צר לי מאד ופלשתים נלחמים בי ואלהים סר מעלי ולא ענני עוד גם ביד הנביאם גם בחלמות ואקראה לך להודיעני מה אעשה׃"));
hebsuperobject.biblemap.put("b9_28_16", new hebsuperobject(9,28,16,"ויאמר שמואל ולמה תשאלני ויהוה סר מעליך ויהי ערך׃"));
hebsuperobject.biblemap.put("b9_28_17", new hebsuperobject(9,28,17,"ויעש יהוה לו כאשר דבר בידי ויקרע יהוה את הממלכה מידך ויתנה לרעך לדוד׃"));
hebsuperobject.biblemap.put("b9_28_18", new hebsuperobject(9,28,18,"כאשר לא שמעת בקול יהוה ולא עשית חרון אפו בעמלק על כן הדבר הזה עשה לך יהוה היום הזה׃"));
hebsuperobject.biblemap.put("b9_28_19", new hebsuperobject(9,28,19,"ויתן יהוה גם את ישראל עמך ביד פלשתים ומחר אתה ובניך עמי גם את מחנה ישראל יתן יהוה ביד פלשתים׃"));
hebsuperobject.biblemap.put("b9_28_20", new hebsuperobject(9,28,20,"וימהר שאול ויפל מלא קומתו ארצה וירא מאד מדברי שמואל גם כח לא היה בו כי לא אכל לחם כל היום וכל הלילה׃"));
hebsuperobject.biblemap.put("b9_28_21", new hebsuperobject(9,28,21,"ותבוא האשה אל שאול ותרא כי נבהל מאד ותאמר אליו הנה שמעה שפחתך בקולך ואשים נפשי בכפי ואשמע את דבריך אשר דברת אלי׃"));
hebsuperobject.biblemap.put("b9_28_22", new hebsuperobject(9,28,22,"ועתה שמע נא גם אתה בקול שפחתך ואשמה לפניך פת לחם ואכול ויהי בך כח כי תלך בדרך׃"));
hebsuperobject.biblemap.put("b9_28_23", new hebsuperobject(9,28,23,"וימאן ויאמר לא אכל ויפרצו בו עבדיו וגם האשה וישמע לקלם ויקם מהארץ וישב אל המטה׃"));
hebsuperobject.biblemap.put("b9_28_24", new hebsuperobject(9,28,24,"ולאשה עגל מרבק בבית ותמהר ותזבחהו ותקח קמח ותלש ותפהו מצות׃"));
hebsuperobject.biblemap.put("b9_28_25", new hebsuperobject(9,28,25,"ותגש לפני שאול ולפני עבדיו ויאכלו ויקמו וילכו בלילה ההוא׃"));
hebsuperobject.biblemap.put("b9_29_1", new hebsuperobject(9,29,1,"ויקבצו פלשתים את כל מחניהם אפקה וישראל חנים בעין אשר ביזרעאל׃"));
hebsuperobject.biblemap.put("b9_29_2", new hebsuperobject(9,29,2,"וסרני פלשתים עברים למאות ולאלפים ודוד ואנשיו עברים באחרנה עם אכיש׃"));
hebsuperobject.biblemap.put("b9_29_3", new hebsuperobject(9,29,3,"ויאמרו שרי פלשתים מה העברים האלה ויאמר אכיש אל שרי פלשתים הלוא זה דוד עבד שאול מלך ישראל אשר היה אתי זה ימים או זה שנים ולא מצאתי בו מאומה מיום נפלו עד היום הזה׃"));
hebsuperobject.biblemap.put("b9_29_4", new hebsuperobject(9,29,4,"ויקצפו עליו שרי פלשתים ויאמרו לו שרי פלשתים השב את האיש וישב אל מקומו אשר הפקדתו שם ולא ירד עמנו במלחמה ולא יהיה לנו לשטן במלחמה ובמה יתרצה זה אל אדניו הלוא בראשי האנשים ההם׃"));
hebsuperobject.biblemap.put("b9_29_5", new hebsuperobject(9,29,5,"הלוא זה דוד אשר יענו לו במחלות לאמר הכה שאול באלפו ודוד ברבבתו׃"));
