hinsuperobject.biblemap.put("b19_5_11", new hinsuperobject(19,5,11,"परन्तु जितने तुझ पर भरोसा रखते हैं वे सब आनन्द करें, वे सर्वदा ऊंचे स्वर से गाते रहें; क्योंकि तू उनकी रक्षा करता है, और जो तेरे नाम के प्रेमी हैं तुझ में प्रफुल्लित हों।"));
hinsuperobject.biblemap.put("b19_5_12", new hinsuperobject(19,5,12,"क्योंकि तू धर्मी को आशिष देगा; हे यहोवा, तू उसको अपने अनुग्रहरूपी ढाल से घेरे रहेगा।।"));
hinsuperobject.biblemap.put("b19_6_1", new hinsuperobject(19,6,1,"हे यहोवा, तू मुझे अपने क्रोध में न डांट, और न झुंझलाहट में मुझे ताड़ना दे।"));
hinsuperobject.biblemap.put("b19_6_2", new hinsuperobject(19,6,2,"हे यहोवा, मुझ पर अनुग्रह कर, क्योंकि मैं कुम्हला गया हूं; हे यहोवा, मुझे चंगा कर, क्योंकि मेरी हडि्डयों में बेचैनी है।"));
hinsuperobject.biblemap.put("b19_6_3", new hinsuperobject(19,6,3,"मेरा प्राण भी बहुत खेदित है। और तू, हे यहोवा, कब तक?"));
hinsuperobject.biblemap.put("b19_6_4", new hinsuperobject(19,6,4,"लौट आ, हे यहोवा, और मेरे प्राण बचा अपनी करूणा के निमित्त मेरा उद्धार कर।"));
hinsuperobject.biblemap.put("b19_6_5", new hinsuperobject(19,6,5,"क्योंकि मृत्यु के बाद तेरा स्मरण नहीं होता; अधोलोक में कौन तेरा धन्यवाद करेगा?"));
hinsuperobject.biblemap.put("b19_6_6", new hinsuperobject(19,6,6,"मैं कराहते कराहते थक गया; मैं अपनी खाट आंसुओं से भिगोता हूं; प्रति रात मेरा बिछौना भीगता है।"));
hinsuperobject.biblemap.put("b19_6_7", new hinsuperobject(19,6,7,"मेरी आंखें शोक से बैठी जाती हैं, और मेरे सब सतानेवालों के कारण वे धुन्धला गई हैं।।"));
hinsuperobject.biblemap.put("b19_6_8", new hinsuperobject(19,6,8,"हे सब अनर्थकारियो मेरे पास से दूर हो; क्योंकि यहोवा ने मेरे रोने का शब्द सुन लिया है।"));
hinsuperobject.biblemap.put("b19_6_9", new hinsuperobject(19,6,9,"यहोवा ने मेरा गिड़गिड़ाना सुना है; यहोवा मेरी प्रार्थना को ग्रहण भी करेगा।"));
hinsuperobject.biblemap.put("b19_6_10", new hinsuperobject(19,6,10,"मेरे सब शत्रु लज्जित होंगे और बहुत घबराएंगे; वे लौट जाएंगे, और एकाएक लज्जित होंगे।।"));
hinsuperobject.biblemap.put("b19_7_1", new hinsuperobject(19,7,1,"हे मेरे परमेश्वर यहोवा, मेरा भरोसा तुझ पर है; सब पीछा करनेवालों से मुझे बचा और छुटकारा दे,"));
hinsuperobject.biblemap.put("b19_7_2", new hinsuperobject(19,7,2,"ऐसा न हो कि वे मुझ को सिंह की नाई फाड़कर टुकड़े टुकड़े कर डालें; और कोई मेरा छुड़ानेवाला न हो।।"));
hinsuperobject.biblemap.put("b19_7_3", new hinsuperobject(19,7,3,"हे मेरे परमेश्वर यहोवा, यदि मैं ने यह किया हो, यदि मेरे हाथों से कुटिल काम हुआ हो,"));
hinsuperobject.biblemap.put("b19_7_4", new hinsuperobject(19,7,4,"यदि मैं ने अपने मेल रखनेवालों से भलाई के बदले बुराई की हो, (वरन मैं ने उसको जो अकारण मेरा बैरी था बचाया है)"));
hinsuperobject.biblemap.put("b19_7_5", new hinsuperobject(19,7,5,"तो शत्रु मेरे प्राण का पीछा करके मुझे आ पकड़े, वरन मेरे प्राण को भूमि पर रौंदे, और मेरी महिमा को मिट्टी में मिला दे।।"));
hinsuperobject.biblemap.put("b19_7_6", new hinsuperobject(19,7,6,"हे यहोवा क्रोध करके उठ; मेरे क्रोधभरे सतानेवाले के विरूद्ध तू खड़ा हो जा; मेरे लिये जाग! तू ने न्याय की आज्ञा तो दे दी है।"));
hinsuperobject.biblemap.put("b19_7_7", new hinsuperobject(19,7,7,"देश देश के लोगों की मण्डली तेरे चारों ओर हो; और तू उनके ऊपर से होकर ऊंचे स्थानों पर लौट जा।"));
hinsuperobject.biblemap.put("b19_7_8", new hinsuperobject(19,7,8,"यहोवा समाज समाज का न्याय करता है; यहोवा मेरे धर्म और खराई के अनुसार मेरा न्याय चुका दे।।"));
hinsuperobject.biblemap.put("b19_7_9", new hinsuperobject(19,7,9,"भला हो कि दुष्टों की बुराई का अन्त हो जाए, परन्तु धर्म को तू स्थिर कर; क्योंकि धर्मी परमेश्वर मन और मर्म का ज्ञाता है।"));
hinsuperobject.biblemap.put("b19_7_10", new hinsuperobject(19,7,10,"मेरी ढाल परमेश्वर के हाथ में है, वह सीधे मनवालों को बचाता है।।"));
hinsuperobject.biblemap.put("b19_7_11", new hinsuperobject(19,7,11,"परमेश्वर धर्मी और न्यायी है, वरन ऐसा ईश्वर है जो प्रति दिन क्रोध करता है।।"));
hinsuperobject.biblemap.put("b19_7_12", new hinsuperobject(19,7,12,"यदि मनुष्य न फिरे तो वह अपनी तलवार पर सान चढ़ाएगा; वह अपना धनुष चढ़ाकर तीर सन्धान चुका है।"));
hinsuperobject.biblemap.put("b19_7_13", new hinsuperobject(19,7,13,"और उस मनुष्य के लिये उस ने मृत्यु के हथियार तैयार कर लिए हैं: वह अपने तीरों को अग्निबाण बनाता है।"));
hinsuperobject.biblemap.put("b19_7_14", new hinsuperobject(19,7,14,"देख दुष्ट को अनर्थ काम की पीड़ाएं हो रही हैं, उसको उत्पात का गर्भ है, और उस से झूठ उत्पन्न हुआ। उस ने गड़हा खोदकर उसे गहिरा किया,"));
hinsuperobject.biblemap.put("b19_7_15", new hinsuperobject(19,7,15,"और जो खाई उस ने बनाई थी उस में वह आप ही गिरा।"));
hinsuperobject.biblemap.put("b19_7_16", new hinsuperobject(19,7,16,"उसका उत्पात पलट कर उसी के सिर पर पडेगा; और उसका उपद्रव उसी के माथे पर पड़ेगा।।"));
hinsuperobject.biblemap.put("b19_7_17", new hinsuperobject(19,7,17,"मैं यहोवा के धर्म के अनुसार उसका धन्यवाद करूंगा, और परमप्रधान यहोवा के नाम का भजन गाऊंगा।।"));
hinsuperobject.biblemap.put("b19_8_1", new hinsuperobject(19,8,1,"हे यहोवा हमारे प्रभु, तेरा नाम सारी पृथ्वी पर क्या ही प्रतापमय है! तू ने अपना विभव स्वर्ग पर दिखाया है।"));
hinsuperobject.biblemap.put("b19_8_2", new hinsuperobject(19,8,2,"तू ने अपने बैरियों के कारण बच्चों और दूध पिउवों के द्वारा सामर्थ्य की नेव डाली है, ताकि तू शत्रु और पलटा लेनेवालों को रोक रखे।"));
hinsuperobject.biblemap.put("b19_8_3", new hinsuperobject(19,8,3,"जब मैं आकाश को, जो तेरे हाथों का कार्य है, और चंद्रमा और तरागण को जो तू ने नियुक्त किए हैं, देखता हूं;"));
hinsuperobject.biblemap.put("b19_8_4", new hinsuperobject(19,8,4,"तो फिर मनुष्य क्या है कि तू उसका स्मरण रखे, और आदमी क्या है कि तू उसकी सुधि ले?"));
hinsuperobject.biblemap.put("b19_8_5", new hinsuperobject(19,8,5,"क्योंकि तू ने उसको परमेश्वर से थोड़ा ही कम बनाया है, और महिमा और प्रताप का मुकुट उसके सिर पर रखा है।"));
hinsuperobject.biblemap.put("b19_8_6", new hinsuperobject(19,8,6,"तू ने उसे अपने हाथों के कार्यों पर प्रभुता दी है; तू ने उसके पांव तले सब कुछ कर दिया है।"));
hinsuperobject.biblemap.put("b19_8_7", new hinsuperobject(19,8,7,"सब भेड़- बकरी और गाय- बैल और जितने वनपशु हैं,"));
hinsuperobject.biblemap.put("b19_8_8", new hinsuperobject(19,8,8,"आकाश के पक्षी और समुद्र की मछलियां, और जितने जीव- जन्तु समुद्रों में चलते फिरते हैं।"));
hinsuperobject.biblemap.put("b19_8_9", new hinsuperobject(19,8,9,"हे यहोवा, हे हमारे प्रभु, तेरा नाम सारी पृथ्वी पर क्या ही प्रतापमय है।।"));
hinsuperobject.biblemap.put("b19_9_1", new hinsuperobject(19,9,1,"हे यहोवा परमेश्वर मैं अपने पूर्ण मन से तेरा धन्यवाद करूंगा; मैं तेरे सब आश्चर्य कर्मों का वर्णन करूंगा।"));
hinsuperobject.biblemap.put("b19_9_2", new hinsuperobject(19,9,2,"मैं तेरे कारण आनन्दित और प्रफुल्लित होऊंगा, हे परमप्रधान, मैं तेरे नाम का भजन गाऊंगा।।"));
hinsuperobject.biblemap.put("b19_9_3", new hinsuperobject(19,9,3,"जब मेरे शत्रु पीछे हटते हैं, तो वे तेरे साम्हने से ठोकर खाकर नाश होते हैं।"));
hinsuperobject.biblemap.put("b19_9_4", new hinsuperobject(19,9,4,"क्योंकि तू ने मेरा न्याय और मुक मा चुकाया है; तू ने सिंहासन पर विराजमान होकर धर्म से न्याय किया।"));
hinsuperobject.biblemap.put("b19_9_5", new hinsuperobject(19,9,5,"तू ने अन्यजातियों को झिड़का और दुष्ट को नाश किया है; तू ने उनका नाम अनन्तकाल के लिये मिटा दिया है।"));
hinsuperobject.biblemap.put("b19_9_6", new hinsuperobject(19,9,6,"शत्रु जो है, वह मर गए, वे अनन्तकाल के लिये उजड़ गए हैं; और जिन नगरों को तू ने ढा दिया, उनका नाम वा निशान भी मिट गया है।"));
hinsuperobject.biblemap.put("b19_9_7", new hinsuperobject(19,9,7,"परन्तु यहोवा सदैव सिंहासन पर विराजमान है, उस ने अपना सिंहासन न्याय के लिये सिद्ध किया है;"));
hinsuperobject.biblemap.put("b19_9_8", new hinsuperobject(19,9,8,"और वह आप ही जगत का न्याय धर्म से करेगा, वह देश देश के लोगों का मुक मा खराई से निपटाएगा।।"));
hinsuperobject.biblemap.put("b19_9_9", new hinsuperobject(19,9,9,"यहोवा पिसे हुओं के लिये ऊंचा गढ़ ठहरेगा, वह संकट के समय के लिये भी ऊंचा गढ़ ठहरेगा।"));
hinsuperobject.biblemap.put("b19_9_10", new hinsuperobject(19,9,10,"और तेरे नाम के जाननेवाले तुझ पर भरोसा रखेंगे, क्योंकि हे यहोवा तू ने अपने खोजियों को त्याग नहीं दिया।।"));
hinsuperobject.biblemap.put("b19_9_11", new hinsuperobject(19,9,11,"यहोवा जो सिरयोन में विराजमान है, उसका भजन गाओ! जाति जाति के लोगों के बीच में उसके महाकर्मों का प्रचार करो!"));
hinsuperobject.biblemap.put("b19_9_12", new hinsuperobject(19,9,12,"क्योंकि खून का पलटा लेनेवाला उनको स्मरण करता है; वह दीन लोगों की दोहाई को भूलता।।"));
hinsuperobject.biblemap.put("b19_9_13", new hinsuperobject(19,9,13,"हे यहोवा, मुझ पर अनुग्रह कर। तू जो मुझे मृत्यु के फाटकों के पास से उठाता है, मेरे दु:ख को देख जो मेरे बैरी मुझे दे रहे हैं;"));
hinsuperobject.biblemap.put("b19_9_14", new hinsuperobject(19,9,14,"ताकि मैं सिरयोन के फाटकों के पास तेरे सब गुणों का वर्णन करूं, और तेरे किए हुए उद्धार से मगन होऊं।।"));
hinsuperobject.biblemap.put("b19_9_15", new hinsuperobject(19,9,15,"अन्य जातिवालों ने जो गड़हा खोदा था, उसी में वे आप गिर पड़े; जो जाल उन्हों ने लगाया था, उस में उन्हीं का पांव फंस गया।"));
hinsuperobject.biblemap.put("b19_9_16", new hinsuperobject(19,9,16,"यहोवा ने अपने को प्रगट किया, उस ने न्याय किया है; दुष्ट अपने किए हुए कामों में फंस जाता है।"));
hinsuperobject.biblemap.put("b19_9_17", new hinsuperobject(19,9,17,"दुष्ट अधोलोक में लौट जाएंगे, तथा वे सब जातियां भी जा परमेश्वर को भूल जाती है।"));
hinsuperobject.biblemap.put("b19_9_18", new hinsuperobject(19,9,18,"क्योंकि दरिद्र लोग अनन्तकाल तक बिसरे हुए न रहेंगे, और न तो नम्र लोगों की आशा सर्वदा के लिये नाश होगी।"));
hinsuperobject.biblemap.put("b19_9_19", new hinsuperobject(19,9,19,"उठ, हे परमेश्वर, मनुष्य प्रबल न होने पाए! जातियों का न्याय तेरे सम्मुख किया जाए।"));
hinsuperobject.biblemap.put("b19_9_20", new hinsuperobject(19,9,20,"हे परमेश्वर, उनको भय दिला! जातियां अपने को मनुष्यमात्रा ही जानें।"));
hinsuperobject.biblemap.put("b19_10_1", new hinsuperobject(19,10,1,"हे यहोवा तू क्यों दूर खड़ा रहता है? संकट के समय में क्यों छिपा रहता है?"));
hinsuperobject.biblemap.put("b19_10_2", new hinsuperobject(19,10,2,"दुष्टों के अहंकार के कारण दी मनुष्य खदेड़े जाते हैं; वे अपनी ही निकाली हुई युक्तियों में फंस जाएं।।"));
hinsuperobject.biblemap.put("b19_10_3", new hinsuperobject(19,10,3,"क्योंकि दुष्ट अपनी अभिलाषा पर घमण्ड करता है, और लोभी परमेश्वर को त्याग देता है और उसका तिरस्कार करता है।।"));
hinsuperobject.biblemap.put("b19_10_4", new hinsuperobject(19,10,4,"दुष्ट अपने अभिमान के कारण कहता है कि वह लेखा नहीं लेने का; उसका पूरा विचार यही है कि कोई परमेश्वर है ही नहीं।।"));
hinsuperobject.biblemap.put("b19_10_5", new hinsuperobject(19,10,5,"वह अपने मार्ग पर दृढ़ता से बना रहता है; तेरे न्याय के विचार ऐसे ऊंचे पर होते हैं, कि उसकी दृष्टि वहां तक नहीं पहुंचती; जितने उसके विरोधी हैं उन पर वह फुंकारता है।"));
hinsuperobject.biblemap.put("b19_10_6", new hinsuperobject(19,10,6,"वह अपने मन में कहता है कि मैं कभी टलने का नहीं: मैं पीढ़ी से पीढ़ी तक दु:ख से बचा रहूंगा।।"));
hinsuperobject.biblemap.put("b19_10_7", new hinsuperobject(19,10,7,"उसका मुंह शाप और छल और अन्धेर से भरा है; उत्पात और अनर्थ की बातें उसके मुंह में हैं।"));
hinsuperobject.biblemap.put("b19_10_8", new hinsuperobject(19,10,8,"वह गांवों के घतों में बैठा करता है, और गुप्त स्थानों में निर्दोष को घात करता है, उसकी आंखे लाचार की घात में लगी रहती है।"));
hinsuperobject.biblemap.put("b19_10_9", new hinsuperobject(19,10,9,"जैसा सिंह अपनी झाड़ी में वैसा ही वह भी छिपकर घात में बैठा करता है; वह दीन को पकड़ने के लिये घात लगाए रहता है,"));
hinsuperobject.biblemap.put("b19_10_10", new hinsuperobject(19,10,10,"वह दीन को अपने जाल में फंसाकर घसीट लाता है, तब उसे पकड़ लेता है।"));
hinsuperobject.biblemap.put("b19_10_11", new hinsuperobject(19,10,11,"वह झुक जाता है और वह दबक कर बैठता है; और लाचार लोग उसके महाबली हाथों से पटके जाते हैं।"));
hinsuperobject.biblemap.put("b19_10_12", new hinsuperobject(19,10,12,"वह अपने मन में सोचता है, कि ईश्वर भूल गया, वह अपना मुंह छिपाता है; वह कभी नहीं देखेगा।।"));
hinsuperobject.biblemap.put("b19_10_13", new hinsuperobject(19,10,13,"उठ, हे यहोवा; हे ईश्वर, अपना हाथ बढ़ा; और दीनों को न भूल।"));
hinsuperobject.biblemap.put("b19_10_14", new hinsuperobject(19,10,14,"परमेश्वर को दुष्ट क्यों तुच्छ जानता है, और अपने मन में कहता है कि तू लेखा न लेगा?"));
hinsuperobject.biblemap.put("b19_10_15", new hinsuperobject(19,10,15,"तू ने देख लिया है, क्योंकि तू उत्पात और कलपाने पर दुष्टि रखता है, ताकि उसका पलटा अपने हाथ में रखे; लाचार अपने को तेरे हाथ में सौंपता है; अनाथों का तू ही सहायक रहा है। दुष्ट की भुजा को तोड़ डाल;"));
hinsuperobject.biblemap.put("b19_10_16", new hinsuperobject(19,10,16,"यहोवा अनन्तकाल के लिये महाराज है; उसके देश में से अन्यजाति लोग नाश हो गए हैं।।"));
hinsuperobject.biblemap.put("b19_10_17", new hinsuperobject(19,10,17,"हे यहोवा, तू ने नम्र लोगों की अभिलाषा सुनी है; तू उनका मन तैयार करेगा, तू कान लगाकर सुनेगा"));
hinsuperobject.biblemap.put("b19_10_18", new hinsuperobject(19,10,18,"कि अनाथ और पिसे हुए का न्याय करे, ताकि मनुष्य जो मिट्टी से बना है फिर भय दिखाने न पाए।।"));
hinsuperobject.biblemap.put("b19_11_1", new hinsuperobject(19,11,1,"मेरा भरोसा परमेश्वर पर है; तुम क्योंकि मेरे प्राण से कहते हो कि पक्षी की नाई अपने पहाड़ पर उड़ जा?"));
hinsuperobject.biblemap.put("b19_11_2", new hinsuperobject(19,11,2,"क्योंकि देखो, दुष्ट अपना धनुष चढ़ाते हैं, और अपना तीर धनुष की डोरी पर रखते हैं, कि सीधे मनवालों पर अन्धियारे में तीर चलाएं।"));
hinsuperobject.biblemap.put("b19_11_3", new hinsuperobject(19,11,3,"यदि नेवें ढ़ा दी जाएं तो धर्मी क्या कर सकता है?"));
hinsuperobject.biblemap.put("b19_11_4", new hinsuperobject(19,11,4,"परमेश्वर अपने पवित्रा भवन में है; परमेश्वर का सिंहासन स्वर्ग में है; उसकी आंखें मनुष्य की सन्तान को नित देखती रहती हैं और उसकी पलकें उनको जांचती हैं।"));
hinsuperobject.biblemap.put("b19_11_5", new hinsuperobject(19,11,5,"यहोवा धर्मीं को परखता है, परन्तु वह उन से जो दुष्ट हैं और उपद्रव से प्रीति रखते हैं अपनी आत्मा में घृणा करता है।"));
hinsuperobject.biblemap.put("b19_11_6", new hinsuperobject(19,11,6,"वह दुष्टों पर फन्दे बरसाएगा; आग और गन्धक और प्रचण्ड लूह उनके कटोरों में बांट दी जाएंगी।"));
hinsuperobject.biblemap.put("b19_11_7", new hinsuperobject(19,11,7,"क्योंकि यहोवा धर्मी है, वह धर्म के ही कामों से प्रसन्न रहता है; धर्मीजन उसका दर्शन पाएंगे।।"));
hinsuperobject.biblemap.put("b19_12_1", new hinsuperobject(19,12,1,"हे परमेश्वर बचा ले, क्योंकि एक भी भक्त नहीं रहा; मनुष्यों में से विश्वासयोग्य लाग मर मिटे हैं।"));
hinsuperobject.biblemap.put("b19_12_2", new hinsuperobject(19,12,2,"उन में से प्रत्येक अपने पड़ोसी से झूठी बातें कहता है; वे चापलूसी के ओठों से दो रंगी बातें करते हैं।।"));
hinsuperobject.biblemap.put("b19_12_3", new hinsuperobject(19,12,3,"प्रभु सब चापलूस ओठों को और उस जीभ को जिस से बड़ा बोल निकलता है काट डालेगा।"));
hinsuperobject.biblemap.put("b19_12_4", new hinsuperobject(19,12,4,"वे कहते हैं कि हम अपनी जीभ ही से जीतेंगे, हमारे ओंठ हमारे ही वश में हैं; हमार प्रभु कौन है?"));
hinsuperobject.biblemap.put("b19_12_5", new hinsuperobject(19,12,5,"दी लोगों के लुट जाने, और दरिद्रों के कराहने के कारण, परमेश्वर कहता है, अब मैं उठूंगा, जिस पर वे फुंकारते हैं उसे मैं चैन विश्राम दूंगा।"));
hinsuperobject.biblemap.put("b19_12_6", new hinsuperobject(19,12,6,"परमेश्वर का वचन पवित्रा है, उस चान्दि के समान जो भट्टी में मिट्टी पर ताई गई, और सात बार निर्मल की गई हो।।"));
hinsuperobject.biblemap.put("b19_12_7", new hinsuperobject(19,12,7,"तू ही हे परमेश्वर उनकी रक्षा करेगा, उनको इस काल के लोगों से सर्वदा के लिये बचाए रखेगा।"));
hinsuperobject.biblemap.put("b19_12_8", new hinsuperobject(19,12,8,"जब मनुष्यों में नीचपन का आदर होता है, तब दुष्ट लोग चारों ओर अकड़ते फिरते हैं।।"));
hinsuperobject.biblemap.put("b19_13_1", new hinsuperobject(19,13,1,"हे परमेश्वर तू कब तक? क्या सदैव मुझे भूला रहेगा? तू कब तक अपना मुखड़ा मुझ से छिपाए रहेगा?"));
hinsuperobject.biblemap.put("b19_13_2", new hinsuperobject(19,13,2,"मैं कब तक अपने मन ही मन में युक्तियां करता रहूं, और दिन भर अपने हृदय में दुखित रहा करूं, कब तक मेरा शत्रु मुझ पर प्रबल रहेगा?"));
hinsuperobject.biblemap.put("b19_13_3", new hinsuperobject(19,13,3,"हे मेरे परमेश्वर यहोवा मेरी ओर ध्यान दे और मुझे उत्तर दे, मेरी आंखों में ज्योति आने दे, नहीं तो मुझे मृत्यु की नींद आ जाएगी;"));
hinsuperobject.biblemap.put("b19_13_4", new hinsuperobject(19,13,4,"ऐसा न हो कि मेरा शत्रु कहे, कि मैं उस पर प्रबल हो गया; और ऐसा न हो कि जब मैं डगमगाने लगूं तो मेरे शत्रु मगन हों।।"));
hinsuperobject.biblemap.put("b19_13_5", new hinsuperobject(19,13,5,"परन्तु मैं ने तो तेरी करूणा पर भरोसा रखा है; मेरा हृदय तेरे उद्धार से मगन होगा।"));
hinsuperobject.biblemap.put("b19_13_6", new hinsuperobject(19,13,6,"मैं परमेश्वर के नाम का भजन गाऊंगा, क्योंकि उस ने मेरी भलाई की है।।"));
hinsuperobject.biblemap.put("b19_14_1", new hinsuperobject(19,14,1,"मूर्ख ने अपने मन में कहा है, कोई परमेश्वर है ही नहीं। वे बिगड़ गए, उन्हों ने घिनौने काम किए हैं, कोई सुकर्मी नहीं।"));
hinsuperobject.biblemap.put("b19_14_2", new hinsuperobject(19,14,2,"परमेश्वर ने स्वर्ग में से मनुष्यों पर दृष्टि की है, कि देखे कि कोई बुद्धिमान, कोई परमेश्वर का खोजी है या नहीं।"));
hinsuperobject.biblemap.put("b19_14_3", new hinsuperobject(19,14,3,"वे सब के सब भटक गए, वे सब भ्रष्ट हो गए; कोई सुकर्मी नहीं, एक भी नहीं।"));
hinsuperobject.biblemap.put("b19_14_4", new hinsuperobject(19,14,4,"क्या किसी अनर्थकारी को कुछ भी ज्ञान नहीं रहता, जो मेरे लोगों को ऐसे खा जाते हैं जैसे रोटी, और परमेश्वर का नाम नहीं लेते?"));
hinsuperobject.biblemap.put("b19_14_5", new hinsuperobject(19,14,5,"वहां उन पर भय छा गया, क्योंकि परमेश्वर धर्मी लोगों के बीच में निरन्तर रहता है।"));
hinsuperobject.biblemap.put("b19_14_6", new hinsuperobject(19,14,6,"तुम तो दीन की युक्ति की हंसी उड़ाते हो इसलिये कि यहोवा उसका शरणस्थान है।"));
hinsuperobject.biblemap.put("b19_14_7", new hinsuperobject(19,14,7,"भला हो कि इस्राएल का उद्धार सिरयोन से प्रगट होता! जब यहोवा अपनी प्रजा को दासत्व से लौटा ले आएगा, तब याकूब मगन और इस्राएल आनन्दित होगा।।"));
hinsuperobject.biblemap.put("b19_15_1", new hinsuperobject(19,15,1,"हे परमेश्वर तेरे तम्बू में कौन रहेगा? तेरे पवित्रा पर्वत पर कौन बसने पाएगा?"));
hinsuperobject.biblemap.put("b19_15_2", new hinsuperobject(19,15,2,"वह जो खराई से चलता और धर्म के काम करता है, और हृदय से सच बोलता है;"));
hinsuperobject.biblemap.put("b19_15_3", new hinsuperobject(19,15,3,"जो अपनी जीभ से निन्दा नहीं करता, और न अपने मित्रा की बुराई करता, और न अपने पड़ोसी की निन्दा सुनता है;"));
hinsuperobject.biblemap.put("b19_15_4", new hinsuperobject(19,15,4,"वह जिसकी दृष्टि में निकम्मा मनुष्य तुच्छ है, और जो यहोवा के डरवैयों का आदर करता है, जो शपथ खाकर बदलता नहीं चाहे हानि उठाना पड़े;"));
hinsuperobject.biblemap.put("b19_15_5", new hinsuperobject(19,15,5,"जो अपना रूपया ब्याज पर नहीं देता, और निर्दोष की हानि करने के लिये घूस नहीं लेता है। जो कोई ऐसी चाल चलता है वह कभी न डगमगाएगा।।"));
hinsuperobject.biblemap.put("b19_16_1", new hinsuperobject(19,16,1,"हे ईश्वर मेरी रक्षा कर, क्योंकि मैं तेरा ही शरणागत हूं।"));
hinsuperobject.biblemap.put("b19_16_2", new hinsuperobject(19,16,2,"मैं ने परमेश्वर से कहा है, कि तू ही मेरा प्रभु है; तेरे सिवाए मेरी भलाई कहीं नहीं।"));
hinsuperobject.biblemap.put("b19_16_3", new hinsuperobject(19,16,3,"पृथ्वी पर जो पवित्रा लोग हैं, वे ही आदर के योग्य हैं, और उन्हीं से मैं प्रसन्न हूं।"));
hinsuperobject.biblemap.put("b19_16_4", new hinsuperobject(19,16,4,"जो पराए देवता के पीछे भागते हैं उनका दु:ख बढ़ जाएगा; मैं उनके लोहूवाले तपावन नहीं तपाऊंगा और उनका नाम अपने ओठों से नहीं लूंगा।।"));
hinsuperobject.biblemap.put("b19_16_5", new hinsuperobject(19,16,5,"यहोवा मेरा भाग और मेरे कटोरे का हिस्सा है; मेरे बांट को तू स्थिर रखता है।"));
hinsuperobject.biblemap.put("b19_16_6", new hinsuperobject(19,16,6,"मेरे लिये माप की डोरी मनभावने स्थान में पड़ी, और मेरा भाग मनभावना है।।"));
hinsuperobject.biblemap.put("b19_16_7", new hinsuperobject(19,16,7,"मैं यहोवा को धन्य कहता हूं, क्योंकि उस ने मुझे सम्मत्ति दी है; वरन मेरा मन भी रात में मुझे शिक्षा देता है।"));
hinsuperobject.biblemap.put("b19_16_8", new hinsuperobject(19,16,8,"मैं ने यहोवा को निरन्तर अपने सम्मुख रखा है : इसलिये कि वह मेरे दहिने हाथ रहता है मैं कभी न डगमगाऊंगा।।"));
hinsuperobject.biblemap.put("b19_16_9", new hinsuperobject(19,16,9,"इस कारण मेरा हृदय आनन्दित और मेरी आत्मा मगन हुई; मेरा शरीर भी चैन से रहेगा।"));
hinsuperobject.biblemap.put("b19_16_10", new hinsuperobject(19,16,10,"क्योंकि तू मेरे प्राण को अधोलोक में न छोड़ेगा, न अपने पवित्रा भक्त को सड़ने देगा।।"));
hinsuperobject.biblemap.put("b19_16_11", new hinsuperobject(19,16,11,"तू मुझे जीवन का रास्ता दिखाएगा; तेरे निकट आनन्द की भरपूरी है, तेरे दहिने हाथ में सुख सर्वदा बना रहता है।।"));
hinsuperobject.biblemap.put("b19_17_1", new hinsuperobject(19,17,1,"हे यहोवा परमेश्वर सच्चाई के वचन सुन, मेरी पुकार की ओर ध्यान दे। मेरी प्रार्थना की ओर जो निष्कपट मुंह से निकलती है कान लगा।"));
hinsuperobject.biblemap.put("b19_17_2", new hinsuperobject(19,17,2,"मेरे मुक में का निर्णय तेरे सम्मुख हो! तेरी आंखें न्याय पर लगी रहें!"));
hinsuperobject.biblemap.put("b19_17_3", new hinsuperobject(19,17,3,"तू ने मेरे हृदय को जांचा है; तू ने रात को मेरी देखभाल की, तू ने मुझे परखा परन्तु कुछ भी खोटापन नहीं पाया; मैं ने ठान लिया है कि मेरे मुंह से अपराध की बात नहीं निकलेगी।"));
hinsuperobject.biblemap.put("b19_17_4", new hinsuperobject(19,17,4,"मानवी कामों में मैं तेरे मुंह के वचन के द्वारा क्रूरों की सी चाल से अपने को बचाए रहा।"));
hinsuperobject.biblemap.put("b19_17_5", new hinsuperobject(19,17,5,"मेरे पांव तेरे पथों में स्थिर रहे, फिसले नहीं।।"));
hinsuperobject.biblemap.put("b19_17_6", new hinsuperobject(19,17,6,"हे ईश्वर, मैं ने तुझ से प्रार्थना की है, क्योंकि तू मुझे उत्तर देगा। अपना कान मेरी ओर लगाकर मेरी बिनती सुन ले।"));
hinsuperobject.biblemap.put("b19_17_7", new hinsuperobject(19,17,7,"तू जो अपने दहिने हाथ के द्वारा अपने शरणगतों को उनके विरोधियों से बचाता है, अपनी अद्भुत करूणा दिखा।"));
hinsuperobject.biblemap.put("b19_17_8", new hinsuperobject(19,17,8,"अपने आंखो की पुतली की नाई सुरक्षित रख; अपने पंखों के तले मुझे छिपा रख,"));
hinsuperobject.biblemap.put("b19_17_9", new hinsuperobject(19,17,9,"उन दुष्टों से जो मुझ पर अत्याचार करते हैं, मेरे प्राण के शत्रुओं से जो मुझे घेरे हुए हैं।।"));
hinsuperobject.biblemap.put("b19_17_10", new hinsuperobject(19,17,10,"उन्हों ने अपने हृदयों को कठोर किया है; उनके मुंह से घमंड की बातें निकलती हैं।"));
hinsuperobject.biblemap.put("b19_17_11", new hinsuperobject(19,17,11,"उन्हों ने पग पग पर हमको घेरा है; वे हमको भूमि पर पटक देने के लिये घात लगाए हुए हैं।"));
hinsuperobject.biblemap.put("b19_17_12", new hinsuperobject(19,17,12,"वह उस सिंह की नाई है जो अपने शिकार की लालसा करता है, और जवान सिंह की नाई घात लगाने के स्थानों में बैठा रहता है।।"));
hinsuperobject.biblemap.put("b19_17_13", new hinsuperobject(19,17,13,"उठ, हे यहोवा उसका सामना कर और उसे पटक दे! अपनी तलवार के बल से मेरे प्राण को दुष्ट से बचा ले।"));
hinsuperobject.biblemap.put("b19_17_14", new hinsuperobject(19,17,14,"अपना हाथ बढ़ाकर हे यहोवा, मुझे मनुष्यों से बचा, अर्थात् संसारी मनुष्यों से जिनका भाग इसी जीवन में है, और जिनका पेट तू अपने भण्डार से भरता है। वे बालबच्चों से सन्तुष्ट हैं; और शेष सम्पति अपने बच्चों के लिये छोड़ जाते हैं।।"));
hinsuperobject.biblemap.put("b19_17_15", new hinsuperobject(19,17,15,"परन्तु मैं तो धर्मी होकर तेरे मुख का दर्शन करूंगा जब मैं जानूंगा तब तेरे स्वरूप से सन्तुष्ट हूंगा।।"));
hinsuperobject.biblemap.put("b19_18_1", new hinsuperobject(19,18,1,"हे परमेश्वर, हे मेरे बल, मैं तुझ से प्रेम करता हूं।"));
hinsuperobject.biblemap.put("b19_18_2", new hinsuperobject(19,18,2,"यहोवा मेरी चट्टान, और मेरा गढ़ और मेरा छुड़ानेवाला है; मेरा ईश्वर, मेरी चट्टान है, जिसका मैं शरणागत हूं, वह मेरी ढ़ाल और मेरी मुक्ति का गढ़ है।"));
hinsuperobject.biblemap.put("b19_18_3", new hinsuperobject(19,18,3,"मैं यहोवा को जो स्तुति के योग्य है पुकारूंगा; इस प्रकार मैं अपने शत्रुओं से बचाया जाऊंगा।।"));
hinsuperobject.biblemap.put("b19_18_4", new hinsuperobject(19,18,4,"मृत्यु की रस्सियों से मैं चारो ओर से घिर गया हूं, और अधर्म की बाढ़ ने मुझ को भयभीत कर दिया;"));
hinsuperobject.biblemap.put("b19_18_5", new hinsuperobject(19,18,5,"पाताल की रस्सियां मेरे चारो ओर थीं, और मृत्यु के फन्दे मुझ पर आए थे।"));
hinsuperobject.biblemap.put("b19_18_6", new hinsuperobject(19,18,6,"अपने संकट में मैं ने यहोवा परमेश्वर को पुकारा; मैं ने अपने परमेश्वर को दोहाई दी। और उस ने अपने मन्दिर में से मेरी बातें सुनी। और मेरी दोहाई उसके पास पहुंचकर उसके कानों में पड़ी।।"));
hinsuperobject.biblemap.put("b19_18_7", new hinsuperobject(19,18,7,"तब पृथ्वी हिल गई, और कांप उठी और पहाड़ों की नेवे कंपित होकर हिल गई क्योंकि वह अति क्रोधित हुआ था।"));
hinsuperobject.biblemap.put("b19_18_8", new hinsuperobject(19,18,8,"उसके नथनों से धुआं निकला, और उसके मुंह से आग निकलकर भस्म करने लगी; जिस से कोएले दहक उठे।"));
hinsuperobject.biblemap.put("b19_18_9", new hinsuperobject(19,18,9,"और वह स्वर्ग को नीचे झुकाकर उतर आया; और उसके पांवों तले घोर अन्धकार था।"));
hinsuperobject.biblemap.put("b19_18_10", new hinsuperobject(19,18,10,"और वह करूब पर सवार होकर उड़ा, वरन पवन के पंखों पर सवारी करके वेग से उड़ा।"));
hinsuperobject.biblemap.put("b19_18_11", new hinsuperobject(19,18,11,"उस ने अन्धियारे को अपने छिपने का स्थान और अपने चारों ओर मेघों के अन्धकार और आकाश की काली घटाओं का मण्डप बनाया।"));
hinsuperobject.biblemap.put("b19_18_12", new hinsuperobject(19,18,12,"उसकी उपस्थिति की झलक से उसकी काली घटाएं फट गई; ओले और अंगारे।"));
hinsuperobject.biblemap.put("b19_18_13", new hinsuperobject(19,18,13,"तब यहोवा आकाश में गरजा, और परमप्रधान ने अपनी वाणी सुनाई, ओले ओर अंगारे।।"));
hinsuperobject.biblemap.put("b19_18_14", new hinsuperobject(19,18,14,"उस ने अपने तीर चला चलाकर उनको तितर बितर किया; वरन बिजलियां गिरा गिराकर उनको परास्त किया।"));
hinsuperobject.biblemap.put("b19_18_15", new hinsuperobject(19,18,15,"तब जल के नाले देख पड़े, और जगत की नेवें प्रगट हुई, यह तो यहोवा तेरी डांट से, और तेरे नथनों की सांस की झोंक से हुआ।।"));
hinsuperobject.biblemap.put("b19_18_16", new hinsuperobject(19,18,16,"उस ने ऊपर से हाथ बढ़ाकर मुझे थांम लिया, और गहिरे जल में से खींच लिया।"));
hinsuperobject.biblemap.put("b19_18_17", new hinsuperobject(19,18,17,"उस ने मेरे बलवन्त शत्रु से, और उन से जो मुझ से घृणा करते थे मुझे छुडाया; क्योंकि वे अधिक सामर्थी थे।"));
hinsuperobject.biblemap.put("b19_18_18", new hinsuperobject(19,18,18,"मेरी विपत्ति के दिन वे मुझ पर आ पडे। परन्तु यहोवा मेरा आश्रय था।"));
hinsuperobject.biblemap.put("b19_18_19", new hinsuperobject(19,18,19,"और उस ने मुझे निकालकर चौड़े स्थान में पहुंचाया, उस ने मुझ को छुड़ाया, क्योंकि वह मुझ से प्रसन्न था।"));
hinsuperobject.biblemap.put("b19_18_20", new hinsuperobject(19,18,20,"यहोवा ने मुझ से मेरे धर्म के अनुसार व्यवहार किया; और मेरे हाथों की शुद्धता के अनुसार उस ने मुझे बदला दिया।"));
hinsuperobject.biblemap.put("b19_18_21", new hinsuperobject(19,18,21,"क्योंकि मैं यहोवा के मार्गों पर चलता रहा, और दुष्टता के कारण अपने परमेश्वर से दूर न हुआ।"));
hinsuperobject.biblemap.put("b19_18_22", new hinsuperobject(19,18,22,"क्योंकि उसके सारे निर्णय मेरे सम्मुख बने रहे और मैं ने उसकी विधियों को न त्यागा।"));
hinsuperobject.biblemap.put("b19_18_23", new hinsuperobject(19,18,23,"और मैं उसके सम्मुख सिद्ध बना रहा, और अधर्म से अपने को बचाए रहा।"));
hinsuperobject.biblemap.put("b19_18_24", new hinsuperobject(19,18,24,"यहोवा ने मुझे मेरे धर्म के अनुसार बदला दिया, और मेरे हाथों की उस शुद्धता के अनुसार जिसे वह देखता था।।"));
hinsuperobject.biblemap.put("b19_18_25", new hinsuperobject(19,18,25,"दयावन्त के साथ तू अपने को दयावन्त दिखाता; और खरे पुरूष के साथ तू अपने को खरा दिखाता है।"));
hinsuperobject.biblemap.put("b19_18_26", new hinsuperobject(19,18,26,"शुद्ध के साथ तू अपने को शुद्ध दिखाता, और टेढ़े के साथ तू तिर्छा बनता है।"));
hinsuperobject.biblemap.put("b19_18_27", new hinsuperobject(19,18,27,"क्योंकि तू दी लोगों को तो बचाता है; परन्तु घमण्ड भरी आंखों को नीची करता है।"));
hinsuperobject.biblemap.put("b19_18_28", new hinsuperobject(19,18,28,"हां, तू ही मेरे दीपक को जलाता है; मेरा परमेश्वर यहोवा मेरे अन्धियारे को उजियाला कर देता है।"));
hinsuperobject.biblemap.put("b19_18_29", new hinsuperobject(19,18,29,"क्योंकि तेरी सहायता से मैं सेना पर धावा करता हूं; और अपने परमेश्वर की सहायता से शहरपनाह को लांघ जाता हूं।"));
hinsuperobject.biblemap.put("b19_18_30", new hinsuperobject(19,18,30,"ईश्वर का मार्ग सच्चाई; यहोवा का वचन ताया हुआ है; वह अपने सब शरणागतों की ढाल है।।"));
hinsuperobject.biblemap.put("b19_18_31", new hinsuperobject(19,18,31,"यहोवा को छोड़ क्या कोई ईश्वर है? हमारे परमेश्वर को छोड़ क्या और कोई चट्टान है?"));
hinsuperobject.biblemap.put("b19_18_32", new hinsuperobject(19,18,32,"यह वही ईश्वर है, जो सामर्थ से मेरा कटिबन्ध बान्धता है, और मेरे मार्ग को सिद्ध करता है।"));
hinsuperobject.biblemap.put("b19_18_33", new hinsuperobject(19,18,33,"वही मेरे पैरों को हरिणियों के पैरों के समान बनाता है, और मुझे मेरे ऊंचे स्थानों पर खड़ा करता है।"));
hinsuperobject.biblemap.put("b19_18_34", new hinsuperobject(19,18,34,"वह मेरे हाथों को युद्ध करना सिखाता है, इसलिये मेरी बाहों से पीतल का धनुष झुक जाता है।"));
hinsuperobject.biblemap.put("b19_18_35", new hinsuperobject(19,18,35,"तू ने मुझ को अपने बचाव की ढाल दी है, तू अपने दहिने हाथ से मुझे सम्भाले हुए है, और मेरी नम्रता ने महत्व दिया है।"));
hinsuperobject.biblemap.put("b19_18_36", new hinsuperobject(19,18,36,"तू ने मेरे पैरों के लिये स्थान चौड़ा कर दिया, और मेरे पैर नहीं फिसले।"));
hinsuperobject.biblemap.put("b19_18_37", new hinsuperobject(19,18,37,"मैं अपने शत्रुओं का पीछा करके उन्हें पकड़ लूंगा; और जब तब उनका अन्त न करूं तब तक न लौटूंगा।"));
hinsuperobject.biblemap.put("b19_18_38", new hinsuperobject(19,18,38,"मैं उन्हें ऐसा बेधूंगा कि वे उठ न सकेंगे; वे मेरे पांवों के नीचे गिर पड़ेंगे।"));
hinsuperobject.biblemap.put("b19_18_39", new hinsuperobject(19,18,39,"क्योंकि तू ने युद्ध के लिये मेरी कमर में शक्ति का पटुका बान्धा है; और मेरे विरोधियों को मेरे सम्मुख नीचा कर दिया।"));
hinsuperobject.biblemap.put("b19_18_40", new hinsuperobject(19,18,40,"तू ने मेरे शत्रुओं की पीठ मेरी ओर फेर दी, ताकि मैं उनको काट डालूं जो मुझ से द्वेष रखते हैं।"));
hinsuperobject.biblemap.put("b19_18_41", new hinsuperobject(19,18,41,"उन्हों ने दोहाई तो दी परन्तु उन्हें कोई भी बचानेवाला न मिला, उन्हों ने यहोवा की भी दोहाई दी, परन्तु उस ने भी उनको उत्तर न दिया।"));
hinsuperobject.biblemap.put("b19_18_42", new hinsuperobject(19,18,42,"तब मैं ने उनको कूट कूटकर पवन से उड़ाई हुई धूलि के समान कर दिया; मैं ने उनको गली कूचों की कीचड़ के समान निकाल फेंका।।"));
hinsuperobject.biblemap.put("b19_18_43", new hinsuperobject(19,18,43,"तू ने मुझे प्रजा के झगड़ों से भी छुड़ाया; तू ने मुझे अन्यजातियों का प्रधान बनाया है; जिन लोगों को मैं जानता भी न था वे मेरे अधीन हो गये।"));
hinsuperobject.biblemap.put("b19_18_44", new hinsuperobject(19,18,44,"मेरा नाम सुनते ही वे मेरी आज्ञा का पालन करेंगे; परदेशी मेरे वश में हो जाएंगे।"));
hinsuperobject.biblemap.put("b19_18_45", new hinsuperobject(19,18,45,"परदेशी मुर्झा जाएंगे, और अपने किलों में से थरथराते हुए निकलेंगे।।"));
hinsuperobject.biblemap.put("b19_18_46", new hinsuperobject(19,18,46,"यहोवा परमेश्वर जीवित है; मेरी चट्टान धन्य है; और मेरे मुक्तिदाता परमेश्वर की बड़ाई हो।"));
hinsuperobject.biblemap.put("b19_18_47", new hinsuperobject(19,18,47,"धन्य है मेरा पलटा लेनेवाला ईश्वर! जिस ने देश देश के लोगों को मेरे वंश में कर दिया है;"));
hinsuperobject.biblemap.put("b19_18_48", new hinsuperobject(19,18,48,"और मुझे मेरे शत्रुओं से छुड़ाया है; तू मुझ को मेरे विरोधियों से ऊंचा करता, और उपद्रवी पुरूष से बचाता है।।"));
hinsuperobject.biblemap.put("b19_18_49", new hinsuperobject(19,18,49,"इस कारण मैं जाति जाति के साम्हने तेरा धन्यवाद करूंगा, और तेरे नाम का भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_18_50", new hinsuperobject(19,18,50,"वह अपने ठहराए हुए राजा का बड़ा उद्धार करता है, वह अपने अभिषिक्त दाऊद पर और उसके वंश पर युगानुयुग करूणा करता रहेगा।।"));
hinsuperobject.biblemap.put("b19_19_1", new hinsuperobject(19,19,1,"आकाश ईश्वर की महिमा वर्णन कर रहा है; और आकशमण्डल उसकी हस्तकला को प्रगट कर रहा है।"));
hinsuperobject.biblemap.put("b19_19_2", new hinsuperobject(19,19,2,"दिन से दिन बातें करता है, और रात को रात ज्ञान सिखाती है।"));
hinsuperobject.biblemap.put("b19_19_3", new hinsuperobject(19,19,3,"न तो कोई बोली है और न कोई भाषा जहां उनका शब्द सुनाई नहीं देता है।"));
hinsuperobject.biblemap.put("b19_19_4", new hinsuperobject(19,19,4,"उनका स्वर सारी पृथ्वी पर गूंज गया है, और उनके वचन जगत की छोर तक पहुंच गए हैं। उन में उस ने सूरर्य के लिये एक मण्डप खड़ा किया है,"));
hinsuperobject.biblemap.put("b19_19_5", new hinsuperobject(19,19,5,"जो दुल्हे के समान अपने महल से निकलता है। वह शूरवीर की नाई अपनी दौड़ दौड़ने को हर्षित होता है।"));
hinsuperobject.biblemap.put("b19_19_6", new hinsuperobject(19,19,6,"वह आकाश की एक छोर से निकलता है, और वह उसकी दूसरी छोर तक चक्कर मारता है; और उसकी गर्मी सबको पहुंचती है।।"));
hinsuperobject.biblemap.put("b19_19_7", new hinsuperobject(19,19,7,"यहोवा की व्यवस्था खरी है, वह प्राण को बहाल कर देती है; यहोवा के नियम विश्वासयोग्य हैं, साधारण लोगों को बुद्धिमान बना देते हैं;"));
hinsuperobject.biblemap.put("b19_19_8", new hinsuperobject(19,19,8,"यहोवा के उपदेश सिद्ध हैं, हृदय को आनन्दित कर देते हैं; यहोवा की आज्ञा निर्मल है, वह आंखों में ज्योति ले आती है;"));
hinsuperobject.biblemap.put("b19_19_9", new hinsuperobject(19,19,9,"यहोवा का भय पवित्रा है, वह अनन्तकाल तक स्थिर रहता है; यहोवा के नियम सत्य और पूरी रीति से धर्ममय हैं।"));
hinsuperobject.biblemap.put("b19_19_10", new hinsuperobject(19,19,10,"वे तो सोने से और बहुत कुन्दन से भी बढ़कर मनोहर हैं; वे मधु से और टपकनेवाले छत्ते से भी बढ़कर मधुर हैं।"));
hinsuperobject.biblemap.put("b19_19_11", new hinsuperobject(19,19,11,"और उन्हीं से तेरा दास चिताया जाता है; उनके पालन करने से बड़ा ही प्रतिफल मिलता है।"));
hinsuperobject.biblemap.put("b19_19_12", new hinsuperobject(19,19,12,"अपनी भूलचूक को कौन समझ सकता है? मेरे गुप्त पापों से तू मुझे पवित्रा कर।"));
hinsuperobject.biblemap.put("b19_19_13", new hinsuperobject(19,19,13,"तू अपने दास को ढिठाई के पापों से भी बचाए रख; वह मुझ पर प्रभुता करने न पाएं! तब मैं सिद्ध हो जाऊंगा, और बड़े अपराधों से बचा रहूंगा।।"));
hinsuperobject.biblemap.put("b19_19_14", new hinsuperobject(19,19,14,"मेरे मुंह के वचन और मेरे हृदय का ध्यान तेरे सम्मुख ग्रहण योग्य हों, हे यहोवा परमेश्वर, मेरी चट्टान और मेरे उद्धार करनेवाले!"));
hinsuperobject.biblemap.put("b19_20_1", new hinsuperobject(19,20,1,"संकट के दिन यहोवा तेरी सुन ले! याकूब के परमेश्वर का नाम तुझे ऊंचे स्थान पर नियुक्त करे!"));
hinsuperobject.biblemap.put("b19_20_2", new hinsuperobject(19,20,2,"वह पवित्रास्थान से तेरी सहायता करे, और सिरयोन से तुझे सम्भाल ले!"));
hinsuperobject.biblemap.put("b19_20_3", new hinsuperobject(19,20,3,"वह तेरे सब अन्नबलियों को स्मरण करे, और तेरे होमबलि को ग्रहण करे।"));
hinsuperobject.biblemap.put("b19_20_4", new hinsuperobject(19,20,4,"वह तेरे मन की इच्छा को पूरी करे, और तेरी सारी युक्ति को सुफल करे!"));
hinsuperobject.biblemap.put("b19_20_5", new hinsuperobject(19,20,5,"तब हम तेरे उद्धार के कारण ऊंचे स्वर से हर्षित होकर गाएंगे, और अपने परमेश्वर के नाम से झण्डे खड़े करेंगे। यहोवा तुझे मुंह मांगा बरदान दे्"));
hinsuperobject.biblemap.put("b19_20_6", new hinsuperobject(19,20,6,"अब मैं जान गया कि यहोवा अपने अभिषिक्त का उद्धार करता है; वह अपने दहिने हाथ के उद्धार करनेवाले पराक्रम से अपने पवित्रा स्वर्ग पर से सुनकर उसे उत्तर देगा।"));
hinsuperobject.biblemap.put("b19_20_7", new hinsuperobject(19,20,7,"किसी को रथों को, और किसी को घोड़ों का भरोसा है, परन्तु हम तो अपने परमेश्वर यहोवा ही का नाम लेंगे।"));
hinsuperobject.biblemap.put("b19_20_8", new hinsuperobject(19,20,8,"वे तो झुक गए और गिर पड़े परन्तु हम उठे और सीधे खड़े हैं।।"));
hinsuperobject.biblemap.put("b19_20_9", new hinsuperobject(19,20,9,"हे यहोवा, बचा ले; जिस दिन हम पुकारें तो महाराजा हमें उत्तर दे।।"));
hinsuperobject.biblemap.put("b19_21_1", new hinsuperobject(19,21,1,"हे यहोवा तेरी सामर्थ्य से राजा आनन्दित होगा; और तेरे किए हुए उद्धार से वह अति मगन होगा।"));
hinsuperobject.biblemap.put("b19_21_2", new hinsuperobject(19,21,2,"तू ने उसके मनोरथ को पूरा किया है, और उसके मुंह की बिनती को तू ने अस्वीकार नहीं किया।"));
hinsuperobject.biblemap.put("b19_21_3", new hinsuperobject(19,21,3,"क्योंकि तू उत्तम आशीषें देता हुआ उस से मिलता है और तू उसके सिर पर कुन्दन का मुकुट पहिनाता है।"));
hinsuperobject.biblemap.put("b19_21_4", new hinsuperobject(19,21,4,"उस ने तुझ से जीवन मांगा, ओर तू ने जीवनदान दिया; तू ने उसको युगानुयुग का जीवन दिया है।"));
hinsuperobject.biblemap.put("b19_21_5", new hinsuperobject(19,21,5,"तेरे उद्धार के कारण उसकी महिमा अधिक है; तू उसको विभव और ऐश्वर्य से आभूषित कर देता है।"));
hinsuperobject.biblemap.put("b19_21_6", new hinsuperobject(19,21,6,"क्योंकि तू ने उसको सर्वदा के लिये आशीषित किया है; तू अपने सम्मुख उसको हर्ष और आनन्द से भर देता है।"));
hinsuperobject.biblemap.put("b19_21_7", new hinsuperobject(19,21,7,"क्योंकि राजा का भरोसा यहोवा के ऊपर है; और परमप्रधान की करूणा से वह कभी नहीं टलने का।।"));
hinsuperobject.biblemap.put("b19_21_8", new hinsuperobject(19,21,8,"तेरा हाथ तेरे सब शत्रुओं को ढूंढ़ निकालेगा, तेरा दहिना हाथ तेरे सब बैरियों का पता लगा लेगा।"));
hinsuperobject.biblemap.put("b19_21_9", new hinsuperobject(19,21,9,"तू अपने मुख के सम्मुख उन्हें जलते हुए भट्टे की नाई जलाएगा। यहोवा अपने क्रोध में उन्हें निगल जाएगा, और आग उनको भस्म कर डालेगी।"));
hinsuperobject.biblemap.put("b19_21_10", new hinsuperobject(19,21,10,"तू उनके फलों को पृथ्वी पर से, और उनके वंश को मनुष्यों में से नष्ट करेगा।"));
hinsuperobject.biblemap.put("b19_21_11", new hinsuperobject(19,21,11,"क्योंकि उन्हों ने तेरी हाति ठानी है, उन्हों ने ऐसी युक्ति निकाली है जिसे वे पूरी न कर सकेंगे।"));
hinsuperobject.biblemap.put("b19_21_12", new hinsuperobject(19,21,12,"क्योंकि तू अपना धुनष उनके विरूद्ध चढ़ाएगा, और वे पीठ दिखाकर भागेंगे।।"));
hinsuperobject.biblemap.put("b19_21_13", new hinsuperobject(19,21,13,"हे यहोवा, अपनी सामर्थ्य में महान हो! और हम गा गाकर तेरे पराक्रम का भजन सुनाएंगे।।"));
hinsuperobject.biblemap.put("b19_22_1", new hinsuperobject(19,22,1,"हे मेरे परमेश्वर, हे मेरे परमेश्वर, तू ने मुझे क्यों छोड़ दिया? तू मेरी पुकार से और मेरी सहायत करने से क्यों दूर रहता है? मेरा उद्धार कहां है?"));
hinsuperobject.biblemap.put("b19_22_2", new hinsuperobject(19,22,2,"हे मेरे परमेश्वर, मैं दिन को पुकारता हूं परन्तु तू उत्तर नहीं देता; और रात को भी मैं चुप नहीं रहता।"));
hinsuperobject.biblemap.put("b19_22_3", new hinsuperobject(19,22,3,"परन्तु हे तू जो इस्राएल की स्तुति के सिहांसन पर विराजमान है, तू तो पवित्रा है।"));
hinsuperobject.biblemap.put("b19_22_4", new hinsuperobject(19,22,4,"हमारे पुरखा तुझी पर भरोसा रखते थे; वे भरोसा रखते थे, और तू उन्हें छुड़ाता था।"));
hinsuperobject.biblemap.put("b19_22_5", new hinsuperobject(19,22,5,"उन्हों ने तेरी दोहाई दी और तू ने उनको छुड़ाया वे तुझी पर भरोसा रखते थे और कभी लज्जित न हुए।।"));
hinsuperobject.biblemap.put("b19_22_6", new hinsuperobject(19,22,6,"परन्तु मैं तो कीड़ा हूं, मनुष्य नहीं; मनुष्यों में मेरी नामधराई है, और लोगों में मेरा अपमान होता है।"));
hinsuperobject.biblemap.put("b19_22_7", new hinsuperobject(19,22,7,"वह सब जो मुझे देखते हैं मेरा ठट्ठा करते हैं, और ओंठ बिचकाते और यह कहते हुए सिर हिलाते हैं,"));
hinsuperobject.biblemap.put("b19_22_8", new hinsuperobject(19,22,8,"कि अपने को यहोवा के वश में कर दे वही उसको छुड़ाए, वह उसको उबारे क्योंकि वह उस से प्रसन्न है।"));
hinsuperobject.biblemap.put("b19_22_9", new hinsuperobject(19,22,9,"परन्तु तू ही ने मुझे गर्भ से निकाला; जब मैं दूधपिउवा बच्च था, तब ही से तू ने मुझे भरोसा रखना सिखलाया।"));
hinsuperobject.biblemap.put("b19_22_10", new hinsuperobject(19,22,10,"मैं जन्मते ही तुझी पर छोड़ दिया गया, माता के गर्भ ही से तू मेरा ईश्वर है।"));
hinsuperobject.biblemap.put("b19_22_11", new hinsuperobject(19,22,11,"मुझ से दूर न हो क्योंकि संकट निकट है, और कोई सहायक नहीं।"));
hinsuperobject.biblemap.put("b19_22_12", new hinsuperobject(19,22,12,"बहुत से सांढ़ों ने मुझे घेर लिया है, बाशान के बलवन्त सांढ़ मेरे चारों ओर मुझे घेरे हुए है।"));
hinsuperobject.biblemap.put("b19_22_13", new hinsuperobject(19,22,13,"वह फाड़ने और गरजनेवाले सिंह की नाईं मुझ पर अपना मुंह पसारे हुए है।।"));
hinsuperobject.biblemap.put("b19_22_14", new hinsuperobject(19,22,14,"मैं जल की नाईं बह गया, और मेरी सब हडि्डयों के जोड़ उखड़ गए: मेरा हृदय मोम हो गया, वह मेरी देह के भीतर पिघल गया।"));
hinsuperobject.biblemap.put("b19_22_15", new hinsuperobject(19,22,15,"मेरा बल टूट गया, मैं ठीकरा हो गया; और मेरी जीभ मेरे तालू से चिपक गई; और तू मुझे मारकर मिट्टी में मिला देता है।"));
hinsuperobject.biblemap.put("b19_22_16", new hinsuperobject(19,22,16,"क्योंकि कुत्तों ने मुझे घेर लिया है; कुकर्मियों की मण्डली मेरी चरों ओर मुझे घेरे हुए है; वह मेरे हाथ और मेरे पैर छेदते हैं।"));
hinsuperobject.biblemap.put("b19_22_17", new hinsuperobject(19,22,17,"मैं अपनी सब हडि्डयां गिन सकता हूं; वे मुझे देखते और निहारते हैं;"));
hinsuperobject.biblemap.put("b19_22_18", new hinsuperobject(19,22,18,"वे मेरे वस्त्रा आपस में बांटते हैं, और मेरे पहिरावे पर चिट्ठी डालते हैं।"));
hinsuperobject.biblemap.put("b19_22_19", new hinsuperobject(19,22,19,"परन्तु हे यहोवा तू दूर न रह! हे मेरे सहायक, मेरी सहायता के लिये फुर्ती कर!"));
hinsuperobject.biblemap.put("b19_22_20", new hinsuperobject(19,22,20,"मेरे प्राण को तलवार से बचा, मेरे प्राण को कुत्ते के पंजे से बचा ले!"));
hinsuperobject.biblemap.put("b19_22_21", new hinsuperobject(19,22,21,"मुझे सिंह के मुंह से बचा, हां, जंगती सांढ़ों के सींगो में से तू ने मुझे बचा लिया है।।"));
hinsuperobject.biblemap.put("b19_22_22", new hinsuperobject(19,22,22,"मैं अपन भाइयों के साम्हने तेरे नाम का प्रचार करूंगा; सभा के बीच में तेरी प्रशंसा करूंगा।"));
hinsuperobject.biblemap.put("b19_22_23", new hinsuperobject(19,22,23,"हे यहोवा के डरवैयों उसकी स्तुति करो! हे याकूब के वंश, तुम उसका भय मानो!"));
hinsuperobject.biblemap.put("b19_22_24", new hinsuperobject(19,22,24,"क्योंकि उस ने दु:खी को तुच्छ नहीं जाना और न उस से घृणा करता है, ओर न उस से अपना मुख छिपाता है; पर जब उस ने उसकी दोहाई दी, तब उसकी सुन ली।।"));
hinsuperobject.biblemap.put("b19_22_25", new hinsuperobject(19,22,25,"बड़ी सभा में मेरा स्तुति करना तेरी ही ओर से होता है; मैं अपने प्रण को उस से भय रखनेवालों के साम्हने पूरा करूंगा"));
hinsuperobject.biblemap.put("b19_22_26", new hinsuperobject(19,22,26,"नम्र लोग भोजन करके तृप्त होंगे; जो यहोवा के खोजी हैं, वे उसकी स्तुति करेंगे। तुम्हारे प्राण सर्वदा जीवित रहें!"));
hinsuperobject.biblemap.put("b19_22_27", new hinsuperobject(19,22,27,"पृथ्वी के सब दूर दूर देशों के लोग उसको स्मरण करेंगे और उसकी ओर फिरेंगे; और जाति जाति के सब कुल तेरे साम्हने दण्डवत् करेंगे।"));
hinsuperobject.biblemap.put("b19_22_28", new hinsuperobject(19,22,28,"क्योंकि राज्य यहोवा की का है, और सब जातियों पर वही प्रभुता करता है।।"));
hinsuperobject.biblemap.put("b19_22_29", new hinsuperobject(19,22,29,"पृथ्वी के सब हृष्टपुष्ट लोग भोजन करके दण्डवत् करेंगे; वह सब जितने मिट्टी में मिल जाते हैं और अपना अपना प्राण नहीं बचा सकते, वे सब उसी के साम्हने घुटने टेकेंगे।"));
hinsuperobject.biblemap.put("b19_22_30", new hinsuperobject(19,22,30,"एक वंश उसकी सेवा करेगा; दूसरा पीढ़ी से प्रभु का वर्णन किया जाएगा।"));
hinsuperobject.biblemap.put("b19_22_31", new hinsuperobject(19,22,31,"वह आएंगे और उसके धर्म के कामों को एक वंश पर जो उत्पन्न होगा यह कहकर प्रगट करेंगे कि उस ने ऐसे ऐसे अद्भुत काम किए।।"));
hinsuperobject.biblemap.put("b19_23_1", new hinsuperobject(19,23,1,"यहोवा मेरा चरवाहा है, मुझे कुछ घटी न होगी।"));
hinsuperobject.biblemap.put("b19_23_2", new hinsuperobject(19,23,2,"वह मुझे हरी हरी चराइयों में बैठाता है; वह मुझे सुखदाई जल के झरने के पास ले चलता है;"));
hinsuperobject.biblemap.put("b19_23_3", new hinsuperobject(19,23,3,"वह मेरे जी में जी ले आता है। धर्म के मार्गो में वह अपने नाम के निमित्त अगुवाई करता है।"));
hinsuperobject.biblemap.put("b19_23_4", new hinsuperobject(19,23,4,"चाहे मैं घोर अन्धकार से भरी हुई तराई में होकर चलूं, तौभी हानि से न डरूंगा, क्योंकि तू मेरे साथ रहता है; तेरे सोंटे और तेरी लाठी से मुझे शान्ति मिलती है।।"));
hinsuperobject.biblemap.put("b19_23_5", new hinsuperobject(19,23,5,"तू मेरे सतानेवालों के साम्हने मेरे लिये मेज बिछाता है; तू ने मेरे सिर पर तेल मला है, मेरा कटोरा उमण्ड रहा है।"));
hinsuperobject.biblemap.put("b19_23_6", new hinsuperobject(19,23,6,"निश्चय भलाई और करूणा जीवन भर मेरे साथ साथ बनी रहेंगी; और मैं यहोवा के धाम में सर्वदा वास करूंगा।।"));
hinsuperobject.biblemap.put("b19_24_1", new hinsuperobject(19,24,1,"पृथ्वी और जो कुछ उस में है यहोवा ही का है; जगत और उस में निवास करनेवाले भी।"));
hinsuperobject.biblemap.put("b19_24_2", new hinsuperobject(19,24,2,"क्योंकि उसी ने उसकी नींव समुद्रों के ऊपर दृढ़ करके रखी, और महानदों के ऊपर स्थिर किया है।।"));
hinsuperobject.biblemap.put("b19_24_3", new hinsuperobject(19,24,3,"यहोवा के पर्वत पर कौन चढ़ सकता है? और उसके पवित्रास्थान में कौन खड़ा हो सकता है?"));
hinsuperobject.biblemap.put("b19_24_4", new hinsuperobject(19,24,4,"जिसके काम निर्दोष और हृदय शुद्ध है, जिस ने अपने मन को व्यर्थ बात की ओर नहीं लगाया, और न कपट से शपथ खाई है।"));
hinsuperobject.biblemap.put("b19_24_5", new hinsuperobject(19,24,5,"वह यहोवा की ओर से आशीष पाएगा, और अपने उद्धार करनेवाले परमेश्वर की ओर से धर्मी ठहरेगा।"));
hinsuperobject.biblemap.put("b19_24_6", new hinsuperobject(19,24,6,"ऐसे ही लोग उसके खोजी है, वे तेरे दर्शन के खोजी याकूबवंशी हैं।।"));
hinsuperobject.biblemap.put("b19_24_7", new hinsuperobject(19,24,7,"हे फाटकों, अपने सिर ऊंचे करो। हे सनातन के द्वारों, ऊंचे हो जाओ। क्योंकि प्रतापी राजा प्रवेश करेगा।"));
hinsuperobject.biblemap.put("b19_24_8", new hinsuperobject(19,24,8,"वह प्रतापी राजा कौन है? परमेश्वर जो सामर्थी और पराक्रमी है, परमेश्वर जो युद्ध में पराक्रमी है!"));
hinsuperobject.biblemap.put("b19_24_9", new hinsuperobject(19,24,9,"हे फाटकों, अपने सिर ऊंचे करो हे सनातन के द्वारों तुम भी खुल जाओ! क्योंकि प्रतापी राजा प्रवेश करेगा!"));
hinsuperobject.biblemap.put("b19_24_10", new hinsuperobject(19,24,10,"वह प्रतापी राजा कौन है? सेनाओं का यहोवा, वही प्रतापी राजा है।।"));
hinsuperobject.biblemap.put("b19_25_1", new hinsuperobject(19,25,1,"हे यहोवा मैं अपने मन को तेरी ओर उठाता हूं।"));
hinsuperobject.biblemap.put("b19_25_2", new hinsuperobject(19,25,2,"हे मेरे परमेश्वर, मैं ने तुझी पर भरोसा रखा है, मुझे लज्जित होने न दे; मेरे शत्रु मुझ पर जयजयकार करने न पाएं।"));
hinsuperobject.biblemap.put("b19_25_3", new hinsuperobject(19,25,3,"वरन जितने तेरी बाट जोहते हैं उन में से कोई लज्जित न होगा; परन्तु जो अकारण विश्वासघाती हैं वे ही लज्जित होंगे।।"));
hinsuperobject.biblemap.put("b19_25_4", new hinsuperobject(19,25,4,"हे यहोवा अपने मार्ग मुझ को दिखला; अपना पथ मुझे बता दे।"));
hinsuperobject.biblemap.put("b19_25_5", new hinsuperobject(19,25,5,"मुझे अपने सत्य पर चला और शिक्षा दे, क्योंकि तू मेरा उद्वार करनेवाला परमेश्वर है; मैं दिन भर तेरी ही बाट जाहता रहता हूं।"));
hinsuperobject.biblemap.put("b19_25_6", new hinsuperobject(19,25,6,"हे यहोवा अपनी दया और करूणा के कामों को स्मरण कर; क्योंकि वे तो अनन्तकाल से होते आए हैं।"));
hinsuperobject.biblemap.put("b19_25_7", new hinsuperobject(19,25,7,"हे यहोवा अपनी भलाई के कारण मेरी जवानी के पापों और मेरे अपराधों को स्मरण न कर; अपनी करूणा ही के अनुसार तू मुझे स्मरण कर।।"));
hinsuperobject.biblemap.put("b19_25_8", new hinsuperobject(19,25,8,"यहोवा भला और सीधा है; इसलिये वह पापियों को अपना मार्ग दिखलाएगा।"));
hinsuperobject.biblemap.put("b19_25_9", new hinsuperobject(19,25,9,"वह नम्र लोगों को न्याय की शिक्षा देता, हां वह नम्र लोगों को अपना मार्ग दिखलाएगा।"));
hinsuperobject.biblemap.put("b19_25_10", new hinsuperobject(19,25,10,"जो यहोवा की वाचा और चितौनियों को मानते हैं, उनके लिये उसके सब मार्ग करूणा और सच्चाई हैं।।"));
hinsuperobject.biblemap.put("b19_25_11", new hinsuperobject(19,25,11,"हे यहोवा अपने नाम के निमित्त मेरे अधर्म को जो बहुत हैं क्षमा कर।।"));
hinsuperobject.biblemap.put("b19_25_12", new hinsuperobject(19,25,12,"वह कौन है जो यहोवा का भय मानता है? यहोवा उसको उसी मार्ग पर जिस से वह प्रसन्न होता है चलाएगा।"));
hinsuperobject.biblemap.put("b19_25_13", new hinsuperobject(19,25,13,"वह कुशल से टिका रहेगा, और उसका वंश पृथ्वी पर अधिकारी होगा।"));
hinsuperobject.biblemap.put("b19_25_14", new hinsuperobject(19,25,14,"यहोवा के भेद को वही जानते हैं जो उस से डरते हैं, और वह अपनी वाचा उन पर प्रगट करेगा।"));
hinsuperobject.biblemap.put("b19_25_15", new hinsuperobject(19,25,15,"मेरी आंखे सदैव यहोवा पर टकटकी लगाए रहती हैं, क्योंकि वही मेरे पांवों को जाल में से छुड़ाएगा।।"));
hinsuperobject.biblemap.put("b19_25_16", new hinsuperobject(19,25,16,"हे यहोवा मेरी ओर फिरकर मुझ पर अनुग्रह कर; क्योंकि मैं अकेला और दीन हूं।"));
hinsuperobject.biblemap.put("b19_25_17", new hinsuperobject(19,25,17,"मेरे हृदय का क्लेश बढ़ गया है, तू मुझ को मेरे दु:खों से छुड़ा ले।"));
hinsuperobject.biblemap.put("b19_25_18", new hinsuperobject(19,25,18,"तू मेरे दु:ख और कष्ट पर दृष्टि कर, और मेरे सब पापों को क्षमा कर।।"));
hinsuperobject.biblemap.put("b19_25_19", new hinsuperobject(19,25,19,"मेरे शत्रुओं को देख कि वे कैसे बढ़ गए हैं, और मुझ से बड़ा बैर रखते हैं।"));
hinsuperobject.biblemap.put("b19_25_20", new hinsuperobject(19,25,20,"मेरे प्राण की रक्षा कर, और मुझे छुड़ा; मुझे लज्जित न होने दे, क्योंकि मैं तेरा शरणागत हूं।"));
hinsuperobject.biblemap.put("b19_25_21", new hinsuperobject(19,25,21,"खराई और सीधाई मुझे सुरक्षित रखें, क्योंकि मुझे तेरे ही आशा है।।"));
hinsuperobject.biblemap.put("b19_25_22", new hinsuperobject(19,25,22,"हे परमेश्वर इस्राएल को उसके सारे संकटों से छुड़ा ले।।"));
hinsuperobject.biblemap.put("b19_26_1", new hinsuperobject(19,26,1,"हे यहोवा, मेरा न्याय कर, क्योंकि मैं खराई से चलता रहा हूं, और मेरा भरोसा यहोवा पर अटल बना है।"));
hinsuperobject.biblemap.put("b19_26_2", new hinsuperobject(19,26,2,"हे यहोवा, मुझ को जांच और परख; मेरे मन और हृदय को परख।"));
hinsuperobject.biblemap.put("b19_26_3", new hinsuperobject(19,26,3,"क्योंकि तेरी करूणा तो मेरी आंखों के साम्हने है, और मैं तेरे सत्य मार्ग पर चलता रहा हूं।।"));
hinsuperobject.biblemap.put("b19_26_4", new hinsuperobject(19,26,4,"मैं निकम्मी चाल चलनेवालों के संग नहीं बैठा, और न मैं कपटियों के साथ कहीं जाऊंगा;"));
hinsuperobject.biblemap.put("b19_26_5", new hinsuperobject(19,26,5,"मैं कुकर्मियों की संगति से घृणा रखता हूं, और दुष्टों के संग न बैठूंगा।।"));
hinsuperobject.biblemap.put("b19_26_6", new hinsuperobject(19,26,6,"मैं अपने हाथों को निर्दोषता के जल से धोऊंगा, तब हे यहोवा मैं तेरी वेदी की प्रदक्षिणा करूंगा,"));
hinsuperobject.biblemap.put("b19_26_7", new hinsuperobject(19,26,7,"ताकि मेरा धन्यवाद ऊंचे शब्द से करूं,"));
hinsuperobject.biblemap.put("b19_26_8", new hinsuperobject(19,26,8,"और तेरे सब आश्चर्यकर्मों का वर्णन करूं।। हे यहोवा, मैं तेरे धाम से तेरी महिमा के निवासस्थान से प्रीति रखता हूं।"));
hinsuperobject.biblemap.put("b19_26_9", new hinsuperobject(19,26,9,"मेरे प्राण को पापियों के साथ, और मेरे जीवन को हत्यारों के साथ न मिला।"));
hinsuperobject.biblemap.put("b19_26_10", new hinsuperobject(19,26,10,"वे तो ओछापन करने में लगे रहते हैं, और उनका दहिना हाथ घूस से भरा रहता है।।"));
hinsuperobject.biblemap.put("b19_26_11", new hinsuperobject(19,26,11,"परन्तु मैं तो खराई से चलता रहूंगा। तू मुझे छुड़ा ले, और मुझ पर अनुग्रह कर।"));
hinsuperobject.biblemap.put("b19_26_12", new hinsuperobject(19,26,12,"मेरे पांव चौरस स्थान में स्थिर है; सभाओं में मैं यहोवा को धन्य कहा करूंगा।।"));
hinsuperobject.biblemap.put("b19_27_1", new hinsuperobject(19,27,1,"यहोवा परमेश्वर मेरी ज्योति और मेरा उद्धार है; मैं किस से डरूं? यहोवा मेरे जीवन का दृढ़ गढ़ ठहरा है, मैं किस का भय खाऊं?"));
hinsuperobject.biblemap.put("b19_27_2", new hinsuperobject(19,27,2,"जब कुकर्मियों ने जो मुझे सताते और मुझी से बैर रखते थे, मुझे खा डालने के लिये मुझ पर चढ़ाई की, तब वे ही ठोकर खाकर गिर पड़ें।।"));
hinsuperobject.biblemap.put("b19_27_3", new hinsuperobject(19,27,3,"चाहे सेना भी मेरे विरूद्ध छावनी डाले, तौभी मैं न डरूंगा; चाहे मेरे विरूद्ध लड़ाई ठन जाए, उस दशा में भी मैं हियाव बान्धे निशिजित रहूंगा।।"));
hinsuperobject.biblemap.put("b19_27_4", new hinsuperobject(19,27,4,"एक वर मैं ने यहोवा से मांगा है, उसी के यत्न में लगा रहूंगा; कि मैं जीवन भर यहोवा के भवन में रहने पाऊं, जिस से यहोवा की मनोहरता पर दृष्टि लगाए रहूं, और उसके मन्दिर में ध्यान किया करूं।।"));
hinsuperobject.biblemap.put("b19_27_5", new hinsuperobject(19,27,5,"क्योंकि वह तो मुझे विपत्ति के दिन में अपने मण्डप में छिपा रखेगा; अपने तम्बू के गुप्तस्थान में वह मुझे छिपा लेगा, और चट्टान पर चढ़ाएगा।"));
hinsuperobject.biblemap.put("b19_27_6", new hinsuperobject(19,27,6,"अब मेरा सिर मेरे चारों ओर के शत्रुओं से ऊंचा होगा; और मैं यहोवा के तम्बू में जयजयकार के साथ बलिदान चढ़ाऊंगा; और उसका भजन गाऊंगा।।"));
hinsuperobject.biblemap.put("b19_27_7", new hinsuperobject(19,27,7,"हे यहोवा, मेरा शब्द सुन, मैं पुकारता हूं, तू मुझ पर अनुग्रह कर और मुझे उत्तर दे।"));
hinsuperobject.biblemap.put("b19_27_8", new hinsuperobject(19,27,8,"तू ने कहा है, कि मेरे दर्शन के खोजी हो। इसलिये मेरा मन तुझ से कहता है, कि हे यहोवा, तेरे दर्शन का मैं खोजी रहूंगा।"));
hinsuperobject.biblemap.put("b19_27_9", new hinsuperobject(19,27,9,"अपना मुख मुझ से न छिपा।। अपने दास को क्रोध करके न हटा, तू मेरा सहायक बना है। हे मेरे उद्धार करनेवाले परमेश्वर मुझे त्याग न दे, और मुझे छोड़ न दे!"));
hinsuperobject.biblemap.put("b19_27_10", new hinsuperobject(19,27,10,"मेरे माता पिता ने तो मुझे छोड़ दिया है, परन्तु यहोवा मुझे सम्भाल लेगा।।"));
hinsuperobject.biblemap.put("b19_27_11", new hinsuperobject(19,27,11,"हे यहोवा, अपने मार्ग में मेरी अगुवाई कर, और मेरे द्रोहियों के कारण मुण् को चौरस रास्ते पर ले चल।"));
hinsuperobject.biblemap.put("b19_27_12", new hinsuperobject(19,27,12,"मुझ को मेरे सतानेवालों की इच्छा पर न छोड़, क्योंकि झूठे साक्षी जो उपद्रव करने की धुन में हैं मेरे विरूद्ध उठे हैं।।"));
hinsuperobject.biblemap.put("b19_27_13", new hinsuperobject(19,27,13,"यदि मुझे विश्वास न होता कि जीवितों की पृथ्वी पर यहोवा की भलाई को देखूंगा, तो मैं मूच्छित हो जाता।"));
hinsuperobject.biblemap.put("b19_27_14", new hinsuperobject(19,27,14,"यहोवा की बाट जोहता रह; हियाव बान्ध और तेरा हृदय दृढ़ रहे; हां, यहोवा ही की बाट जोहता रह!"));
hinsuperobject.biblemap.put("b19_28_1", new hinsuperobject(19,28,1,"हे यहोवा, मैं तुझी को पुकारूंगा; हे मेरी चट्टान, मेरी सुनी अनसुनी न कर, ऐसा न हो कि तेरे चुप रहने से मैं कब्र में पड़े हुओं के समान हो जाऊं जो पाताल में चले जाते हैं।"));
hinsuperobject.biblemap.put("b19_28_2", new hinsuperobject(19,28,2,"जब मैं तेरी दोहाई दूं, और तेरे पवित्रास्थान की भीतरी कोठरी की ओर अपने हाथ उठाऊं, तब मेरी गिड़गिड़ाहट की बात सुन ले।"));
hinsuperobject.biblemap.put("b19_28_3", new hinsuperobject(19,28,3,"उन दुष्टों और अनर्थकारियों के संग मुझे न घसीट; जो अपने पड़ोसियों से बातें तो मेल की बोलते हैं परन्तु हृदय में बुराई रखते हैं।"));
hinsuperobject.biblemap.put("b19_28_4", new hinsuperobject(19,28,4,"उनके कामों के और उनकी करनी की बुराई के अनुसार उन से बर्ताव कर, उनके हाथों के काम के अनुसार उन्हें बदला दे; उनके कामों का पलटा उन्हें दे।"));
hinsuperobject.biblemap.put("b19_28_5", new hinsuperobject(19,28,5,"वे यहोवा के कामों पर और उसके हाथों के कामों पर ध्यान नहीं करते, इसलिये वह उन्हें पछाड़ेगा और फिर न उठाएगा।।"));
hinsuperobject.biblemap.put("b19_28_6", new hinsuperobject(19,28,6,"यहोवा धन्य है; क्योंकि उस ने मेरी गिड़गिड़ाहट को सुना है।"));
hinsuperobject.biblemap.put("b19_28_7", new hinsuperobject(19,28,7,"यहोवा मेरा बल और मेरी ढ़ाल है; उस पर भरोसा रखने से मेरे मन को सहायता मिली है; इसलिये मेरा हृदय प्रफुल्लित है; और मैं गीत गाकर उसका धन्यवाद करूंगा।"));
hinsuperobject.biblemap.put("b19_28_8", new hinsuperobject(19,28,8,"यहोवा उनका बल है, वह अपने अभिषिक्त के लिये उद्धार का दृढ़ गढ़ है।"));
hinsuperobject.biblemap.put("b19_28_9", new hinsuperobject(19,28,9,"हे यहोवा अपनी प्रजा का उद्धार कर, और अपने निज भाग के लोगों को आशीष दे; और उनकी चरवाही कर और सदैव उन्हें सम्भाले रह।।"));
hinsuperobject.biblemap.put("b19_29_1", new hinsuperobject(19,29,1,"हे परमेश्वर के पुत्रों यहोवा का, हां यहोवा की का गुणानुवाद करो, यहोवा की महिमा और सामर्थ को सराहो।"));
hinsuperobject.biblemap.put("b19_29_2", new hinsuperobject(19,29,2,"यहोवा के नाम की महिमा करो; पवित्राता से शोभायमान होकर यहोवा को दण्डवत् करो।"));
hinsuperobject.biblemap.put("b19_29_3", new hinsuperobject(19,29,3,"यहोवा की वाणी मेघों के ऊपर सुन पड़ती है; प्रतामी ईश्वर गरजता है, यहोवा घने मेघों के ऊपर रहता है।"));
hinsuperobject.biblemap.put("b19_29_4", new hinsuperobject(19,29,4,"यहोवा की वाणी शक्तिशाली है, यहोवा की वाणी प्रतापमय है।"));
hinsuperobject.biblemap.put("b19_29_5", new hinsuperobject(19,29,5,"यहोवा की वाणी देवदारों को तोड़ डालती है; यहोवा लबानोन के देवदारों को भी तोड़ डालता है।"));
hinsuperobject.biblemap.put("b19_29_6", new hinsuperobject(19,29,6,"वह उन्हें बछड़े की नाई और लबानोन और शिर्योन को जंगली बछड़े के समान उछालता है।।"));
hinsuperobject.biblemap.put("b19_29_7", new hinsuperobject(19,29,7,"यहोवा की वाणी आग की लपटों को चीरती है।"));
hinsuperobject.biblemap.put("b19_29_8", new hinsuperobject(19,29,8,"यहोवा की वाणी वन को हिला देती है, यहोवा कादेश के वन को भी कंपाता है।।"));
hinsuperobject.biblemap.put("b19_29_9", new hinsuperobject(19,29,9,"यहोवा की वाणी से हरिणियों का गर्भपात हो जाता है। और अरण्य में पतझड़ होती है; और उसके मन्दिर में सब कोई महिमा ही महिमा बोलता रहता है।।"));
hinsuperobject.biblemap.put("b19_29_10", new hinsuperobject(19,29,10,"जलप्रलय के समय यहोवा विराजमान था; और यहोवा सर्वदा के लिये राजा होकर विराजमान रहता है।"));
hinsuperobject.biblemap.put("b19_29_11", new hinsuperobject(19,29,11,"यहोवा अपनी प्रजा को बल देगा; यहोवा अपनी प्रजा को शान्ति की आशीष देगा।।"));
hinsuperobject.biblemap.put("b19_30_1", new hinsuperobject(19,30,1,"हे यहोवा मैं तुझे सराहूंगा, क्योंकि तू ने मुझे खींचकर निकाला है, और मेरे शत्रुओं को मुझ पर आनन्द करने नहीं दिया।"));
hinsuperobject.biblemap.put("b19_30_2", new hinsuperobject(19,30,2,"हे मेरे परमेश्वर यहोवा, मैं ने तेरी दोहाई दी और तू ने मुझे चंगा किया है।"));
hinsuperobject.biblemap.put("b19_30_3", new hinsuperobject(19,30,3,"हे यहोवा, तू ने मेरा प्राण अधोलोक में से निकाला है, तू ने मुझ को जीवित रखा और कब्र में पड़ने से बचाया है।।"));
hinsuperobject.biblemap.put("b19_30_4", new hinsuperobject(19,30,4,"हे यहोवा के भक्तों, उसका भजन गाओ, और जिस पवित्रा नाम से उसका स्मरण होता है, उसका धन्यवाद करो।"));
hinsuperobject.biblemap.put("b19_30_5", new hinsuperobject(19,30,5,"क्योंकि उसका क्रोध, तो क्षण भर का होता है, परन्तु उसकी प्रसन्नता जीवन भर की होती है। कदाचित् रात को रोना पड़े, परन्तु सबेरे आनन्द पहुंचेगा।।"));
hinsuperobject.biblemap.put("b19_30_6", new hinsuperobject(19,30,6,"मैं ने तो चैन के समय कहा था, कि मैं कभी नहीं टलने का।"));
hinsuperobject.biblemap.put("b19_30_7", new hinsuperobject(19,30,7,"हे यहोवा अपनी प्रसन्नता से तू ने मेरे पहाड़ को दृढ़ और स्थिर किया था; जब तू ने अपना मुख फेर लिया तब मैं घबरा गया।।"));
hinsuperobject.biblemap.put("b19_30_8", new hinsuperobject(19,30,8,"हे यहोवा मैं ने तुझी को पुकारा; और यहोवा से गिड़गिड़ाकर यह बिनती की, कि"));
hinsuperobject.biblemap.put("b19_30_9", new hinsuperobject(19,30,9,"जब मैं कब्र में चला जाऊंगा तब मेरे लोहू से क्या लाभ होगा? क्या मिट्टी तेरा धन्यवाद कर सकती है? क्या वह तेरी सच्चाई का प्रचार कर सकती है?"));
hinsuperobject.biblemap.put("b19_30_10", new hinsuperobject(19,30,10,"हे यहोवा, सुन, मुझ पर अनुग्रह कर; हे यहोवा, तू मेरा सहायक हो।।"));
hinsuperobject.biblemap.put("b19_30_11", new hinsuperobject(19,30,11,"तू ने मेरे लिये विलाप को नृत्य में बदल डाला, तू ने मेरा टाट उतरवाकर मेरी कमर में आनन्द का पटुका बान्धा है;"));
hinsuperobject.biblemap.put("b19_30_12", new hinsuperobject(19,30,12,"ताकि मेरी आत्मा तेरा भजन गाती रहे और कभी चुप न हो। हे मेरे परमेश्वर यहोवा, मैं सर्वदा तेरा धन्यवाद करता रहूंगा।।"));
hinsuperobject.biblemap.put("b19_31_1", new hinsuperobject(19,31,1,"हे यहोवा मेरा भरोसा तुझ पर है; मुझे कभी लज्जित होना न पड़े; तू अपने धर्मी होने के कारण मुझै छुड़ा ले!"));
hinsuperobject.biblemap.put("b19_31_2", new hinsuperobject(19,31,2,"अपना कान मेरी ओर लगाकर तुरन्त मुझे छुड़ा ले!"));
hinsuperobject.biblemap.put("b19_31_3", new hinsuperobject(19,31,3,"क्योंकि तू ने मेरे लिये चट्टान और मेरा गढ़ है; इसलिये अपने नाम के निमित्त मेरी अगुवाई कर, और मुझे आगे ले चल।"));
hinsuperobject.biblemap.put("b19_31_4", new hinsuperobject(19,31,4,"जो जाल उन्हों ने मेरे लिये बिछाया है उस से तू मुझ को छुड़ा ले, क्योंकि तू ही मेरा दृढ़ गढ़ है।"));
hinsuperobject.biblemap.put("b19_31_5", new hinsuperobject(19,31,5,"मैं अपनी आत्मा को तेरे ही हाथ में सौंप देता हूं; हे यहोवा, हे सत्यवादी ईश्वर, तू ने मुझे मोल लेकर मुक्त किया है।।"));
hinsuperobject.biblemap.put("b19_31_6", new hinsuperobject(19,31,6,"जो व्यर्थ वस्तुओं पर मन लगाते हैं, उन से मैं घृणा करता हूं; परन्तु मेरा भरोसा यहोवा ही पर है।"));
hinsuperobject.biblemap.put("b19_31_7", new hinsuperobject(19,31,7,"मैं तेरी करूणा से मगन और आनन्दित हूं, क्योंकि तू ने मेरे दु:ख पर दृष्टि की है, मेरे कष्ट के समय तू ने मेरी सुधि ली है,"));
hinsuperobject.biblemap.put("b19_31_8", new hinsuperobject(19,31,8,"और तू ने मुझे शत्रु के हाथ में पड़ने नहीं दिया; तू ने मेरे पांवों को चौड़े स्थान में खड़ा किया है।।"));
hinsuperobject.biblemap.put("b19_31_9", new hinsuperobject(19,31,9,"हे यहोवा, मुझ पर अनुग्रह कर क्योंकि मैं संकट में हूं; मेरी आंखे वरन मेरा प्राण और शरीर सब शोक के मारे घुले जाते हैं।"));
hinsuperobject.biblemap.put("b19_31_10", new hinsuperobject(19,31,10,"मेरा जीवन शोक के मारे और मेरी अवस्था कराहते कराहते घट चली है; मेरा बल मेरे अधर्म के कारण जाता रह, ओर मेरी हडि्डयां घुल गई।।"));
hinsuperobject.biblemap.put("b19_31_11", new hinsuperobject(19,31,11,"अपने सब विरोधियों के कारण मेरे पड़ोसियों में मेरी नामधराई हुई है, अपने जानपहिचानवालों के लिये डर का कारण हूं; जो मुझ को सड़क पर देखते है वह मुझ से दूर भाग जाते हैं।"));
hinsuperobject.biblemap.put("b19_31_12", new hinsuperobject(19,31,12,"मैं मृतक की नाई लोगों के मन से बिसर गया; मैं टूटे बासन के समान हो गया हूं।"));
hinsuperobject.biblemap.put("b19_31_13", new hinsuperobject(19,31,13,"मैं ने बहुतों के मुंह से अपना अपवाद सुना, चारों ओर भय ही भय है! जब उन्हों ने मेरे विरूद्ध आपस में सम्मति की तब मेरे प्राण लेने की युक्ति की।।"));
hinsuperobject.biblemap.put("b19_31_14", new hinsuperobject(19,31,14,"परन्तु हे यहोवा मैं ने तो तुझी पर भरोसा रखा है, मैं ने कहा, तू मेरा परमेश्वर है।"));
hinsuperobject.biblemap.put("b19_31_15", new hinsuperobject(19,31,15,"मेरे दिन तेरे हाथ में है; तू मुझे मेरे शत्रुओं और मेरे सतानेवालों के हाथ से छुडा।"));
hinsuperobject.biblemap.put("b19_31_16", new hinsuperobject(19,31,16,"अपने दास पर अपने मुंह का प्रकाश चमका; अपनी करूणा से मेरा उद्धार कर।।"));
hinsuperobject.biblemap.put("b19_31_17", new hinsuperobject(19,31,17,"हे यहोवा, मुझे लज्जित न होने दे क्योंकि मैं ने तुझ को पुकारा है; दुष्ट लज्जित हों और वे पाताल में चुपचाप पड़े रहें।"));
hinsuperobject.biblemap.put("b19_31_18", new hinsuperobject(19,31,18,"जो अंहकार और अपमान से धर्मी की निन्दा करते हैं, उनके झूठ बोलनेवाले मुंह बन्द किए जाएं।।"));
hinsuperobject.biblemap.put("b19_31_19", new hinsuperobject(19,31,19,"आहा, तेरी भलाई क्या ही बड़ी है जो तू ने अपने डरवैयों के लिये रख छोड़ी है, और अपने शरणागतों के लिये मनुष्यों के साम्हने प्रगट भी की है!"));
hinsuperobject.biblemap.put("b19_31_20", new hinsuperobject(19,31,20,"तू उन्हें दर्शन देने के गुप्तस्थान में मनुष्यों की बुरी गोष्ठी से गुप्त रखेगा; तू उनको अपने मण्डप में झगड़े- रगड़े से छिपा रखेगा।।"));
hinsuperobject.biblemap.put("b19_31_21", new hinsuperobject(19,31,21,"यहोवा धन्य है, क्योंकि उस ने मुझे गढ़वाले नगर में रखकर मुझ पर अद्धभुत करूणा की है।"));
hinsuperobject.biblemap.put("b19_31_22", new hinsuperobject(19,31,22,"मैं ने तो घबराकर कहा था कि मैं यहोवा की दृष्टि से दूर हो गया। तौभी जब मैं ने तेरी दोहाई दी, तब तू ने मेरी गिड़गिड़ाहट को सुन लिया।।"));
hinsuperobject.biblemap.put("b19_31_23", new hinsuperobject(19,31,23,"हे यहोवा के सब भक्तों उस से प्रेम रखो! यहोवा सच्चे लोगों की तो रक्षा करता है, परन्तु जो अहंकार करता है, उसको वह भली भांति बदला देता है।"));
hinsuperobject.biblemap.put("b19_31_24", new hinsuperobject(19,31,24,"हे यहोवा पर आशा रखनेवालों हियाव बान्धों और तुम्हारे हृदय दृढ़ रहें!"));
hinsuperobject.biblemap.put("b19_32_1", new hinsuperobject(19,32,1,"क्या ही धन्य है वह जिसका अपराध क्षमा किया गया, और जिसका पाप ढ़ापा गया हो।"));
hinsuperobject.biblemap.put("b19_32_2", new hinsuperobject(19,32,2,"क्या ही धन्य है वह मनुष्य जिसके अधर्म का यहोवा लेखा न ले, और जिसकी आत्मा में कपट न हो।।"));
hinsuperobject.biblemap.put("b19_32_3", new hinsuperobject(19,32,3,"जब मैं चुप रहा तक दिन भर कहरते कहरते मेरी हडि्डयां पिघल गई।"));
hinsuperobject.biblemap.put("b19_32_4", new hinsuperobject(19,32,4,"क्योंकि रात दिन मैं तेरे हाथ के नीचे दबा रहा; और मेरी तरावट धूप काल की सी झुर्राहट बनती गई।।"));
hinsuperobject.biblemap.put("b19_32_5", new hinsuperobject(19,32,5,"जब मैं ने अपना पाप तुझ पर प्रगट किया और अपना अधर्म न छिपाया, और कहा, मैं यहोवा के साम्हने अपने अपराधों को मान लूंगा; तब तू ने मेरे अधर्म और पाप को क्षमा कर दिया।।"));
hinsuperobject.biblemap.put("b19_32_6", new hinsuperobject(19,32,6,"इस कारण हर एक भक्त तुझ से ऐसे समय में प्रार्थना करे जब कि तू मिल सकता है। निश्चय जब जल की बड़ी बाढ़ आए तौभी उस भक्त के पास न पहुंचेगी।"));
hinsuperobject.biblemap.put("b19_32_7", new hinsuperobject(19,32,7,"तू मेरे छिपने का स्थान है; तू संकट से मेरी रक्षा करेगा; तू मुझे चारों ओर से छुटकारे के गीतों से घेर लेगा।।"));
hinsuperobject.biblemap.put("b19_32_8", new hinsuperobject(19,32,8,"मैं तुझे बुद्धि दूंगा, और जिस मार्ग में तुझे चलना होगा उस में तेरी अगुवाई करूंगा; मैं तुझ पर कृपादृष्टि रखूंगा और सम्मत्ति दिया करूंगा।"));
hinsuperobject.biblemap.put("b19_32_9", new hinsuperobject(19,32,9,"तुम घोड़े और खच्चर के समान न बनो जो समझ नहीं रखते, उनकी उमंग लगाम और बाग से रोकनी पड़ती है, नहीं तो वे तेरे वश में नहीं अपने के।।"));
hinsuperobject.biblemap.put("b19_32_10", new hinsuperobject(19,32,10,"दुष्ट को तो बहुत पीड़ा होगी; परन्तु जो यहोवा पर भरोसा रखता है वह करूणा से घिरा रहेगा।"));
hinsuperobject.biblemap.put("b19_32_11", new hinsuperobject(19,32,11,"हे धर्मियों यहोवा के कारण आनन्दित और मगन हो, और हे सब सीधे मनवालों आनन्द से जयजयकार करो!"));
hinsuperobject.biblemap.put("b19_33_1", new hinsuperobject(19,33,1,"हे धर्मियों यहोवा के कारण जयजयकार करो् क्योंकि धर्मी लोगों को स्तुति करनी सोहती है।"));
hinsuperobject.biblemap.put("b19_33_2", new hinsuperobject(19,33,2,"वीणा बजा बजाकर यहोवा का धन्यवाद करो, दस तारवाली सारंगी बजा बजाकर उसका भजन गाओ।"));
hinsuperobject.biblemap.put("b19_33_3", new hinsuperobject(19,33,3,"उसके लिये नया गीत गाओ, जयजयकार के साथ भली भांति बजाओ।।"));
hinsuperobject.biblemap.put("b19_33_4", new hinsuperobject(19,33,4,"क्योंकि यहोवा का वचन सीधा है; और उसका सब काम सच्चाई से होता है।"));
hinsuperobject.biblemap.put("b19_33_5", new hinsuperobject(19,33,5,"वह धर्म और न्याय से प्रीति रखता है; यहोवा की करूणा से पृथ्वी भरपूर है।।"));
hinsuperobject.biblemap.put("b19_33_6", new hinsuperobject(19,33,6,"आकाशमण्डल यहोवा के वचन से, और उसके सारे गण उसके मुंह ही श्वास से बने।"));
hinsuperobject.biblemap.put("b19_33_7", new hinsuperobject(19,33,7,"वह समुद्र का जल ढेर की नाई इकट्ठा करता; वह गहिरे सागर को अपने भण्डार में रखता है।।"));
hinsuperobject.biblemap.put("b19_33_8", new hinsuperobject(19,33,8,"सारी पृथ्वी के लोग यहोवा से डरें, जगत के सब निवासी उसका भय मानें!"));
hinsuperobject.biblemap.put("b19_33_9", new hinsuperobject(19,33,9,"क्योंकि जब उस ने कहा, तब हो गया; जब उस ने आज्ञा दी, तब वास्तव में वैसा ही हो गया।।"));
hinsuperobject.biblemap.put("b19_33_10", new hinsuperobject(19,33,10,"यहोवा अन्यअन्यजाथियो की युक्ति को व्यर्थ कर देता है; वह देश देश के लोगों की कल्पनाओं को निष्फल करता है।"));
hinsuperobject.biblemap.put("b19_33_11", new hinsuperobject(19,33,11,"यहोवा की युक्ति सर्वदा स्थिर रहेगी, उसके मन की कल्पनाएं पीढ़ी से पीढ़ी तक बनी रहेंगी।"));
hinsuperobject.biblemap.put("b19_33_12", new hinsuperobject(19,33,12,"क्या ही धन्य है वह जाति जिसका परमेश्वर यहोवा है, और वह समाज जिसे उस ने अपना निज भाग होने के लिये चुन लिया हो!"));
hinsuperobject.biblemap.put("b19_33_13", new hinsuperobject(19,33,13,"यहोवा स्वर्ग से दृष्टि करता है, वह सब मनुष्यों को निहारता है;"));
hinsuperobject.biblemap.put("b19_33_14", new hinsuperobject(19,33,14,"अपने निवास के स्थान से वह पृथ्वी के सब रहनेवालों को देखता है,"));
hinsuperobject.biblemap.put("b19_33_15", new hinsuperobject(19,33,15,"वही जो उन सभों के हृदयों को गढ़ता, और उनके सब कामों का विचार करता है।"));
hinsuperobject.biblemap.put("b19_33_16", new hinsuperobject(19,33,16,"कोई ऐसा राजा नहीं, जो सेना की बहुतायत के कारण बच सके; वीर अपनी बड़ी शक्ति के कारण छूट नहीं जाता।"));
hinsuperobject.biblemap.put("b19_33_17", new hinsuperobject(19,33,17,"बच निकलने के लिये घोड़ा व्यर्थ है, वह अपने बड़े बल के द्वारा किसी को नहीं बचा सकता है।।"));
hinsuperobject.biblemap.put("b19_33_18", new hinsuperobject(19,33,18,"देखो, यहोवा की दृष्टि उसके डरवैयों पर और उन पर जो उसकी करूणा की आशा रखते हैं बनी रहती है,"));
hinsuperobject.biblemap.put("b19_33_19", new hinsuperobject(19,33,19,"कि वह उनके प्राण को मृत्यु से बचाए, और अकाल के समय उनको जीवित रखे।।"));
hinsuperobject.biblemap.put("b19_33_20", new hinsuperobject(19,33,20,"हम यहोवा का आसरा देखते आए हैं; वह हमारा सहायक और हमारी ढाल ठहरा है।"));
hinsuperobject.biblemap.put("b19_33_21", new hinsuperobject(19,33,21,"हमारा हृदय उसके कारण आनन्दित होगा, क्योंकि हम ने उसके पवित्रा नाम का भरोसा रखा है।"));
hinsuperobject.biblemap.put("b19_33_22", new hinsuperobject(19,33,22,"हे यहोवा जैसी तुझ पर हमारी आशा है, वैसी ही तेरी करूणा भी हम पर हो।।"));
hinsuperobject.biblemap.put("b19_34_1", new hinsuperobject(19,34,1,"मैं हर समय यहोवा को धन्य कहा करूंगा; उसकी स्तुति निरन्तर मेरे मुख से होती रहेगी।"));
hinsuperobject.biblemap.put("b19_34_2", new hinsuperobject(19,34,2,"मैं यहोवा पर घमण्ड करूंगा; नम्र लोग यह सुनकर आनन्दित होंगे।"));
hinsuperobject.biblemap.put("b19_34_3", new hinsuperobject(19,34,3,"मेरे साथ यहोवा की बड़ाई करो, और आओ हम मिलकर उसके नाम की स्तुति करें।"));
hinsuperobject.biblemap.put("b19_34_4", new hinsuperobject(19,34,4,"मैं यहोवा के पास गया, तब उस ने मेरी सुन ली, और मुझे पूरी रीति से निर्भय किया।"));
hinsuperobject.biblemap.put("b19_34_5", new hinsuperobject(19,34,5,"जिन्हों ने उसकी ओर दृष्टि की उन्हों ने ज्योति पाई; और उनका मुंह कभी काला न होने पाया।"));
hinsuperobject.biblemap.put("b19_34_6", new hinsuperobject(19,34,6,"इस दीन जन ने पुकारा तब यहोवा ने सुन लिया, और उसको उसके सब कष्टों से छुड़ा लिया।।"));
hinsuperobject.biblemap.put("b19_34_7", new hinsuperobject(19,34,7,"यहोवा के डरवैयों के चारों ओर उसका दूत छावनी किए हुए उनको बचाता है।"));
hinsuperobject.biblemap.put("b19_34_8", new hinsuperobject(19,34,8,"परखकर देखो कि यहोवा कैसा भला है! क्या ही धन्य है वह पुरूष जो उसकी शरण लेता है।"));
hinsuperobject.biblemap.put("b19_34_9", new hinsuperobject(19,34,9,"ृहे यहोवा के पवित्रा लोगो, उसका भय मानो, क्योंकि उसके डरवैयों को किसी बात की घटी नहीं होती!"));
hinsuperobject.biblemap.put("b19_34_10", new hinsuperobject(19,34,10,"जवान सिहों को तो घटी होती और वे भूखे भी रह जाते हैं; परन्तु यहोवा के खोजियों को किसी भली वस्तु की घटी न होवेगी।।"));
hinsuperobject.biblemap.put("b19_34_11", new hinsuperobject(19,34,11,"हे लड़कों, आओ, मेरी सुनो, मैं तुम को यहोवा का भय मानना सिखाऊंगा।"));
hinsuperobject.biblemap.put("b19_34_12", new hinsuperobject(19,34,12,"वह कौन मनुष्य है जो जीवन की इच्छा रखता, और दीर्घायु चाहता है ताकि भलाई देखे?"));
hinsuperobject.biblemap.put("b19_34_13", new hinsuperobject(19,34,13,"अपनी जीभ को बुराई से रोक रख, और अपने मुंह की चौकसी कर कि उस से छल की बात न निकले।"));
hinsuperobject.biblemap.put("b19_34_14", new hinsuperobject(19,34,14,"बुराई को छोड़ और भलाई कर; मेल को ढूंढ और उसी का पीछा कर।।"));
hinsuperobject.biblemap.put("b19_34_15", new hinsuperobject(19,34,15,"यहोवा की आंखे धर्मियों पर लगी रहती हैं, और उसके कान भी उसकी दोहाई की ओर लगे रहते हैं।"));
hinsuperobject.biblemap.put("b19_34_16", new hinsuperobject(19,34,16,"यहोवा बुराई करनेवालों के विमुख रहता है, ताकि उनका स्मरण पृथ्वी पर से मिटा डाले।"));
hinsuperobject.biblemap.put("b19_34_17", new hinsuperobject(19,34,17,"धर्मी दोहाई देते हैं और यहोवा सुनता है, और उनको सब विपत्तियों से छुड़ाता है।"));
hinsuperobject.biblemap.put("b19_34_18", new hinsuperobject(19,34,18,"यहोवा टूटे मनवालों के समीप रहता है, और पिसे हुओं का उद्वार करता है।।"));
hinsuperobject.biblemap.put("b19_34_19", new hinsuperobject(19,34,19,"धर्मी पर बहुत सी विपत्तियां पड़ती तो हैं, परन्त यहोवा उसको उन सब से मुक्त करता है।"));
hinsuperobject.biblemap.put("b19_34_20", new hinsuperobject(19,34,20,"वह उसकी हड्डी हड्डी की रक्षा करता है; और उन में से एक भी टूटने नहीं पाती।"));
hinsuperobject.biblemap.put("b19_34_21", new hinsuperobject(19,34,21,"दुष्ट अपनी बुराई के द्वारा मारा जाएगा; और धर्मी के बैरी दोषी ठहरेंगे।"));
hinsuperobject.biblemap.put("b19_34_22", new hinsuperobject(19,34,22,"यहोवा अपने दासों का प्राण मोल लेकर बचा लेता है; और जितने उसके शरणागत हैं उन में से कोई भी दोषी न ठहरेगा।।"));
hinsuperobject.biblemap.put("b19_35_1", new hinsuperobject(19,35,1,"हे यहोवा जो मेरे साथ मुक मा लड़ते हैं, उनके साथ तू भी मुक मा लड़; जो मुझ से युद्व करते हैं, उन से तू युद्व कर।"));
hinsuperobject.biblemap.put("b19_35_2", new hinsuperobject(19,35,2,"ढाल और भाला लेकर मेरी सहायता करने को खड़ा हो।"));
hinsuperobject.biblemap.put("b19_35_3", new hinsuperobject(19,35,3,"बर्छी को खींच और मेरा पीछा करनेवालों के साम्हने आकर उनको रोक; और मुझ से कह, कि मैं तेरा उद्वार हूं।।"));
hinsuperobject.biblemap.put("b19_35_4", new hinsuperobject(19,35,4,"जो मेरे प्राण के ग्राहक हैं वे लज्जित और निरादर हों! जो मेरी हाति की कल्पना करते हैं, वह पीछे हटाए जाएं और उनका मुंह काला हो!"));
hinsuperobject.biblemap.put("b19_35_5", new hinsuperobject(19,35,5,"वे वायु से उड़ जानेवाली भूसी के समान हों, और यहोवा का दूत उन्हें हांकता जाए!"));
hinsuperobject.biblemap.put("b19_35_6", new hinsuperobject(19,35,6,"उनका मार्ग अन्धियारा और फिसलाहा हो, और यहोवा का दूत उनको खदेड़ता जाए।।"));
hinsuperobject.biblemap.put("b19_35_7", new hinsuperobject(19,35,7,"क्योंकि अकारण उन्हों ने मेरे लिये अपना जाल गड़हे में बिछाया; अकारण ही उन्हों ने मेरा प्राण लेने के लिये गड़हा खोदा है।"));
hinsuperobject.biblemap.put("b19_35_8", new hinsuperobject(19,35,8,"अचानक उन पर विपत्ति आ पड़े! और जो जाल उन्हों ने बिछाया है उसी में वे आप ही फंसे; और उसी विपत्ति में वे आप ही पड़ें!"));
hinsuperobject.biblemap.put("b19_35_9", new hinsuperobject(19,35,9,"परन्तु मैं यहोवा के कारण अपने मन में मगन होऊंगा, मैं उसके किए हुए उद्वार से हर्षित होऊंगा।"));
hinsuperobject.biblemap.put("b19_35_10", new hinsuperobject(19,35,10,"मेरी हड्डी हड्डी कहेंगी, हे यहोवा तेरे तुल्य कौन है, जो दी को बड़े बड़े बलवन्तों से बचाता है, और लुटेरों से दीन दरिद्र लोगों की रक्षा करता है?"));
hinsuperobject.biblemap.put("b19_35_11", new hinsuperobject(19,35,11,"झूठे साक्षी खड़े होते हैं; और जो बात मैं नहीं जानता, वही मुझ से पूछते हैं।"));
hinsuperobject.biblemap.put("b19_35_12", new hinsuperobject(19,35,12,"वे मुझ से भलाई के बदले बुराई करते हैं; यहां तक कि मेरा प्राण ऊब जाता है।"));
hinsuperobject.biblemap.put("b19_35_13", new hinsuperobject(19,35,13,"जब वे रोगी थे तब तो मैं टाट पहिने रहा, और उपवास कर करके दु:ख उठाता रहा; और मेरी प्रार्थना का फल मेरी गोद में लौट आया।"));
hinsuperobject.biblemap.put("b19_35_14", new hinsuperobject(19,35,14,"मैं ऐसा भाव रखता था कि मानो वे मेरे संगी वा भाई हैं; जैसा कोई माता के लिये विलाप करता हो, वैसा ही मैं ने शोक का पहिरावा पहिने हुए सिर झुकाकर शोक किया।।"));
hinsuperobject.biblemap.put("b19_35_15", new hinsuperobject(19,35,15,"परन्तु जब मैं लंगड़ाने लगा तब वे लोग आनन्दित होकर इकट्ठे हुए, नीच लोग और जिन्हें मैं जानता भी न था वे मेरे विरूद्व इकट्ठे हुए; वे मुझे लगातार फाड़ते रहे;"));
hinsuperobject.biblemap.put("b19_35_16", new hinsuperobject(19,35,16,"उन पाखण्डी भांड़ों की नाई जो पेट के लिये उपहास करते हैं, वे भी मुझ पर दांत पीसते हैं।।"));
hinsuperobject.biblemap.put("b19_35_17", new hinsuperobject(19,35,17,"हे प्रभु तू कब तक देखता रहेगा? इस विपत्ति से, जिस में उन्हों ने मुझे डाला है मुझ को छुड़ा! जवान सिहों से मेरे प्राण को बचा ले!"));
hinsuperobject.biblemap.put("b19_35_18", new hinsuperobject(19,35,18,"मैं बड़ी सभा में तेरा धन्यवाद करूंगा; बहुतेरे लोगों के बीच में तेरी स्तुति करूंगा।।"));
hinsuperobject.biblemap.put("b19_35_19", new hinsuperobject(19,35,19,"मेरे झूठ बोलनेवाले शत्रु मेरे विरूद्व आनन्द न करने पाएं, जो अकारण मेरे बैरी हैं, वे आपस में नैन से सैन न करने पांए।"));
hinsuperobject.biblemap.put("b19_35_20", new hinsuperobject(19,35,20,"क्योंकि वे मेल की बातें नहीं बोलते, परन्तु देश में जो चुपचाप रहते हैं, उनके विरूद्व छल की कल्पनाएं करते हैं।"));
hinsuperobject.biblemap.put("b19_35_21", new hinsuperobject(19,35,21,"और उन्हों ने मेरे विरूद्व मुंह पसारके कहा; आहा, आहा, हम ने अपनी आंखों से देखा है!"));
hinsuperobject.biblemap.put("b19_35_22", new hinsuperobject(19,35,22,"हे यहोवा, तू ने तो देखा है; चुप न रह! हे प्रभु, मुझ से दूर न रह!"));
hinsuperobject.biblemap.put("b19_35_23", new hinsuperobject(19,35,23,"उठ, मेरे न्याय के लिये जाग, हे मेरे परमेश्वर, हे मेरे प्रभु, मेरे मुक मा निपटाने के लिये आ!"));
hinsuperobject.biblemap.put("b19_35_24", new hinsuperobject(19,35,24,"हे मेरे परमेश्वर यहोवा, तू अपने धर्म के अनुसार मेरा न्याय चुका; ओश्र उन्हें मेरे विरूद्व आनन्द करने न दे!"));
hinsuperobject.biblemap.put("b19_35_25", new hinsuperobject(19,35,25,"वे मन में न कहने पाएं, कि आहा! हमारी तो इच्छा पूरी हुई! वह यह न कहें कि हम उसे निगल गए हैं।।"));
hinsuperobject.biblemap.put("b19_35_26", new hinsuperobject(19,35,26,"जो मेरी हाति से आनन्दित होते हैं उनके मुंह लज्जा के मारे एक साथ काले हों! जो मेरे विरूद्ध बड़ाई मारते हैं वह लज्जा और अनादर से ढ़ंप जाएं!"));
hinsuperobject.biblemap.put("b19_35_27", new hinsuperobject(19,35,27,"जो मेरे धर्म से प्रसन्न रहते हैं, वह जयजयकार और आनन्द करें, और निरन्तर करते रहें, यहोवा की बड़ाई हो, जो अपने दास के कुशल से प्रसन्न होता है!"));
hinsuperobject.biblemap.put("b19_35_28", new hinsuperobject(19,35,28,"तब मेरे मुंह से तेरे धर्म की चर्चा होगी, और दिन भर तेरी स्तुति निकलेगी।।"));
hinsuperobject.biblemap.put("b19_36_1", new hinsuperobject(19,36,1,"दुष्ट जन का अपराण मेरे हृदय के भीतर यह कहता है कि परमेश्वर का भय उसकी दृष्टि में नहीं है।"));
hinsuperobject.biblemap.put("b19_36_2", new hinsuperobject(19,36,2,"वह अपने अधर्म के प्रगट होने और घृणित ठहरने के विषय अपने मन में चिकनी चुपड़ी बातें विचारता है।"));
hinsuperobject.biblemap.put("b19_36_3", new hinsuperobject(19,36,3,"उसकी बातें अनर्थ और छल की हैं; उस ने बुद्धि और भलाई के काम करने से हाथ उठाया है।"));
hinsuperobject.biblemap.put("b19_36_4", new hinsuperobject(19,36,4,"वह अपने बिछौने पर पड़े पड़े अनर्थ की कल्पना करता है; वह अपने कुमार्ग पर दृढ़ता से बना रहता है; बुराई से वह हाथ नहीं उठाता।।"));
hinsuperobject.biblemap.put("b19_36_5", new hinsuperobject(19,36,5,"हे यहोवा तेरी करूणा स्वर्ग में है, तेरी सच्चाई आकाशमण्डल तक पहुंची है।"));
hinsuperobject.biblemap.put("b19_36_6", new hinsuperobject(19,36,6,"तेरा धर्म ऊंचे पर्वतों के समान है, तेरे नियम अथाह सागर ठहरे हैं; हे यहोवा तू मनुष्य और पशु दोनों की रक्षा करता है।।"));
hinsuperobject.biblemap.put("b19_36_7", new hinsuperobject(19,36,7,"हे परमेश्वर तेरी करूणा, कैसी अनमोल है! मनुष्य तेरे पंखो के तले शरण लेते हैं।"));
hinsuperobject.biblemap.put("b19_36_8", new hinsuperobject(19,36,8,"वे तेरे भवन के चिकने भोजन से तृप्त होंगे, और तू अपनी सुख की नदी में से उन्हें पिलाएगा।"));
hinsuperobject.biblemap.put("b19_36_9", new hinsuperobject(19,36,9,"क्योंकि जीवन का सोता तेरे ही पास है; तेरे प्रकाश के द्वारा हम प्रकाश पाएंगे।।"));
hinsuperobject.biblemap.put("b19_36_10", new hinsuperobject(19,36,10,"अपने जाननेवालों पर करूणा करता रह, और अपने धर्म के काम सीधे मनवालों में करता रह!"));
hinsuperobject.biblemap.put("b19_36_11", new hinsuperobject(19,36,11,"अहंकारी मुझ पर लात उठाने न पाए, और न दुष्ट अपने हाथ के बल से मुझे भगाने पाए।"));
hinsuperobject.biblemap.put("b19_36_12", new hinsuperobject(19,36,12,"वहां अनर्थकारी गिर पड़े हैं; वे ढकेल दिए गए, और फिर उठ न सकेंगे।।"));
hinsuperobject.biblemap.put("b19_37_1", new hinsuperobject(19,37,1,"कुकर्मियों के कारण मत कुढ़, कुटिल काम करनेवालों के विषय डाह न कर!"));
hinsuperobject.biblemap.put("b19_37_2", new hinsuperobject(19,37,2,"क्योंकि वे घास की नाई झट कट जाएंगे, और हरी घास की नाई मुर्झा जाएंगे।"));
hinsuperobject.biblemap.put("b19_37_3", new hinsuperobject(19,37,3,"यहोवा पर भरोसा रख, और भला कर; देश में बसा रह, और सच्चाई में मन लगाए रह।"));
hinsuperobject.biblemap.put("b19_37_4", new hinsuperobject(19,37,4,"यहोवा को अपने सुख का मूल जान, और वह तेरे मनोरथों को मूरा करेगा।।"));
hinsuperobject.biblemap.put("b19_37_5", new hinsuperobject(19,37,5,"अपने मार्ग की चिन्ता यहोवा पर छोड़; और उस पर भरोसा रख, वही पूरा करेगा।"));
hinsuperobject.biblemap.put("b19_37_6", new hinsuperobject(19,37,6,"और वह तेरा धर्म ज्योति की नाई, और तेरा न्याय दोपहर के उजियाले की नाई प्रगट करेगा।।"));
hinsuperobject.biblemap.put("b19_37_7", new hinsuperobject(19,37,7,"यहोवा के साम्हने चुपचाप रह, और धीरज से उसका आस्त्रा रख; उस मनुष्य के कारण न कुढ़, जिसके काम सुफल होते हैं, और वह कुरी युक्तियों को निकालता है!"));
hinsuperobject.biblemap.put("b19_37_8", new hinsuperobject(19,37,8,"क्रोध से परे रह, और जलजलाहट को छोड़ दे! मत कुढ़, उस से बुराई ही निकलेगी।"));
hinsuperobject.biblemap.put("b19_37_9", new hinsuperobject(19,37,9,"क्योंकि कुकर्मी लोग काट डाले जाएंगे; और जो यहोवा की बाट जोहते हैं, वही पृथ्वी के अधिकारी होंगे।"));
hinsuperobject.biblemap.put("b19_37_10", new hinsuperobject(19,37,10,"थोड़े दिन के बीतने पर दुष्ट रहेगा ही नहीं; और तू उसके स्थान को भलीं भांति देखने पर भी उसको न पाएगा।"));
hinsuperobject.biblemap.put("b19_37_11", new hinsuperobject(19,37,11,"परन्तु नम्र लोग पृथ्वी के अधिकारी होंगे, और बड़ी शान्ति के कारण आनन्द मनाएंगे।"));
hinsuperobject.biblemap.put("b19_37_12", new hinsuperobject(19,37,12,"दुष्ट धर्मी के विरूद्ध बुरी युक्ति निकालता है, और उस पर दांत पीसता है;"));
hinsuperobject.biblemap.put("b19_37_13", new hinsuperobject(19,37,13,"परन्तु प्रभु उस पर हंसेगा, क्योंकि वह देखता है कि उसका दिन आनेवाला है।।"));
hinsuperobject.biblemap.put("b19_37_14", new hinsuperobject(19,37,14,"दुष्ट लोग तलवार खींचे और धनुष बढ़ाए हुए हैं, ताकि दीन दरिद्र को गिरा दें, और सीधी चाल चलनेवालों को वध करें।"));
hinsuperobject.biblemap.put("b19_37_15", new hinsuperobject(19,37,15,"उनकी तलवारों से उन्हीं के हृदय छिदेंगे, और उनके धनुष तोड़े जाएंगे।।"));
hinsuperobject.biblemap.put("b19_37_16", new hinsuperobject(19,37,16,"धर्मी को थोड़ा से माल दुष्टों के बहुत से धन से उत्तम है।"));
hinsuperobject.biblemap.put("b19_37_17", new hinsuperobject(19,37,17,"क्योंकि दुष्टों की भुजाएं तो तोड़ी जाएंगी; परन्तु यहोवा धर्मियों को सम्भालता है।।"));
hinsuperobject.biblemap.put("b19_37_18", new hinsuperobject(19,37,18,"यहोवा खरे लोगों की आयु की सुधि रखता है, और उनका भाग सदैव बना रहेगा।"));
hinsuperobject.biblemap.put("b19_37_19", new hinsuperobject(19,37,19,"विपत्ति के समय, उनकी आशा न टूटेगी और न वे लज्जित होंगे, और अकाल के दिनों में वे तृप्त रहेंगे।।"));
hinsuperobject.biblemap.put("b19_37_20", new hinsuperobject(19,37,20,"दुष्ट लोग नाश हो जाएंगे; और यहोवा के शत्रु खेत की सुथरी घास की नाई नाश होंगे, वे धूएं की नाई बिलाय जाएंगे।।"));
hinsuperobject.biblemap.put("b19_37_21", new hinsuperobject(19,37,21,"दुष्ट ऋण लेता है, और भरता नहीं परनतु धर्मीं अनुग्रह करके दान देता है;"));
hinsuperobject.biblemap.put("b19_37_22", new hinsuperobject(19,37,22,"क्योंकि जो उस से आशीष पाते हैं वे तो पृथ्वी के अधिकारी होंगे, परन्तु जो उस से शापित होते हैं, वे नाश को जाएंगे।।"));
hinsuperobject.biblemap.put("b19_37_23", new hinsuperobject(19,37,23,"मनुष्य की गति यहोवा की ओर से दृढ़ होती है, और उसके चलन से वह प्रसन्न रहता है;"));
hinsuperobject.biblemap.put("b19_37_24", new hinsuperobject(19,37,24,"चाहे वह गिरे तौभी पड़ा न रह जाएगा, क्योंकि यहोवा उसका हाथ थांभे रहता है।।"));
hinsuperobject.biblemap.put("b19_37_25", new hinsuperobject(19,37,25,"मैं लड़कपन से लेकर बुढ़ापे तक देखता आया हूं; परन्तु न तो कभी धर्मी को त्यागा हुआ, और न उसके वंश को टुकड़े मांगते देखा है।"));
hinsuperobject.biblemap.put("b19_37_26", new hinsuperobject(19,37,26,"वह तो दिन भर अनुग्रह कर करके ऋण देता है, और उसके वंश पर आशीष फलती रहती है।।"));
hinsuperobject.biblemap.put("b19_37_27", new hinsuperobject(19,37,27,"बुराई को छोड़ भलाई कर; और तू सर्वदा बना रहेगा।"));
hinsuperobject.biblemap.put("b19_37_28", new hinsuperobject(19,37,28,"क्योंकि यहोवा न्याय से प्रीति रखता; और अपने भक्तों को न तजेगा। उनकी तो रक्षा सदा होती है, परन्तु दुष्टों का वंश काट डाला जाएगा।"));
hinsuperobject.biblemap.put("b19_37_29", new hinsuperobject(19,37,29,"धर्मी लोग पृथ्वी के अधिकारी होंगे, और उस में सदा बसे रहेंगे।।"));
hinsuperobject.biblemap.put("b19_37_30", new hinsuperobject(19,37,30,"धर्मी अपने मुंह से बुद्धि की बातें करता, और न्याय का वचन कहता है।"));
hinsuperobject.biblemap.put("b19_37_31", new hinsuperobject(19,37,31,"उसके परमेश्वर की व्यवस्था उसके हृदय में बनी रहती है, उसके पैर नहीं फिसलते।।"));
hinsuperobject.biblemap.put("b19_37_32", new hinsuperobject(19,37,32,"दुष्ट धर्मी की ताक में रहता है। और उसके मार डालने का यत्न करता है।"));
hinsuperobject.biblemap.put("b19_37_33", new hinsuperobject(19,37,33,"यहोवा उसको उसके हाथ में न छोड़ेगा, और जब उसका विचार किया जाए तब वह उसे दोषी न ठहराएगा।।"));
hinsuperobject.biblemap.put("b19_37_34", new hinsuperobject(19,37,34,"यहोवा की बाट जोहता रह, और उसके मार्ग पर बना रह, और वह तुझे बढ़ाकर पृथ्वी का अधिकारी कर देगा; जब दुष्ट काट डाले जाएंगे, तब तू देखेगा।।"));
hinsuperobject.biblemap.put("b19_37_35", new hinsuperobject(19,37,35,"मैं ने दुष्ट को बड़ा पराक्रमी और ऐसा फैलता हुए देखा, जैसा कोई हरा पेड़ अपने निज भूमि में फैलता है।"));
hinsuperobject.biblemap.put("b19_37_36", new hinsuperobject(19,37,36,"परन्तु जब कोई उधर से गया तो देखा कि वह वहां है ही नहीं; और मैं ने भी उसे ढूंढ़ा, परन्तु कहीं न पाया।।"));
hinsuperobject.biblemap.put("b19_37_37", new hinsuperobject(19,37,37,"खरे मनुष्य पर दृष्टि कर और धर्मी को देख, क्योंकि मेल से रहनेवाले पुरूष का अन्तफल अच्छा है।"));
hinsuperobject.biblemap.put("b19_37_38", new hinsuperobject(19,37,38,"परन्तु अपराधी एक साथ सत्यानाश किए जाएंगे; दुष्टों का अन्तफल सर्वनाश है।।"));
hinsuperobject.biblemap.put("b19_37_39", new hinsuperobject(19,37,39,"धर्मियों की मुक्ति यहोवा की ओर से होती है; संकट के समय वह उनका दृढ़ गढ़ है।"));
hinsuperobject.biblemap.put("b19_37_40", new hinsuperobject(19,37,40,"और यहोवा उनकी सहायता करके उनको बचाता है; वह उनको दुष्टों से छुड़ाकर उनका उद्वार करता है, इसलिये कि उन्हों ने उस में अपनी शरण ली है।।"));
hinsuperobject.biblemap.put("b19_38_1", new hinsuperobject(19,38,1,"हे यहोवा क्रोध में आकर मुझे झिड़क न दे, और न जलजलाहट में आकर मेरी ताड़ना कर!"));
hinsuperobject.biblemap.put("b19_38_2", new hinsuperobject(19,38,2,"क्योंकि तेरे तीर मुझ में लगे हैं, और मैं तेरे हाथ के नीचे दबा हूं।"));
hinsuperobject.biblemap.put("b19_38_3", new hinsuperobject(19,38,3,"तेरे क्रोध के कारण मेरे शरीर में कुछ भी आरोग्यता नहीं; और मेरे पाप के कारण मेरी हडि्डयों में कुछ भी चैन नहीं।"));
hinsuperobject.biblemap.put("b19_38_4", new hinsuperobject(19,38,4,"क्योंकि मेरे अधर्म के कामों में मेरा सिर डूब गया, और वे भारी बोझ की नाई मेरे सहने से बाहर हो गए हैं।।"));
hinsuperobject.biblemap.put("b19_38_5", new hinsuperobject(19,38,5,"मेरी मूढ़ता के कारण से मेरे कोड़े खाने के घाव बसाते हैं और सड़ गए हैं।"));
hinsuperobject.biblemap.put("b19_38_6", new hinsuperobject(19,38,6,"मैं बहुत दुखी हूं और झूक गया हूं; दिन भर मैं शौक का पहिरावा पहिने हुए चलता फिरता हूं।"));
hinsuperobject.biblemap.put("b19_38_7", new hinsuperobject(19,38,7,"क्योंकि मेरी कमर में जलन है, और मेरे शरीर में आरोग्यता नहीं।"));
hinsuperobject.biblemap.put("b19_38_8", new hinsuperobject(19,38,8,"मैं निर्बल और बहुत ही चूर हो गया हूं; मैं अपने मन की घबराहट से कराहता हूं।।"));
hinsuperobject.biblemap.put("b19_38_9", new hinsuperobject(19,38,9,"हे प्रभु मेरी सारी अभिलाषा तेरे सम्मुख है, और मेरा कराहना तुझ से छिपा नहीं।"));
hinsuperobject.biblemap.put("b19_38_10", new hinsuperobject(19,38,10,"मेरा हृदय धड़कता है, मेरा बल घटता जाता है; और मेरी आंखों की ज्योति भी मुझ से जाती रही।"));
hinsuperobject.biblemap.put("b19_38_11", new hinsuperobject(19,38,11,"मेरे मित्रा और मेरे संगी मेरी विपत्ति में अलग हो गए, और मेरे कुटुम्बी भी दूर जा खड़े हुए।।"));
hinsuperobject.biblemap.put("b19_38_12", new hinsuperobject(19,38,12,"मेरे प्राण के ग्राहक मेरे लिये जाल बिछाते हैं, और मेरी हाति के यत्न करनेवाले दुष्टता की बातें बोलते, और दिन भर छल की युक्ति सोचते हैं।"));
hinsuperobject.biblemap.put("b19_38_13", new hinsuperobject(19,38,13,"परन्तु मैं बहिरे की नाई सुनता ही नहीं, और मैं गूंगे के समान मूंह नहीं खोलता।"));
hinsuperobject.biblemap.put("b19_38_14", new hinsuperobject(19,38,14,"वरन मैं ऐसे मनुष्य के तुल्य हूं जो कुछ नहीं सुनता, और जिसके मुंह से विवाद की कोई बात नहीं निकलती।।"));
hinsuperobject.biblemap.put("b19_38_15", new hinsuperobject(19,38,15,"परन्तु हे यहोवा, मैं ने तुझ ही पर अपनी आशा लगाई है; हे प्रभु, मेरे परमेश्वर, तू ही उत्तर देगा।"));
hinsuperobject.biblemap.put("b19_38_16", new hinsuperobject(19,38,16,"क्योंकि मैं ने कहा, ऐसा न हो कि वे मुझ पर आनन्द करें; जो, जब मेरा पांव फिसल जाता है, तब मुझ पर अपनी बड़ाई मारते हैं।।"));
hinsuperobject.biblemap.put("b19_38_17", new hinsuperobject(19,38,17,"क्योंकि मैं तो अब गिरने ही पर हूं, और मेरा शोक निरन्तर मेरे साम्हने है।"));
hinsuperobject.biblemap.put("b19_38_18", new hinsuperobject(19,38,18,"इसलिये कि मैं तो अपने अधर्म को प्रगट करूंगा, और अपने पाप के कारण खेदित रहूंगा।"));
hinsuperobject.biblemap.put("b19_38_19", new hinsuperobject(19,38,19,"परन्तु मेरे शत्रु फुर्तीले और सामर्थी हैं, और मेरे विरोधी बैरी बहुत हो गए हैं।"));
hinsuperobject.biblemap.put("b19_38_20", new hinsuperobject(19,38,20,"जो भलाई के बदले में बुराई करते हैं, वह भी मेरे भलाई के पीछे चलने के कारण मुझ से विरोध करते हैं।।"));
hinsuperobject.biblemap.put("b19_38_21", new hinsuperobject(19,38,21,"हे यहोवा, मुझे छोड़ न दे! हे मेरे परमेश्वर, मुझ से दूर न हो!"));
hinsuperobject.biblemap.put("b19_38_22", new hinsuperobject(19,38,22,"हे यहोवा, हे मेरे उद्वारकर्त्ता, मेरी सहायता के लिये फुर्ती कर!"));
hinsuperobject.biblemap.put("b19_39_1", new hinsuperobject(19,39,1,"मैं ने कहा, मैं अपनी चालचलन में चौकसी करूंगा, ताकि मेरी जीभ से पाप न हो; जब तक दुष्ट मेरे साम्हने है, तब तक मैं लगाम लगाए अपना मुंह बन्द किए रहूंगा।"));
hinsuperobject.biblemap.put("b19_39_2", new hinsuperobject(19,39,2,"मैं मौन धारण कर गूंगा बन गया, और भलाई की ओर से भी चुप्पी साधे रहा; और मेरी पीड़ा बढ़ गई,"));
hinsuperobject.biblemap.put("b19_39_3", new hinsuperobject(19,39,3,"मेरा हृदय अन्दर ही अन्दर जल रहा था। सोचते सोचते आग भड़क उठी; तब मैं अपनी जीभ से बोल उठा;"));
hinsuperobject.biblemap.put("b19_39_4", new hinsuperobject(19,39,4,"हे यहोवा ऐसा कर कि मेरा अन्त मुझे मालुम हो जाए, और यह भी कि मेरी आयु के दिन कितने हैं; जिस से मैं जान लूं कि कैसा अनित्य हूं!"));
hinsuperobject.biblemap.put("b19_39_5", new hinsuperobject(19,39,5,"देख, तू ने मेरे आयु बालिश्त भर की रखी है, और मेरी अवस्था तेरी दृष्टि में कुछ है ही नहीं। सचमुच सब मनुष्य कैसे ही स्थिर क्यों न हों तौभी व्यर्थ ठहरे हैं।"));
hinsuperobject.biblemap.put("b19_39_6", new hinsuperobject(19,39,6,"सचमुच मनुष्य छाया सा चलता फिरता है; सचमुच वे व्यर्थ घबराते हैं; वह धन का संचय तो करता है परन्तु नहीं जानता कि उसे कौन लेगा!"));
hinsuperobject.biblemap.put("b19_39_7", new hinsuperobject(19,39,7,"और अब हे प्रभु, मैं किस बात की बाट जोहूं? मेरी आशा तो तेरी ओर लगी है।"));
hinsuperobject.biblemap.put("b19_39_8", new hinsuperobject(19,39,8,"मुझे मेरे सब अपराधों के बन्धन से छुड़ा ले। मूढ़ मेरी निन्दा न करने पाए।"));
hinsuperobject.biblemap.put("b19_39_9", new hinsuperobject(19,39,9,"मैं गूंगा बन गया और मुंह न खोला; क्योंकि यह काम तू ही ने किया है।"));
hinsuperobject.biblemap.put("b19_39_10", new hinsuperobject(19,39,10,"तू ने जो विपत्ति मुझ पर डाली है उसे मुझ से दूर कर दे, क्योंकि मैं तो तरे हाथ की मार से भस्म हुआ जाता हूं।"));
hinsuperobject.biblemap.put("b19_39_11", new hinsuperobject(19,39,11,"जब तू मनुष्य को अधर्म के कारण दपट दपटकर ताड़ना देता है; तब तू उसकी सुन्दरता को पतिंगे की नाई नाश करता है; सचमुच सब मनुष्य वृथाभिमान करते हैं।।"));
hinsuperobject.biblemap.put("b19_39_12", new hinsuperobject(19,39,12,"हे यहोवा, मेरी प्रार्थना सुन, और मेरी दोहाई पर कान लगा; मेरा रोना सुनकर शांत न रह! क्योंकि मैं तेरे संग एक परदेशी यात्री की नाई रहता हूं, और अपने सब पुरखाओं के समान परदेशी हूं।"));
hinsuperobject.biblemap.put("b19_39_13", new hinsuperobject(19,39,13,"आह! इस से पहिले कि मैं यहां से चला जाऊं और न रह जाऊं, मुझे बचा ले जिस से मैं प्रदीप्त जीवन प्राप्त करूं!"));
hinsuperobject.biblemap.put("b19_40_1", new hinsuperobject(19,40,1,"मैं धीरज से यहोवा की बाट जोहता रहा; और उस ने मेरी ओर झुककर मेरी दोहाई सुनी।"));
hinsuperobject.biblemap.put("b19_40_2", new hinsuperobject(19,40,2,"उस ने मुझे सत्यानाश के गड़हे और दलदल की कीच में से उबारा, और मुझ को चट्टान पर खड़ा करके मेरे पैरों को दृढ़ किया है।"));
hinsuperobject.biblemap.put("b19_40_3", new hinsuperobject(19,40,3,"और उस ने मुझे एक नया गीत सिखाया जो हमारे परमेश्वर की स्तुति का है। बहुतेरे यह देखकर डरेंगे, और यहोवा पर भरोसा रखेंगे।।"));
hinsuperobject.biblemap.put("b19_40_4", new hinsuperobject(19,40,4,"क्या ही धन्य है वह पुरूष, जो यहोवा पर भरोसा करता है, और अभिमानियों और मिथ्या की ओर मुड़नेवालों की ओर मुंह न फेरता हो।"));
hinsuperobject.biblemap.put("b19_40_5", new hinsuperobject(19,40,5,"हे मेरे परमेश्वर यहोवा, तू ने बहुत से काम किए हैं! जो आश्चर्यकर्म और कल्पनाएं तू हमारे लिये करता है वह बहुत सी हैं; तेरे तुल्य कोई नहीं! मैं तो चाहता हूं की खोलकर उनकी चर्चा करूं, परन्तु उनकी गिनती नहीं हो सकती।।"));
hinsuperobject.biblemap.put("b19_40_6", new hinsuperobject(19,40,6,"मेलबलि और अन्नबलि से तू प्रसन्न नहीं होता तू ने मेरे कान खोदकर खोले हैं। होमबलि और पापबलि तू ने नहीं चाहा।"));
hinsuperobject.biblemap.put("b19_40_7", new hinsuperobject(19,40,7,"तब मैं ने कहा, देख, मैं आया हूं; क्योंकि पुस्तक में मेरे विषय ऐसा ही लिखा हुआ है।"));
hinsuperobject.biblemap.put("b19_40_8", new hinsuperobject(19,40,8,"हे मेरे परमेश्वर मैं तेरी इच्छा पूरी करने से प्रसन्न हूं; और तेरी व्यवस्था मेरे अन्त:करण में बनी है।।"));
hinsuperobject.biblemap.put("b19_40_9", new hinsuperobject(19,40,9,"मैं ने बड़ी सभा में धर्म के शुभ समाचार का प्रचार किया है; देख, मैं ने अपना मुंह बन्द नहीं किया हे यहोवा, तू इसे जानता है।"));
hinsuperobject.biblemap.put("b19_40_10", new hinsuperobject(19,40,10,"मैं ने तेरा धर्म मन ही में नहीं रखा; मैं ने तेरी सच्चाई और तेरे किए हुए उधार की चर्चा की है; मैं ने तेरी करूणा और सत्यता बड़ी सभा से गुप्त नहीं रखी।।"));
hinsuperobject.biblemap.put("b19_40_11", new hinsuperobject(19,40,11,"हे यहोवा, तू भी अपनी बड़ी दया मुझ पर से न हटा ले, तेरी करूणा और सत्यता से निरन्तर मेरी रक्षा होती रहे!"));
hinsuperobject.biblemap.put("b19_40_12", new hinsuperobject(19,40,12,"क्योंकि मैं अनगिनत बुराइयों से घिरा हुआ हूं; मेरे अधर्म के कामों ने मुझे आ पकड़ा और मैं दृष्टि नहीं उठा सकता; वे गिनती में मेरे सिर के बालों से भी अधिक हैं; इसलिये मेरा हृदय टूट गया।।"));
hinsuperobject.biblemap.put("b19_40_13", new hinsuperobject(19,40,13,"हे यहोवा, कृपा करके मुझे छुड़ा ले! हे यहोवा, मेरी सहायता के लिये फुर्ती कर!"));
hinsuperobject.biblemap.put("b19_40_14", new hinsuperobject(19,40,14,"जो मेरे प्राण की खोज में हैं, वे सब लज्जित हों; और उनके मुंह काले हों और वे पीछे हटाए और निरादर किए जाएं जो मेरी हाति से प्रसन्न होते हैं।"));
hinsuperobject.biblemap.put("b19_40_15", new hinsuperobject(19,40,15,"जो मुझ से आहा, आहा, कहते हैं, वे अपनी लज्जा के मारे विस्मित हों।।"));
hinsuperobject.biblemap.put("b19_40_16", new hinsuperobject(19,40,16,"परन्तु जितने तुझे ढूंढ़ते हैं, वह सब तेरे कारण हर्षित औश्र आनन्दित हों; जो तेरा किया हुआ उद्धार चाहते हैं, वे निरन्तर कहते रहें, यहोवा की बड़ाई हो!"));
hinsuperobject.biblemap.put("b19_40_17", new hinsuperobject(19,40,17,"मैं तो दीन और दरिद्र हूं, तौभी प्रभु मेरी चिन्ता करता है। तू मेरा सहायक और छुड़ानेवाला है; हे मेरे परमेश्वर विलम्ब न कर।।"));
hinsuperobject.biblemap.put("b19_41_1", new hinsuperobject(19,41,1,"क्या ही धन्य है वह, जो कंगाल की सुधि रखता है! विपत्ति के दिन यहोवा उसको बचाएगा।"));
hinsuperobject.biblemap.put("b19_41_2", new hinsuperobject(19,41,2,"यहोवा उसकी रक्षा करके उसको जीवित रखेगा, और वह पृथ्वी पर भाग्यवान होगा। तू उसको शत्रुओं की इच्छा पर न छोड़।"));
hinsuperobject.biblemap.put("b19_41_3", new hinsuperobject(19,41,3,"जब वह व्याधि के मारे सेज पर पड़ा हो, तब यहोवा उसे सम्भालेगा; तू रोग में उसके पूरे बिछौने को उलटकर ठीक करेगा।।"));
hinsuperobject.biblemap.put("b19_41_4", new hinsuperobject(19,41,4,"मैं ने कहा, हे यहोवा, मुझ पर अनुग्रह कर; मुझ को चंगा कर, क्योंकि मैं ने तो तेरे विरूद्ध पाप किया है!"));
hinsuperobject.biblemap.put("b19_41_5", new hinsuperobject(19,41,5,"मेरे शत्रु यह कहकर मेरी बुराई करते हैं: वह कब मरेगा, और उसका नाम कब मिटेगा?"));
hinsuperobject.biblemap.put("b19_41_6", new hinsuperobject(19,41,6,"और जब वह मुझ से मिलने को आता है, तब वह व्यर्थ बातें बकता है, जब कि उसका मन अपने अन्दर अधर्म की बातें संचय करता है; और बाहर जाकर उनकी चर्चा करता है।"));
hinsuperobject.biblemap.put("b19_41_7", new hinsuperobject(19,41,7,"मेरे सब बैरी मिलकर मेरे विरूद्ध कानाफूसी करते हैं; मे मेरे विरूद्ध होकर मेरी हानि की कल्पना करते हैं।।"));
hinsuperobject.biblemap.put("b19_41_8", new hinsuperobject(19,41,8,"वे कहते हैं कि इसे तो कोई बुरा रोग लग गया है; अब जो यह पड़ा है, तो फिर कभी उठने का नहीं।"));
hinsuperobject.biblemap.put("b19_41_9", new hinsuperobject(19,41,9,"मेरा परम मित्रा जिस पर मैं भरोसा रखता था, जो मेरी रोटी खाता था, उस ने भी मेरे विरूद्ध लात उठाई है।"));
hinsuperobject.biblemap.put("b19_41_10", new hinsuperobject(19,41,10,"परन्तु हे यहोवा, तु मुझ पर अनुग्रह करके मुझ को उठा ले कि मैं उनको बदला दूं!"));
hinsuperobject.biblemap.put("b19_41_11", new hinsuperobject(19,41,11,"मेरा शत्रु जो मुझ पर जयवन्त नहीं हो पाता, इस से मैं ने जान लिया है कि तू मुझ से प्रसन्न है।"));
hinsuperobject.biblemap.put("b19_41_12", new hinsuperobject(19,41,12,"और मुझे तो तू खराई से सम्भालता, और सर्वदा के लिये अपने सम्मुख स्थिर करता है।।"));
hinsuperobject.biblemap.put("b19_41_13", new hinsuperobject(19,41,13,"इस्राएल का परमेश्वर यहोवा आदि से अनन्तकाल तक धन्य है आमीन, फिर आमीन।।"));
hinsuperobject.biblemap.put("b19_42_1", new hinsuperobject(19,42,1,"जैसे हरिणी नदी के जल के लिये हांफती है, वैसे ही, हे परमेश्वर, मैं तेरे लिये हांफता हूं।"));
hinsuperobject.biblemap.put("b19_42_2", new hinsuperobject(19,42,2,"जीवते ईश्वर परमेश्वर का मैं प्यासा हूं, मैं कब जाकर परमेश्वर को अपना मुंह दिखाऊंगा?"));
hinsuperobject.biblemap.put("b19_42_3", new hinsuperobject(19,42,3,"मेरे आंसू दिन और रात मेरा आहार हुए हैं; और लोग दिन भर मुझ से कहते रहते हैं, तेरा परमेश्वर कहां है?"));
hinsuperobject.biblemap.put("b19_42_4", new hinsuperobject(19,42,4,"मैं भीड़ के संग जाया करता था, मैं जयजयकार और धन्यवाद के साथ उत्सव करनेवाली भीड़ के बीच में परमेश्वर के भवन को धीरे धीरे जाया करता था; यह स्मरण करके मेरा प्राण शोकित हो जाता है।"));
hinsuperobject.biblemap.put("b19_42_5", new hinsuperobject(19,42,5,"हे मेरे प्राण, तू क्यों गिरा जाता है? और तू अन्दर ही अन्दर क्यों व्याकुल है? परमेश्वर पर आशा लगाए रह; क्योंकि मैं उसके दर्शन से उद्धार पाकर फिर उसका धन्यवाद करूंगा।।"));
hinsuperobject.biblemap.put("b19_42_6", new hinsuperobject(19,42,6,"हे मेरे परमेश्वर; मेरा प्राण मेरे भीतर गिरा जाता है, इसलिये मैं यर्दन के पास के देश से और हर्मोन के पहाड़ों और मिसगार की पहाड़ी के ऊपर से तुझे स्मरण करता हूं।"));
hinsuperobject.biblemap.put("b19_42_7", new hinsuperobject(19,42,7,"तेरी जलधाराओं का शब्द सुनकर जल, जल को पुकारता है; तेरी सारी तरंगों और लहरों में मैं डूब गया हूं।"));
hinsuperobject.biblemap.put("b19_42_8", new hinsuperobject(19,42,8,"तौभी दिन को यहोवा अपनी शक्ति और करूणा प्रगट करेगा; और रात को भी मैं उसका गीत गाऊंगा, और अपने जीवनदाता ईश्वर से प्रार्थना करूंगा।।"));
hinsuperobject.biblemap.put("b19_42_9", new hinsuperobject(19,42,9,"मैं ईश्वर से जो मेरी चट्टान है कहूंगा, तू मुझे क्यों भूल गया? मैं शत्रु के अन्धेर के मारे क्यों शोक का पहिरावा पहिने हुए चलता फिरता हूं?"));
hinsuperobject.biblemap.put("b19_42_10", new hinsuperobject(19,42,10,"मेरे सतानेवाले जो मेरी निन्दा करते हैं मानो उस में मेरी हडि्डयां चूर चूर होती हैं, मानो कटार से छिदी जाती हैं, क्योंकि वे दिन भर मुझ से कहते रहते हैं, तेरा परमेश्वर कहां है?"));
hinsuperobject.biblemap.put("b19_42_11", new hinsuperobject(19,42,11,"हे मेरे प्राण तू क्यों गिरा जाता है? तू अन्दर ही अन्दर क्यों व्याकुल है? परमेश्वर पर भरोसा रख; क्योंकि वह मेरे मुख की चमक और मेरा परमेश्वर है, मैं फिर उसका धन्यवाद करूंगा।।"));
hinsuperobject.biblemap.put("b19_43_1", new hinsuperobject(19,43,1,"हे परमेश्वर, मेरा न्याय चुका और विधर्मी जाति से मेरा मुक मा लड़; मुझ को छली और कुटिल पुरूष से बचा।"));
hinsuperobject.biblemap.put("b19_43_2", new hinsuperobject(19,43,2,"क्योंकि हे परमेश्वर, तू ही मेरी शरण है, तू ने क्यों मुझे त्याग दिया है? मैं शत्रु के अन्धेर के मारे शोक का पहिरावा पहिने हुए क्यों फिरता रहूं?"));
hinsuperobject.biblemap.put("b19_43_3", new hinsuperobject(19,43,3,"अपने प्रकाश और अपनी सच्चाई को भेज; वे मेरी अगुवाई करें, वे ही मुझ को तेरे पवित्रा पर्वत पर और तेरे निवास स्थान में पहुंचाए!"));
hinsuperobject.biblemap.put("b19_43_4", new hinsuperobject(19,43,4,"तब मैं परमेश्वर की वेदी के पास जाऊंगा, उस ईश्वर के पास जो मेरे अति आनन्द का कुण्ड है; और हे परमेश्वर, हे मेरे परमेश्वर मैं वीणा बजा बजाकर तेरा धन्यवाद करूंगा।।"));
hinsuperobject.biblemap.put("b19_43_5", new hinsuperobject(19,43,5,"हे मेरे प्राण तू क्यों गिरा जाता है? तू अन्दर ही अन्दर क्यों व्याकुल है? परमेश्वर पर भरोसा रख, क्योंकि वह मेरे मुख की चमक और मेरा परमेश्वर है; मैं फिर उसका धन्यवाद करूंगा।।"));
hinsuperobject.biblemap.put("b19_44_1", new hinsuperobject(19,44,1,"हे परमेश्वर हम ने अपने कानों से सुना, हमारे बापदादों ने हम से वर्णन किया है, कि तू ने उनके दिनों में और प्राचीनकाल में क्या क्या काम किए हैं।"));
hinsuperobject.biblemap.put("b19_44_2", new hinsuperobject(19,44,2,"तू ने अपने हाथ से जातियों को निकाल दिया, और इनको बसाया; तू ने देश देश के लोगों को दु:ख दिया, और इनको चारों ओर फैला दिया;"));
hinsuperobject.biblemap.put("b19_44_3", new hinsuperobject(19,44,3,"क्योंकि वे न तो अपनी तलवार के बल से इस देश के अधिकारी हुए, और न अपने बाहुबल से; परन्तु तेरे दहिने हाथ और तेरी भुजा और तेरे प्रसन्न मुख के कारण जयवन्त हुए; क्योंकि तू उनको चाहता था।।"));
hinsuperobject.biblemap.put("b19_44_4", new hinsuperobject(19,44,4,"हे परमेश्वर, तू ही हमारा महाराजा है, तू याकूब के उद्धार की आज्ञा देता है।"));
hinsuperobject.biblemap.put("b19_44_5", new hinsuperobject(19,44,5,"तेरे सहारे से हम अपने द्रोहियों को ढकेलकर गिरा देंगे; तेरे नाम के प्रताप से हम अपने विरोधियों को रौंदेंगे।"));
hinsuperobject.biblemap.put("b19_44_6", new hinsuperobject(19,44,6,"क्योंकि मैं अपने धनुष पर भरोसा न रखूंगा, और न अपनी तलवार के बल से बचूगा।"));
hinsuperobject.biblemap.put("b19_44_7", new hinsuperobject(19,44,7,"परन्तु तू ही ने हम को द्रोहियों से बचाया है, और हमारे बैरियों को निराश और लज्जित किया है।"));
hinsuperobject.biblemap.put("b19_44_8", new hinsuperobject(19,44,8,"हम परमेश्वर की बड़ाई दिन भर करते रहते हैं, और सदैव तेरे नाम का धन्यवाद करते रहेंगे।।"));
hinsuperobject.biblemap.put("b19_44_9", new hinsuperobject(19,44,9,"तौभी तू ने अब हम को त्याग दिया और हमारा अनादर किया है, और हमारे दलों के साथ आगे नहीं जाता।"));
hinsuperobject.biblemap.put("b19_44_10", new hinsuperobject(19,44,10,"तू हम को शत्रु के साम्हने से हटा देता है, और हमारे बैरी मनमाने लूट मार करते हैं।"));
hinsuperobject.biblemap.put("b19_44_11", new hinsuperobject(19,44,11,"तू ने हमें कसाई की भेडों के समान कर दिया है, और हम को अन्य जातियों में तित्तर बित्तर किया है।"));
hinsuperobject.biblemap.put("b19_44_12", new hinsuperobject(19,44,12,"तू अपनी प्रजा को सेंतमेंत बेच डालता है, परन्तु उनके मोल से तू धनी नहीं होता।।"));
hinsuperobject.biblemap.put("b19_44_13", new hinsuperobject(19,44,13,"तू हमारे पड़ोसियों से हमारी नामधराई कराता है, और हमारे चारों ओर से रहनेवाले हम से हंसी ठट्ठा करते हैं।"));
hinsuperobject.biblemap.put("b19_44_14", new hinsuperobject(19,44,14,"तू हम को अन्यजातियों के बीच में उपमा ठहराता है, और देश देश के लेग हमारे कारण सिर हिलाते हैं। दिन भर हमें तिरस्कार सहना पड़ता है,"));
hinsuperobject.biblemap.put("b19_44_15", new hinsuperobject(19,44,15,"और कलंक लगाने और निन्दा करनेवाले के बोल से,"));
hinsuperobject.biblemap.put("b19_44_16", new hinsuperobject(19,44,16,"और शत्रु और बदला लेनेवालों के कारण, बुरा- भला कहनेवालों और निन्दा करनेवालों के कारण।"));
hinsuperobject.biblemap.put("b19_44_17", new hinsuperobject(19,44,17,"यह सब कुछ हम पर बीता तौभी हम तुझे नहीं भूले, न तेरी वाचा के विषय विश्वासघात किया है।"));
hinsuperobject.biblemap.put("b19_44_18", new hinsuperobject(19,44,18,"हमारे मन न बहके, न हमारे पैर तरी बाट से मुड़े;"));
hinsuperobject.biblemap.put("b19_44_19", new hinsuperobject(19,44,19,"तौभी तू ने हमें गीदड़ों के स्थान में पीस डाला, और हम को घोर अन्धकार में छिपा दिया है।।"));
hinsuperobject.biblemap.put("b19_44_20", new hinsuperobject(19,44,20,"यदि हम अपने परमेश्वर का नाम भूल जाते, वा किसी पराए देवता की ओर अपने हाथ फैलाते,"));
hinsuperobject.biblemap.put("b19_44_21", new hinsuperobject(19,44,21,"तो क्या परमेश्वर इसका विचार न करता? क्योंकि वह तो मन की गुप्त बातों को जानता है।"));
hinsuperobject.biblemap.put("b19_44_22", new hinsuperobject(19,44,22,"परन्तु हम दिन भर तेरे निमित्त मार डाले जाते हैं, और उन भेड़ों के समान समझे जाते हैं जो वध होने पर हैं।।"));
hinsuperobject.biblemap.put("b19_44_23", new hinsuperobject(19,44,23,"हे प्रभु, जाग! तू क्यों सोता है? उठ! हम को सदा के लिये त्याग न दे!"));
hinsuperobject.biblemap.put("b19_44_24", new hinsuperobject(19,44,24,"तू क्यों अपना मुंह छिपा लेता है? और हमारा दु:ख और सताया जाना भूल जाता है?"));
hinsuperobject.biblemap.put("b19_44_25", new hinsuperobject(19,44,25,"हमारा प्राण मिट्टी से लग गया; हमारा पेट भूमि से सट गया है।"));
hinsuperobject.biblemap.put("b19_44_26", new hinsuperobject(19,44,26,"हमारी सहायता के लिये उठ खड़ा हो! और अपनी करूणा के निमित्त हम को छुड़ा ले।।"));
hinsuperobject.biblemap.put("b19_45_1", new hinsuperobject(19,45,1,"मेरा हृदय एक सुन्दर विषय की उमंग से उमण्ड रहा है, जो बात मैं ने राजा के विषय रची है उसको सुनाता हूं; मेरी जीभ निपुण लेखक की लेखनी बनी है।"));
hinsuperobject.biblemap.put("b19_45_2", new hinsuperobject(19,45,2,"तू मनुष्य की सन्तानों में परम सुन्दर है; तेरे ओठों में अनुग्रह भरा हुआ है; इसलिये परमेश्वर ने तुझे सदा के लिये आशीष दी है।"));
hinsuperobject.biblemap.put("b19_45_3", new hinsuperobject(19,45,3,"हे वीर, तू अपनी तलवार को जो तेरा विभव और प्रताप है अपनी कटि पर बान्ध!"));
hinsuperobject.biblemap.put("b19_45_4", new hinsuperobject(19,45,4,"सत्यता, नम्रता और धर्म के निमित्त अपने ऐश्वर्य और प्रताप पर सफलता से सवार हो; तेरा दहिना हाथ तुझे भयानक काम सिखलाए!"));
hinsuperobject.biblemap.put("b19_45_5", new hinsuperobject(19,45,5,"तेरे तीर तो तेज हैं, तेरे साम्हने देश देश के लोग गिरेंगे; राजा के शत्रुओं के हृदय उन से छिदेंगे।।"));
hinsuperobject.biblemap.put("b19_45_6", new hinsuperobject(19,45,6,"हे परमेश्वर, तेरा सिंहासन सदा सर्वदा बना रहेगा; तेरा राजदण्ड न्याय का है।"));
hinsuperobject.biblemap.put("b19_45_7", new hinsuperobject(19,45,7,"तू ने धर्म से प्रीति और दुष्टता से बैर रखा है। इस कारण परमेश्वर ने हां तेरे परमेश्वर ने तुझ को तेरे साथियों से अधिक हर्ष के तेल से अभिषेक किया है।"));
hinsuperobject.biblemap.put("b19_45_8", new hinsuperobject(19,45,8,"तेरे सारे वस्त्रा, गन्धरस, अगर, और तेल से सुगन्धित हैं, तू हाथीदांत के मन्दिरों में तारवाले बाजों के कारण आनन्दित हुआ है।"));
hinsuperobject.biblemap.put("b19_45_9", new hinsuperobject(19,45,9,"तेरी प्रतिष्ठित स्त्रियों में राजकुमारियां भी हैं; तेरी दहिनी ओर पटरानी, ओपीर के कुन्दन से विभूषित खड़ी है।।"));
hinsuperobject.biblemap.put("b19_45_10", new hinsuperobject(19,45,10,"हे राजकुमारी सुन, और कान लगाकर ध्यान दे; अपने लोगों और अपने पिता के घर को भूल जा;"));
hinsuperobject.biblemap.put("b19_45_11", new hinsuperobject(19,45,11,"और राजा तेरे रूप की चाह करेगा। क्योंकि वह तो तेरा प्रभु है, तू उसे दण्डवत् कर।"));
hinsuperobject.biblemap.put("b19_45_12", new hinsuperobject(19,45,12,"सोर की राजकुमारी भी भेंट करने के लिये उपस्थित होगी, प्रजा के धनवान लोग तुझे प्रसन्न करने का यत्न करेंगे।।"));
hinsuperobject.biblemap.put("b19_45_13", new hinsuperobject(19,45,13,"राजकुमारी महल में अति शोभायमान है, उसके वस्त्रा में सुनहले बूटे कढ़े हुए हैं;"));
hinsuperobject.biblemap.put("b19_45_14", new hinsuperobject(19,45,14,"वह बूटेदार वस्त्रा पहिने हुए राजा के पास पहुंचाई जाएगी। जो कुमारियां उसकी सहेलियां हैं, वे उसके पीछे पीछे चलती हुई तेरे पास पहुंचाई जाएंगी।"));
hinsuperobject.biblemap.put("b19_45_15", new hinsuperobject(19,45,15,"वे आनन्दित और मगन होकर पहुंचाई जाएंगी, और वे राजा के महल में प्रवेश करेंगी।।"));
hinsuperobject.biblemap.put("b19_45_16", new hinsuperobject(19,45,16,"तेरे पितरों के स्थान पर तेरे पुत्रा होंगे; जिनको तू सारी पृथ्वी पर हाकिम ठहराएगा।"));
hinsuperobject.biblemap.put("b19_45_17", new hinsuperobject(19,45,17,"मैं ऐसा करूंगा, कि तेरी नाम की चर्चा पीढ़ी से पीढ़ी तक होती रहेगी; इस कारण देश देश के लोग सदा सर्वदा तेरा धन्यवाद करते रहेंगे।।"));
hinsuperobject.biblemap.put("b19_46_1", new hinsuperobject(19,46,1,"परमेश्वर हमारा शरणस्थान और बल है, संकट में अति सहज से मिलनेवाला सहायक।"));
hinsuperobject.biblemap.put("b19_46_2", new hinsuperobject(19,46,2,"इस कारण हम को कोई भय नहीं चाहे पृथ्वी उलट जाए, और पहाड़ समुद्र के बीच में डाल दिए जाएं;"));
hinsuperobject.biblemap.put("b19_46_3", new hinsuperobject(19,46,3,"चाहे समुद्र गरजे और फेन उठाए, और पहाड़ उसकी बाढ़ से कांप उठे।।"));
hinsuperobject.biblemap.put("b19_46_4", new hinsuperobject(19,46,4,"एक नदी है जिसकी नहरों से परमेश्वर के नगर में अर्थात् परमप्रधान के पवित्रा निवास भवन में आनन्द होता है।"));
hinsuperobject.biblemap.put("b19_46_5", new hinsuperobject(19,46,5,"परमेश्वर उस नगर के बीच में है, वह कभी टलने का नहीं; पौ फटते ही परमेश्वर उसकी सहायता करता है।"));
hinsuperobject.biblemap.put("b19_46_6", new hinsuperobject(19,46,6,"जाति जाति के लोग झल्ला उठे, राज्य राज्य के लोग डगमगाने लगे; वह बोल उठा, और पृथ्वी पिघल गई।"));
hinsuperobject.biblemap.put("b19_46_7", new hinsuperobject(19,46,7,"सेनाओं का यहोवा हमारे संगे है; याकूब का परमेश्वर हमारा ऊंचा गढ़ है।।"));
hinsuperobject.biblemap.put("b19_46_8", new hinsuperobject(19,46,8,"आओ, यहोवा के महाकर्म देखो, कि उस ने पृथ्वी पर कैसा कैसा उजाड़ किया है।"));
hinsuperobject.biblemap.put("b19_46_9", new hinsuperobject(19,46,9,"वह पृथ्वी की छोर तक लड़ाइयों को मिटाता है; वह धनुष को तोड़ता, और भाले को दो टुकड़े कर डालता है, और रथों को आग में झोंक देता है!"));
hinsuperobject.biblemap.put("b19_46_10", new hinsuperobject(19,46,10,"चुप हो जाओ, और जान लो, कि मैं ही परमेश्वर हूं। मैं जातियों में महान् हूं, मैं पृथ्वी भर में महान् हूं!"));
hinsuperobject.biblemap.put("b19_46_11", new hinsuperobject(19,46,11,"सेनाओं का यहोवा हमारे संग है; याकूब का परमेश्वर हमारा ऊंचा गढ़ है।।"));
hinsuperobject.biblemap.put("b19_47_1", new hinsuperobject(19,47,1,"हे देश देश के सब लोगों, तालियों बजाओ! ऊंचे शब्द से परमेश्वर के लिये जयजयकार करो!"));
hinsuperobject.biblemap.put("b19_47_2", new hinsuperobject(19,47,2,"क्योंकि यहोवा परमप्रधान और भययोग्य है, वह सारी पृथ्वी के ऊपर महाराजा है।"));
hinsuperobject.biblemap.put("b19_47_3", new hinsuperobject(19,47,3,"वह देश के लोगों को हमारे सम्मुख नीचा करता, और अन्यजातियों को हमारे पांवों के नीचे कर देता है।"));
hinsuperobject.biblemap.put("b19_47_4", new hinsuperobject(19,47,4,"वह हमारे लिये उत्तम भाग चुन लेगा, जो उसके प्रिय याकूब के घमण्ड का कारण है।।"));
hinsuperobject.biblemap.put("b19_47_5", new hinsuperobject(19,47,5,"परमेश्वर जयजयकार सहित, यहोवा नरसिंगे के शब्द के साथ ऊपर गया है।"));
hinsuperobject.biblemap.put("b19_47_6", new hinsuperobject(19,47,6,"परमेश्वर का भजन गाओ, भजन गाओ! हमारे महाराजा का भजन गाओ, भजन गाओ!"));
hinsuperobject.biblemap.put("b19_47_7", new hinsuperobject(19,47,7,"क्योंकि परमेश्वर सारी पृथ्वी का महाराजा है; समझ बूझकर बुद्धि से भजन गाओ"));
hinsuperobject.biblemap.put("b19_47_8", new hinsuperobject(19,47,8,"परमेश्वर जाति जाति पर राज्य करता है; परमेश्वर अपने पवित्रा सिंहासन पर विराजमान है।"));
hinsuperobject.biblemap.put("b19_47_9", new hinsuperobject(19,47,9,"राज्य राज्य के रईस इब्राहीम के परमेश्वर की प्रजा होने के लिये इकट्ठे हुए हैं। क्योंकि पृथ्वी की ढालें परमेश्वर के वश में हैं, वह तो शिरोमणि है!"));
hinsuperobject.biblemap.put("b19_48_1", new hinsuperobject(19,48,1,"हमारे परमेश्वर के नगर में, और अपने पवित्रा पर्वत पर यहोवा महान् और अति स्तुति के योग्य है!"));
hinsuperobject.biblemap.put("b19_48_2", new hinsuperobject(19,48,2,"सिरयोन पर्वत ऊंचाई में सुन्दर और सारी पृथ्वी के हर्ष का कारण है, राजाधिराज का नगर उत्तरीय सिरे पर है।"));
hinsuperobject.biblemap.put("b19_48_3", new hinsuperobject(19,48,3,"उसके महलों में परमेश्वर ऊंचा गढ़ माना गया है।"));
hinsuperobject.biblemap.put("b19_48_4", new hinsuperobject(19,48,4,"क्योंकि देखो, राजा लोग इकट्ठे हुए, वे एक संग आगे बढ़ गए।"));
hinsuperobject.biblemap.put("b19_48_5", new hinsuperobject(19,48,5,"उन्हों ने आप ही देखा और देखते ही विस्मित हुए, वे घबराकर भाग गए।"));
hinsuperobject.biblemap.put("b19_48_6", new hinsuperobject(19,48,6,"वहां कपकपी ने उनको आ पकड़ा, और जच्चा की सी पीड़ाएं उन्हें होने लगीं।"));
hinsuperobject.biblemap.put("b19_48_7", new hinsuperobject(19,48,7,"तू पूर्वी वायु से तर्शीश के जहाजों को तोड़ डालता है।"));
hinsuperobject.biblemap.put("b19_48_8", new hinsuperobject(19,48,8,"सेनाओं के यहोवा के नगर में, अपने परमेश्वर के नगर में, जैसा हम ने सुना था, वैसा देखा भी है; परमेश्वर उसको सदा दृढ़ और स्थिर रखेगा।।"));
hinsuperobject.biblemap.put("b19_48_9", new hinsuperobject(19,48,9,"हे परमेश्वर हम ने तेरे मन्दिर के भीतर तेरी करूणा पर ध्यान किया है।"));
hinsuperobject.biblemap.put("b19_48_10", new hinsuperobject(19,48,10,"हे परमेश्वर तेरे नाम के योग्य तेरी स्तुति पृथ्वी की छोर तक होती है। तेरा दहिना हाथ धर्म से भरा है;"));
hinsuperobject.biblemap.put("b19_48_11", new hinsuperobject(19,48,11,"तेरे न्याय के कामों के कारण सिरयोन पर्वत आनन्द करे, और यहूदा के नगर की पुत्रियां मगन हों!"));
hinsuperobject.biblemap.put("b19_48_12", new hinsuperobject(19,48,12,"सिरयोन के चारों ओर चलो, और उसकी परिक्रमा करो, उसके गुम्मटों को गिन लो,"));
hinsuperobject.biblemap.put("b19_48_13", new hinsuperobject(19,48,13,"उसकी शहरपनाह पर दृष्टि लगाओ, उसके महलों को ध्यान से देखो; जिस से कि तुम आनेवाली पीढ़ी के लोगों से इस बात का वर्णन कर सको।"));
hinsuperobject.biblemap.put("b19_48_14", new hinsuperobject(19,48,14,"क्योंकि वह परमेश्वर सदा सर्वदा हमारा परमेश्वर है, वह मृत्यु तक हमारी अगुवाई करेगा।।"));
hinsuperobject.biblemap.put("b19_49_1", new hinsuperobject(19,49,1,"हे देश देश के सब लोगों यह सुनो! हे संसार के सब निवासियों, कान लगाओ!"));
hinsuperobject.biblemap.put("b19_49_2", new hinsuperobject(19,49,2,"क्या ऊंच, क्या नीच क्या धनी, क्या दरिद्र, कान लगाओ!"));
hinsuperobject.biblemap.put("b19_49_3", new hinsuperobject(19,49,3,"मेरे मुंह से बुद्धि की बातें निकलेंगी; और मेरे हृदय की बातें समझ की होंगी।"));
hinsuperobject.biblemap.put("b19_49_4", new hinsuperobject(19,49,4,"मैं नीतिवचन की ओर अपना कान लगाऊंगा, मैं वीणा बजाते हुए अपनी गुप्त बात प्रकाशित करूंगा।।"));
hinsuperobject.biblemap.put("b19_49_5", new hinsuperobject(19,49,5,"विपत्ति के दिनों में जब मैं अपने अड़ंगा मारनेवालों की बुराइयों से घिरूं, तब मैं क्यों डरूं?"));
hinsuperobject.biblemap.put("b19_49_6", new hinsuperobject(19,49,6,"जो अपनी सम्पत्ति पर भरोसा रखते, और अपने धन की बहुतायत पर फूलते हैं,"));
hinsuperobject.biblemap.put("b19_49_7", new hinsuperobject(19,49,7,"उन में से कोई अपने भाई को किसी भांति छुड़ा नहीं सकता है; और न परमेश्वर को उसकी सन्ती प्रायश्चित्त में कुछ दे सकता है,"));
hinsuperobject.biblemap.put("b19_49_8", new hinsuperobject(19,49,8,"(क्योंकि उनके प्राण की छुड़ौती भारी है वह अन्त तक कभी न चुका सकेंगे)।"));
hinsuperobject.biblemap.put("b19_49_9", new hinsuperobject(19,49,9,"कोई ऐसा नहीं जो सदैव जीवित रहे, और कब्र को न देखे।।"));
hinsuperobject.biblemap.put("b19_49_10", new hinsuperobject(19,49,10,"क्योंकि देखने में आता है, कि बुद्धिमान भी मरते हैं, और मूर्ख और पशु सरीखे मनुष्य भी दोनों नाश होते हैं, और अपनी सम्पत्ति औरों के लिये छोड़ जाते हैं।"));
hinsuperobject.biblemap.put("b19_49_11", new hinsuperobject(19,49,11,"वे मन ही मन यह सोचते हैं, कि उनका घर सदा स्थिर रहेगा, और उनके निवास पीढ़ी से पीढ़ी तक बने रहेंगे; इसलिये वे अपनी अपनी भूमि का नाम अपने अपने नाम पर रखते हैं।"));
hinsuperobject.biblemap.put("b19_49_12", new hinsuperobject(19,49,12,"परन्तु मनुष्य प्रतिष्ठा पाकर भी स्थिर नहीं रहता, वह पशुओं के समान होता है, जो मर मिटते हैं।।"));
hinsuperobject.biblemap.put("b19_49_13", new hinsuperobject(19,49,13,"उनकी यह चाल उनकी मूर्खता है, तौभी उनके बाद लोग उनकी बातों से प्रसन्न होते हैं।"));
hinsuperobject.biblemap.put("b19_49_14", new hinsuperobject(19,49,14,"वे अधोलोक की मानों भेड़- बकरियां ठहराए गए हैं; मृत्यु उनका गड़ेरिया ठहरी; और बिहान को सीधे लोग उन पर प्रभुता करेंगे; और उनका सुन्दर रूप अधोलोक का कौर हो जाएगा और उनका कोई आधार न रहेगा।"));
hinsuperobject.biblemap.put("b19_49_15", new hinsuperobject(19,49,15,"परन्तु परमेश्वर मेरे प्राण को अधोलोक के वश से छुड़ा लेगा, क्योंकि वही मुझे ग्रहण कर अपनाएगा।।"));
hinsuperobject.biblemap.put("b19_49_16", new hinsuperobject(19,49,16,"जब कोई धनी हो जाए और उसके घर का विभव बढ़ जाए, तब तू भय न खाना।"));
hinsuperobject.biblemap.put("b19_49_17", new hinsuperobject(19,49,17,"क्योंकि वह मर कर कुछ भी साथ न ले जाएगा; न उसका विभव उसके साथ कब्र में जाएगा।"));
hinsuperobject.biblemap.put("b19_49_18", new hinsuperobject(19,49,18,"चाहे वह जीते जी अपने आप को धन्य कहता रहे, (जब तू अपनी भलाई करता है, तब वे लोग तेरी प्रशंसा करते हैं)"));
hinsuperobject.biblemap.put("b19_49_19", new hinsuperobject(19,49,19,"तौभी वह अपने पुरखाओं के समाज में मिलाया जाएगा, जो कभी उजियाला न देखेंगे।"));
hinsuperobject.biblemap.put("b19_49_20", new hinsuperobject(19,49,20,"मनुष्य चाहे प्रतिष्ठित भी हों परन्तु यदि वे समझ नहीं रखते, तो वे पशुओं के समान हैं जो मर मिटते हैं।।"));
hinsuperobject.biblemap.put("b19_50_1", new hinsuperobject(19,50,1,"ईश्वर परमेश्वर यहोवा ने कहा है, और उदयाचल से लेकर अस्ताचल तक पृथ्वी के लोगों को बुलाया है।"));
hinsuperobject.biblemap.put("b19_50_2", new hinsuperobject(19,50,2,"सिरयोन से, जो परम सुन्दर है, परमेश्वर ने अपना तेज दिखाया है।"));
hinsuperobject.biblemap.put("b19_50_3", new hinsuperobject(19,50,3,"हमारा परमेश्वर आएगा और चुपचाप न रहेगा, आग उसके आगे आगे भस्म करती जाएगी; और उसके चारों ओर बड़ी आंधी चलेगी।"));
hinsuperobject.biblemap.put("b19_50_4", new hinsuperobject(19,50,4,"वह अपनी प्रजा का न्याय करने के लिये ऊपर से आकाश को और पृथ्वी को भी पुकारेगा:"));
hinsuperobject.biblemap.put("b19_50_5", new hinsuperobject(19,50,5,"मेरे भक्तों को मेरे पास इकट्ठा करो, जिन्हों ने बलिदान चढ़ाकर मुझ से वाचा बान्धी है!"));
hinsuperobject.biblemap.put("b19_50_6", new hinsuperobject(19,50,6,"और स्वर्ग उसके धर्मी होने का प्रचार करेगा क्योंकि परमेश्वर तो आप ही न्यायी है।।"));
hinsuperobject.biblemap.put("b19_50_7", new hinsuperobject(19,50,7,"हे मेरी प्रजा, सुन, मैं बोलता हूं, और हे इस्राएल, मैं तेरे विषय साक्षी देता हूं। परमेश्वर तेरा परमेश्वर मैं ही हूं।"));
hinsuperobject.biblemap.put("b19_50_8", new hinsuperobject(19,50,8,"मैं तुझ पर तेरे मेलबलियों के विषय दोष नहीं लगाता, तेरे होमबलि तो नित्य मेरे लिये चढ़ते हैं।"));
hinsuperobject.biblemap.put("b19_50_9", new hinsuperobject(19,50,9,"मैं न तो तेरे घर से बैल न तेरे पशुशालों से बकरे ले लूंगा।"));
hinsuperobject.biblemap.put("b19_50_10", new hinsuperobject(19,50,10,"क्योंकि वन के सारे जीवजन्तु और हजारों पहाड़ों के जानवर मेरे ही हैं।"));
hinsuperobject.biblemap.put("b19_50_11", new hinsuperobject(19,50,11,"पहाड़ों के सब पक्षियों को मैं जानता हूं, और मैदान पर चलने फिरनेवाले जानवार मेरे ही हैं।।"));
hinsuperobject.biblemap.put("b19_50_12", new hinsuperobject(19,50,12,"यदि मैं भूखा होता तो तुझ से न कहता; क्योंकि जगत् और जो कुछ उस में है वह मेरा है।"));
hinsuperobject.biblemap.put("b19_50_13", new hinsuperobject(19,50,13,"क्या मैं बैल का मांस खाऊं, वा बकरों का लोहू पीऊं?"));
hinsuperobject.biblemap.put("b19_50_14", new hinsuperobject(19,50,14,"परमेश्वर को धन्यवाद ही का बलिदान चढ़ा, और परमप्रधान के लिये अपनी मन्नतें पूरी कर;"));
hinsuperobject.biblemap.put("b19_50_15", new hinsuperobject(19,50,15,"और संकट के दिन मुझे पुकार; मैं तुझे छुड़ाऊंगा, और तू मेरी महिमा करने पाएगा।।"));
hinsuperobject.biblemap.put("b19_50_16", new hinsuperobject(19,50,16,"परन्तु दुष्ट से परमेश्वर कहता है: तुझे मेरी विधियों का वर्णन करने से क्या काम? तू मरी वाचा की चर्चा क्यों करता है?"));
hinsuperobject.biblemap.put("b19_50_17", new hinsuperobject(19,50,17,"तू तो शिक्षा से बैर करता, और मेरे वचनों को तुच्छ जातना है।"));
hinsuperobject.biblemap.put("b19_50_18", new hinsuperobject(19,50,18,"जब तू ने चोर को देखा, तब उसकी संगति से प्रसन्न हुआ; और परस्त्रीगामियों के साथ भागी हुआ।।"));
hinsuperobject.biblemap.put("b19_50_19", new hinsuperobject(19,50,19,"तू ने अपना मुंह बुराई करने के लिये खोला, और तेरी जीभ छल की बातें गढ़ती है।"));
hinsuperobject.biblemap.put("b19_50_20", new hinsuperobject(19,50,20,"तू बैठा हुआ अपने भाई के विरूद्ध बोलता; और अपने सगे भाई की चुगली खाता है।"));
hinsuperobject.biblemap.put("b19_50_21", new hinsuperobject(19,50,21,"यह काम तू ने किया, और मैं चुप रहा; इसलिये तू ने समझ लिया कि परमेश्वर बिलकुल मेरे साम्हने है। परन्तु मैं तुझे समझाऊंगा, और तेरी आंखों के साम्हने सब कुछ अलग अलग दिखाऊंगा।।"));
hinsuperobject.biblemap.put("b19_50_22", new hinsuperobject(19,50,22,"हे ईश्वर को भूलनेवालो यह बात भली भांति समझ लो, कहीं ऐसा न हो कि मैं तुम्हें फाड़ डालूं, और कोई छुडानेवाला न हो!"));
hinsuperobject.biblemap.put("b19_50_23", new hinsuperobject(19,50,23,"धन्यवाद के बलिदान का चढ़ानेवाला मेरी महिमा करता है; और जो अपना चरित्रा उत्तम रखता है उसको मैं परमेश्वर का किया हुआ उद्धार दिखाऊंगा!"));
hinsuperobject.biblemap.put("b19_51_1", new hinsuperobject(19,51,1,"हे परमेश्वर, अपनी करूणा के अनुसार मुझ पर अनुग्रह कर; अपनी बड़ी दया के अनुसार मेरे अपराधों को मिटा दे।"));
hinsuperobject.biblemap.put("b19_51_2", new hinsuperobject(19,51,2,"मुझे भलीं भांति धोकर मेरा अधर्म दूर कर, और मेरा पाप छुड़ाकर मुझे शुद्ध कर!"));
hinsuperobject.biblemap.put("b19_51_3", new hinsuperobject(19,51,3,"मैं तो अपने अपराधों कों जानता हूं, और मेरा पाप निरन्तर मेरी दृष्टि में रहता है।"));
hinsuperobject.biblemap.put("b19_51_4", new hinsuperobject(19,51,4,"मैं ने केवल तेरे ही विरूद्ध पाप किया, और जो तेरी दृष्टि में बुरा है, वही किया है, ताकि तू बोलने में धर्मी और न्याय करने में निष्कलंक ठहरे।"));
hinsuperobject.biblemap.put("b19_51_5", new hinsuperobject(19,51,5,"देख, मैं अधर्म के साथ उत्पन्न हुआ, और पाप के साथ अपनी माता के गर्भ में पड़ा।।"));
hinsuperobject.biblemap.put("b19_51_6", new hinsuperobject(19,51,6,"देख, तू हृदय की सच्चाई से प्रसन्न होता है; और मेरे मन ही में ज्ञान सिखाएगा।"));
hinsuperobject.biblemap.put("b19_51_7", new hinsuperobject(19,51,7,"जूफा से मुझे शुद्ध कर, तो मैं पवित्रा हो जाऊंगा; मुझे धो, और मैं हिम से भी अधिक श्वेत बनूंगा।"));
hinsuperobject.biblemap.put("b19_51_8", new hinsuperobject(19,51,8,"मुझे हर्ष और आनन्द की बातें सुना, जिस से जो हडि्डयां तू ने तोड़ डाली हैं वह मगन हो जाएं।"));
hinsuperobject.biblemap.put("b19_51_9", new hinsuperobject(19,51,9,"अपना मुख मेरे पापों की ओर से फेर ले, और मेरे सारे अधर्म के कामों को मिटा डाल।।"));
hinsuperobject.biblemap.put("b19_51_10", new hinsuperobject(19,51,10,"हे परमेश्वर, मेरे अन्दर शुद्ध मन उत्पन्न कर, और मेरे भीतर स्थिर आत्मा नये सिरे से उत्पन्न कर।"));
hinsuperobject.biblemap.put("b19_51_11", new hinsuperobject(19,51,11,"मुझे अपने साम्हने से निकाल न दे, और अपने पवित्रा आत्मा को मुझ से अलग न कर।"));
hinsuperobject.biblemap.put("b19_51_12", new hinsuperobject(19,51,12,"अपने किए हुए उद्धार का हर्ष मुझे फिर से दे, और उदार आत्मा देकर मुझे सम्भाल।।"));
hinsuperobject.biblemap.put("b19_51_13", new hinsuperobject(19,51,13,"जब मैं अपराधियों को तेरा मार्ग सिखाऊंगा, और पापी तेरी ओर फिरेंगे।"));
hinsuperobject.biblemap.put("b19_51_14", new hinsuperobject(19,51,14,"हे परमेश्वर, हे मेरे उद्धारकर्ता परमेश्वर, मुझे हत्या के अपराध से छुड़ा ले, तब मैं तेरे धर्म का जयजयकार करने पाऊंगा।।"));
hinsuperobject.biblemap.put("b19_51_15", new hinsuperobject(19,51,15,"हे प्रभु, मेरा मुंह खोल दे तब मैं तेरा गुणानुवाद कर सकूंगा।"));
hinsuperobject.biblemap.put("b19_51_16", new hinsuperobject(19,51,16,"क्योकि तू मेलबलि में प्रसन्न नहीं होता, नहीं तो मैं देता; होमबलि से भी तू प्रसन्न नहीं होता।"));
hinsuperobject.biblemap.put("b19_51_17", new hinsuperobject(19,51,17,"टूटा मन परमेश्वर के योग्य बलिदान है; हे परमेश्वर, तू टूटे और पिसे हुए मन को तुच्छ नहीं जानता।।"));
hinsuperobject.biblemap.put("b19_51_18", new hinsuperobject(19,51,18,"प्रसन्न होकर सिरयोन की भलाई कर, यरूशलेम की शहरपनाह को तू बना,"));
hinsuperobject.biblemap.put("b19_51_19", new hinsuperobject(19,51,19,"तब तू धर्म के बलिदानों से अर्थात् सर्वांग पशुओं के होमबलि से प्रसन्न होगा; तब लोग तेरी वेदी पर बैल चढ़ाएंगे।।"));
hinsuperobject.biblemap.put("b19_52_1", new hinsuperobject(19,52,1,"हे वीर, तू बुराई करने पर क्यों घमण्ड करता है? ईश्वर की करूणा तो अनन्त है।"));
hinsuperobject.biblemap.put("b19_52_2", new hinsuperobject(19,52,2,"तेरी जीभ केवल दुष्टता गढ़ती है; सारे धरे हुए अस्तुरे की नाईं वह छल का काम करती है।"));
hinsuperobject.biblemap.put("b19_52_3", new hinsuperobject(19,52,3,"तू भलाई से बढ़कर बुराई में और अधर्म की बात से बढ़कर झूठ से प्रीति रखता है।"));
hinsuperobject.biblemap.put("b19_52_4", new hinsuperobject(19,52,4,"हे छली जीभ तू सब विनाश करनेवाली बातों से प्रसन्न रहती है।।"));
hinsuperobject.biblemap.put("b19_52_5", new hinsuperobject(19,52,5,"हे ईश्वर तुझे सदा के लिये नाश कर देगा; वह तुझे पकड़कर तेरे डेरे से निकाल देगा; और जीवतों के लोक में तुझे उखाड़ डालेगा।"));
hinsuperobject.biblemap.put("b19_52_6", new hinsuperobject(19,52,6,"तब धर्मी लोग इस घटना को देखकर डर जाएंगे, और यह कहकर उस पर हंसेंगे, कि"));
hinsuperobject.biblemap.put("b19_52_7", new hinsuperobject(19,52,7,"देखो, यह वही पुरूष है जिस ने परमेश्वर को अपनी शरण नहीं माना, परन्तु अपने धन की बहुतायत पर भरोसा रखता था, और अपने को दुष्टता में दृढ़ करता रहा!"));
hinsuperobject.biblemap.put("b19_52_8", new hinsuperobject(19,52,8,"परन्तु मैं तो परमेश्वर के भवन में हरे जलपाई के वृक्ष के समान हूं। मैं ने परमेश्वर की करूणा पर सदा सर्वदा के लिये भरोसा रखा है।"));
hinsuperobject.biblemap.put("b19_52_9", new hinsuperobject(19,52,9,"मैं तेरा धन्यवाद सर्वदा करता रहूंगा, क्योंकि तू ही ने यह काम किया है। मैं तेरे ही नाम की बाट जोहता रहूंगा, क्योंकि यह तेरे पवित्रा भक्तों के साम्हने उत्तम है।।"));
hinsuperobject.biblemap.put("b19_53_1", new hinsuperobject(19,53,1,"मूढ़ ने अपने मन में कहा है, कि कोई परमेश्वर है ही नहीं। वे बिगड़ गए, उन्हों ने कुटिलता के घिनौने काम किए हैं; कोई सुकर्मी नहीं।।"));
hinsuperobject.biblemap.put("b19_53_2", new hinsuperobject(19,53,2,"परमेश्वर ने स्वर्ग पर से मनुष्यों के ऊपर दृष्टि की ताकि देखे कि कोई बुद्धि से चलनेवाला वा परमेश्वर को पूछनेवाला है कि नहीं।।"));
hinsuperobject.biblemap.put("b19_53_3", new hinsuperobject(19,53,3,"वे सब के सब हट गए; सब एक साथ बिगड़ गए; कोई सुकर्मी नहीं, एक भी नहीं।। क्या उन सब अनर्थकारियों को कुछ भी ज्ञान नहीं"));
hinsuperobject.biblemap.put("b19_53_4", new hinsuperobject(19,53,4,"जो मेरे लोगों को ऐसे खाते हैं जैसे रोटी और परमेश्वर का नाम नहीं लेते?"));
hinsuperobject.biblemap.put("b19_53_5", new hinsuperobject(19,53,5,"वहां उन पर भय छा गया जहां भय का कोई कारण न था। क्योंकि यहोवा न उनकी हडि्डयों को, जो तेरे विरूद्ध छावनी डाले पड़े थे, तितर बितर कर दिया; तू ने तो उन्हें लज्जित कर दिया इसलिये कि परमेश्वर ने उनको निकम्मा ठहराया है।।"));
hinsuperobject.biblemap.put("b19_53_6", new hinsuperobject(19,53,6,"भला होता कि इस्राएल का पूरा उद्धार सिरयोन से निकलता! जब परमेश्वर अपनी प्रजा को बन्धुवाई से लौटा ले आएगा तब याकूब मगन और इस्राएल आनन्दित होगा।।"));
hinsuperobject.biblemap.put("b19_54_1", new hinsuperobject(19,54,1,"हे परमेश्वर अपने नाम के द्वारा मेरा उद्वार कर, और अपने पराक्रम से मेरा न्याय कर।"));
hinsuperobject.biblemap.put("b19_54_2", new hinsuperobject(19,54,2,"हे परमेश्वर, मेरी प्रार्थना सुन ले; मेरे मुंह के वचनों की ओर कान लगा।।"));
hinsuperobject.biblemap.put("b19_54_3", new hinsuperobject(19,54,3,"क्योंकि परदेशी मेरे विरूद्व उठे हैं, और बलात्कारी मेरे प्राण के ग्राहक हुए हैं; उन्हों ने परमेश्वर को अपने सम्मुख नहीं जाना।।"));
hinsuperobject.biblemap.put("b19_54_4", new hinsuperobject(19,54,4,"देखो, परमेश्वर मेरा सहायक है; प्रभु मेरे प्राण के सम्भालनेवालों के संग है।"));
hinsuperobject.biblemap.put("b19_54_5", new hinsuperobject(19,54,5,"वह मेरे द्रोहियों की बुराई को उन्हीं पर लौटा देगा; हे परमेश्वर, अपनी सच्चाई के कारण उन्हें विनाश कर।।"));
hinsuperobject.biblemap.put("b19_54_6", new hinsuperobject(19,54,6,"मैं तुझे स्वेच्छाबलि चढ़ाऊंगा; हे यहोवा, मैं तेरे नाम का धन्यवाद करूंगा, क्योंकि यह उत्तम है।"));
hinsuperobject.biblemap.put("b19_54_7", new hinsuperobject(19,54,7,"क्योंकि तू ने मुझे सब दुखों से छुड़ाया है, और मैं अपने शत्रुओं पर दृष्टि करके सन्तुष्ट हुआ हूं।।"));
hinsuperobject.biblemap.put("b19_55_1", new hinsuperobject(19,55,1,"हे परमेश्वर, मेरी प्रार्थना की ओर कान लगा; और मेरी गिड़गिड़ाहट से मुंह न मोड़!"));
hinsuperobject.biblemap.put("b19_55_2", new hinsuperobject(19,55,2,"मेरी ओर ध्यान देकर, मुझे उत्तर दे; मैं चिन्ता के मारे छटपटाता हूं और व्याकुल रहता हूं।"));
hinsuperobject.biblemap.put("b19_55_3", new hinsuperobject(19,55,3,"क्योंकि शत्रु कोलाहल और दुष्ट उपद्रव कर रहें हैं; वे मुझ पर दोषारोपण करते हैं, और क्रोध में आकर मुझे सताते हैं।।"));
hinsuperobject.biblemap.put("b19_55_4", new hinsuperobject(19,55,4,"मेरा मन भीतर ही भीतर संकट में है, और मृत्यु का भय मुझ में समा गया है।"));
hinsuperobject.biblemap.put("b19_55_5", new hinsuperobject(19,55,5,"भय और कंपकपी ने मुझे पकड़ लिया है, और भय के कारण मेरे रोंए रोंए खड़े हो गए हैं।"));
hinsuperobject.biblemap.put("b19_55_6", new hinsuperobject(19,55,6,"और मैं ने कहा, भला होता कि मेरे कबूतर के से पंख होते तो मैं उड़ जाता और विश्राम पाता!"));
hinsuperobject.biblemap.put("b19_55_7", new hinsuperobject(19,55,7,"देखो, फिर तो मैं उड़ते उड़ते दूर निकल जाता और जंगल में बसेरा लेता,"));
hinsuperobject.biblemap.put("b19_55_8", new hinsuperobject(19,55,8,"मैं प्रचण्ड बयार और आन्धी के झोंके से बचकर किसी शरण स्थान में भाग जाता।।"));
hinsuperobject.biblemap.put("b19_55_9", new hinsuperobject(19,55,9,"हे प्रभु, उनको सत्यानाश कर, और उनकी भाषा में गड़बड़ी डाल दे; क्योंकि मैं ने नगर में उपद्रव और झगड़ा देखा है।"));
hinsuperobject.biblemap.put("b19_55_10", new hinsuperobject(19,55,10,"रात दिन वे उसकी शहरपनाह पर चढ़कर चारों ओर घूमते हैं; और उसके भीतर दुष्टता और उत्पात होता है।"));
hinsuperobject.biblemap.put("b19_55_11", new hinsuperobject(19,55,11,"उसके भीतर दुष्टता ने बसेरा डाला है; और अन्धेर, अत्याचार और छल उसके चौक से दूर नहीं होते।।"));
hinsuperobject.biblemap.put("b19_55_12", new hinsuperobject(19,55,12,"जो मेरी नामधराई करता है वह शत्रु नहीं था, नहीं तो मैं उसको सह लेता; जो मेरे विरूद्व बड़ाई मारता है वह मेरा बैरी नहीं है, नहीं तो मैं उस से छिप जाता।"));
hinsuperobject.biblemap.put("b19_55_13", new hinsuperobject(19,55,13,"परन्तु वह तो तू ही था जो मेरी बराबरी का मनुष्य मेरा परममित्रा और मेरी जान पहचान का था।"));
hinsuperobject.biblemap.put("b19_55_14", new hinsuperobject(19,55,14,"हम दोनों आपस में कैसी मीठी मीठी बातें करते थे; हम भीड़ के साथ परमेश्वर के भवन को जाते थे।"));
hinsuperobject.biblemap.put("b19_55_15", new hinsuperobject(19,55,15,"उनको मृत्यु अचानक आ दबाए; वे जीवित ही अधोलोक में उतर जाएं; क्योंकि उनके घर और मन दोनों में बुराइयां और उत्पात भरा है।।"));
hinsuperobject.biblemap.put("b19_55_16", new hinsuperobject(19,55,16,"परन्तु मैं तो परमेश्वर को पुकारूंगा; और यहोवा मुझे बचा लेगा।"));
hinsuperobject.biblemap.put("b19_55_17", new hinsuperobject(19,55,17,"सांझ को, भोर को, दोपहर को, तीनों पहर मैं दोहाई दूंगा और कराहता रहूंगा। और वह मेरा शब्द सुन लेगा।"));
hinsuperobject.biblemap.put("b19_55_18", new hinsuperobject(19,55,18,"जो लड़ाई मेरे विरूद्व मची थी उस से उस ने मुझे कुशल के साथ बचा लिया है। उन्हों ने तो बहुतों को संग लेकर मेरा साम्हना किया था।"));
hinsuperobject.biblemap.put("b19_55_19", new hinsuperobject(19,55,19,"ईश्वर जो आदि से विराजमान है यह सुनकर उनको उत्तर देगा। ये वे है जिन में कोई परिवर्तन नहीं और उन में परमेश्वर का भय है ही नहीं।।"));
hinsuperobject.biblemap.put("b19_55_20", new hinsuperobject(19,55,20,"उस ने अपने मेल रखनेवालों पर भी हाथ छोड़ा है, उस ने अपनी वाचा को तोड़ दिया है।"));
hinsuperobject.biblemap.put("b19_55_21", new hinsuperobject(19,55,21,"उसके मुंह की बातें तो मक्खन सी चिकनी थी परन्तु उसके मन में लड़ाई की बातें थीं; उसके वचन तेल से अधिक नरम तो थे परन्तु नंगी तलवारें थीं।।"));
hinsuperobject.biblemap.put("b19_55_22", new hinsuperobject(19,55,22,"अपना बोझ यहोवा पर डाल दे वह तुझे सम्भालेगा; वह धर्मी को कभी टलने न देगा।।"));
hinsuperobject.biblemap.put("b19_55_23", new hinsuperobject(19,55,23,"परन्तु हे परमेश्वर, तू उन लोगों को विनाश के गड़हे में गिरा देगा; हत्यारे और छली मनुष्य अपनी आधी आयु तक भी जीवित न रहेंगे। परन्तु मैं तुझ पर भरोसा रखे रहूंगा।।"));
hinsuperobject.biblemap.put("b19_56_1", new hinsuperobject(19,56,1,"हे परमेश्वर, मुझ पर अनुग्रह कर, क्योंकि मनुष्य मुझे निगलना चाहते हैं। वे दिन भर लड़कर मुझे सताते हैं।"));
hinsuperobject.biblemap.put("b19_56_2", new hinsuperobject(19,56,2,"मेरे द्रोही दिन भर मुझे निगलना चाहते हैं, क्योंकि जो लोग अभिमान करके मुझ से लड़ते हैं वे बहुत हैं।"));
hinsuperobject.biblemap.put("b19_56_3", new hinsuperobject(19,56,3,"जिस समय मुझे डल लगेगा, मैं तुझ पर भरोसा रखूंगा।"));
hinsuperobject.biblemap.put("b19_56_4", new hinsuperobject(19,56,4,"परमेश्वर की सहायता से मैं उसके वचन की प्रशंसा करूंगा, परमेश्वर पर मैं ने भरोसा रखा है, मैं नहीं डरूंगा। कोई प्राणी मेरा क्या कर सकता है?"));
hinsuperobject.biblemap.put("b19_56_5", new hinsuperobject(19,56,5,"वे दिन भर मेरे वचनों को, उलटा अर्थ लगा लगाकर मरोड़ते रहते हैंद्ध उनकी सारी कल्पनाएं मेरी ही बुराई करने की होती है।"));
hinsuperobject.biblemap.put("b19_56_6", new hinsuperobject(19,56,6,"वे सब मिलकर इकट्ठे होते हैं और छिपकर बैठते हैं; वे मेरे कदमों को देखते भालते हैं मानों वे मेरे प्राणों की घात में ताक लगाए बैठें हों।"));
hinsuperobject.biblemap.put("b19_56_7", new hinsuperobject(19,56,7,"क्या वे बुराई करके भी बच जाएंगे? हे परमेश्वर, अपने क्रोध से देश देश के लोगों को गिरा दे!"));
hinsuperobject.biblemap.put("b19_56_8", new hinsuperobject(19,56,8,"तू मेरे मारे मारे फिरने का हिसाब रखता है; तू मेरे आंसुओं को अपनी कुप्पी में रख ले! क्या उनकी चर्चा तेरी पुस्तक में नहीं है?"));
hinsuperobject.biblemap.put("b19_56_9", new hinsuperobject(19,56,9,"जब जिस समय मैं पुकारूंगा, उसी समय मेरे शत्रु उलटे फिरेंगे। यह मैं जानता हूं, कि परमेश्वर मेरी ओर है।"));
hinsuperobject.biblemap.put("b19_56_10", new hinsuperobject(19,56,10,"परमेश्वर की सहायता से मैं उसके वचन की प्रशंसा करूंगा, यहोवा की सहायता से मैं उसके वचन की प्रशंसा करूंगा।"));
hinsuperobject.biblemap.put("b19_56_11", new hinsuperobject(19,56,11,"मैं ने परमेश्वर पर भरोसा रखा है, मैं न डरूंगा। मनुष्य मेरा क्या कर सकता है?"));
hinsuperobject.biblemap.put("b19_56_12", new hinsuperobject(19,56,12,"हे परमेश्वर, तेरी मन्नतों का भरा मुझ पर बना है; मैं तुझ को धन्यवाद बलि चढ़ाऊंगा।"));
hinsuperobject.biblemap.put("b19_56_13", new hinsuperobject(19,56,13,"क्योंकि तू ने मुझ को मृत्यु से बचाया है; तू ने मेरे पैरों को भी फिसलने से न बचाया, ताकि मैं ईश्वर के साम्हने जीवतों के उजियाले में चलूं फिरूं?"));
hinsuperobject.biblemap.put("b19_57_1", new hinsuperobject(19,57,1,"हे परमेश्वर, मुझ पर अनुग्रह कर, मुझ पर अनुग्रह कर, क्योंकि मैं तेरा शरणागत हूं; और जब तक ये आपत्तियां निकल न जाएं, तब तक मैं तेरे पंखों के तले शरण लिए रहूंगा।"));
hinsuperobject.biblemap.put("b19_57_2", new hinsuperobject(19,57,2,"मैं परम प्रधान परमेश्वर को पुकारूंगा, ईश्वर को जो मेरे लिये सब कुछ सिद्ध करता है।"));
hinsuperobject.biblemap.put("b19_57_3", new hinsuperobject(19,57,3,"ईश्वर स्वर्ग से भेजकर मुझे बचा लेगा, जब मेरा निगलनेवाला निन्दा कर रहा हो। परमेश्वर अपनी करूणा और सच्चाई प्रगट करेगा।।"));
hinsuperobject.biblemap.put("b19_57_4", new hinsuperobject(19,57,4,"मेरा प्राण सिंहों के बीच में है, मुझे जलते हुओं के बीच में लेटना पड़ता है, अर्थात् ऐसे मनुष्यों के बीच में जिन के दांत बर्छी और तीर हैं, और जिनकी जीभ तेज तलवार है।।"));
hinsuperobject.biblemap.put("b19_57_5", new hinsuperobject(19,57,5,"हे परमेश्वर तू स्वर्ग के ऊपर अति महान और तेजोमय है, तेरी महिमा सारी पृथ्वी के ऊपर फैल जाए!"));
hinsuperobject.biblemap.put("b19_57_6", new hinsuperobject(19,57,6,"उन्हों ने मेरे पैरों के लिये जाल लगाया है; मेरा प्राण ढला जाता है। उन्हों ने मेरे आगे गड़हा खोदा, परन्तु आप ही उस में गिर पड़े।।"));
hinsuperobject.biblemap.put("b19_57_7", new hinsuperobject(19,57,7,"हे परमेश्वर, मेरा मन स्थिर है, मेरा मन स्थिर है; मैं गाऊंगा वरन भजन कीर्तन करूंगा।"));
hinsuperobject.biblemap.put("b19_57_8", new hinsuperobject(19,57,8,"हे मेरी आत्मा जाग जा! हे सारंगी और वीणा जाग जाओ। मैं भी पौ फटते ही जाग उठूंगा।"));
hinsuperobject.biblemap.put("b19_57_9", new hinsuperobject(19,57,9,"हे प्रभु, मैं देश के लोगों के बीच तेरा धन्यवाद करूंगा; मैं राज्य राज्य के लोगों के बीच में तेरा भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_57_10", new hinsuperobject(19,57,10,"क्योंकि तेरी करूणा स्वर्ग तक बड़ी है, और तेरी सच्चाई आकाशमण्डल तक पहुंचती है।।"));
hinsuperobject.biblemap.put("b19_57_11", new hinsuperobject(19,57,11,"हे परमेश्वर, तू स्वर्ग के ऊपर अति महान है! तेरी महिमा सारी पृथ्वी के ऊपर फैल जाए!"));
hinsuperobject.biblemap.put("b19_58_1", new hinsuperobject(19,58,1,"हे मनुष्यों, क्या तुम सचमुच धर्म की बात बोलते हो? और हे मनुष्यवंशियों क्या तुम सीधाई से न्याय करते हो?"));
hinsuperobject.biblemap.put("b19_58_2", new hinsuperobject(19,58,2,"नहीं, तुम मन ही मन में कुटिल काम करते हो; तुम देश भर में उपद्रव करते जाते हो।।"));
hinsuperobject.biblemap.put("b19_58_3", new hinsuperobject(19,58,3,"दुष्ट लोग जन्मते ही पराए हो जाते हैं, वे पेट से निकलते ही झूठ बालते हुए भटक जाते हैं।"));
hinsuperobject.biblemap.put("b19_58_4", new hinsuperobject(19,58,4,"उन में सर्प का सा विष है; वे उस नाम के समान हें, जो सुनना नहीं चाहता;"));
hinsuperobject.biblemap.put("b19_58_5", new hinsuperobject(19,58,5,"और सपेरा कैसी ही निपुणता से क्यों न मंत्रा पढ़े, तौभी उसकी नहीं सुनता।।"));
hinsuperobject.biblemap.put("b19_58_6", new hinsuperobject(19,58,6,"हे परमेश्वर, उनके मुंह में से दांतों को तोड़ दे; हे यहोवा उन जवान सिंहों की दाढ़ों को उखाड़ डाल!"));
hinsuperobject.biblemap.put("b19_58_7", new hinsuperobject(19,58,7,"वे घुलकर बहते हुए पानी के समान हो जाएं; जब वे अपने तीर चढ़ाएं, तब तीर मानों दो टुकड़े हो जाएं।"));
hinsuperobject.biblemap.put("b19_58_8", new hinsuperobject(19,58,8,"वे घोंघे के समान हो जाएं जो घुलकर नाश हो जाता है, और स्त्री के गिरे हुए गर्भ के समान हो जिस ने सूरज को देखा ही नहीं।"));
hinsuperobject.biblemap.put("b19_58_9", new hinsuperobject(19,58,9,"उस से पहिले कि तुम्हारी हांबियों में कांटों की आंच लगे, हरे व जले, दोनों को वह बवंडर से उड़ा ले जाएगा।।"));
hinsuperobject.biblemap.put("b19_58_10", new hinsuperobject(19,58,10,"धर्मी ऐसा पलटा देखकर आनन्दित होगा; वह अपने पांव दुष्ट के लोहू में धोएगा।।"));
hinsuperobject.biblemap.put("b19_58_11", new hinsuperobject(19,58,11,"तब मनुष्य कहने लगेंगे, निश्चय धर्मी के लिये फल है; निश्चय परमेश्वर है, जो पृथ्वी पर न्याय करता है।।"));
hinsuperobject.biblemap.put("b19_59_1", new hinsuperobject(19,59,1,"हे मेरे परमेश्वर, मुझ को शत्रुओं से बचा, मुझे ऊंचे स्थान पर रखकर मेरे विरोधियों से बचा,"));
hinsuperobject.biblemap.put("b19_59_2", new hinsuperobject(19,59,2,"मुझ को बुराई करनेवालों के हाथ से बचा, और हत्यारों से मेरा उद्वार कर।।"));
hinsuperobject.biblemap.put("b19_59_3", new hinsuperobject(19,59,3,"क्योंकि देख, वे मेरी घात में लगे हैं; हे यहोवा, मेरा कोई दोष वा पाप नहीं है, तौभी बलवन्त लोग मेरे विरूद्ध इकट्ठे होते हैं।"));
hinsuperobject.biblemap.put("b19_59_4", new hinsuperobject(19,59,4,"वह मुझ निर्दोष पर दौड़े दौड़कर लड़ने को तैयार हो जाते हैं।। मुझ से मिलने के लिये जाग उठ, और यह देख!"));
hinsuperobject.biblemap.put("b19_59_5", new hinsuperobject(19,59,5,"हे सेनाओं के परमेश्वर यहोवा, हे इस्राएल के परमेश्वर सब अन्यजातिवालों को दण्ड देने के लिये जाग; किसी विश्वासघाती अत्याचारी पर अनुग्रह न कर।।"));
hinsuperobject.biblemap.put("b19_59_6", new hinsuperobject(19,59,6,"वे लागे सांझ को लौटकर कुत्ते की नाईं गुर्राते हैं, और नगर के चारों ओर घूमते हैं। देख वे डकारते हैं,"));
hinsuperobject.biblemap.put("b19_59_7", new hinsuperobject(19,59,7,"उनके मुंह के भीतर तलवारें हैं, क्योंकि वे कहते हैं, कौन सुनता है?"));
hinsuperobject.biblemap.put("b19_59_8", new hinsuperobject(19,59,8,"परन्तु हे यहोवा, तू उन पर हंसेगा; तू सब अन्य जातियों को ठट्ठां में उड़ाएगा।"));
hinsuperobject.biblemap.put("b19_59_9", new hinsuperobject(19,59,9,"हे मेरे बल, मुझे तेरी ही आस होगी; क्योंकि परमेश्वर मेरा ऊंचा गढ़ है।।"));
hinsuperobject.biblemap.put("b19_59_10", new hinsuperobject(19,59,10,"परमेश्वर करूणा करता हुआ मुझ से मिलेगा; परमेश्वर मेरे द्रोहियों के विषय मेरी इच्छा पूरी कर देगा।।"));
hinsuperobject.biblemap.put("b19_59_11", new hinsuperobject(19,59,11,"उन्हें घात न कर, न हो कि मेरी प्रजा भूल जाए; हे प्रभु, हे हमारी ढाल! अपनी शक्ति के उन्हें तितर बितर कर, उन्हें दबा दे।"));
hinsuperobject.biblemap.put("b19_59_12", new hinsuperobject(19,59,12,"वह अपने मुंह के पाप, और ओठों के वचन, और शाप देने, और झूठ बोलने के कारण, अभिमान में फंसे हुए पकड़े जाएं।"));
hinsuperobject.biblemap.put("b19_59_13", new hinsuperobject(19,59,13,"जलजलाहट में आकर उनका अन्त कर, उनका अन्त कर दे ताकि वे नष्ट हो जाएं तब लोग जानेंगे कि परमेश्वर याकूब पर, वरन पृथ्वी की छोर तक प्रभुता करता है।।"));
hinsuperobject.biblemap.put("b19_59_14", new hinsuperobject(19,59,14,"वे सांझ को लौटकर कुत्ते की नाईं गुर्राएं, और नगर के चारों ओर घूमें।"));
hinsuperobject.biblemap.put("b19_59_15", new hinsuperobject(19,59,15,"वे टुकड़े के लिये मारे मारे फिरें, और तृप्त न होन पर रात भर वहीं ठहरे रहें।।"));
hinsuperobject.biblemap.put("b19_59_16", new hinsuperobject(19,59,16,"परन्तु मैं तेरी सामर्थ्य का यश गाऊंगा, और भोर को तेरी करूणा का जय जयकार करूंगा। क्योंकि तू मेरा ऊंचा गढ़ है, और संकट के समय मेरा शरणस्थान ठहरा है।"));
hinsuperobject.biblemap.put("b19_59_17", new hinsuperobject(19,59,17,"हे मेरे बल, मैं तेरा भजन गाऊंगा, क्योंकि हे परमेश्वर, तू मेरा ऊंचा गढ़ और मेरा करूणामय परमेश्वर है।।"));
hinsuperobject.biblemap.put("b19_60_1", new hinsuperobject(19,60,1,"हे परमेश्वर तू ने हम को त्याग दिया, और हम को तोड़ डाला है; तू क्रोधित हुआ; फिर हम को ज्यों का त्यों कर दे।"));
hinsuperobject.biblemap.put("b19_60_2", new hinsuperobject(19,60,2,"तू ने भूमि को कंपाया और फाड़ डाला है; उसके दरारों को भर दे, क्योंकि वह डगमगा रही है।"));
hinsuperobject.biblemap.put("b19_60_3", new hinsuperobject(19,60,3,"तू ने अपनी प्रजा को कठिन दु:ख भुगताया; तू ने हमें लड़खड़ा देनेवाला दाखमधु पिलाया है।।"));
hinsuperobject.biblemap.put("b19_60_4", new hinsuperobject(19,60,4,"तू ने अपने डरवैयों को झण्डा दिया है, कि वह सच्चाई के कारण फहराया जाए।"));
hinsuperobject.biblemap.put("b19_60_5", new hinsuperobject(19,60,5,"तू अपने दहिने हाथ से बचा, और हमारी सुन ले कि तेरे प्रिय छुड़ाए जाएं।।"));
hinsuperobject.biblemap.put("b19_60_6", new hinsuperobject(19,60,6,"परमेश्वर पवित्राता के साथ बोला है मैं प्रफुल्लित हूंगा; मैं शकेम को बांट लूंगा, और सुक्कोत की तराई को नपवाऊंगा।"));
hinsuperobject.biblemap.put("b19_60_7", new hinsuperobject(19,60,7,"गिलाद मेरा है; मनश्शे भी मेरा है; और एप्रैम मेरे सिर का टोप, यहूदा मेरा राजदण्ड है।"));
hinsuperobject.biblemap.put("b19_60_8", new hinsuperobject(19,60,8,"मोआब मेरे धोने का पात्रा है; मैं एदोम पर अपना जूता फेंकूंबा; हे पलिश्तीन मेरे ही कारण जयजयकार कर।।"));
hinsuperobject.biblemap.put("b19_60_9", new hinsuperobject(19,60,9,"मुझे गढ़वाले नगर में कौन पहुंचाएगा? एदोम तक मेरी अगुवाई किस ने की है?"));
hinsuperobject.biblemap.put("b19_60_10", new hinsuperobject(19,60,10,"हे परमेश्वर, क्या तू ने हम को त्याग नही दिया? हे परमेश्वर, तू हमारी सेना के साथ नहीं जाता।"));
hinsuperobject.biblemap.put("b19_60_11", new hinsuperobject(19,60,11,"द्रोही के विरूद्ध हमारी सहायता कर, क्योंकि मनुष्य का किया हुआ छुटकारा व्यर्थ होता है।"));
hinsuperobject.biblemap.put("b19_60_12", new hinsuperobject(19,60,12,"परमेश्वर की सहायता से हम वीरता दिखाएंगे, क्योंकि हमारे द्रोहियों को वही रौंदेगा।।"));
hinsuperobject.biblemap.put("b19_61_1", new hinsuperobject(19,61,1,"हे परमेश्वर, मेरा चिल्लाना सुन, मेरी प्रार्थना की ओर घ्यान दे।"));
hinsuperobject.biblemap.put("b19_61_2", new hinsuperobject(19,61,2,"मूर्छा खाते समय मैं पृथ्वी की छोर से भी तुझे पुकारूंगा, जो चट्टान मेरे लिये ऊंची है, उस पर मुझ को ले चला;"));
hinsuperobject.biblemap.put("b19_61_3", new hinsuperobject(19,61,3,"क्योंकि तू मेरा शरणस्थान है, और शत्रु से बचने के लिये ऊंचा गढ़ है।।"));
hinsuperobject.biblemap.put("b19_61_4", new hinsuperobject(19,61,4,"मै तेरे तम्बू में युगानुयुग बना रहूंगा।"));
hinsuperobject.biblemap.put("b19_61_5", new hinsuperobject(19,61,5,"क्योंकि हे परमेश्वर, तू ने मेरी मन्नतें सुनीं, जो तेरे नाम के डरवैये हैं, उनका सा भाग तू ने मुझे दिया है।।"));
hinsuperobject.biblemap.put("b19_61_6", new hinsuperobject(19,61,6,"तू राजा की आयु को बहुत बढ़ाएगा; उसके वर्ष पीढ़ी पीढ़ी के बराबर होंगे।"));
hinsuperobject.biblemap.put("b19_61_7", new hinsuperobject(19,61,7,"वह परमेश्वर के सम्मुख सदा बना रहेगा; तू अपनी करूणा और सच्चाई को उसकी रक्षा के लिये ठहरा रख।"));
hinsuperobject.biblemap.put("b19_61_8", new hinsuperobject(19,61,8,"और मैं सर्वदा तेरे नाम का भजन गा गाकर अपनी मन्नतें हर दिन पूरी किया करूंगा।।"));
hinsuperobject.biblemap.put("b19_62_1", new hinsuperobject(19,62,1,"सचमुच मैं चुपचाप होकर परमेरश्वर की ओर मन लगाए हूं; मेरा उद्धार उसी से होता है।"));
hinsuperobject.biblemap.put("b19_62_2", new hinsuperobject(19,62,2,"सचमुच वही, मेरी चट्टान और मेरा उद्धार है, वह मेरा गढ़ है; मैं बहुत न डिगूंगा।।"));
hinsuperobject.biblemap.put("b19_62_3", new hinsuperobject(19,62,3,"तुम कब तक एक पुरूष पर धावा करते रहोगे, कि सब मिलकर उसका घात करो? वह तो झुकी हुई भीत वा गिरते हुए बाड़े के समान है।"));
hinsuperobject.biblemap.put("b19_62_4", new hinsuperobject(19,62,4,"सचमुच वे उसको, उसके ऊंचे पद से गिराने की सम्मति करते हैं; वे झूठ से प्रसन्न रहते हैं। मुंह से तो वे आशीर्वाद देते पर मन में कोसते हैं।।"));
hinsuperobject.biblemap.put("b19_62_5", new hinsuperobject(19,62,5,"हे मेरे मन, परमेश्वर के साम्हने चुपचाप रह, क्योंकि मेरी आशा उसी से है।"));
hinsuperobject.biblemap.put("b19_62_6", new hinsuperobject(19,62,6,"सचमुच वही मेरी चट्टान, और मेरा उद्धार है, वह मेरा गढ़ है; इसलिये मैं न डिगूंगा।"));
hinsuperobject.biblemap.put("b19_62_7", new hinsuperobject(19,62,7,"मेरा उद्धार और मेरी महिमा का आधार परमेश्वर है; मेरी दृढ़ चट्टान, और मेरा शरणस्थान परमेश्वर है।"));
hinsuperobject.biblemap.put("b19_62_8", new hinsuperobject(19,62,8,"हे लोगो, हर समय उस पर भरोसा रखो; उस से अपने अपने मन की बातें खोलकर कहो; परमेश्वर हमारा शरणस्थान है।"));
hinsuperobject.biblemap.put("b19_62_9", new hinsuperobject(19,62,9,"सचमुच नीच लोग तो अस्थाई, और बड़े लोग मिथ्या ही हैं; तौल में वे हलके निकलते हैं; वे सब के सब सांस से भी हलके हैं।"));
hinsuperobject.biblemap.put("b19_62_10", new hinsuperobject(19,62,10,"अन्धेर करने पर भरोसा मत रखो, और लूट पाट करने पर मत फूलो; चाहे धन सम्पति बढ़े, तौभी उस पर मन न लगाना।।"));
hinsuperobject.biblemap.put("b19_62_11", new hinsuperobject(19,62,11,"परमेश्वर ने एक बार कहा है; और दो बार मैं ने यह सुना है: कि सामर्थ्य परमेश्वर का है।"));
hinsuperobject.biblemap.put("b19_62_12", new hinsuperobject(19,62,12,"और हे प्रभु, करूणा भी तेरी है। क्योंकि तू एक एक जन को उसके काम के अनुसार फल देता है।।"));
hinsuperobject.biblemap.put("b19_63_1", new hinsuperobject(19,63,1,"हे परमेश्वर, तू मेरा ईश्वर है, मैं तुझे यत्न से ढूंढूंगा; सूखी और निर्जल ऊसर भूमि पर, मेरा मन तेरा प्यासा है, मेरा शरीर तेरा अति अभिलाषी है।"));
hinsuperobject.biblemap.put("b19_63_2", new hinsuperobject(19,63,2,"इस प्रकार से मैं ने पवित्रास्थान में तुझ पर दृष्टि की, कि तेरी सामर्थ्य और महिमा को देखूं।"));
hinsuperobject.biblemap.put("b19_63_3", new hinsuperobject(19,63,3,"क्योंकि तेरी करूणा जीवन से भी उत्तम है मैं तेरी प्रशंसा करूंगा।"));
hinsuperobject.biblemap.put("b19_63_4", new hinsuperobject(19,63,4,"इसी प्रकार मैं जीवन भर तुझे धन्य कहता रहूंगा; और तेरा नाम लेकर अपने हाथ उठाऊंगा।।"));
hinsuperobject.biblemap.put("b19_63_5", new hinsuperobject(19,63,5,"मेरा जीव मानो चर्बी और चिकने भोजन से तृप्त होगा, और मैं जयजयकार करके तेरी स्तुति करूंगा।"));
hinsuperobject.biblemap.put("b19_63_6", new hinsuperobject(19,63,6,"जब मैं बिछौने पर पड़ा तेरा स्मरण करूंगा, तब रात के एक एक पहर में तुझ पर ध्यान करूंगा;"));
hinsuperobject.biblemap.put("b19_63_7", new hinsuperobject(19,63,7,"क्योंकि तू मेरा सहायक बना है, इसलिये मैं तेरे पंखों की छाया में जयजयकार करूंगा।"));
hinsuperobject.biblemap.put("b19_63_8", new hinsuperobject(19,63,8,"मेरा मन तेरे पीछे पीछे लगा चलता है; और मुझे तो तू अपने दहिने हाथ से थाम रखता है।।"));
hinsuperobject.biblemap.put("b19_63_9", new hinsuperobject(19,63,9,"परन्तु जो मेरे प्राण के खोजी हैं, वे पृथ्वी के नीचे स्थानों में जा पडेंगे;"));
hinsuperobject.biblemap.put("b19_63_10", new hinsuperobject(19,63,10,"वे तलवार से मारे जाएंगे, और गीदड़ों का आहार हो जाएंगे।"));
hinsuperobject.biblemap.put("b19_63_11", new hinsuperobject(19,63,11,"परन्तु राजा परमेश्वर के कारण आनन्दित होगा; जो कोई ईश्वर की शपथ खाए, वह बड़ाई करने पाएगा; परन्तु झूठ बोलनेवालों का मुंह बन्द किया जाएगा।।"));
hinsuperobject.biblemap.put("b19_64_1", new hinsuperobject(19,64,1,"हे परमेश्वर, जब मैं तेरी दोहाई दूं, तब मेरी सुन; शत्रु के उपजाए हुए भय के समय मेरे प्राण की रक्षा कर।"));
hinsuperobject.biblemap.put("b19_64_2", new hinsuperobject(19,64,2,"कुकर्मियों की गोष्ठी से, और अनर्थकारियों के हुल्लड़ से मेरी आड़ हो।"));
hinsuperobject.biblemap.put("b19_64_3", new hinsuperobject(19,64,3,"उन्हों ने अपनी जीभ को तलवार की नाईं तेज किया है, और अपने कड़वे बचनों के तीरों को चढ़ाया है;"));
hinsuperobject.biblemap.put("b19_64_4", new hinsuperobject(19,64,4,"ताकि छिपकर खरे मनुष्य को मारें; वे निडर होकर उसको अचानक मारते भी हैं।"));
hinsuperobject.biblemap.put("b19_64_5", new hinsuperobject(19,64,5,"वे बुरे काम करने को हियाव बान्धते हैं; वे फन्दे लगने के विषय बातचीत करते हैं; और कहते हैं, कि हम को कौन देखेगा?"));
hinsuperobject.biblemap.put("b19_64_6", new hinsuperobject(19,64,6,"वे कुटिलता की युक्ति निकालते हैं; और कहते हैं, कि हम ने पक्की युक्ति खोजकर निकाली है। क्योंकि मनुष्य का मन और हृदय अथाह हैं!"));
hinsuperobject.biblemap.put("b19_64_7", new hinsuperobject(19,64,7,"परन्तु परमेश्वर उन पर तीर चलाएगा; वे अचानक घायल हो जाएंगे।"));
hinsuperobject.biblemap.put("b19_64_8", new hinsuperobject(19,64,8,"वे अपने ही वचनों के कारण ठोकर खाकर गिर पड़ेंगे; जितने उन पर दृष्टि करेंगे वे सब अपने अपने सिर हिलाएंगे"));
hinsuperobject.biblemap.put("b19_64_9", new hinsuperobject(19,64,9,"तब सारे लोग डर जाएंगे; और परमेश्वर के कामों का बखान करंगे, और उसके कार्यक्रम को भली भांति समझेंगे।।"));
hinsuperobject.biblemap.put("b19_64_10", new hinsuperobject(19,64,10,"धर्मी तो यहोवा के कारण आनन्दित होकर उसका शरणागत होगा, और सब सीधे मनवाले बड़ाई करेंगे।।"));
hinsuperobject.biblemap.put("b19_65_1", new hinsuperobject(19,65,1,"हे परमेश्वर, सिरयोन में स्तुति तेरी बाट जोहती है; और तेरे लिये मन्नतें पूरी की जाएंगी।"));
hinsuperobject.biblemap.put("b19_65_2", new hinsuperobject(19,65,2,"हे प्रार्थना के सुननेवाले! सब प्राणी तेरे ही पास आएंगे।"));
hinsuperobject.biblemap.put("b19_65_3", new hinsuperobject(19,65,3,"अर्धम के काम मुझ पर प्रबल हुए हैं; हमारे अपराधों को तू ढांप देगा।"));
hinsuperobject.biblemap.put("b19_65_4", new hinsuperobject(19,65,4,"क्या ही धन्य है वह; जिसको तू चुनकर अपने समीप आने देता है, कि वह तेरे आंगनों में बास करे! हम तेरे भवन के, अर्थात् तेरे पवित्रा मन्दिर के उत्तम उत्तम पदार्थों से तृप्त होंगे।।"));
hinsuperobject.biblemap.put("b19_65_5", new hinsuperobject(19,65,5,"हे हमारे उद्धारकर्ता परमेश्वर, हे पृथ्वी के सब दूर दूर देशों के और दूर के समुद्र पर के रहनेवालों के आधार, तू धर्म से किए हुए भयानक कामों के द्वारा हमारा मुंह मांगा वर देगा;"));
hinsuperobject.biblemap.put("b19_65_6", new hinsuperobject(19,65,6,"तू जो पराक्रम का फेंटा कसे हुए, अपनी सामर्थ्य के पर्वतों को स्थिर करता है;"));
hinsuperobject.biblemap.put("b19_65_7", new hinsuperobject(19,65,7,"तू जो समुद्र का महाशब्द, उसकी तरंगो का महाशब्द, और देश देश के लोगों का कोलाहल शन्त करता है;"));
hinsuperobject.biblemap.put("b19_65_8", new hinsuperobject(19,65,8,"इसलिये दूर दूर देशों के रहनेवाले तेरे चिन्ह देखकर डर गए हैं; तू उदयाचल और अस्ताचल दोनों से जयजयकार कराता है।।"));
hinsuperobject.biblemap.put("b19_65_9", new hinsuperobject(19,65,9,"तू भूमि की सुधि लेकर उसको सींचता हैं, तू उसको बहुत फलदायक करता है; परमेश्वर की नहर जल से भरी रहती है; तू पृथ्वी को तैयार करके मनुष्यों के लिये अन्न को तैयार करता है।"));
hinsuperobject.biblemap.put("b19_65_10", new hinsuperobject(19,65,10,"तू रेघारियों को भली भांति सींचता है, और उनके बीच की मिट्टी को बैठाता है, तू भूमि को मेंह से नरम करता है, और उसकी उपज पर आशीष देता है।"));
hinsuperobject.biblemap.put("b19_65_11", new hinsuperobject(19,65,11,"अपनी भलाई से भरे हुए वर्ष पर तू ने मानो मुकुट धर दिया है; तेरे मार्गों में उत्तम उत्तम पदार्थ पाए जाते हैं।"));
hinsuperobject.biblemap.put("b19_65_12", new hinsuperobject(19,65,12,"वे जंगल की चराइयों में पाए जाते हैं; और पहाड़ियां हर्ष का फेंटा बान्धे हुए है।।"));
hinsuperobject.biblemap.put("b19_65_13", new hinsuperobject(19,65,13,"चराइयां भेड़- बकरियों से भरी हुई हैं; और तराइयां अन्न से ढंपी हुई हैं, वे जयजयकार करतीं और गाती भी हैं।।"));
hinsuperobject.biblemap.put("b19_66_1", new hinsuperobject(19,66,1,"हे सारी पृथ्वी के लोगों, परमेश्वर के लिये जयजयकार करो;"));
hinsuperobject.biblemap.put("b19_66_2", new hinsuperobject(19,66,2,"उसके नाम की महिमा का भजन गाओ; उसकी स्तुति करते हुए, उसकी महिमा करो।"));
hinsuperobject.biblemap.put("b19_66_3", new hinsuperobject(19,66,3,"परमेश्वर से कहो, कि तेरे काम क्या ही भयानक हैं! तेरी महासामर्थ्य के कारण तेरे शत्रु तेरी चापलूसी करेंगे।"));
hinsuperobject.biblemap.put("b19_66_4", new hinsuperobject(19,66,4,"सारी पृथ्वी के लोग तुझे दण्डवत् करेंगे, और तेरा भजन गाएंगे; वे तेरे नाम का भजन गाएंगे।।"));
hinsuperobject.biblemap.put("b19_66_5", new hinsuperobject(19,66,5,"आओ परमेश्वर के कामों को दखो; वह अपने कार्यों के कारण मनुष्यों को भययोग्य देख पड़ता है।"));
hinsuperobject.biblemap.put("b19_66_6", new hinsuperobject(19,66,6,"उस ने समुद्र को सूखी भूमि कर डाला; वे महानद में से पांव पावं पार उतरे। वहां हम उसके कारण आनन्दित हुए,"));
hinsuperobject.biblemap.put("b19_66_7", new hinsuperobject(19,66,7,"जो पराक्रम से सर्वदा प्रभुता करता है, और अपनी आंखों से जाति जाति को ताकता है। हठीले अपने सिर न उठाएं।।"));
hinsuperobject.biblemap.put("b19_66_8", new hinsuperobject(19,66,8,"हे देश देश के लोगो, हमारे परमेश्वर को धन्य कहो, और उसकी स्तुति में राग उठाओ,"));
hinsuperobject.biblemap.put("b19_66_9", new hinsuperobject(19,66,9,"जो हम को जीवित रखता है; और हमारे पांव को टलने नहीं देता।"));
hinsuperobject.biblemap.put("b19_66_10", new hinsuperobject(19,66,10,"क्योंकि हे परमेश्वर तू ने हम को जांचा; तू ने हमें चान्दी की नाईं ताया था।"));
hinsuperobject.biblemap.put("b19_66_11", new hinsuperobject(19,66,11,"तू ने हम को जाल में फंसाया; और हमारी कटि पर भारी बोझ बान्धा था;"));
hinsuperobject.biblemap.put("b19_66_12", new hinsuperobject(19,66,12,"तू ने घुड़चढ़ों को हमारे सिरों के ऊपर से चलाया, हम आग और जल से होकर गए; परन्तु तू ने हम को उबार के सुख से भर दिया है।।"));
hinsuperobject.biblemap.put("b19_66_13", new hinsuperobject(19,66,13,"मैं होमबलि लेकर तेरे भवन में आंऊंगा मैं उन मन्नतों को तेरे लिये पूरी करूंगा,"));
hinsuperobject.biblemap.put("b19_66_14", new hinsuperobject(19,66,14,"जो मैं ने मुंह खोलकर मानीं, और संकट के समय कही थीं।"));
hinsuperobject.biblemap.put("b19_66_15", new hinsuperobject(19,66,15,"मैं तुझे मोटे पशुओं के होमबलि, मेंढ़ों की चर्बी के धूप समेत चढ़ऊंगा; मैं बकरों समेत बैल चढ़ाऊंगा।।"));
hinsuperobject.biblemap.put("b19_66_16", new hinsuperobject(19,66,16,"हे परमेश्वर के सब डरवैयों आकर सुनो, मैं बताऊंगा कि उस ने मेरे लिये क्या क्या किया है।"));
hinsuperobject.biblemap.put("b19_66_17", new hinsuperobject(19,66,17,"मैं ने उसको पुकारा, और उसी का गुणानुवाद मुझ से हुआ।"));
hinsuperobject.biblemap.put("b19_66_18", new hinsuperobject(19,66,18,"यदि मैं मन में अनर्थ बात सोचता तो प्रभु मेरी न सुनता।"));
hinsuperobject.biblemap.put("b19_66_19", new hinsuperobject(19,66,19,"परन्तु परमेश्वर ने तो सुना है; उस ने मेरी प्रार्थना की ओर ध्यान दिया है।।"));
hinsuperobject.biblemap.put("b19_66_20", new hinsuperobject(19,66,20,"धन्य है परमेश्वर, जिस ने न तो मेरी प्रार्थना अनसुनी की, और न मुझ से अपनी करूणा दूर कर दी है!"));
hinsuperobject.biblemap.put("b19_67_1", new hinsuperobject(19,67,1,"परमेश्वर हम पर अनुग्रह करे और हम को आशीष दे; वह हम पर अपने मुख को प्रकाश चमकाए"));
hinsuperobject.biblemap.put("b19_67_2", new hinsuperobject(19,67,2,"जिस से तेरी गति प्थ्वी पर, और तेरा किया हुआ उद्वार सारी जातियों में जाना जाए।"));
hinsuperobject.biblemap.put("b19_67_3", new hinsuperobject(19,67,3,"हे परमेश्वर, देश देश के लोग तेरा धन्यवाद करें; देश देश के सब लोग तेरा धन्यवाद करें।।"));
hinsuperobject.biblemap.put("b19_67_4", new hinsuperobject(19,67,4,"राज्य राज्य के लोग आनन्द करें, और जयजयकार करें, क्योंकि तू देश देश के लोंगों का न्याय धर्म से करेगा, और पृथ्वी के राज्य राज्य के लोगों की अगुवाई करेगा।।"));
hinsuperobject.biblemap.put("b19_67_5", new hinsuperobject(19,67,5,"हे परमेश्वर, देश देश के लोग तेरा धन्यवाद करें; देश देश के सब लोग तेरा धन्यवाद करें।।"));
hinsuperobject.biblemap.put("b19_67_6", new hinsuperobject(19,67,6,"भूमि ने अपनी उपज दी है, परमेश्वर जो हमारा परमेश्वर है, उस ने हमें आशीष दी है।"));
hinsuperobject.biblemap.put("b19_67_7", new hinsuperobject(19,67,7,"परमेश्वर हम को आशीष देगा; और पृथ्वी के दूर दूर देशों के सब लोग उसका भय मानेंगे।।"));
hinsuperobject.biblemap.put("b19_68_1", new hinsuperobject(19,68,1,"परमेश्वर उठे, उसके शत्रु तित्तर बितर हों; और उसके बैरी उसके साम्हने से भाग जाएं।"));
hinsuperobject.biblemap.put("b19_68_2", new hinsuperobject(19,68,2,"जैसे धुआं उड़ जाता है, वैसे ही तू उनको उड़ा दे; जैसे मोम आग की आंच से पिघल जाता है, वैसे ही दुष्ट लोग परमेश्वर की उपस्थिति से नाश हों।"));
hinsuperobject.biblemap.put("b19_68_3", new hinsuperobject(19,68,3,"परन्तु धर्मी आनन्दित हों; वे परमेश्वर के साम्हने प्रफुल्लित हों; वे आनन्द से मगन हों!"));
hinsuperobject.biblemap.put("b19_68_4", new hinsuperobject(19,68,4,"परमेश्वर का गीत गाओ, उसके नाम का भजन गाओ; जो निर्जल देशों में सवार होकर चलता है, उसके लिये सड़क बनाओ; उसका नाम याह है, इसलिये तुम उसके साम्हने प्रफुल्लित हो!"));
hinsuperobject.biblemap.put("b19_68_5", new hinsuperobject(19,68,5,"परमेश्वर अपने पवित्रा धाम में, अनाथों का पिता और विधवाओं का न्यायी है।"));
hinsuperobject.biblemap.put("b19_68_6", new hinsuperobject(19,68,6,"परमेश्वर अनाथों का घर बसाता है; और बन्धुओं को छुड़ाकर भाग्यवान करता है; परन्तु हठीलों को सूखी भूमि पर रहना पड़ता है।।"));
hinsuperobject.biblemap.put("b19_68_7", new hinsuperobject(19,68,7,"हे परमेश्वर, जब तू अपनी प्रजा के आगे आगे चलता था, जब तू निर्जल भूमि में सेना समेत चला,"));
hinsuperobject.biblemap.put("b19_68_8", new hinsuperobject(19,68,8,"तब पृथ्वी कांप उठी, और आकाश भी परमेश्वर के साम्हने टपकने लगा, उधर सीनै पर्वत परमेश्वर, हां इस्राएल के परमेश्वर के साम्हने कांप उठा।"));
hinsuperobject.biblemap.put("b19_68_9", new hinsuperobject(19,68,9,"हे परमेश्वर, तू ने बहुत से वरदान बरसाए; तेरा निज भाग तो बहुत सूखा था, परन्तू तू ने उसको हरा भरा किया है;"));
hinsuperobject.biblemap.put("b19_68_10", new hinsuperobject(19,68,10,"तेरा झुण्ड उस में बसने लगा; हे परमेश्वर तू ने अपनी भलाई से दीन जन के लिये तैयारी की है।"));
hinsuperobject.biblemap.put("b19_68_11", new hinsuperobject(19,68,11,"प्रभु आज्ञा देता है, तब शुभ समाचार सुनानेवालियों की बड़ी सेना हो जाती है।"));
hinsuperobject.biblemap.put("b19_68_12", new hinsuperobject(19,68,12,"अपनी अपनी सेना समेत राजा भागे चले जाते हैं, और गृहस्थिन लूट को बांट लेती है।"));
hinsuperobject.biblemap.put("b19_68_13", new hinsuperobject(19,68,13,"क्या तुम भेड़शालों के बीच लेट जाओगे? और ऐसी कबूतरी के समान होगे जिसके पंख चान्दी से और जिसके पर पीले सोने से मढ़े हुए हों?"));
hinsuperobject.biblemap.put("b19_68_14", new hinsuperobject(19,68,14,"जब सर्वशक्तिमान ने उस में राजाओं को तित्तर बितर किया, तब मानो सल्मोन पर्वत पर हिम पड़ा।।"));
hinsuperobject.biblemap.put("b19_68_15", new hinsuperobject(19,68,15,"बाशान का पहाड़ परमेश्वर का पहाड़ है; बाशान का पहाड़ बहुत शिखरवाला पहाड़ है।"));
hinsuperobject.biblemap.put("b19_68_16", new hinsuperobject(19,68,16,"परन्तु हे शिखरवाले पहाड़ों, तुम क्यों उस पर्वत को घूरते हो, जिसे परमेश्वर ने अपने वास के लिये चाहा है, और जहां यहोवा सदा वास किए रहेगा?"));
hinsuperobject.biblemap.put("b19_68_17", new hinsuperobject(19,68,17,"परमेश्वर के रथ बीस हजार, वरन हजारों हजार हैं; प्रभु उनके बीच में है, जैसे वह सीनै पवित्रास्थान में है।"));
hinsuperobject.biblemap.put("b19_68_18", new hinsuperobject(19,68,18,"तू ऊंचे पर चढ़ा, तू लोगों को बन्धुवाई में ले गया; तू ने मनुष्यों से, वरन हठीले मनुष्यों से भी भेंटें लीं, जिस से याह परमेश्वर उन में वास करे।।"));
hinsuperobject.biblemap.put("b19_68_19", new hinsuperobject(19,68,19,"धन्य है प्रभु, जो प्रति दिन हमारा बोझ उठाता है; वही हमारा उद्धारकर्ता ईश्वर है।"));
hinsuperobject.biblemap.put("b19_68_20", new hinsuperobject(19,68,20,"वही हमारे लिये बचानेवाला ईश्वर ठहरा; यहोवा प्रभु मृत्यु से भी बचाता है।।"));
hinsuperobject.biblemap.put("b19_68_21", new hinsuperobject(19,68,21,"निश्चय परमेश्वर अपने शत्रुओं के सिर पर, और जो अधर्म के र्माग पर चलता रहता है, उसके बाल भरे चोंडें पर मार मार के उसे चूर करेगा।"));
hinsuperobject.biblemap.put("b19_68_22", new hinsuperobject(19,68,22,"प्रभु ने कहा है, कि मैं उन्हें बाशान से निकाल लाऊंगा, मैं उनको गहिरे सागर के तल से भी फेर ले आऊंगा,"));
hinsuperobject.biblemap.put("b19_68_23", new hinsuperobject(19,68,23,"कि तू अपने पांव को लोहू में डुबोए, और तेरे शत्रु तेरे कुत्तों का भाग ठहरें।।"));
hinsuperobject.biblemap.put("b19_68_24", new hinsuperobject(19,68,24,"हे परमशॆवर तेरी गति देखी गई, मेरे ईश्वर, मेरे राजा की गति पवित्रास्थान में दिखाई दी है;"));
hinsuperobject.biblemap.put("b19_68_25", new hinsuperobject(19,68,25,"गानेवाले आगे आगे और तारवाले बाजों के बजानेवाले पीछे पीछे गए, चारों ओर कुमारियां डफ बजाती थीं।"));
hinsuperobject.biblemap.put("b19_68_26", new hinsuperobject(19,68,26,"सभाओं में परमेश्वर का, हे इस्राएल के सोते से निकले हुए लोगों, प्रभु का धन्यवाद करो।"));
hinsuperobject.biblemap.put("b19_68_27", new hinsuperobject(19,68,27,"वहां उनको अध्यक्ष छोटा बिन्यामीन है, वहां यहूदा के हाकिम अपने अनुचरों समेत हैं, वहां जबूलून और नप्ताली के भी हाकिम हैं।।"));
hinsuperobject.biblemap.put("b19_68_28", new hinsuperobject(19,68,28,"तेरे परमेश्वर ने आज्ञा दी, कि तुझे सामर्थ्य मिले; हे परमेश्वर जो कुछ तू ने हमारे लिये किया है, उसे दृढ़ कर।"));
hinsuperobject.biblemap.put("b19_68_29", new hinsuperobject(19,68,29,"तेरे मन्दिर के कारण जो यरूशलेम में हैं, राजा तेरे लिये भेंट ले आएंगे।"));
hinsuperobject.biblemap.put("b19_68_30", new hinsuperobject(19,68,30,"नरकटों में रहनेवाले बनैले पशुओं को, सांड़ों के झुण्ड को और देश देश के बछड़ों को झिड़क दे। वे चान्दी के टुकड़े लिये हुए प्रणाम करेंगे; जो लोगे युद्ध से प्रसन्न रहते हैं, उनको उस ने तितर बितर किया है।"));
hinsuperobject.biblemap.put("b19_68_31", new hinsuperobject(19,68,31,"मि  से रईस आएंगे; कूशी अपने हाथों को परमेश्वर की ओर फुर्ती से फैलाएंगे।।"));
hinsuperobject.biblemap.put("b19_68_32", new hinsuperobject(19,68,32,"हे पृथ्वी पर के राज्य राज्य के लोगों परमेश्वर का गीत गाओ; प्रभु का भजन गाओ,"));
hinsuperobject.biblemap.put("b19_68_33", new hinsuperobject(19,68,33,"जो सब से ऊंचे सनातन स्वर्ग में सवार होकर चलता है; देखो वह अपनी वाणी सुनाता है, वह गम्भीर वाणी शक्तिशाली है।"));
hinsuperobject.biblemap.put("b19_68_34", new hinsuperobject(19,68,34,"परमशॆवर की सामर्थ्य की स्तुति करो, उसका प्रताप इस्राएल पर छाया हुआ है, और उसकी सामर्थ्य आकाशमण्डल में है।"));
hinsuperobject.biblemap.put("b19_68_35", new hinsuperobject(19,68,35,"हे परमेश्वर, तू अपने पवित्रास्थानों में भययोग्य है, इस्राएल का ईश्वर ही अपनी प्रजा को सामर्थ्य और शक्ति का देनेवाला है। परमेश्वर धन्य है।।"));
hinsuperobject.biblemap.put("b19_69_1", new hinsuperobject(19,69,1,"हे परमेश्वर, मेरा उद्धार कर, मैं जल में डूबा जाता हूं।"));
hinsuperobject.biblemap.put("b19_69_2", new hinsuperobject(19,69,2,"मैं बड़े दलदल में धसा जाता हूं, और मेरे पैर कहीं नहीं रूकते; मैं गहिरे जल में आ गया, और धारा में डूबा जाता हूं।"));
hinsuperobject.biblemap.put("b19_69_3", new hinsuperobject(19,69,3,"मैं पुकारते पुकारते थक गया, मेरा गला सूख गया है; अपने परमेश्वर की बाट जोहते जोहते, मेरी आंखे रह गई हैं।।"));
hinsuperobject.biblemap.put("b19_69_4", new hinsuperobject(19,69,4,"जो अकारण मेरे बैरी हैं, वे गिनती में मेरे सिर के बालों से अधिक हैं; मेरे विनाश करनेवाले जो व्यर्थ मेरे शत्रु हैं, वे सामर्थीं हैं, इसलिये जो मैं ने लूटा नहीं वह भी मुझ को देना पड़ा है।"));
hinsuperobject.biblemap.put("b19_69_5", new hinsuperobject(19,69,5,"हे परमेश्वर, तू तो मेरी मूढ़ता को जानता है, और मेरे दोष तुझ से छिपे नहीं हैं।।"));
hinsuperobject.biblemap.put("b19_69_6", new hinsuperobject(19,69,6,"हे प्रभु, हे सेनाओं के यहोवा, जो तेरी बाट जोहते हैं, उनकी आशा मेरे कारण न टूटे; हे इस्राएल के परमेश्वर, जो तुझे ढूंढते हैं उनका मुंह मेरे कारण काला न हो।"));
hinsuperobject.biblemap.put("b19_69_7", new hinsuperobject(19,69,7,"तेरे ही कारण मेरी निन्दा हुई है, और मेरा मुंह लज्जा से ढंपा है।"));
hinsuperobject.biblemap.put("b19_69_8", new hinsuperobject(19,69,8,"मैं अपने भाइयों के साम्हने अजनबी हुआ, और अपने सगे भाइयों की दृष्टि में परदेशी ठहरा हूं।।"));
hinsuperobject.biblemap.put("b19_69_9", new hinsuperobject(19,69,9,"क्योंकि मैं तेरे भवन के निमित्त जलते जलते भस्म हुआ, और जो निन्दा वे तेरी करते हैं, वही निन्दा मुझ को सहनी पड़ी है।"));
hinsuperobject.biblemap.put("b19_69_10", new hinsuperobject(19,69,10,"जब मैं रोकर और उपवास करके दु:ख उठाता था, तब उस से भी मेरी नामधराई ही हुई।"));
hinsuperobject.biblemap.put("b19_69_11", new hinsuperobject(19,69,11,"और जब मैं टाट का वस्त्रा पहिने था, तब मेरा दृष्टान्त उन में चलता था।"));
hinsuperobject.biblemap.put("b19_69_12", new hinsuperobject(19,69,12,"फाटक के पास बैठनेवाले मेरे विषय बातचीत करते हैं, और मदिरा पीनेवाले मुझ पर लगता हुआ गीत गाते हैं।"));
hinsuperobject.biblemap.put("b19_69_13", new hinsuperobject(19,69,13,"परन्तु हे यहोवा, मेरी प्रार्थना तो तेरी प्रसन्नता के समय में हो रही है; हे परमेश्वर अपनी करूणा की बहुतायात से, और बचाने की अपनी सच्ची प्रतिज्ञा के अनुसार मेरी सुन ले।"));
hinsuperobject.biblemap.put("b19_69_14", new hinsuperobject(19,69,14,"मुझ को दलदल में से उबार, कि मैं धंस न जाऊं; मैं अपने बैरियों से, और गहिरे जल में से बच जाऊं।"));
hinsuperobject.biblemap.put("b19_69_15", new hinsuperobject(19,69,15,"मैं धारा में डूब न जाऊं, और न मैं गहिरे जल में डूब मरूं, और न पाताल का मुंह मेरे ऊपर बन्द हो।।"));
hinsuperobject.biblemap.put("b19_69_16", new hinsuperobject(19,69,16,"हे यहोवा, मेरी सुन ले, क्योंकि तेरी करूणा उत्तम है; अपनी दया की बहुतायत के अनुसार मेरी ओर ध्यान दे।"));
hinsuperobject.biblemap.put("b19_69_17", new hinsuperobject(19,69,17,"अपने दास से अपना मुंह न मोड़; क्योंकि मैं संकट में हूं, फुर्ती से मेरी सुन ले।"));
hinsuperobject.biblemap.put("b19_69_18", new hinsuperobject(19,69,18,"मेरे निकट आकर मुझे छुड़ा ले, मेरे शत्रुओं से मुझ को छुटकारा दे।।"));
hinsuperobject.biblemap.put("b19_69_19", new hinsuperobject(19,69,19,"मेरी नामधराई और लज्जा और अनादर को तू जानता है: मेरे सब द्रोही तेरे साम्हने हैं।"));
hinsuperobject.biblemap.put("b19_69_20", new hinsuperobject(19,69,20,"मेरा हृदय नामधराई के कारण फट गया, और मैं बहुत उदास हूं। मैं ने किसी तरस खानेवाले की आशा तो की, परन्तु किसी को न पाया, और शान्ति देनेवाले ढूंढ़ता तो रहा, परन्तु कोई न मिला।"));
hinsuperobject.biblemap.put("b19_69_21", new hinsuperobject(19,69,21,"और लोगों ने मेरे खाने के लिये इन्द्रायन दिया, और मेरी प्यास बुझाने के लिये मुझे सिरका पिलाया।।"));
hinsuperobject.biblemap.put("b19_69_22", new hinsuperobject(19,69,22,"उनको भोजन उनके लिये फन्दा हो जाए; और उनके सुख के समय जाल बन जाए।"));
hinsuperobject.biblemap.put("b19_69_23", new hinsuperobject(19,69,23,"उनकी आंखों पर अन्धेरा छा जाए, ताकि वे देख न सकें; और तू उनकी कटि को निरन्तर कंपाता रह।"));
hinsuperobject.biblemap.put("b19_69_24", new hinsuperobject(19,69,24,"उनके ऊपर अपना रोष भड़का, और तेरे क्रोध की आंच उनको लगे।"));
hinsuperobject.biblemap.put("b19_69_25", new hinsuperobject(19,69,25,"उनकी छावनी उजड़ जाए, उनके डेरों में कोई न रहे।"));
hinsuperobject.biblemap.put("b19_69_26", new hinsuperobject(19,69,26,"क्योंकि जिसको तू ने मारा, वे उसके पीछे पड़े हैं, और जिनको तू ने घायल किया, वे उनकी पीड़ा की चर्चा करते हैं।"));
hinsuperobject.biblemap.put("b19_69_27", new hinsuperobject(19,69,27,"उनके अधर्म पर अधर्म बढ़ा; और वे तेरे धर्म को प्राप्त न करें।"));
hinsuperobject.biblemap.put("b19_69_28", new hinsuperobject(19,69,28,"उनका नाम जीवन की पुस्तक में से काटा जाए, और धर्मियों के संग लिखा न जाए।।"));
hinsuperobject.biblemap.put("b19_69_29", new hinsuperobject(19,69,29,"परन्तु मैं तो दु:खी और पीड़ित हूं, इसलिये हे परमेश्वर तू मेरा उद्धार करके मुझे ऊंचे स्थान पर बैठा।"));
hinsuperobject.biblemap.put("b19_69_30", new hinsuperobject(19,69,30,"मैं गीत गाकर तेरे नाम की स्तुति करूंगा, और धन्यवाद करता हुआ तेरी बड़ाई करूंगा।"));
hinsuperobject.biblemap.put("b19_69_31", new hinsuperobject(19,69,31,"यह यहोवा को बैल से अधिक, वरन सींग और खुरवाले बैल से भी अधिक भाएगा।"));
hinsuperobject.biblemap.put("b19_69_32", new hinsuperobject(19,69,32,"नम्र लोग इसे देखकर आनन्दित होंगे, हे परमेश्वर के खोजियों तुम्हारा मन हरा हो जाए।"));
hinsuperobject.biblemap.put("b19_69_33", new hinsuperobject(19,69,33,"क्योंकि यहोवा दरिद्रों की ओर कान लगाता है, और अपने लोगों को जो बन्धुए हैं तुच्छ नही जानता।।"));
hinsuperobject.biblemap.put("b19_69_34", new hinsuperobject(19,69,34,"स्वर्ग और पृथ्वी उसकी स्तुति करें, और समुद्र अपने सब जीव जन्तुओं समेत उसकी स्तुति करे।"));
hinsuperobject.biblemap.put("b19_69_35", new hinsuperobject(19,69,35,"क्योंकि परमेश्वर सिरयोन का उद्धार करेगा, और यहूदा के नगरों को फिर बसाएगा; और लोग फिर वहां बसकर उसके अधिकारी हो जाएंगे।"));
hinsuperobject.biblemap.put("b19_69_36", new hinsuperobject(19,69,36,"उसके दासों को वंश उसको अपने भाग में पाएगा, और उसके नाम के प्रेमी उस में वास करेंगे।।"));
hinsuperobject.biblemap.put("b19_70_1", new hinsuperobject(19,70,1,"हे परमेश्वर मुझे छुड़ाने के लिये, हे यहोवा मेरी सहायता करने के लिये फुर्ती कर!"));
hinsuperobject.biblemap.put("b19_70_2", new hinsuperobject(19,70,2,"जो मेरे प्राण के खोजी हैं, उनकी आशा टूटे, और मुंह काला हो जाए! जो मेरी हानि से प्रसन्न होते हैं, वे पीछे हटाए और निरादर किए जाएं।"));
hinsuperobject.biblemap.put("b19_70_3", new hinsuperobject(19,70,3,"जो कहते हैं, आहा, आहा, वे अपनी लज्जा के मारे उलटे फेरे जाएं।।"));
hinsuperobject.biblemap.put("b19_70_4", new hinsuperobject(19,70,4,"जितने तुझे ढूंढ़ते हैं, वे सब तेरे कारण हर्षित और आनन्दित हों! और जो तेरा उद्धार चाहते हैं, वे निरन्तर कहते रहें, कि परमेश्वर की बड़ाई हो।"));
hinsuperobject.biblemap.put("b19_70_5", new hinsuperobject(19,70,5,"मैं तो दीन और दरिद्र हूं; हे परमेश्वर मेरे लिये फुर्ती कर! तू मेरा सहायक और छुडानेवाला है; हे यहोवा विलम्ब न कर!"));
hinsuperobject.biblemap.put("b19_71_1", new hinsuperobject(19,71,1,"हे यहोवा मैं तेरा शरणागत हूं; मेरी आशा कभी टूटने न पाए!"));
hinsuperobject.biblemap.put("b19_71_2", new hinsuperobject(19,71,2,"तू तो धर्मी है, मुझे छुड़ा और मेरा उद्धार कर; मेरी ओर कान लगा, और मेरा उद्धार कर!"));
hinsuperobject.biblemap.put("b19_71_3", new hinsuperobject(19,71,3,"मेरे लिये सनातन काल की चट्टान का धाम बन, जिस में मैं नित्य जा सकूं; तू ने मेरे उद्धार की आज्ञा तो दी है, क्योंकि तू मेरी चट्टान और मेरा गढ़ ठहरा है।।"));
hinsuperobject.biblemap.put("b19_71_4", new hinsuperobject(19,71,4,"हे मेरे परमेश्वर दुष्ट के, और कुटिल और क्रूर मनुष्य के हाथ से मेरी रक्षा कर।"));
hinsuperobject.biblemap.put("b19_71_5", new hinsuperobject(19,71,5,"क्योंकि हे प्रभु यहोवा, मैं तेरी ही बाट जोहता आया हूं; बचपन से मेरा आधार तू है।"));
hinsuperobject.biblemap.put("b19_71_6", new hinsuperobject(19,71,6,"मैं गर्भ से निकलते ही, तुझ से सम्भाला गया; मुझे मां की कोख से तू ही ने निकाला; इसलिये मैं नित्य तेरी स्तुति करता रहूंगा।।"));
hinsuperobject.biblemap.put("b19_71_7", new hinsuperobject(19,71,7,"मैं बहुतों के लिये चमत्कार बना हूं; परन्तु तू मेरा दृढ़ शरणस्थान है।"));
hinsuperobject.biblemap.put("b19_71_8", new hinsuperobject(19,71,8,"मेरे मुंह से तेरे गुणानुवाद, और दिन भर तेरी शोभा का वर्णन बहुत हुआ करे।"));
hinsuperobject.biblemap.put("b19_71_9", new hinsuperobject(19,71,9,"बुढ़ापे के समय मेरा त्याग न कर; जब मेरा बल घटे तब मुझ को छोड़ न दे।"));
hinsuperobject.biblemap.put("b19_71_10", new hinsuperobject(19,71,10,"क्योंकि मेरे शत्रु मेरे विषय बातें करते हैं, और जो मेरे प्राण की ताक में हैं, वे आपस में यह सम्मति करते हैं, कि"));
hinsuperobject.biblemap.put("b19_71_11", new hinsuperobject(19,71,11,"परमेश्वर ने उसको छोड़ दिया है; उसका पीछा करके उसे पकड़ लो, क्योंकि उसका कोई छुड़ानेवाला नहीं।।"));
hinsuperobject.biblemap.put("b19_71_12", new hinsuperobject(19,71,12,"हे परमेश्वर, मुझ से दूर न रह; हे मेरे परमेश्वर, मेरी सहायता के लिये फुर्ती कर!"));
hinsuperobject.biblemap.put("b19_71_13", new hinsuperobject(19,71,13,"जो मेरे प्राण के विरोधी हैं, उनकी आशा टूटे और उनका अन्त हो जाए; जो मेरी हानि के अभिलाषी हैं, वे नामधराई और अनादर में गड़ जाएं।"));
hinsuperobject.biblemap.put("b19_71_14", new hinsuperobject(19,71,14,"मैं तो निरन्तर आशा लगाए रहूंगा, और तेरी स्तुति अधिक अधिक करता जाऊंगा।"));
hinsuperobject.biblemap.put("b19_71_15", new hinsuperobject(19,71,15,"मैं अपने मुंह से तेरे धर्म का, और तेरे किए हुए उद्धार का वर्णन दिन भर करता रहूंगा, परन्तु उनका पूरा ब्योरा जाना भी नहीं जाता।"));
hinsuperobject.biblemap.put("b19_71_16", new hinsuperobject(19,71,16,"मैं प्रभु यहोवा के पराक्रम के कामों का वर्णन करता हुआ आऊंगा, मैं केवल तेरे ही धर्म की चर्चा किया करूंगा।।"));
hinsuperobject.biblemap.put("b19_71_17", new hinsuperobject(19,71,17,"हे परमेश्वर, तू तो मुझ को बचपन ही से सिखाता आया है, और अब तक मैं तेरे आश्चर्य कर्मों का प्रचार करता आया हूं।"));
hinsuperobject.biblemap.put("b19_71_18", new hinsuperobject(19,71,18,"इसलिये हे परमेश्वर जब मैं बूढ़ा हो जाऊं और मेरे बाल पक जाएं, तब भी तू मुझे न छोड़, जब तक मैं आनेवाली पीढ़ी के लोगों को तेरा बाहुबल और सब उत्पन्न होनेवालों को तेरा पराक्रम सुनाऊं।"));
hinsuperobject.biblemap.put("b19_71_19", new hinsuperobject(19,71,19,"और हे परमेश्वर, तेरा धर्म अति महान है।। तू जिस ने महाकार्य किए हैं, हे परमेवर तेरे तुल्य कौन है?"));
hinsuperobject.biblemap.put("b19_71_20", new hinsuperobject(19,71,20,"तू ने तो हम को बहुत से कठिन कष्ट दिखाए हैं परन्तु अब तू फिर से हम को जिलाएगा; और पृथ्वी के गहिरे गड़हे में से उबार लेगा।"));
hinsuperobject.biblemap.put("b19_71_21", new hinsuperobject(19,71,21,"तू मेरी बड़ाई को बढ़ाएगा, और फिरकर मुझे शान्ति देगा।।"));
hinsuperobject.biblemap.put("b19_71_22", new hinsuperobject(19,71,22,"हे मेरे परमेश्वर, मैं भी तेरी सच्चाई को धन्यवाद सारंगी बजाकर गाऊंगा; हे इस्राएल के पवित्रा मैं वीणा बजाकर तेरा भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_71_23", new hinsuperobject(19,71,23,"जब मैं तेरा भजन गाऊंगा, तब अपने मुंह से और अपने प्राण से भी जो तू ने बचा लिया है, जयजयकार करूंगा।"));
hinsuperobject.biblemap.put("b19_71_24", new hinsuperobject(19,71,24,"और मैं तेरे धर्म की चर्चा दिन भर करता रहूंगा; क्योंकि जो मेरी हानि के अभिलाषी थे, उनकी आशा टूट गई और मुंह काले हो गए हैं।।"));
hinsuperobject.biblemap.put("b19_72_1", new hinsuperobject(19,72,1,"हे परमेश्वर, राजा को अपना नियम बता, राजपुत्रा को अपना धर्म सिखला!"));
hinsuperobject.biblemap.put("b19_72_2", new hinsuperobject(19,72,2,"वह तेरी प्रजा का न्याय धर्म से, और तेरे दी लोगों का न्याय ठीक ठीक चुकाएगा।"));
hinsuperobject.biblemap.put("b19_72_3", new hinsuperobject(19,72,3,"पहाडों और पहाड़ियों से प्रजा के लिये, धर्म के द्वारा शान्ति मिला करेगी"));
hinsuperobject.biblemap.put("b19_72_4", new hinsuperobject(19,72,4,"वह प्रजा के दीन लोगों का न्याय करेगा, और दरिद्र लोगों को बचाएगा; और अन्धेर करनेवालों को चूर करेगा।।"));
hinsuperobject.biblemap.put("b19_72_5", new hinsuperobject(19,72,5,"जब तक सूर्य और चन्द्रमा बने रहेंगे तब तक लोग पीढ़ी- पीढ़ी तेरा भय मानते रहेंगे।"));
hinsuperobject.biblemap.put("b19_72_6", new hinsuperobject(19,72,6,"वह घास की खूंटी पर बरसनेवाले मेंह, और भूमि सींचनेवाली झाड़ियों के समान होगा।"));
hinsuperobject.biblemap.put("b19_72_7", new hinsuperobject(19,72,7,"उसके दिनों में धर्मी फूले फलेंगे, और जब तक चन्द्रमा बना रहेगा, तब तक शान्ति बहुत रहेगी।।"));
hinsuperobject.biblemap.put("b19_72_8", new hinsuperobject(19,72,8,"वह समुद्र से समुद्र तक और महानद से पृथ्वी की छोर तक प्रभुता करेगा।"));
hinsuperobject.biblemap.put("b19_72_9", new hinsuperobject(19,72,9,"उसके साम्हने जंगल के रहनेवाले घुटने टेकेंगे, और उसके शत्रु मिट्टी चाटेंगे।"));
hinsuperobject.biblemap.put("b19_72_10", new hinsuperobject(19,72,10,"तर्शीश और द्वीप द्वीप के राजा भेंट ले आएंगे, शेबा और सबा दोनों के राजा द्रव्य पहुंचाएंगे।"));
hinsuperobject.biblemap.put("b19_72_11", new hinsuperobject(19,72,11,"सब राजा उसको दण्डवत् करेंगे, जाति जाति के लोग उसके अधीन हो जाएंगे।।"));
hinsuperobject.biblemap.put("b19_72_12", new hinsuperobject(19,72,12,"क्योंकि वह दोहाई देनेवाले दरिद्र को, और दु:खी और असहाय मनुष्य का उद्धार करेगा।"));
hinsuperobject.biblemap.put("b19_72_13", new hinsuperobject(19,72,13,"वह कंगाल और दरिद्र पर तरस खाएगा, और दरिद्रों के प्राणो को बचाएगा।"));
hinsuperobject.biblemap.put("b19_72_14", new hinsuperobject(19,72,14,"वह उनके प्राणों को अन्धेर और उपद्रव से छुड़ा लेगा; और उनका लोहू उसकी दृष्टि में अनमोल ठहरेगा।।"));
hinsuperobject.biblemap.put("b19_72_15", new hinsuperobject(19,72,15,"वह तो जीवित रहेगा और शेबा के सोने में से उसको दिया जाएगा। लोग उसके लिये नित्य प्रार्थना करेंगे; और दिन भर उसको धन्य कहते हरेंगे।"));
hinsuperobject.biblemap.put("b19_72_16", new hinsuperobject(19,72,16,"देश में पहाड़ों की चोटियों पर बहुत से अन्न होगा; जिसकी बालें लबानोन के देवदारूओं की नाईं झूमेंगी; और नगर के लोग घास की नाई लहलहाएंगे।"));
hinsuperobject.biblemap.put("b19_72_17", new hinsuperobject(19,72,17,"उसका नाम सदा सर्वदा बना रहेगा; जब तक सूर्य बना रहेगा, तब तक उसका नाम नित्य नया होता रहेगा, और लोग अपने को उसके कारण धन्य गिनेंगे, सारी जातियां उसको भाग्यवान कहेंगी।।"));
hinsuperobject.biblemap.put("b19_72_18", new hinsuperobject(19,72,18,"धन्य है, यहोवा परमेश्वर जो इस्राएल का परमेश्वर है; आश्चर्य कर्म केवल वही करता है।"));
hinsuperobject.biblemap.put("b19_72_19", new hinsuperobject(19,72,19,"उसका महिमायुक्त नाम सर्वदा धन्य रहेगा; और सारी पृथ्वी उसकी महिमा से परिपूर्ण होगी। आमीन फिर आमीन।।"));
hinsuperobject.biblemap.put("b19_72_20", new hinsuperobject(19,72,20,"यिशै के पुत्रा दाऊद की प्रार्थना समाप्त हुई।।"));
hinsuperobject.biblemap.put("b19_73_1", new hinsuperobject(19,73,1,"सचमुच इस्त्राएल के लिये अर्थात् शुद्ध मनवालों के लिये परमेश्वर भला है।"));
hinsuperobject.biblemap.put("b19_73_2", new hinsuperobject(19,73,2,"मेरे डग तो उखड़ना चाहते थे, मेरे डग फिसलने ही पर थे।"));
hinsuperobject.biblemap.put("b19_73_3", new hinsuperobject(19,73,3,"क्योंकि जब मैं दुष्टों का कुशल देखता था, तब उन घमण्डियों के विषय डाह करता था।।"));
hinsuperobject.biblemap.put("b19_73_4", new hinsuperobject(19,73,4,"क्योंकि उनकी मृत्यु में बेधनाएं नहीं होतीं, परन्तु उनका बल अटूट रहता है।"));
hinsuperobject.biblemap.put("b19_73_5", new hinsuperobject(19,73,5,"उनको दूसरे मनुष्यों की नाईं कष्ट नहीं होता; और और मनुष्यों के समान उन पर विपत्ति नहीं पड़ती।"));
hinsuperobject.biblemap.put("b19_73_6", new hinsuperobject(19,73,6,"इस कारण अहंकार उनके गले का हार बना है; उनका ओढ़ना उपद्रव है।"));
hinsuperobject.biblemap.put("b19_73_7", new hinsuperobject(19,73,7,"उनकी आंखें चर्बीं से झलकती हैं, उनके मन की भवनाएं उमण्डती हैं।"));
hinsuperobject.biblemap.put("b19_73_8", new hinsuperobject(19,73,8,"वे ठट्ठा मारते हैं, और दुष्टता से अन्धेर की बात बोलते हैं;"));
hinsuperobject.biblemap.put("b19_73_9", new hinsuperobject(19,73,9,"वे डींग मारते हैं। वे मानों स्वर्ग में बैठे हुए बोलते हैं, और वे पृथ्वी में बोलते फिरते हैं।।"));
hinsuperobject.biblemap.put("b19_73_10", new hinsuperobject(19,73,10,"तौभी उसकी प्रजा इधर लौट आएगी, और उनको भरे हुए प्याले का जल मिलेगा।"));
hinsuperobject.biblemap.put("b19_73_11", new hinsuperobject(19,73,11,"फिर वे कहते हैं, ईश्वर कैसे जानता है? क्या परमप्रधान को कुछ ज्ञान है?"));
hinsuperobject.biblemap.put("b19_73_12", new hinsuperobject(19,73,12,"देखो, ये तो दुष्ट लोग हैं; तौभी सदा सुभागी रहकर, धन सम्पत्ति बटोरते रहते हैं।"));
hinsuperobject.biblemap.put("b19_73_13", new hinsuperobject(19,73,13,"निश्चय, मैं ने अपने हृदय को व्यर्थ शुद्ध किया और अपने हाथों को निर्दोषता में धोया है;"));
hinsuperobject.biblemap.put("b19_73_14", new hinsuperobject(19,73,14,"क्योंकि मैं दिन भर मार खाता आया हूं और प्रति भोर को मेरी ताड़ना होती आई है।।"));
hinsuperobject.biblemap.put("b19_73_15", new hinsuperobject(19,73,15,"यदि मैं ने कहा होता कि मैं ऐसा ही कहूंगा, तो देख मैं तेरे लड़कों की सन्तान के साथ क्रूरता का व्यवहार करता,"));
hinsuperobject.biblemap.put("b19_73_16", new hinsuperobject(19,73,16,"जब मैं सोचने लगा कि इसे मैं कैसे समझूं, तो यह मेरी दृष्टि में अति कठिन समस्या थी,"));
hinsuperobject.biblemap.put("b19_73_17", new hinsuperobject(19,73,17,"जब तक कि मैं ने ईश्वर के पवित्रा स्थान में जाकर उन लोगों के परिणाम को न सोचा।"));
hinsuperobject.biblemap.put("b19_73_18", new hinsuperobject(19,73,18,"निश्चय तू उन्हें फिसलनेवाले स्थानों में रखता है; और गिराकर सत्यानाश कर देता है।"));
hinsuperobject.biblemap.put("b19_73_19", new hinsuperobject(19,73,19,"अहा, वे क्षण भर में कैसे उजड़ गए हैं! वे मिट गए, वे घबराते घबराते नाश हो गए हैं।"));
hinsuperobject.biblemap.put("b19_73_20", new hinsuperobject(19,73,20,"जैसे जागनेहारा स्वप्न को तुच्छ जानता है, वैसे ही हे प्रभु जब तू उठेगा, तब उनको छाया से समझकर तुच्छ जानेगा।।"));
hinsuperobject.biblemap.put("b19_73_21", new hinsuperobject(19,73,21,"मेरा मन तो चिड़चिड़ा हो गया, मेरा अन्त:करण छिद गया था,"));
hinsuperobject.biblemap.put("b19_73_22", new hinsuperobject(19,73,22,"मैं तो पशु सरीखा था, और समझता न था, मैं तेरे संग रहकर भी, पशु बन गया था।"));
hinsuperobject.biblemap.put("b19_73_23", new hinsuperobject(19,73,23,"तौभी मैं निरन्तर तेरे संग ही था; तू ने मेरे दहिने हाथ को पकड़ रखा।"));
hinsuperobject.biblemap.put("b19_73_24", new hinsuperobject(19,73,24,"तू सम्मति देता हुआ, मेरी अगुवाई करेगा, और तब मेरी महिमा करके मुझ को अपने पास रखेगा।"));
hinsuperobject.biblemap.put("b19_73_25", new hinsuperobject(19,73,25,"स्वर्ग में मेरा और कौन है? तेरे संग रहते हुए मैं पृथ्वी पर और कुछ नहीं चाहता।"));
hinsuperobject.biblemap.put("b19_73_26", new hinsuperobject(19,73,26,"मेरे हृदय और मन दोनों तो हार गए हैं, परन्तु परमेश्वर सर्वदा के लिये मेरा भाग और मेरे हृदय की चट्टान बना है।।"));
hinsuperobject.biblemap.put("b19_73_27", new hinsuperobject(19,73,27,"जो तुझ से दूर रहते हैं वे तो नाश होंगे; जो कोई तेरे विरूद्ध व्यभिचार करता है, उसको तू विनाश करता है।"));
hinsuperobject.biblemap.put("b19_73_28", new hinsuperobject(19,73,28,"परन्तु परमेश्वर के समीप रहना, यही मेरे लिये भला है; मैं ने प्रभु यहोवा को अपना शरणस्थान माना है, जिस से मैं तेरे सब कामों को वर्णन करूं।।"));
hinsuperobject.biblemap.put("b19_74_1", new hinsuperobject(19,74,1,"हे परमेश्वर, तू ने हमें क्यों सदा के लिये छोड़ दिया है? तेरी कोपाग्नि का धुआं तेरी चराई की भेंड़ों के विरूद्ध क्यों उठ रहा है?"));
hinsuperobject.biblemap.put("b19_74_2", new hinsuperobject(19,74,2,"अपनी मण्डली को जिसे तू ने प्राचीनकाल में मोल लिया था, और अपने निज भाग का गोत्रा होने के लिये छुड़ा लिया था, और इस सिरयोन पर्वत को भी, जिस पर तू ने वास किया था, स्मारण कर!"));
hinsuperobject.biblemap.put("b19_74_3", new hinsuperobject(19,74,3,"अपने डग सनातन की खंडहर की ओर बढ़ा; अर्थात् उन सब बुराइयों की ओर जो शत्राृ ने पवित्रास्थान में किए हैं।।"));
hinsuperobject.biblemap.put("b19_74_4", new hinsuperobject(19,74,4,"तेरे द्रोही तेरे सभास्थान के बीच गरजते रहे हैं; उन्हों ने अपनी ही ध्वजाओं को चिन्ह ठहराया है। वे उन मनुष्यों के समान थे"));
hinsuperobject.biblemap.put("b19_74_5", new hinsuperobject(19,74,5,"जो घने वन के पेड़ों पर कुल्हाड़े चलाते हैं।"));
hinsuperobject.biblemap.put("b19_74_6", new hinsuperobject(19,74,6,"और अब वे उस भवन की नक्काशी को, कुल्हाडियों और हथौड़ों से बिलकुल तोड़े डालते हैं।"));
hinsuperobject.biblemap.put("b19_74_7", new hinsuperobject(19,74,7,"उन्हों ने तेरे पवित्रास्थान को आग में झोंक दिया है, और तेरे नाम के निवास को गिराकर अशुद्ध कर डाला है।"));
hinsuperobject.biblemap.put("b19_74_8", new hinsuperobject(19,74,8,"उन्हों ने मन में कहा है कि हम इनको एकदक दबा दें; उन्हों ने इस देश में ईश्वर के सब सभास्थानों कों फूंक दिया है।।"));
hinsuperobject.biblemap.put("b19_74_9", new hinsuperobject(19,74,9,"हम को हमारे निशान नहीं देख पड़ते; अब कोई नबी नहीं रहा, न हमारे बीच कोई जानता है कि कब तक यह दशा रहेगी।"));
hinsuperobject.biblemap.put("b19_74_10", new hinsuperobject(19,74,10,"हे परमेश्वर द्रोही कब तक नामधराई करता रहेगा? क्या शत्रु, तेरे नाम की निन्दा सदा करता रहेगा?"));
hinsuperobject.biblemap.put("b19_74_11", new hinsuperobject(19,74,11,"तू अपना दहिना हाथ क्यों रोके रहता है? उसे अपने पांजर से निकाल कर उनका अन्त कर दे।।"));
hinsuperobject.biblemap.put("b19_74_12", new hinsuperobject(19,74,12,"परमेश्वर तो प्राचीनकाल से मेरा राजा है, वह पृथ्वी पर उद्धार के काम करता आया है।"));
hinsuperobject.biblemap.put("b19_74_13", new hinsuperobject(19,74,13,"तू ने अपनी शक्ति से समुद्र को दो भाग कर दिया; तू ने जल में मगरमच्छों के सिरों को फोड़ दिया।"));
hinsuperobject.biblemap.put("b19_74_14", new hinsuperobject(19,74,14,"तू ने तो लिव्यातानों के सिर टुकड़े टुकड़े करके जंगली जन्तुओं को खिला दिए।"));
hinsuperobject.biblemap.put("b19_74_15", new hinsuperobject(19,74,15,"तू ने तो सोता खोलकर जल की धारा बहाई, तू ने तो बारहमासी नदियों को सुखा डाला।"));
hinsuperobject.biblemap.put("b19_74_16", new hinsuperobject(19,74,16,"दिन तेरा है रात भी तेरी है; सूर्य और चन्द्रमा को तू ने स्थिर किया है।"));
hinsuperobject.biblemap.put("b19_74_17", new hinsuperobject(19,74,17,"तू ने तो पृथ्वी के सब सिवानों को ठहराया; धूपकाल और जाड़ा दोनों तू ने ठहराए हैं।।"));
