hinsuperobject.biblemap.put("b19_78_17", new hinsuperobject(19,78,17,"तौभी वे फिर उसके विरूद्ध अघिक पाप करते गए, और निर्जल देश में परमप्रधान के विरूद्ध उठते रहे।"));
hinsuperobject.biblemap.put("b19_78_18", new hinsuperobject(19,78,18,"और अपनी चाह के अनुसार भोजन मांगकर मन ही मन ईश्वर की परीक्षा की।"));
hinsuperobject.biblemap.put("b19_78_19", new hinsuperobject(19,78,19,"वे परमेश्वर के विरूद्ध बोले, और कहने लगे, क्या ईश्वर जंगल में मेज लगा सकता है?"));
hinsuperobject.biblemap.put("b19_78_20", new hinsuperobject(19,78,20,"उस ने चट्टान पर मारके जल बहा तो दिया, और धाराएं उमण्ड़ चली, परन्तु क्या वह रोटी भी दे सकता है? क्या वह अपनी प्रजा के लिये मांस भी तैयार कर सकता?"));
hinsuperobject.biblemap.put("b19_78_21", new hinsuperobject(19,78,21,"यहोवा सुनकर क्रोध से भर गया, तब याकूब के बीच आग लगी, और इस्त्राएल के विरूद्ध क्रोध भड़का;"));
hinsuperobject.biblemap.put("b19_78_22", new hinsuperobject(19,78,22,"इसलिए कि उन्हों ने परमेश्वर पर विश्वास नहीं रखा था, न उसकी उठ्ठार करने की शक्ति पर भरोसा किया।"));
hinsuperobject.biblemap.put("b19_78_23", new hinsuperobject(19,78,23,"तौभी उस ने आकाश को आज्ञा दी, और स्वर्ग के ठ्ठारों को खोला;"));
hinsuperobject.biblemap.put("b19_78_24", new hinsuperobject(19,78,24,"और उनके लिये खाने को मान बरसाया, और उन्हे स्वर्ग का अन्न दिया।"));
hinsuperobject.biblemap.put("b19_78_25", new hinsuperobject(19,78,25,"उनको शूरवीरों की सी रोटी मिली; उस ने उनको मनमाना भोजन दिया।"));
hinsuperobject.biblemap.put("b19_78_26", new hinsuperobject(19,78,26,"उस ने आकाश में पुरवाई को चलाया, और अपनी शक्ति से दक्खिनी बहाई;"));
hinsuperobject.biblemap.put("b19_78_27", new hinsuperobject(19,78,27,"और उनके लिये मांस धूलि की नाई बहुत बरसाया, और समुद्र के बालू के समान अनगिनित पक्षी भेजे;"));
hinsuperobject.biblemap.put("b19_78_28", new hinsuperobject(19,78,28,"और उनकी छावनी के बीच में, उनके निवासों के चारों ओर गिराए।"));
hinsuperobject.biblemap.put("b19_78_29", new hinsuperobject(19,78,29,"और वे खाकर अति तृप्त हुए, और उस ने उनकी कामना पूरी की।"));
hinsuperobject.biblemap.put("b19_78_30", new hinsuperobject(19,78,30,"उनकी कामना बनी ही रही, उनका भोजन उनके मुंह ही में था,"));
hinsuperobject.biblemap.put("b19_78_31", new hinsuperobject(19,78,31,"कि परमेश्वर का क्रोध उन पर भड़का, और उस ने उनके हष्टपुष्टों को घात किया, और इस्त्राएल के जवानों को गिरा दिया।।"));
hinsuperobject.biblemap.put("b19_78_32", new hinsuperobject(19,78,32,"इतने पर भी वे और अधिक पाप करते गए; और परमेश्वर के आश्चर्यकर्मों की प्रतीति न की।"));
hinsuperobject.biblemap.put("b19_78_33", new hinsuperobject(19,78,33,"तब उस ने उनके दिनों को व्यर्थ श्रम में, और उनके वर्षों को धबराहट में कटवाया।"));
hinsuperobject.biblemap.put("b19_78_34", new hinsuperobject(19,78,34,"जब जब वह उन्हे घात करने लगता, तब तब वे उसको पूछते थे; और फिरकर ईश्वर को यत्न से खोजते थे।"));
hinsuperobject.biblemap.put("b19_78_35", new hinsuperobject(19,78,35,"और उनको स्मरण होता था कि परमेश्वर हमारी चट्टान है, और परमप्रधान ईश्वर हमारा छुड़ानेवाला है।"));
hinsuperobject.biblemap.put("b19_78_36", new hinsuperobject(19,78,36,"तौभी उन्हों ने उस से चापलूसी की; वे उस से झूठ बोले।"));
hinsuperobject.biblemap.put("b19_78_37", new hinsuperobject(19,78,37,"क्योंकि उनका ह्यदय उसकी ओर दृढ़ न था; न वे उसकी वाचा के विषय सच्चे थे।"));
hinsuperobject.biblemap.put("b19_78_38", new hinsuperobject(19,78,38,"परन्तु वह जो दयालु है, वह अधर्म को ढांपता, और नाश नहीं करता; वह बारबार अपने क्रोध को ठण्डा करता है, और अपनी जलजलाहट को पूरी रीति से भड़कने नहीं देता।"));
hinsuperobject.biblemap.put("b19_78_39", new hinsuperobject(19,78,39,"उसको स्मरण हुआ कि ये नाशमान हैं, ये वायु के समान हैं जो चली जाती और लौट नहीं आती।"));
hinsuperobject.biblemap.put("b19_78_40", new hinsuperobject(19,78,40,"उन्हों ने कितनी ही बार जंगल में उस से बलवा किया, और निर्जल देश में उसको उदास किया!"));
hinsuperobject.biblemap.put("b19_78_41", new hinsuperobject(19,78,41,"वे बारबार ईश्वर की परीक्षा करते थे, और इस्त्राएल के पवित्रा को खेदित करते थे।"));
hinsuperobject.biblemap.put("b19_78_42", new hinsuperobject(19,78,42,"उन्होने न तो उसका भुजबल स्मरण किया, न वह दिन जब उस ने उनको द्रोही के वश से छुड़ाया था;"));
hinsuperobject.biblemap.put("b19_78_43", new hinsuperobject(19,78,43,"कि उस ने क्योंकर अपने चिन्ह मिस्त्रा में, और अपने चमत्कार सोअन के मैदान में किए थे।"));
hinsuperobject.biblemap.put("b19_78_44", new hinsuperobject(19,78,44,"उस ने तो मिस्त्रियों की नहरों को लोहू बना डाला, और वे अपनी नदियों का जल पी न सके।"));
hinsuperobject.biblemap.put("b19_78_45", new hinsuperobject(19,78,45,"उस ने उनके बीच में डांस भेजे जिन्हों ने उन्हे काट खाया, और मेंढक भी भेजे, जिन्हों ने उनका बिगाड़ किया।"));
hinsuperobject.biblemap.put("b19_78_46", new hinsuperobject(19,78,46,"उस ने उनकी भूमि की उपज कीड़ों को, और उनकी खेतीबारी टिड्डयों को खिला दी थी।"));
hinsuperobject.biblemap.put("b19_78_47", new hinsuperobject(19,78,47,"उस ने उनकी दाखलताओं को ओेलों से, और उनके गूलर के पेड़ों को बड़े बड़े पत्थ्र बरसाकर नाश किया।"));
hinsuperobject.biblemap.put("b19_78_48", new hinsuperobject(19,78,48,"उस ने उनके पशुओं को ओलों से, और उनके ढोरों को बिजलियों से मिटा दिया।"));
hinsuperobject.biblemap.put("b19_78_49", new hinsuperobject(19,78,49,"उस ने उनके ऊपर अपना प्रचणड क्रोध और रोष भड़काया, और उन्हे संकट में डाला, और दुखदाई दूतों का दल भेजा।"));
hinsuperobject.biblemap.put("b19_78_50", new hinsuperobject(19,78,50,"उस ने अपने क्रोध का मार्ग खोला, और उनके प्राणों को मृत्यु से न बचाया, परन्तु उनको मरी के वश में कर दिया।"));
hinsuperobject.biblemap.put("b19_78_51", new hinsuperobject(19,78,51,"उस ने मिस्त्रा के सब पहिलौठों को मारा, जो हाम के डेरों में पौरूष के पहिले फल थे;"));
hinsuperobject.biblemap.put("b19_78_52", new hinsuperobject(19,78,52,"परन्तु अपनी प्रजा को भेड़- बकरियों की नाई पयान कराया, और जंगल में उनकी अगुवाई पशुओं के झुण्ड की सी की।"));
hinsuperobject.biblemap.put("b19_78_53", new hinsuperobject(19,78,53,"तब वे उसके चलाने से बेखटके चले और उनको कुछ भय न हुआ, परन्तु उनके शत्रु समुद्र में डूब गए।"));
hinsuperobject.biblemap.put("b19_78_54", new hinsuperobject(19,78,54,"और उस ने उनको अपने पवित्रा देश के सिवाने तक, इसी पहाड़ी देश में पहुंचाया, जो उस ने अपने दहिने हाथ से प्राप्त किया था।"));
hinsuperobject.biblemap.put("b19_78_55", new hinsuperobject(19,78,55,"उस ने उनके साम्हने से अन्यजातियों को भगा दिया; और उनकी भूमि को डोरी से माप मापकर बांट दिया; और इस्त्राएल के गोत्रों को उनके डेरों में बसाया।।"));
hinsuperobject.biblemap.put("b19_78_56", new hinsuperobject(19,78,56,"तौभी उन्होने परमप्रधान परमेश्वर की परीक्षा की और उस से बलवा किया, और उसकी चितौनियों को न माना,"));
hinsuperobject.biblemap.put("b19_78_57", new hinsuperobject(19,78,57,"और मुड़कर अपने पुरखाओं की नाई विश्वासघात किया; उन्हों ने निकम्मे धनुष की नाई धोखा दिया।"));
hinsuperobject.biblemap.put("b19_78_58", new hinsuperobject(19,78,58,"क्योंकि उन्हों ने ऊंचे स्थान बनाकर उसको रिस दिलाई, और खुदी हुई मुर्तियों के द्वारा उस में जलन उपजाई।"));
hinsuperobject.biblemap.put("b19_78_59", new hinsuperobject(19,78,59,"परमेश्वर सुनकर रोष से भर गया, और उस ने इस्त्राएल को बिलकुल तज दिया।"));
hinsuperobject.biblemap.put("b19_78_60", new hinsuperobject(19,78,60,"उस ने शीलो के निवास, अर्थात् उस तम्बु को जो उस ने मनुष्यों के बीच खडा किया था, त्याग दिया,"));
hinsuperobject.biblemap.put("b19_78_61", new hinsuperobject(19,78,61,"और अपनी सामर्थ को बन्धुआई में जाने दिया, और अपनी शोभा को द्रोही के वश में कर दिया।"));
hinsuperobject.biblemap.put("b19_78_62", new hinsuperobject(19,78,62,"उस ने अपनी प्रजा को तलवार से मरवा दिया, और अपने निज भाग के लोगों पर रोष से भर गया।"));
hinsuperobject.biblemap.put("b19_78_63", new hinsuperobject(19,78,63,"उन के जवान आग से भस्म हुए, और उनकी कुमारियों के विवाह के गीत न गाए गए।"));
hinsuperobject.biblemap.put("b19_78_64", new hinsuperobject(19,78,64,"उनके याजक तलवार से मारे गए, और उनकी विधवाएं रोने न पाई।"));
hinsuperobject.biblemap.put("b19_78_65", new hinsuperobject(19,78,65,"तब प्रभु मानो नींद से चौंक उठा, और ऐसे वीर के समान उठा जो दाखमधु पीकर ललकारता हो।"));
hinsuperobject.biblemap.put("b19_78_66", new hinsuperobject(19,78,66,"और उस ने अपने द्रोहियों को मारकर पीछे हटा दिया; और उनकी सदा की नामधराई कराई।।"));
hinsuperobject.biblemap.put("b19_78_67", new hinsuperobject(19,78,67,"फिर उस ने यूसुफ के तप्बु को तज दिया; और एप्रैम के गोत्रा को न चुना;"));
hinsuperobject.biblemap.put("b19_78_68", new hinsuperobject(19,78,68,"परन्तु यहूदा ही के गोत्रा को, और अपने प्रिय सिरयोन पर्वत को चुन लिया।"));
hinsuperobject.biblemap.put("b19_78_69", new hinsuperobject(19,78,69,"उस ने अपने पवित्रास्थान को बहुत ऊंचा बना दिया, और पृथ्वी के समान स्थिर बनाया, जिसकी नेव उस ने सदा के लिये डाली है।"));
hinsuperobject.biblemap.put("b19_78_70", new hinsuperobject(19,78,70,"फिर उसने अपने दास दाऊद को चुनकर भेड़शालाओं में से ले लिया;"));
hinsuperobject.biblemap.put("b19_78_71", new hinsuperobject(19,78,71,"वह उसको बच्चेवाली भेड़ों के पीछे पीछे फिरने से ले आया कि वह उसकी प्रजा याकूब की अर्थात उसके निज भाग इस्त्राएल की चरवाही करे।"));
hinsuperobject.biblemap.put("b19_78_72", new hinsuperobject(19,78,72,"तब उस ने खरे मन से उनकी चरवाही की, और अपने हाथ की कुशलता से उनकी अगुवाई की।।"));
hinsuperobject.biblemap.put("b19_79_1", new hinsuperobject(19,79,1,"हे परमेश्वर अन्यजातियां तेरे निज भग में घुस आई; उन्हों ने तेेरे पवित्रा मन्दिर को अशुठ्ठ किया; और यरूशलेम को खंडहर कर दिया है।"));
hinsuperobject.biblemap.put("b19_79_2", new hinsuperobject(19,79,2,"उन्हों ने तेरे दासों की लोथों को आकाश के पक्षियों का आहार कर दिया, और तेरे भक्तों का मांस वनपशुओें को खिला दिया है।"));
hinsuperobject.biblemap.put("b19_79_3", new hinsuperobject(19,79,3,"उन्हों ने उनका लोहू यरूशलेम के चारों ओर जल की नाई बहाया, और उनको मिट्टी देनेवाला कोई न था।"));
hinsuperobject.biblemap.put("b19_79_4", new hinsuperobject(19,79,4,"पड़ोसियों के बीच हमारी नामधराई हुई; चारों ओर के रहनेवाले हम पर हंसते, और ठट्ठा करते हैं।।"));
hinsuperobject.biblemap.put("b19_79_5", new hinsuperobject(19,79,5,"हे यहोवा, तू कब तक लगातार क्रोध करता रहेगा? तुझ में आग की सी जलन कब तक भड़कती रहेगी?"));
hinsuperobject.biblemap.put("b19_79_6", new hinsuperobject(19,79,6,"जो जातियां तुझ को नहीं जानती, और जिन राज्यों के लोग तुझ से प्रार्थना नहीं करते, उन्ही पर अपनी सब जलजलाहट भड़का!"));
hinsuperobject.biblemap.put("b19_79_7", new hinsuperobject(19,79,7,"क्योंकि उन्हों ने याकूब को निगल लिया, और उसके वासस्थान को उजाड़ दिया है।"));
hinsuperobject.biblemap.put("b19_79_8", new hinsuperobject(19,79,8,"हमारी हानि के लिये हमारे पुरखाओं के अधर्म के कामों को स्मरण न कर; तेरी दया हम पर शीघ्र हो, क्योंकि हम बड़ी दुर्दशा में पड़े हैं।"));
hinsuperobject.biblemap.put("b19_79_9", new hinsuperobject(19,79,9,"हे हमारे उठ्ठारकर्त्ता परमेश्वर, अपने नाम की महिमा के निमित हमारी सहायता कर; और अपने नाम के निमित हम को छुड़ाकर हमारे पापों को ढांप दे।"));
hinsuperobject.biblemap.put("b19_79_10", new hinsuperobject(19,79,10,"अनयजातियां क्यों कहने पाएं कि उनका परमेश्वर कहां रहा? अन्यजातियों के बीच तेरे दासों के खून का पलटा लेना हमारे देखते उन्हें मालूम हो जाए।।"));
hinsuperobject.biblemap.put("b19_79_11", new hinsuperobject(19,79,11,"बन्धुओं का कराहना तेरे कान तक पहुंचे; घात होनेवालों को अपने भुजबल के द्वारा बचा।"));
hinsuperobject.biblemap.put("b19_79_12", new hinsuperobject(19,79,12,"और हे प्रभु, हमारे पड़ोसियों ने जो तेरी निन्दा की है, उसका सातगुणा बदला उनको दे!"));
hinsuperobject.biblemap.put("b19_79_13", new hinsuperobject(19,79,13,"तब हम जो तेरी प्रजा और तेरी चराई की भेड़ें हैं, तेरा धन्यवाद सदा करते रहेंगे; और पीढ़ी से पीढ़ी तक तेरा गुणानुवाद करते रहेंगें।।"));
hinsuperobject.biblemap.put("b19_80_1", new hinsuperobject(19,80,1,"हे इस्त्राएल के चरवाहे, तू जो यूसुफ की अगुवाई भेड़ों की सी करता है, कान लगा! तू जो करूबों पर विराजमान है, अपना तेज दिखा!"));
hinsuperobject.biblemap.put("b19_80_2", new hinsuperobject(19,80,2,"एप्रैम, बिन्यामीन, और मनश्शे के साम्हने अपना पराक्रम दिखाकर, हमारा उठ्ठार करने को आ!"));
hinsuperobject.biblemap.put("b19_80_3", new hinsuperobject(19,80,3,"हे परमेश्वर, हम को ज्यों के त्यों कर दे; और अपने मुख का प्रकाश चमका, तब हमारा उठ्ठार हो जाएगा!"));
hinsuperobject.biblemap.put("b19_80_4", new hinsuperobject(19,80,4,"हे सेनाओं के परमेश्वर यहोवा, तू कब तक अपनी प्रजा की प्रार्थना पर क्रोधित रहेगा?"));
hinsuperobject.biblemap.put("b19_80_5", new hinsuperobject(19,80,5,"तू ने आंसुओं को उनका आहार कर दिया, और मटके भर भरके उन्हें आंसु पिलाए हैं।"));
hinsuperobject.biblemap.put("b19_80_6", new hinsuperobject(19,80,6,"तू हमें हमारे पड़ोसियों के झगड़ने का कारण कर देता है; और हमारे शत्रु मनमाने ठट्ठा करते हैं।।"));
hinsuperobject.biblemap.put("b19_80_7", new hinsuperobject(19,80,7,"हे सेनाओं के परमेश्वर, हम को ज्यों के त्यों कर दे; और अपने मुख का प्रकाश हम पर चमका, तब हमारा उठ्ठार हो जाएगा।।"));
hinsuperobject.biblemap.put("b19_80_8", new hinsuperobject(19,80,8,"तू मि  से एक दाखलता ले आया; और अन्यजातियों को निकालकर उसे लगा दिया।"));
hinsuperobject.biblemap.put("b19_80_9", new hinsuperobject(19,80,9,"तू ने उसके लिये स्थान तैयार किया है; और उस ने जड़ पकड़ी और फैलकर देश को भर दिया।"));
hinsuperobject.biblemap.put("b19_80_10", new hinsuperobject(19,80,10,"उसकी छाया पहाड़ों पर फैल गई, और उसकी डालियां ईश्वर के देवदारों के समान हुई;"));
hinsuperobject.biblemap.put("b19_80_11", new hinsuperobject(19,80,11,"उसकी शाखाएं समुद्र तक बढ़ गई, और उसके अंकुर महानद तक फैल गए।"));
hinsuperobject.biblemap.put("b19_80_12", new hinsuperobject(19,80,12,"फिर तू ने उसके बाड़ों को क्यों गिरा दिया, कि सब बटोही उसके फलों को तोड़ते है?"));
hinsuperobject.biblemap.put("b19_80_13", new hinsuperobject(19,80,13,"वनसूअर उसको नाश किए डालता है, और मैदान के सब पशु उसे चर जाते हैं।।"));
hinsuperobject.biblemap.put("b19_80_14", new hinsuperobject(19,80,14,"हे सेनाओं के परमेश्वर, फिर आ! स्वर्ग से ध्यान देकर देख, और इस दाखलता की सुधि ले,"));
hinsuperobject.biblemap.put("b19_80_15", new hinsuperobject(19,80,15,"ये पौधा तू ने अपने दहिने हाथ से लगाया, और जो लता की शाखा तू ने अपने लिये दृढ़ की है।"));
hinsuperobject.biblemap.put("b19_80_16", new hinsuperobject(19,80,16,"वह जल गई, वह कट गई है; तेरी घुड़की से वे नाश होते हैं।"));
hinsuperobject.biblemap.put("b19_80_17", new hinsuperobject(19,80,17,"तेरे दहिने हाथ के सम्भाले हुअ पुरूष पर तेरा हाथ रखा रहे, उस आदमी पर, जिसे तू ने अपने लिये दृढ़ किया है।"));
hinsuperobject.biblemap.put("b19_80_18", new hinsuperobject(19,80,18,"तब हम लोग तुझ से न मुड़ेंगे: तू हम को जिला, और हम तुझ से प्रार्थना कर सकेंगे।"));
hinsuperobject.biblemap.put("b19_80_19", new hinsuperobject(19,80,19,"हे सेनाओं के परमेश्वर यहोवा, हम को ज्यों का त्यों कर दे! और अपने मुख का प्रकाश हम पर चमका, तब हमारा उठ्ठार हो जाएगा!"));
hinsuperobject.biblemap.put("b19_81_1", new hinsuperobject(19,81,1,"परमेश्वर जो हमारा बल है, उसका गीत आनन्द से गाओ; याकूब के परमेश्वर का जयजयकार करो!"));
hinsuperobject.biblemap.put("b19_81_2", new hinsuperobject(19,81,2,"भजन उठाओ, डफ और मधुर बजनेवाली वीणा और सारंगी को ले आओ।"));
hinsuperobject.biblemap.put("b19_81_3", new hinsuperobject(19,81,3,"नये चाँद के दिन, और पूणमासी को हमारे पर्व के दिन नरसिंगा फुको।"));
hinsuperobject.biblemap.put("b19_81_4", new hinsuperobject(19,81,4,"क्योंकि यह इस्त्राएल के लिये विधि, और याकूब के परमेश्वर का ठहराया हुआ नियम है।"));
hinsuperobject.biblemap.put("b19_81_5", new hinsuperobject(19,81,5,"इसको उस ने यूसुफ में चितौनी की रीति पर उस समय चलाया, जब वह मिस्त्र देश के विरूद्ध चला।। वहां मैं ने एक अनजानी भाषा सुनी;"));
hinsuperobject.biblemap.put("b19_81_6", new hinsuperobject(19,81,6,"मैं ने उनके कन्धों पर से बोझ को उतार दिया; उनका टोकरी ढोना छुट गया।"));
hinsuperobject.biblemap.put("b19_81_7", new hinsuperobject(19,81,7,"तू ने संकट में पड़कर पुकारा, तब मैं ने तुझे छुड़ाया; बादल गरजने के गुप्त स्थान में से मैं ने तेरी सुनी, और मरीबा नाम सोते के पास तेरी परीक्षा की। (सेला)"));
hinsuperobject.biblemap.put("b19_81_8", new hinsuperobject(19,81,8,"हे मेरी प्रजा, सुन, मैं तुझे चिता देता हूं! हे इस्त्राएल भला हो कि तू मेरी सुने!"));
hinsuperobject.biblemap.put("b19_81_9", new hinsuperobject(19,81,9,"तेरे बीच में पराया ईश्वर न हो; और न तू किसी पराए देवता को दणडवत् करना!"));
hinsuperobject.biblemap.put("b19_81_10", new hinsuperobject(19,81,10,"तेरा परमेश्वर यहोवा मैं हूं, जो तुझे मिस्त्र देश से निकाल लाया है। तू अपना मुंह पसार, मैं उसे भर दूंगा।।"));
hinsuperobject.biblemap.put("b19_81_11", new hinsuperobject(19,81,11,"परन्तु मेरी प्रजा ने मेरी न सुनी; इस्त्राएल ने मुझ को न चाहा।"));
hinsuperobject.biblemap.put("b19_81_12", new hinsuperobject(19,81,12,"इसलिये मैं ने उसको उसके मन के हठ पर छोड़ दिया, कि वह अपनी ही युक्तियों के अनुसार चले।"));
hinsuperobject.biblemap.put("b19_81_13", new hinsuperobject(19,81,13,"यदि मेरी प्रजा मेरी सुने, यदि इस्त्राएल मेरे मार्गों पर चले,"));
hinsuperobject.biblemap.put("b19_81_14", new hinsuperobject(19,81,14,"तो क्षण भर में उनके शत्रुओं को दबाऊं, और अपना हाथ उनके द्रोहयों के विरूद्ध चलाऊं।"));
hinsuperobject.biblemap.put("b19_81_15", new hinsuperobject(19,81,15,"यहोवा के बैरी तो उस के वश में हो जाते, और वे सदाकाल बने रहते हैं।"));
hinsuperobject.biblemap.put("b19_81_16", new hinsuperobject(19,81,16,"और वह उनको उत्तम से उत्तम गेहूं खिलाता, और मैं चट्टान में के मधु से उनको तृप्त करूं।।"));
hinsuperobject.biblemap.put("b19_82_1", new hinsuperobject(19,82,1,"परमेश्वर की सभा में परमेश्वर ही खड़ा है; वह ईश्वरों के बीच में न्याय करता है।"));
hinsuperobject.biblemap.put("b19_82_2", new hinsuperobject(19,82,2,"तुम लोग कब तक टेढ़ा न्याय करते और दुष्टों का पक्ष लेते रहोगे?"));
hinsuperobject.biblemap.put("b19_82_3", new hinsuperobject(19,82,3,"कंगाल और अनाथों का न्याय चुकाओ, दीन दरिद्र का विचार धर्म से करो।"));
hinsuperobject.biblemap.put("b19_82_4", new hinsuperobject(19,82,4,"कंगाल और निर्धन को बचा लो; दुष्टों के हाथ से उन्हें छुड़ाओ।।"));
hinsuperobject.biblemap.put("b19_82_5", new hinsuperobject(19,82,5,"वे न तो कुछ समझते और न कुछ बूझते हैं, परन्तु अन्धेरे में चलते फिरते रहते हैं; पृथ्वी की पूरी नीव हिल जाती है।।"));
hinsuperobject.biblemap.put("b19_82_6", new hinsuperobject(19,82,6,"मैं ने कहा था कि तुम ईश्वर हो, और सब के सब परमप्रधान के पुत्रा हो;"));
hinsuperobject.biblemap.put("b19_82_7", new hinsuperobject(19,82,7,"तौभी तुम मनुष्यों की नाई मरोगे, और किसी प्रधान के समान गिर जाओगे।।"));
hinsuperobject.biblemap.put("b19_82_8", new hinsuperobject(19,82,8,"हे परमेश्वर उठ, पृथ्वी का न्याय कर; क्योंकि तू ही सब जातियों को अपने भाग में लेगा!"));
hinsuperobject.biblemap.put("b19_83_1", new hinsuperobject(19,83,1,"हे परमेश्वर मौन न रह; हे ईश्वर चुप न रह, और न शांत रह!"));
hinsuperobject.biblemap.put("b19_83_2", new hinsuperobject(19,83,2,"क्योंकि देख तेरे शत्रु धूम मचा रहे हैं; और तेरे बैरियों ने सिर उठाया है।"));
hinsuperobject.biblemap.put("b19_83_3", new hinsuperobject(19,83,3,"वे चतुराई से तेरी प्रजा की हानि की सम्मति करते, और तेरे रक्षित लोगों के विरूद्ध युक्तियां निकालते हैं।"));
hinsuperobject.biblemap.put("b19_83_4", new hinsuperobject(19,83,4,"उन्हों ने कहा, आओ, हम उनको ऐसा नाश करें कि राज्य भी मिट जाए; और इस्त्राएल का नाम आगे को स्मरण न रहे।"));
hinsuperobject.biblemap.put("b19_83_5", new hinsuperobject(19,83,5,"उन्हों ने एक मन होकर युक्ति निकाली है, और तेरे ही विरूद्ध वाचा बान्धी है।"));
hinsuperobject.biblemap.put("b19_83_6", new hinsuperobject(19,83,6,"ये तो एदोम के तम्बूवाले और इश्माइली, मोआबी और हुग्री,"));
hinsuperobject.biblemap.put("b19_83_7", new hinsuperobject(19,83,7,"गबाली, अम्मोनी, अमालेकी, और सोर समेत पलिश्ती हैं।"));
hinsuperobject.biblemap.put("b19_83_8", new hinsuperobject(19,83,8,"इनके संग अश्शूरी भी मिल गए हैं; उन से भी लोतवंशियों को सहारा मिला है।"));
hinsuperobject.biblemap.put("b19_83_9", new hinsuperobject(19,83,9,"इन से ऐसा कर जैसा मिद्यानियों से, और कीशोन नाले में सीसरा और याबीन से किया था, जो एन्दोर में नाश हुए,"));
hinsuperobject.biblemap.put("b19_83_10", new hinsuperobject(19,83,10,"और भूमि के लिये खाद बन गए।"));
hinsuperobject.biblemap.put("b19_83_11", new hinsuperobject(19,83,11,"इनके रईसों को ओरेब और जाएब सरीखे, और इनके सब प्रधानों को जेबह और सल्मुन्ना के समान कर दे,"));
hinsuperobject.biblemap.put("b19_83_12", new hinsuperobject(19,83,12,"जिन्हों ने कहा था, कि हम परमेश्वर की चराइयों के अधिकारी आप ही हो जाएं।।"));
hinsuperobject.biblemap.put("b19_83_13", new hinsuperobject(19,83,13,"हे मेरे परमेश्वर इनको बवन्डर की धूलि, वा पवन से उड़ाए हुए भूसे के समान कर दे।"));
hinsuperobject.biblemap.put("b19_83_14", new hinsuperobject(19,83,14,"उस आग की नाई जो वन को भस्म करती है, और उस लौ की नाई जो पहाड़ों को जला देती है,"));
hinsuperobject.biblemap.put("b19_83_15", new hinsuperobject(19,83,15,"तू इन्हे अपनी आंधी से भाग दे, और अपने बवन्डर से घबरा दे!"));
hinsuperobject.biblemap.put("b19_83_16", new hinsuperobject(19,83,16,"इनके मुंह को अति लज्जित कर, कि हे यहोवा ये तेरे नाम को ढूंढ़ें।"));
hinsuperobject.biblemap.put("b19_83_17", new hinsuperobject(19,83,17,"ये सदा के लिये लज्जित और घबराए रहें इनके मुंह काले हों, और इनका नाश हो जाए,"));
hinsuperobject.biblemap.put("b19_83_18", new hinsuperobject(19,83,18,"जिस से यह जानें कि केवल तू जिसका नाम यहोवा है, सारी पृथ्वी के ऊपर परमप्रधान है।।"));
hinsuperobject.biblemap.put("b19_84_1", new hinsuperobject(19,84,1,"हे सेनाओं के यहोवा, तेरे निवास क्या ही प्रिय हैं!"));
hinsuperobject.biblemap.put("b19_84_2", new hinsuperobject(19,84,2,"मेरा प्राण यहोवा के आंगनों की अभिलाषा करते करते मूर्र्छित हो चला; मेरा तन मन दोंनो जीवते ईश्वर को पुकार रहे।।"));
hinsuperobject.biblemap.put("b19_84_3", new hinsuperobject(19,84,3,"हे सेनाओं के यहोवा, हे मेरे राजा, और मेरे परमेश्वर, तेरी वेदियों मे गौरैया ने अपना बसेरा और शूपाबेनी ने घोंसला बना लिया है जिस में वह अपने बच्चे रखे।"));
hinsuperobject.biblemap.put("b19_84_4", new hinsuperobject(19,84,4,"क्या ही धन्य हैं वे, जो तेरे भवन में रहते हैं; वे तेरी स्तुति निरन्तर करते रहेंगे।।"));
hinsuperobject.biblemap.put("b19_84_5", new hinsuperobject(19,84,5,"क्या ही धन्य है, वह मनुष्य जो तुझ से शक्ति पाता है, और वे जिनको सिरयोन की सड़क की सुधि रहती है।"));
hinsuperobject.biblemap.put("b19_84_6", new hinsuperobject(19,84,6,"वें रोने की तराई में जाते हुए उसको सोतों का स्थान बनाते हैं; फिर बरसात की अगली वृष्टि उसमें आशीष ही आशीष उपजाती है।"));
hinsuperobject.biblemap.put("b19_84_7", new hinsuperobject(19,84,7,"वे बल पर बल पाते जाते हैं; उन में से हर एक जन सिरयोन में परमेश्वर को अपना मुंह दिखाएगा।।"));
hinsuperobject.biblemap.put("b19_84_8", new hinsuperobject(19,84,8,"हे सेनाओं के परमेश्वर यहोवा, मेरी प्रार्थना सुन, हे याकूब के परमेश्वर, कान लगा!"));
hinsuperobject.biblemap.put("b19_84_9", new hinsuperobject(19,84,9,"हे परमेश्वर, हे हमारी ढ़ाल, दृष्टि कर; और अपने अभिषिक्ति का मुख देख!"));
hinsuperobject.biblemap.put("b19_84_10", new hinsuperobject(19,84,10,"क्योंकि तेरे आंगनों में का एक दिन और कहीं के हजार दिन से उत्तम है। दुष्टों के डेरों में वास करने से अपने परमेश्वर के भवन की डेवढ़ी पर खड़ा रहना ही मुझे अधिक भावता है।"));
hinsuperobject.biblemap.put("b19_84_11", new hinsuperobject(19,84,11,"क्योंकि यहोवा परमेश्वर सूर्य और ढाल है; यहोवा अनुग्रह करेगा, और महिमा देगा; और जो लोग खरी चाल चलते हैं; उन से वह कोई अच्छा पदार्थ रख न छोड़ेगा।"));
hinsuperobject.biblemap.put("b19_84_12", new hinsuperobject(19,84,12,"हे सेनाओं के यहोवा, क्या ही धन्य वह मनुष्य है, जो तुझ पर भरोसा रखता है!"));
hinsuperobject.biblemap.put("b19_85_1", new hinsuperobject(19,85,1,"हे यहोवा, तू अपने देश पर प्रसन्न हुआ, याकूब को बन्धुआई से लौटा ले आया है।"));
hinsuperobject.biblemap.put("b19_85_2", new hinsuperobject(19,85,2,"तू ने अपनी प्रजा के अधर्म को क्षमा किया है; और उसके सब पापों को ढांप दिया है।"));
hinsuperobject.biblemap.put("b19_85_3", new hinsuperobject(19,85,3,"तू ने अपने रोष को शान्त किया है; और अपने भड़के हुए कोप को दूर किया है।।"));
hinsuperobject.biblemap.put("b19_85_4", new hinsuperobject(19,85,4,"हे हमारे उठ्ठारकर्त्ता परमेश्वर हम को फेर, और अपना क्रोध हम पर से दूर कर!"));
hinsuperobject.biblemap.put("b19_85_5", new hinsuperobject(19,85,5,"क्या तू हम पर सदा कोपित रहेगा? क्या तू पीढ़ी से पीढ़ी तक कोप करता रहेगा?"));
hinsuperobject.biblemap.put("b19_85_6", new hinsuperobject(19,85,6,"क्या तू हम को फिर न जिलाएगा, कि तेरी प्रजा तुझ में आनन्द करे?"));
hinsuperobject.biblemap.put("b19_85_7", new hinsuperobject(19,85,7,"हे यहोवा अपनी करूणा हमें दिखा, और तू हमारा उठ्ठार कर।।"));
hinsuperobject.biblemap.put("b19_85_8", new hinsuperobject(19,85,8,"मैं कान लगाए रहूंगा, कि ईश्वर यहोवा क्या कहता है, वह तो अपनी प्रजा से जो उसके भक्त है, शान्ति की बातें कहेगा; परन्तु वे फिरके मूर्खता न करने लगें।"));
hinsuperobject.biblemap.put("b19_85_9", new hinsuperobject(19,85,9,"निश्चय उसके डरवैयों के उठ्ठार का समय निकट है, तब हमारे देश में महिमा का निवास होगा।।"));
hinsuperobject.biblemap.put("b19_85_10", new hinsuperobject(19,85,10,"करूणा और सच्चाई आपस में मिल गई हैं; धर्म और मेल ने आपस में चुम्बन किया हैं।"));
hinsuperobject.biblemap.put("b19_85_11", new hinsuperobject(19,85,11,"पृथ्वी में से सच्चाई उगती और स्वर्ग से धर्म झुकता है।"));
hinsuperobject.biblemap.put("b19_85_12", new hinsuperobject(19,85,12,"फिर यहोवा उत्तम पदार्थ देगा, और हमारी भूमि अपनी उपज देगी।"));
hinsuperobject.biblemap.put("b19_85_13", new hinsuperobject(19,85,13,"धर्म उसके आगे आगे चलेगा, और उसके पांवों के चिन्हों को हमारे लिये मार्ग बनाएगा।।"));
hinsuperobject.biblemap.put("b19_86_1", new hinsuperobject(19,86,1,"हे यहोवा कान लगाकर मेरी सुन ले, क्योंकि मैं दीन और दरिद्र हूं।"));
hinsuperobject.biblemap.put("b19_86_2", new hinsuperobject(19,86,2,"मेरे प्राण की रक्षा कर, क्योंकि मैं भक्त हूं; तू मेरा परमेश्वर है, इसलिये अपने दास का, जिसका भरोसा तुझ पर है, उठ्ठार कर।"));
hinsuperobject.biblemap.put("b19_86_3", new hinsuperobject(19,86,3,"हे प्रभु मुझ पर अनुग्रह कर, क्योंकि मैं तुझी को लगातार पुकारता रहता हूं।"));
hinsuperobject.biblemap.put("b19_86_4", new hinsuperobject(19,86,4,"अपने दास के मन को आनन्दित कर, क्योंकि हे प्रभु, मैं अपना मन तेरी ही ओर लगाता हूं।"));
hinsuperobject.biblemap.put("b19_86_5", new hinsuperobject(19,86,5,"क्योंकि हे प्रभु, तू भला और क्षमा करनेवाला है, और जितने तुझे पुकारते हैं उन सभों के लिये तू अति करूणामय है।"));
hinsuperobject.biblemap.put("b19_86_6", new hinsuperobject(19,86,6,"हे यहोवा मेरी प्रार्थना की ओर कान लगा, और मेरे गिड़गिड़ाने को ध्यान से सुन।"));
hinsuperobject.biblemap.put("b19_86_7", new hinsuperobject(19,86,7,"संकट के दिन मैं तुझ को पुकारूंगा, क्योंकि तू मेरी सुन लेगा।।"));
hinsuperobject.biblemap.put("b19_86_8", new hinsuperobject(19,86,8,"हे प्रभु देवताओं में से कोई भी तेरे तुल्य नहीं, और ने किसी के काम तेरे कामों के बराबर हैं।"));
hinsuperobject.biblemap.put("b19_86_9", new hinsuperobject(19,86,9,"हे प्रभु जितनी जातियों को तू ने बनाया है, सब आकर तेरे साम्हने दणडवत् करेंगी, और तेरे नाम की महिमा करेंगी।"));
hinsuperobject.biblemap.put("b19_86_10", new hinsuperobject(19,86,10,"क्योंकि तू महान् और आश्चर्यकर्म करनेवाला है, केवल तू ही परमेश्वर है।"));
hinsuperobject.biblemap.put("b19_86_11", new hinsuperobject(19,86,11,"हे यहोवा अपना मार्ग मुझे दिखा, तब मैं तेरे सत्य मार्ग पर चलूंगा, मुझ को एक चित्त कर कि मैं तेरे नाम का भय मानूं।"));
hinsuperobject.biblemap.put("b19_86_12", new hinsuperobject(19,86,12,"हे प्रभु हे मेरे परमेश्वर मैं अपने सम्पूर्ण मन से तेरा धन्यवाद करूंगा, और तेरे नाम की महिमा सदा करता रहूंगा।"));
hinsuperobject.biblemap.put("b19_86_13", new hinsuperobject(19,86,13,"क्योंकि तेरी करूणा मेरे ऊपर बड़ी है; और तू ने मुझ को अधोलोक की तह में जाने से बचा लिया है।।"));
hinsuperobject.biblemap.put("b19_86_14", new hinsuperobject(19,86,14,"हे परमेश्वर अभिमानी लोग तो मेरे विरूद्ध उठे हैं, और बलात्कारियों का समाज मेरे प्राण का खोजी हुआ है, और वे तेरा कुछ विचार नहीं रखते।"));
hinsuperobject.biblemap.put("b19_86_15", new hinsuperobject(19,86,15,"परन्तु प्रभु दयालु और अनुग्रहकारी ईश्वर है, तू विलम्ब से कोप करनेवाला और अति करूणामय है।"));
hinsuperobject.biblemap.put("b19_86_16", new hinsuperobject(19,86,16,"मेरी ओर फिरकर मुझ पर अनुग्रह कर; अपने दास को तू शक्ति दे, और अपनी दासी के पुत्रा का उठ्ठार कर।।"));
hinsuperobject.biblemap.put("b19_86_17", new hinsuperobject(19,86,17,"मुझे भलाई का कोई लक्षण दिखा, जिसे देखकर मेरे बैरी निराश हों, क्योंकि हे यहोवा तू ने आप मेरी सहायता की और मुझे शान्ति दी है।।"));
hinsuperobject.biblemap.put("b19_87_1", new hinsuperobject(19,87,1,"उसकी नेव पवित्रा पर्वतों में है;"));
hinsuperobject.biblemap.put("b19_87_2", new hinsuperobject(19,87,2,"और यहोवा सिरयोन के फाटकों को याकूब के सारे निवासों से बढ़कर प्रीति रखता है।"));
hinsuperobject.biblemap.put("b19_87_3", new hinsuperobject(19,87,3,"हे परमेश्वर के नगर, तेरे विषय महिमा की बातें कही गई हैं।"));
hinsuperobject.biblemap.put("b19_87_4", new hinsuperobject(19,87,4,"मैं अपने जान- पहचानवालों से रहब और बाबेल की भी चर्चा करूंगा; पलिश्त, सोर और कूश को देखो, यह वहां उत्पन्न हुआ था।"));
hinsuperobject.biblemap.put("b19_87_5", new hinsuperobject(19,87,5,"और सिरयोन के विषय में यह कहा जाएगा, कि अमुक अमुक मनुष्य उस में उत्पन्न हुआ था; और परमप्रधान आप ही उसको स्थिर रखेगा।"));
hinsuperobject.biblemap.put("b19_87_6", new hinsuperobject(19,87,6,"यहोेवा जब देश देश के लोगों के नाम लिखकर गिन लेगा, तब यह कहेगा, कि यह वहां उत्पन्न हुआ था।।"));
hinsuperobject.biblemap.put("b19_87_7", new hinsuperobject(19,87,7,"गवैये और नृतक दोनों कहेंगे कि हमारे सब सोते तुझी में पाए जाते हैं।।"));
hinsuperobject.biblemap.put("b19_88_1", new hinsuperobject(19,88,1,"हे मेरे उठ्ठारकर्त्ता परमेश्वर यहोवा, मैं दिन को और रात को तेरे आगे चिल्लाता आया हूं।"));
hinsuperobject.biblemap.put("b19_88_2", new hinsuperobject(19,88,2,"मेरी प्रार्थना तुझ तक पहुंचे, मेरे चिल्लाने की ओर कान लगा!"));
hinsuperobject.biblemap.put("b19_88_3", new hinsuperobject(19,88,3,"क्योंकि मेरा प्राण क्लेश में भरा हुआ है, और मेरा प्राण अधोलोक के निकट पहुंचा है।"));
hinsuperobject.biblemap.put("b19_88_4", new hinsuperobject(19,88,4,"मैं कबर में पड़नेवालों में गिना गया हूं; मैं बलहीन पुरूष के समान हो गया हूं।"));
hinsuperobject.biblemap.put("b19_88_5", new hinsuperobject(19,88,5,"मैं मुर्दों के बीच छोड़ा गया हूं, और जो घात होकर कबर में पड़े हैं, जिनको तू फिर स्मरण नहीं करता और वे तेरी सहायता रहित हैं, उनके समान मैं हो गया हूं।"));
hinsuperobject.biblemap.put("b19_88_6", new hinsuperobject(19,88,6,"तू ने मुझे गड़हे के तल ही में, अन्धेरे और गहिरे स्थान में रखा है।"));
hinsuperobject.biblemap.put("b19_88_7", new hinsuperobject(19,88,7,"तेरी जलजलाहट मुझी पर बनी हुई है, और तू ने अपने सब तरंगों से मुझे दु:ख दिया है;"));
hinsuperobject.biblemap.put("b19_88_8", new hinsuperobject(19,88,8,"तू ने मेरे पहिचानवालों को मुझ से दूर किया है; और मुझ को उनकी दृष्टि में घिनौना किया है। मैं बन्दी हूं और निकल नही सकता;"));
hinsuperobject.biblemap.put("b19_88_9", new hinsuperobject(19,88,9,"दु:ख भोगते भोगते मेरी आंखे धुन्धला गई। हे यहोवा मैं लगातार तुझे पुकारता और अपने हाथ तेरी ओर फैलाता आया हूं।"));
hinsuperobject.biblemap.put("b19_88_10", new hinsuperobject(19,88,10,"क्या तू मुर्दों के लिये अदभुत् काम करेगा? क्या मरे लोग उठकर तेरा धन्यवाद करेंगे?"));
hinsuperobject.biblemap.put("b19_88_11", new hinsuperobject(19,88,11,"क्या कबर में तेरी करूणा का, और विनाश की दशा में तेरी सच्चाई का वर्णन किया जाएगा?"));
hinsuperobject.biblemap.put("b19_88_12", new hinsuperobject(19,88,12,"क्या तेरे अदभुत् काम अन्धकार में, वा तेरा धर्म विश्वासघात की दशा में जाना जाएगा?"));
hinsuperobject.biblemap.put("b19_88_13", new hinsuperobject(19,88,13,"परन्तु हे यहोवा, मैं ने तेरी दोहाई दी है; और भोर को मेरी प्रार्थना तुझ तक पहुंचेगी।"));
hinsuperobject.biblemap.put("b19_88_14", new hinsuperobject(19,88,14,"हे यहोवा, तू मुझ को क्यों छोड़ता है? तू अपना मुख मुझ से क्यों छिपाता रहता है?"));
hinsuperobject.biblemap.put("b19_88_15", new hinsuperobject(19,88,15,"मैं बचपन ही से दु:खी वरन अधमुआ हूं, तुझ से भय खाते मैं अति व्याकुल हो गया हूं।"));
hinsuperobject.biblemap.put("b19_88_16", new hinsuperobject(19,88,16,"तेरा क्रोध मुझ पर पड़ा है; उस भय से मैं मिट गया हूं।"));
hinsuperobject.biblemap.put("b19_88_17", new hinsuperobject(19,88,17,"वह दिन भर जल की नाई मुझे घेरे रहता है; वह मेरे चारों ओर दिखाई देता है।"));
hinsuperobject.biblemap.put("b19_88_18", new hinsuperobject(19,88,18,"तू ने मित्रा और भाईबन्धु दोनों को मुझ से दूर किया है; और मेरे जान- पहिचानवालों को अन्धकार में डाल दिया है।।"));
hinsuperobject.biblemap.put("b19_89_1", new hinsuperobject(19,89,1,"मैं यहोवा की सारी करूणा के विषय सदा गाता रहूंगा; मैं तेरी सच्चाई पीढ़ी पीढ़ी तक जताता रहूंगा।"));
hinsuperobject.biblemap.put("b19_89_2", new hinsuperobject(19,89,2,"क्योंकि मैं ने कहा है, तेरी करूणा सदा बनी रहेगी, तू स्वर्ग में अपनी सच्चाई को स्थिर रखेगा।"));
hinsuperobject.biblemap.put("b19_89_3", new hinsuperobject(19,89,3,"मैं ने अपने चुने हुए से वाचा बान्धी है, मैं ने अपने दास दाऊद से शपथ खाई है,"));
hinsuperobject.biblemap.put("b19_89_4", new hinsuperobject(19,89,4,"कि मैं तेरे वंश को सदा स्थिर रखूंगा; और तेरी राजगद्दी को पीढ़ी पीढ़ी तक बनाए रखूंगा।"));
hinsuperobject.biblemap.put("b19_89_5", new hinsuperobject(19,89,5,"हे यहोवा, स्वर्ग में तेरे अद्भुत काम की, और पवित्रों की सभा में तेरी सच्चाई की प्रशंसा होगी।"));
hinsuperobject.biblemap.put("b19_89_6", new hinsuperobject(19,89,6,"क्योंकि आकाशमण्डल में यहोवा के तुल्य कौन ठहरेगा? बलवन्तों के पुत्रों में से कौन है जिसके साथ यहोवा की उपमा दी जाएगी?"));
hinsuperobject.biblemap.put("b19_89_7", new hinsuperobject(19,89,7,"ईश्वर पवित्रों की गोष्ठी में अत्यन्त प्रतिष्ठा के योग्य, और अपने चारों ओर सब रहनेवालों से अधिक भययोग्य है।"));
hinsuperobject.biblemap.put("b19_89_8", new hinsuperobject(19,89,8,"हे सेनाओं के परमेश्वर यहोवा, हे याह, तेरे तुल्य कौन सामर्थी है? तेरी सच्चाई तो तेरे चारों ओर है!"));
hinsuperobject.biblemap.put("b19_89_9", new hinsuperobject(19,89,9,"समुद्र के गर्व को तू ही तोड़ता है; जब उसके तरंग उठते हैं, तब तू उनको शान्त कर देता है।"));
hinsuperobject.biblemap.put("b19_89_10", new hinsuperobject(19,89,10,"तू ने रहब को घात किए हुए के समान कुचल डाला, और अपने शत्रुओं को अपने बाहुबल से तितर बितर किया है।"));
hinsuperobject.biblemap.put("b19_89_11", new hinsuperobject(19,89,11,"आकाश तेरा है, पृथ्वी भी तेरी है; जगत और जो कुछ उस में है, उसे तू ही ने स्थिर किया है।"));
hinsuperobject.biblemap.put("b19_89_12", new hinsuperobject(19,89,12,"उत्तर और दक्खिन को तू ही ने सिरजा; ताबोर और हेर्मोन तेरे नाम का जयजयकार करते हैं।"));
hinsuperobject.biblemap.put("b19_89_13", new hinsuperobject(19,89,13,"तेरी भुजा बलवन्त है; तेरा हाथ शक्तिमान और तेरा दहिना हाथ प्रबल है।"));
hinsuperobject.biblemap.put("b19_89_14", new hinsuperobject(19,89,14,"तेरे सिंहासन का मूल, धर्म और न्याय है; करूणा और सच्चाई तेरे आगे आगे चलती है।"));
hinsuperobject.biblemap.put("b19_89_15", new hinsuperobject(19,89,15,"क्या ही धन्य है वह समाज जो आनन्द के ललकार को पहिचानता है; हे हयोवा वे लोग मेरे मुख के प्रकाश में चलते हैं,"));
hinsuperobject.biblemap.put("b19_89_16", new hinsuperobject(19,89,16,"वे तेरे नाम के हेतु दिन भर मगन रहते हैं, और तेरे धर्म के कारण महान हो जाते हैं।"));
hinsuperobject.biblemap.put("b19_89_17", new hinsuperobject(19,89,17,"क्योंकि तू उनके बल की शोभा है, और अपनी प्रसन्नता से हमारे सींग को ऊंचा करेगा।"));
hinsuperobject.biblemap.put("b19_89_18", new hinsuperobject(19,89,18,"क्योंकि हमारी ढाल यहोवा की ओर से है हमारा राजा इस्राएल के पवित्रा की ओर से है।।"));
hinsuperobject.biblemap.put("b19_89_19", new hinsuperobject(19,89,19,"एक समय तू ने अपने भक्त को दर्शन देकर बातें की; और कहा, मैं ने सहायता करने का भार एक वीर पर रखा है, और प्रजा में से एक को चुनकर बढ़ाया है।"));
hinsuperobject.biblemap.put("b19_89_20", new hinsuperobject(19,89,20,"मैं ने अपने दास दाऊद को लेकर, अपने पवित्रा तेल से उसका अभिषेक किया है।"));
hinsuperobject.biblemap.put("b19_89_21", new hinsuperobject(19,89,21,"मेरा हाथ उसके साथ बना रहेगा, और मेरी भुजा उसे दृढ़ रखेगी।"));
hinsuperobject.biblemap.put("b19_89_22", new hinsuperobject(19,89,22,"शत्रु उसको तंग करने न पाएगा, और न कुटिल जल उसको दु:ख देने पाएगा।"));
hinsuperobject.biblemap.put("b19_89_23", new hinsuperobject(19,89,23,"मैं उसके द्रोहियों को उसके साम्हने से नाश करूंगा, और उसके बैरियों पर विपत्ति डालूंगा।"));
hinsuperobject.biblemap.put("b19_89_24", new hinsuperobject(19,89,24,"परन्तु मेरी सच्चाई और करूणा उस पर बनी रहेंगी, और मेरे नाम के द्वारा उसका सींग ऊंचा हो जाएगा।"));
hinsuperobject.biblemap.put("b19_89_25", new hinsuperobject(19,89,25,"मैं समुद्र को उसके हाथ के नीचे और महानदों को उसके दहिने हाथ के नीचे कर दूंगा।"));
hinsuperobject.biblemap.put("b19_89_26", new hinsuperobject(19,89,26,"वह मुझे पुकारके कहेगा, कि तू मेरा पिता है, मेरा ईश्वर और मेरे बचने की चट्टान है।"));
hinsuperobject.biblemap.put("b19_89_27", new hinsuperobject(19,89,27,"फिर मैं उसको अपना पहिलौठा, और पृथ्वी के राजाओं पर प्रधान ठहराऊंगा।"));
hinsuperobject.biblemap.put("b19_89_28", new hinsuperobject(19,89,28,"मैं अपनी करूणा उस पर सदा बनाए रहूंगा, और मेरी वाचा उसके लिये अटल रहेगी।"));
hinsuperobject.biblemap.put("b19_89_29", new hinsuperobject(19,89,29,"मैं उसके वंश को सदा बनाए रखूंगा, और उसकी राजगद्दी स्वर्ग के समान सर्वदा बनी रहेगी।"));
hinsuperobject.biblemap.put("b19_89_30", new hinsuperobject(19,89,30,"यदि उसके वंश के लोग मेरी व्यवस्था को छोड़ें और मेरे नियमों के अनुसार न चलें,"));
hinsuperobject.biblemap.put("b19_89_31", new hinsuperobject(19,89,31,"यदि वे मेरी विधियों का उल्लंघन करें, और मेरी आज्ञाओं को न मानें,"));
hinsuperobject.biblemap.put("b19_89_32", new hinsuperobject(19,89,32,"तो मैं उनके अपराध का दण्ड सोंटें से, और उनके अधर्म का दण्ड कोड़ों से दूंगा।"));
hinsuperobject.biblemap.put("b19_89_33", new hinsuperobject(19,89,33,"परन्तु मैं अपनी करूणा उस पर से हटाऊंगा, और न सच्चाई त्यागकर झूठा ठहरूंगा।"));
hinsuperobject.biblemap.put("b19_89_34", new hinsuperobject(19,89,34,"मैं अपनी वाचा न तोडूंगा, और जो मेरे मुंह से निकल चुका है, उसे न बदलूंगा।"));
hinsuperobject.biblemap.put("b19_89_35", new hinsuperobject(19,89,35,"एक बार मैं अपनी पवित्राता की शपथ खा चुका हूं; मैं दाऊद को कभी धोखा न दूंगा।"));
hinsuperobject.biblemap.put("b19_89_36", new hinsuperobject(19,89,36,"उसका वंश सर्वदा रहेगा, और उसकी राजगद्दी सूर्य की नाई मेरे सम्मुख ठहरी रहेगी।"));
hinsuperobject.biblemap.put("b19_89_37", new hinsuperobject(19,89,37,"वह चन्द्रमा की नाईं, और आकाशमण्डल के विश्वासयोग्य साक्षी की नाई सदा बना रहेगा।"));
hinsuperobject.biblemap.put("b19_89_38", new hinsuperobject(19,89,38,"तौभी तू ने अपने अभिषिक्त को छोड़ा और उसे तज दिया, और उस पर अति क्रोध किया है।"));
hinsuperobject.biblemap.put("b19_89_39", new hinsuperobject(19,89,39,"तू अपने दास के साथ की वाचा से घिनाया, और उसके मुकुट को भूमि पर गिराकर अशुद्ध किया है।"));
hinsuperobject.biblemap.put("b19_89_40", new hinsuperobject(19,89,40,"तू ने उसके सब बाड़ों को तेड़ डाला है, और उसके गढ़ों को उजाड़ दिया है।"));
hinsuperobject.biblemap.put("b19_89_41", new hinsuperobject(19,89,41,"सब बटोही उसको लूट लेते हैं, और उसके पड़ोसियों से उसकी नामधराई होती है।"));
hinsuperobject.biblemap.put("b19_89_42", new hinsuperobject(19,89,42,"तू ने उसके द्रोहियों को प्रबल किया; और उसके सब शत्रुओं को आनन्दित किया; और उसके सब शत्रुओं को आनन्दित किया है।"));
hinsuperobject.biblemap.put("b19_89_43", new hinsuperobject(19,89,43,"फिर तू उसकी तलवार की धार को मोड़ देता है, और युद्ध में उसके पांव जमने नहीं देता।"));
hinsuperobject.biblemap.put("b19_89_44", new hinsuperobject(19,89,44,"तू ने उसका तेज हर लिया है और उसके सिंहासन को भूमि पर पटक दिया है।"));
hinsuperobject.biblemap.put("b19_89_45", new hinsuperobject(19,89,45,"तू ने उसकी जवानी को घटाया, और उसको लज्जा से ढांप दिया है।।"));
hinsuperobject.biblemap.put("b19_89_46", new hinsuperobject(19,89,46,"हे यहोवा तू कब तक लगातार मूंह फेरे रहेगा, तेरी जलजलाहट कब तक आग की नाईं भड़की रहेगी।।"));
hinsuperobject.biblemap.put("b19_89_47", new hinsuperobject(19,89,47,"मेरा स्मरण कर, कि मैं कैसा अनित्य हूं, तू ने सब मनुष्यों को क्यों व्यर्थ सिरजा है?"));
hinsuperobject.biblemap.put("b19_89_48", new hinsuperobject(19,89,48,"कौन पुरूष सदा अमर रहेगा? क्या कोई अपने प्राण को अधोलोक से बचा सकता है?"));
hinsuperobject.biblemap.put("b19_89_49", new hinsuperobject(19,89,49,"हे प्रभु तेरी प्राचीनकाल की करूणा कहां रही, जिसके विषय में तू ने अपनी सच्चाई की शपथ दाऊद से खाई थी?"));
hinsuperobject.biblemap.put("b19_89_50", new hinsuperobject(19,89,50,"हे प्रभु अपने दासों की नामधराई की सुधि कर; मैं तो सब सामर्थी जातियों का बोझ लिए रहता हूं।"));
hinsuperobject.biblemap.put("b19_89_51", new hinsuperobject(19,89,51,"तेरे उन शत्रुओं ने तो हे यहोवा तेरे अभिषिक्त के पीछे पड़कर उसकी नामधराई की है।।"));
hinsuperobject.biblemap.put("b19_89_52", new hinsuperobject(19,89,52,"यहोवा सर्वदा धन्य रहेगा! आमीन फिर आमीन।।"));
hinsuperobject.biblemap.put("b19_90_1", new hinsuperobject(19,90,1,"हे प्रभु, तू पीढ़ी से पीढ़ी तक हमारे लिये धाम बना है।"));
hinsuperobject.biblemap.put("b19_90_2", new hinsuperobject(19,90,2,"इस से पहिले कि पहाड़ उत्पन्न हुए, वा तू ने पृथ्वी और जगत की रचना की, वरन अनादिकाल से अनन्तकाल तक तू ही ईश्वर है।।"));
hinsuperobject.biblemap.put("b19_90_3", new hinsuperobject(19,90,3,"तू मनुष्य को लौटाकर चूर करता है, और कहता है, कि हे आदमियों, लौट आओ!"));
hinsuperobject.biblemap.put("b19_90_4", new hinsuperobject(19,90,4,"क्योंकि हजार वर्ष तेरी दृष्टि में ऐसे हैं, जैसा कल का दिन जो बीत गया, वा रात का एक पहर।।"));
hinsuperobject.biblemap.put("b19_90_5", new hinsuperobject(19,90,5,"तू मनुष्यों को धारा में बहा देता है; वे स्वप्न से ठहरते हैं, वे भोर को बढ़नेवाली घास के समान होते हैं।"));
hinsuperobject.biblemap.put("b19_90_6", new hinsuperobject(19,90,6,"वह भोर को फूलती और बढ़ती है, और सांझ तक काटकर मुर्झा जाती है।।"));
hinsuperobject.biblemap.put("b19_90_7", new hinsuperobject(19,90,7,"क्योंकि हम तेरे क्रोध से नाश हुए हैं; और तेरी जलजलाहट से घबरा गए हैं।"));
hinsuperobject.biblemap.put("b19_90_8", new hinsuperobject(19,90,8,"तू ने हमारे अधर्म के कामों से अपने सम्मुख, और हमारे छिपे हुए पापों को अपने मुख की ज्योति में रखा है।।"));
hinsuperobject.biblemap.put("b19_90_9", new hinsuperobject(19,90,9,"क्योंकि हमारे सब दिन तेरे क्रोध में बीत जाते हैं, हम अपने वर्ष शब्द की नाई बिताते हैं।"));
hinsuperobject.biblemap.put("b19_90_10", new hinsuperobject(19,90,10,"हमारी आयु के वर्ष सत्तर तो होते हैं, और चाहे बल के कारण अस्सी वर्ष के भी हो जाएं, तौभी उनका घमण्ड केवल नष्ट और शोक ही शोक है; क्योंकि वह जल्दी कट जाती है, और हम जाते रहते हैं।"));
hinsuperobject.biblemap.put("b19_90_11", new hinsuperobject(19,90,11,"तेरे क्रोध की शक्ति को और तेरे भय के योग्य रोष को कौन समझता है?"));
hinsuperobject.biblemap.put("b19_90_12", new hinsuperobject(19,90,12,"हम को अपने दिन गिनने की समझ दे कि हम बुद्धिमान हो जाएं।।"));
hinsuperobject.biblemap.put("b19_90_13", new hinsuperobject(19,90,13,"हे यहोवा लौट आ! कब तक? और अपने दासों पर तरस खा!"));
hinsuperobject.biblemap.put("b19_90_14", new hinsuperobject(19,90,14,"भोर को हमें अपनी करूणा से तृप्त कर, कि हम जीवन भर जयजयकार और आनन्द करते रहें।"));
hinsuperobject.biblemap.put("b19_90_15", new hinsuperobject(19,90,15,"जितने दिन तू ने हमें दु:ख देता आया, और जितने वर्ष हम क्लेश भोगते आए हैं उतने ही वर्ष हम को आनन्द दे।"));
hinsuperobject.biblemap.put("b19_90_16", new hinsuperobject(19,90,16,"तेरी काम तेरे दासों को, और तेरा प्रताप उनकी सन्तान पर प्रगट हो।"));
hinsuperobject.biblemap.put("b19_90_17", new hinsuperobject(19,90,17,"और हमारे परमेश्वर यहोवा की मनोहरता हम पर प्रगट हो, तू हमारे हाथों का काम हमारे लिये दृढ़ कर, हमारे हाथों के काम को दृढ़ कर।।"));
hinsuperobject.biblemap.put("b19_91_1", new hinsuperobject(19,91,1,"जो परमप्रधान के छाए हुए स्थान में बैठा रहे, वह सर्वशक्तिमान की छाया में ठिकाना पाएगा।"));
hinsuperobject.biblemap.put("b19_91_2", new hinsuperobject(19,91,2,"मैं यहोवा के विषय कहूंगा, कि वह मेरा शरणस्थान और गढ़ है; वह मेरा परमेश्वर है, मैं उस पर भरोसा रखूंगा।"));
hinsuperobject.biblemap.put("b19_91_3", new hinsuperobject(19,91,3,"वह तो मुझे बहेलिये के जाल से, और महामारी से बचाएगा;"));
hinsuperobject.biblemap.put("b19_91_4", new hinsuperobject(19,91,4,"वह तुझे अपने पंखों की आड़ में ले लेगा, और तू उसके पैरों के नीचे शरण पाएगा; उसकी सच्चाई तेरे लिये ढाल और झिलम ठहरेगी।"));
hinsuperobject.biblemap.put("b19_91_5", new hinsuperobject(19,91,5,"तू न रात के भय से डरेगा, और न उस तीर से जो दिन को उड़ता है,"));
hinsuperobject.biblemap.put("b19_91_6", new hinsuperobject(19,91,6,"न उस मरी से जो अन्धेरे में फैलती है, और न उस महारोग से जो दिन दुपहरी में उजाड़ता है।।"));
hinsuperobject.biblemap.put("b19_91_7", new hinsuperobject(19,91,7,"तेरे निकट हजार, और तेरी दहिनी ओर दस हजार गिरेंगे; परन्तु वह तेरे पास न आएगा।"));
hinsuperobject.biblemap.put("b19_91_8", new hinsuperobject(19,91,8,"परनतु तू अपनी आंखों की दृष्टि करेगा और दुष्टों के अन्त को देखेगा।।"));
hinsuperobject.biblemap.put("b19_91_9", new hinsuperobject(19,91,9,"हे यहोवा, तू मेरा शरणस्थान ठहरा है। तू ने जो परमप्रधान को अपना धाम मान लिया है,"));
hinsuperobject.biblemap.put("b19_91_10", new hinsuperobject(19,91,10,"इसलिये कोई विपत्ति तुझ पर न पड़ेगी, न कोई दु:ख तेरे डेरे के निकट आएगा।।"));
hinsuperobject.biblemap.put("b19_91_11", new hinsuperobject(19,91,11,"क्योंकि वह अपने दूतों को तेरे निमित्त आज्ञा देगा, कि जहां कहीं तू जाए वे तेरी रक्षा करें।"));
hinsuperobject.biblemap.put("b19_91_12", new hinsuperobject(19,91,12,"वे तुझ को हाथों हाथ उठा लेंगे, ऐसा न हो कि तेरे पांवों में पत्थर से ठेस लगे।"));
hinsuperobject.biblemap.put("b19_91_13", new hinsuperobject(19,91,13,"तू सिंह और नाग को कुचलेगा, तू जवान सिंह और अजगर को लताड़ेगा।"));
hinsuperobject.biblemap.put("b19_91_14", new hinsuperobject(19,91,14,"उस ने जो मुझ से स्नेह किया है, इसलिये मैं उसको छुड़ाऊंगा; मैं उसको ऊंचे स्थान पर रखूंगा, क्योंकि उस ने मेरे नाम को जान लिया है।"));
hinsuperobject.biblemap.put("b19_91_15", new hinsuperobject(19,91,15,"जब वह मुझ को पुकारे, तब मैं उसकी सुनूंगा; संकट में मैं उसके संग रहूंगा, मैं उसको बचाकर उसकी महिमा बढ़ाऊंगा।"));
hinsuperobject.biblemap.put("b19_91_16", new hinsuperobject(19,91,16,"मैं उसको दीर्घायु से तृप्त करूंगा, और अपने किए हुए उद्धार का दर्शन दिखाऊंगा।।"));
hinsuperobject.biblemap.put("b19_92_1", new hinsuperobject(19,92,1,"यहोवा का धन्यवाद करना भला है, हे परमप्रधान, तेरे नाम का भजन गाना;"));
hinsuperobject.biblemap.put("b19_92_2", new hinsuperobject(19,92,2,"प्रात:काल को तेरी करूणा, और प्रति रात तेरी सच्चाई का प्रचार करना,"));
hinsuperobject.biblemap.put("b19_92_3", new hinsuperobject(19,92,3,"दस तारवाले बाजे और सारंगी पर, और वीणा पर गम्भीर स्वर से गाना भला है।"));
hinsuperobject.biblemap.put("b19_92_4", new hinsuperobject(19,92,4,"क्योंकि, हे यहोवा, तू ने मुझ को अपने काम से आनन्दित किया है; और मैं तेरे हाथों के कामों के कारण जयजयकार करूंगा।।"));
hinsuperobject.biblemap.put("b19_92_5", new hinsuperobject(19,92,5,"हे यहोवा, तेरे काम क्या ही बड़े है! तेरी कल्पनाएं बहुत गम्भीर है!"));
hinsuperobject.biblemap.put("b19_92_6", new hinsuperobject(19,92,6,"पशु समान मनुष्य इसको नहीं समझता, और मूर्ख इसका विचार नहीं करता:"));
hinsuperobject.biblemap.put("b19_92_7", new hinsuperobject(19,92,7,"कि दुष्ट जो घास की नाईं फूलते- फलते हैं, और सब अनर्थकारी जो प्रफुल्लित होते हैं, यह इसलिये होता है, कि वे सर्वदा के लिये नाश हो जाएं,"));
hinsuperobject.biblemap.put("b19_92_8", new hinsuperobject(19,92,8,"परन्तु हे यहोवा, तू सदा विराजमान रहेगा।"));
hinsuperobject.biblemap.put("b19_92_9", new hinsuperobject(19,92,9,"क्योंकि ये यहोवा, तेरे शत्रु, हां तेरे शत्रु नाश होंगे; सब अनर्थकारी तितर बितर होंगे।।"));
hinsuperobject.biblemap.put("b19_92_10", new hinsuperobject(19,92,10,"परन्तु मेरा सींग तू ने जंगली सांढ़ का सा ऊंचा किया है; मैं टटके तेल से चुपड़ा गया हूं।"));
hinsuperobject.biblemap.put("b19_92_11", new hinsuperobject(19,92,11,"और मैं अपने द्रोहियों पर दृष्टि करके, और उन कुकर्मियों का हाल मेरे विरूद्ध उठे थे, सुनकर सन्तुष्ट हुआ हूं।।"));
hinsuperobject.biblemap.put("b19_92_12", new hinsuperobject(19,92,12,"धर्मी लोग खजूर की नाई फूले फलेंगे, और लबानोन के देवदार की नाई बढ़ते रहेंगे।"));
hinsuperobject.biblemap.put("b19_92_13", new hinsuperobject(19,92,13,"वे यहोवा के भवन में रोपे जाकर, हमारे परमेश्वर के आंगनों में फूले फलेंगे।"));
hinsuperobject.biblemap.put("b19_92_14", new hinsuperobject(19,92,14,"वे पुराने होने पर भी फलते रहेंगे, और रस भरे और लहलहाते रहेंगे,"));
hinsuperobject.biblemap.put("b19_92_15", new hinsuperobject(19,92,15,"जिस से यह प्रगट हो, कि यहोवा सीधा है; वह मेरी चट्टान है, और उस में कुटिलता कुछ भी नहीं।।"));
hinsuperobject.biblemap.put("b19_93_1", new hinsuperobject(19,93,1,"यहोवा राजा है; उस ने माहात्म्य का पहिरावा पहिना है; यहोवा पहिरावा पहिने हुए, और सामर्थ्य का फेटा बान्धे है। इस कारण जगत स्थिर है, वह नहीं टलने का।"));
hinsuperobject.biblemap.put("b19_93_2", new hinsuperobject(19,93,2,"हे यहोवा, तेरी राजगद्दी अनादिकाल से स्थिर है, तू सर्वदा से है।।"));
hinsuperobject.biblemap.put("b19_93_3", new hinsuperobject(19,93,3,"हे यहोवा, महानदों का कोलाहल हो रहा है, महानदों का बड़ा शब्द हो रहा है, महानद गरजते हैं।"));
hinsuperobject.biblemap.put("b19_93_4", new hinsuperobject(19,93,4,"महासागर के शब्द से, और समुद्र की महातरंगों से, विराजमान यहोवा अधिक महान है।।"));
hinsuperobject.biblemap.put("b19_93_5", new hinsuperobject(19,93,5,"तेरी चितौनियां अति विश्वासयोग्य हैं; हे यहोवा तेरे भवन को युग युग पवित्राता ही शोभा देती है।।"));
hinsuperobject.biblemap.put("b19_94_1", new hinsuperobject(19,94,1,"हे यहोवा, हे पलटा लेनेवाले ईश्वर, हे पलटा लेनेवाले ईश्वर, अपना तेज दिखा!"));
hinsuperobject.biblemap.put("b19_94_2", new hinsuperobject(19,94,2,"हे पृथ्वी के न्यायी उठ; और घमण्ड़ियों को बदला दे!"));
hinsuperobject.biblemap.put("b19_94_3", new hinsuperobject(19,94,3,"हे यहोवा, दुष्ट लोग कब तक, दुष्ट लोग कब तक डींग मारते रहेंगे?"));
hinsuperobject.biblemap.put("b19_94_4", new hinsuperobject(19,94,4,"वे बकते और ढ़िठाई की बातें बोलते हैं, सब अनर्थकारी बड़ाई मारते हैं।"));
hinsuperobject.biblemap.put("b19_94_5", new hinsuperobject(19,94,5,"हे यहोवा, वे तेरी प्रजा को पीस डालते हैं, वे तेरे निज भाग को दु:ख देते हैं।"));
hinsuperobject.biblemap.put("b19_94_6", new hinsuperobject(19,94,6,"वे विधवा और परदेशी का घात करते, और बपमूओं को मार डालते हैं;"));
hinsuperobject.biblemap.put("b19_94_7", new hinsuperobject(19,94,7,"और कहते हैं, कि याह न देखेगा, याकूब का परमेश्वर विचार न करेगा।।"));
hinsuperobject.biblemap.put("b19_94_8", new hinsuperobject(19,94,8,"तुम जो प्रजा में पशु सरीखे हो, विचार करो; और हे मूर्खों तुम कब तक बुद्धिमान हो जाओगे?"));
hinsuperobject.biblemap.put("b19_94_9", new hinsuperobject(19,94,9,"जिस ने कान दिया, क्या वह आप नहीं सुनता? जिस ने आंख रची, क्या वह आप नहीं देखता?"));
hinsuperobject.biblemap.put("b19_94_10", new hinsuperobject(19,94,10,"जो जाति जाति को ताड़ना देता, और मनुष्य को ज्ञान सिखाता है, क्या वह न समझाएगा?"));
hinsuperobject.biblemap.put("b19_94_11", new hinsuperobject(19,94,11,"यहोवा मनुष्य की कल्पनाओं को तो जानता है कि वे मिथ्या हैं।।"));
hinsuperobject.biblemap.put("b19_94_12", new hinsuperobject(19,94,12,"हे यहा, क्या ही धन्य है वह पुरूष जिसको तू ताड़ना देता है, और अपनी व्यवस्था सिखाता है,"));
hinsuperobject.biblemap.put("b19_94_13", new hinsuperobject(19,94,13,"क्योंकि तू उसको विपत्ति के दिनों में उस समय तक चैन देता रहता है, जब तक दुष्टों के लिये गड़हा नहीं खोदा जाता।"));
hinsuperobject.biblemap.put("b19_94_14", new hinsuperobject(19,94,14,"क्योंकि यहोवा अपनी प्रजा को न तजेगा, वह अपने निज भाग को न छोड़ेगा;"));
hinsuperobject.biblemap.put("b19_94_15", new hinsuperobject(19,94,15,"परन्तु न्याय फिर धर्म के अनुसार किया जाएगा, और सारे सीधे मनवाले उसके पीछे पीछे हो लेंगे।।"));
hinsuperobject.biblemap.put("b19_94_16", new hinsuperobject(19,94,16,"कुकर्मियों के विरूद्ध मेरी ओर कौन खड़ा होगा? मेरी ओर से अनर्थकारियों का कौन साम्हना करेगा?"));
hinsuperobject.biblemap.put("b19_94_17", new hinsuperobject(19,94,17,"यदि यहोवा मेरा सहायक न होता, तो क्षण भर में मुझे चुपचाप होकर रहना पड़ता।"));
hinsuperobject.biblemap.put("b19_94_18", new hinsuperobject(19,94,18,"जब मैं ने कहा, कि मेरा पांव फिसलने लगा है, तब हे यहोवा, तेरी करूणा ने मुझे थाम लिया।"));
hinsuperobject.biblemap.put("b19_94_19", new hinsuperobject(19,94,19,"जब मेरे मन में बहुत सी चिन्ताएं होती हैं, तब हे यहोवा, तेरी दी हुई शान्ति से मुझ को सुख होता है।"));
hinsuperobject.biblemap.put("b19_94_20", new hinsuperobject(19,94,20,"क्या तेरे और दुष्टों के सिंसाहन के बीच सन्धि होगी, जो कानून की आड़ में उत्पात मचाते हैं?"));
hinsuperobject.biblemap.put("b19_94_21", new hinsuperobject(19,94,21,"वे धर्मी का प्राण लेने को दल बान्धते हैं, और निर्दोष को प्राणदण्ड देते हैं।"));
hinsuperobject.biblemap.put("b19_94_22", new hinsuperobject(19,94,22,"परन्तु यहोवा मेरा गढ़, और मेरा परमेश्वर मेरी शरण की चट्टान ठहरा है।"));
hinsuperobject.biblemap.put("b19_94_23", new hinsuperobject(19,94,23,"और उस ने उनका अनर्थ काम उन्हीं पर लौटाया है, और वह उन्हें उन्हीं की बुराई के द्वारा सन्यानाश करेगा; हमारा परमेश्वर यहोवा उनको सत्यानाश करेगा।।"));
hinsuperobject.biblemap.put("b19_95_1", new hinsuperobject(19,95,1,"आओ हम यहोवा के लिये ऊंचे स्वर से गाएं, अपने उद्धार की चट्टान का जयजयकार करें!"));
hinsuperobject.biblemap.put("b19_95_2", new hinsuperobject(19,95,2,"हम धन्यवाद करते हुए उसके सम्मुख आएं, और भजन गाते हुए उसका जयजयकार करें!"));
hinsuperobject.biblemap.put("b19_95_3", new hinsuperobject(19,95,3,"क्योंकि यहोवा महान ईश्वर है, और सब देवताओं के ऊपर महान राजा है।"));
hinsuperobject.biblemap.put("b19_95_4", new hinsuperobject(19,95,4,"पृथ्वी के गहिरे स्थान उसी के हाथ में हैं; और पहाड़ों की चोटियां भी उसी की हैं।"));
hinsuperobject.biblemap.put("b19_95_5", new hinsuperobject(19,95,5,"समुद्र उसका है, और उसी ने उसको बनाया, और स्थल भी उसी के हाथ का रख है।।"));
hinsuperobject.biblemap.put("b19_95_6", new hinsuperobject(19,95,6,"आओ हम झुककर दण्डवत् करें, और अपने कर्ता यहोवा के साम्हने घुटने टेकें!"));
hinsuperobject.biblemap.put("b19_95_7", new hinsuperobject(19,95,7,"क्योंकि वही हमारा परमेश्वर है, और हम उसकी चराई की प्रजा, और उसके हाथ की भेड़ें हैं।। भला होता, कि आज तुम उसकी बात सुनते!"));
hinsuperobject.biblemap.put("b19_95_8", new hinsuperobject(19,95,8,"अपना अपना हृदय ऐसा कठोर मत करो, जैसा मरीबा में, वा मस्सा के दिन जंगल में हुआ था,"));
hinsuperobject.biblemap.put("b19_95_9", new hinsuperobject(19,95,9,"जब तुम्हारे पुरखाओं ने मुझे परखा, उन्हों ने मुझ को जांचा और मेरे काम को भी देखा।"));
hinsuperobject.biblemap.put("b19_95_10", new hinsuperobject(19,95,10,"चालीस वर्ष तक मैं उस पीढ़ी के लोगों से रूठा रहा, और मैं ने कहा, ये तो भरमनेवाले मन के हैं, और इन्हों ने मेरे मार्गों को नहीं पहिचाना।"));
hinsuperobject.biblemap.put("b19_95_11", new hinsuperobject(19,95,11,"इस कारण मैं ने क्रोध में आकर शपथ खाई कि ये मेरे विश्रामस्थान में कभी प्रवेश न करने पाएंगे।।"));
hinsuperobject.biblemap.put("b19_96_1", new hinsuperobject(19,96,1,"यहोवा के लिये एक नया गीत गाओ, हे सारी पृथ्वी के लोगों यहोवा के लिये गाओ!"));
hinsuperobject.biblemap.put("b19_96_2", new hinsuperobject(19,96,2,"यहोवा के लिये गाओ, उसके नाम को धन्य कहो; दिन दिन उसके किए हुए उद्धार का शुभसमाचार सुनाते रहो।"));
hinsuperobject.biblemap.put("b19_96_3", new hinsuperobject(19,96,3,"अन्य जातियों में उसकी महिमा का, और देश देश के लोगों में उसके आश्चर्यकर्मों का वर्णन करो।"));
hinsuperobject.biblemap.put("b19_96_4", new hinsuperobject(19,96,4,"क्योंकि यहोवा महान और अति स्तुति के योग्य है; वह तो सब देवताओं से अधिक भययोग्य है।"));
hinsuperobject.biblemap.put("b19_96_5", new hinsuperobject(19,96,5,"क्योंकि देश देश के सब देवता तो मूरतें ही हैं; परन्तु यहोवा ही ने स्वर्ग को बनाया है।"));
hinsuperobject.biblemap.put("b19_96_6", new hinsuperobject(19,96,6,"उसके चारों और विभव और ऐश्वर्य है; उसके पवित्रास्थान में सामर्थ्य और शोभा है।"));
hinsuperobject.biblemap.put("b19_96_7", new hinsuperobject(19,96,7,"हे देश देश के कुलों, यहोवा का गुणानुवाद करो, यहोवा की महिमा और सामर्थ्य को मानो!"));
hinsuperobject.biblemap.put("b19_96_8", new hinsuperobject(19,96,8,"यहोवा के नाम की ऐसी महिमा करो जो उसके योग्य है; भेंट लेकर उसके आंगनों में आओ!"));
hinsuperobject.biblemap.put("b19_96_9", new hinsuperobject(19,96,9,"पवित्राता से शोभायमान होकर यहोवा को दण्डवत करो; हे सारी पृथ्वी के लोगों उसके साम्हने कांपते रहो!"));
hinsuperobject.biblemap.put("b19_96_10", new hinsuperobject(19,96,10,"जाति जाति में कहो, यहोवा राजा हुआ है! और जगत ऐसा स्थिर है, कि वह टलने का नहीं; वह देश देश के लोगों का न्याय सीधाई से करेगा।।"));
hinsuperobject.biblemap.put("b19_96_11", new hinsuperobject(19,96,11,"आकाश आनन्द करे, और पृथ्वी मगन हो; समुद्र और उस में की सब वस्तुएं गरज उठें;"));
hinsuperobject.biblemap.put("b19_96_12", new hinsuperobject(19,96,12,"मैदान और जो कुछ उस में है, वह प्रफुल्लित हो; उसी समय वन के सारे वृक्ष जयजयकार करेंगे।"));
hinsuperobject.biblemap.put("b19_96_13", new hinsuperobject(19,96,13,"यह यहोवा के साम्हने हो, क्योंकि वह आनेवाला है। वह पृथ्वी का न्याय करने को आने वाला है, वह धर्म से जगत का, और सच्चाई से देश देश के लोगों का न्याय करेगा।।"));
hinsuperobject.biblemap.put("b19_97_1", new hinsuperobject(19,97,1,"यहोवा राजा हुआ है, पृथ्वी मगन हो; और द्वीप जो बहुतेरे हैं, वह भी आनन्द करें!"));
hinsuperobject.biblemap.put("b19_97_2", new hinsuperobject(19,97,2,"बादल और अन्धकार उसके चारों ओर हैं; उसके सिंहासन का मूल धर्म और न्याय है।"));
hinsuperobject.biblemap.put("b19_97_3", new hinsuperobject(19,97,3,"उसके आगे आगे आग चलती हुइ उसके द्रोहियों को चारों ओर भस्म करती है।"));
hinsuperobject.biblemap.put("b19_97_4", new hinsuperobject(19,97,4,"उसकी बिजलियों से जगल प्रकाशित हुआ, पृथ्वी देखकर थरथरा गई है!"));
hinsuperobject.biblemap.put("b19_97_5", new hinsuperobject(19,97,5,"पहाड़ यहोवा के साम्हने, मोम की नाई पिघल गए, अर्थात् सारी पृथ्वी के परमेश्वर के साम्हने।।"));
hinsuperobject.biblemap.put("b19_97_6", new hinsuperobject(19,97,6,"आकाश ने उसके धर्म की साक्षी दी; और देश देश के सब लोगों ने उसकी महिमा देखी है।"));
hinsuperobject.biblemap.put("b19_97_7", new hinsuperobject(19,97,7,"जितने खुदी हुई मूत्तियों की उपासना करते और मूरतों पर फूलते हैं, वे लज्जित हों; हे सब देवताओं तुम उसी को दण्डवत् करो।"));
hinsuperobject.biblemap.put("b19_97_8", new hinsuperobject(19,97,8,"सिरयोन सुनकर आनन्दित हुई, और यहूदा की बेटियां मगन हुई; हे यहोवा, यह तेरे नियमों के कारण हुआ।"));
hinsuperobject.biblemap.put("b19_97_9", new hinsuperobject(19,97,9,"क्योंकि हे यहोवा, तू सारी पृथ्वी के ऊपर परमप्रधान है; तू सारे देवताओं से अधिक महान ठहरा है।"));
hinsuperobject.biblemap.put("b19_97_10", new hinsuperobject(19,97,10,"हे यहोवा के प्रेमियों, बुराई से घृणा करो; वह अपने भक्तों के प्राणो की रक्षा करता, और उन्हें दुष्टों के हाथ से बचाना है।"));
hinsuperobject.biblemap.put("b19_97_11", new hinsuperobject(19,97,11,"धर्मी के लिये ज्योति, और सीधे मनवालों के लिये आनन्द बोया गया है।"));
hinsuperobject.biblemap.put("b19_97_12", new hinsuperobject(19,97,12,"हे धर्मियों यहोवा के कारण आनन्दित हो; और जिस पवित्रा नाम से उसका स्मरण होता है, उसका धन्यवाद करो!"));
hinsuperobject.biblemap.put("b19_98_1", new hinsuperobject(19,98,1,"यहोवा के लिये एक नया गीत गाओ, क्योंकि उस ने आश्चर्यकर्म किए है! उसके दहिने हाथ और पवित्रा भुजा ने उसके लिये उद्धार किया है!"));
hinsuperobject.biblemap.put("b19_98_2", new hinsuperobject(19,98,2,"यहोवा ने अपना किया हुआ उद्धार प्रकाशित किया, उस ने अन्यजातियों की दृष्टि में अपना धर्म प्रगट किया है।"));
hinsuperobject.biblemap.put("b19_98_3", new hinsuperobject(19,98,3,"उस ने इस्राएल के घराने पर की अपनी करूणा और सच्चाई की सुधि ली, और पृथ्वी के सब दूर दूर देशों ने हमारे परमेश्वर का किया हुआ उद्धार देखा है।।"));
hinsuperobject.biblemap.put("b19_98_4", new hinsuperobject(19,98,4,"हे सारी पृथ्वी के लोगों यहोवा का जयजयकार करो; उत्साहपूर्वक जयजयकार करो, और भजन गाओ!"));
hinsuperobject.biblemap.put("b19_98_5", new hinsuperobject(19,98,5,"वीणा बजाकर यहोवा का भजन गाओ, वीणा बजाकर भजन का स्वर सुनाओं।"));
hinsuperobject.biblemap.put("b19_98_6", new hinsuperobject(19,98,6,"तुरहियां और नरसिंगे फूंक फूंककर यहोवा राजा का जयजयकार करो।।"));
hinsuperobject.biblemap.put("b19_98_7", new hinsuperobject(19,98,7,"समुद्र औश्र उस में की सब वस्तुएं गरज उठें; जगत और उसके निवासी महाशब्द करें!"));
hinsuperobject.biblemap.put("b19_98_8", new hinsuperobject(19,98,8,"नदियां तालियां बजाएं; पहाड़ मिलकर जयजयकार करें।"));
hinsuperobject.biblemap.put("b19_98_9", new hinsuperobject(19,98,9,"यह यहोवा के साम्हने हो, क्योंकि वह पृथ्वी का न्याय करने को आनेवाला है। वह धर्म से जगत का, और सीधाई से देश देश के लोगों का न्याय करेगा।।"));
hinsuperobject.biblemap.put("b19_99_1", new hinsuperobject(19,99,1,"यहोवा राजा हुआ है; देश देश के लोग कांप उठें! वह करूबों पर विराजमान है; पृथ्वी डोल उठे!"));
hinsuperobject.biblemap.put("b19_99_2", new hinsuperobject(19,99,2,"यहोवा सिरयोन में महान है; और वह देश देश के लोगों के ऊपर प्रधान है।"));
hinsuperobject.biblemap.put("b19_99_3", new hinsuperobject(19,99,3,"वे तेरे महान और भययोग्य नाम का धन्यवाद करें! वह तो पवित्रा है।"));
hinsuperobject.biblemap.put("b19_99_4", new hinsuperobject(19,99,4,"राजा की सामर्थ्य न्याय से मेल रखती है, तू ही ने सीधाई को स्थापित किया; न्याय और धर्म को याकूब में तू ही ने चालू किया है।"));
hinsuperobject.biblemap.put("b19_99_5", new hinsuperobject(19,99,5,"हमारे परमेश्वर यहोवा को सराहो; और उसके चरणों की चौकी के साम्हने दण्डवत् करो! वह पवित्रा है!"));
hinsuperobject.biblemap.put("b19_99_6", new hinsuperobject(19,99,6,"उसके याजकों में मूसा और हारून, और उसके प्रार्थना करनेवालों में से शमूएल यहोवा को पुकारते थे, और वह उनकी सुन लेता था।"));
hinsuperobject.biblemap.put("b19_99_7", new hinsuperobject(19,99,7,"वह बादल के खम्भे में होकर उन से बातें करता था; और वे उसी चितौनियों और उसकी दी हुई विधियों पर चलते थे।।"));
hinsuperobject.biblemap.put("b19_99_8", new hinsuperobject(19,99,8,"हे हमारे परमेश्वर यहोवा तू उनकी सुन लेता था; तू उनके कामों का पलटा तो लेता था तौभी उनके लिये क्षमा करनेवाला ईश्वर था।"));
hinsuperobject.biblemap.put("b19_99_9", new hinsuperobject(19,99,9,"हमारे परमेश्वर यहोवा को सराहो, और उसके पवित्रा पर्वत पर दण्डवत् करो; क्योंकि हमारा परमेश्वर यहोवा पवित्रा है!"));
hinsuperobject.biblemap.put("b19_100_1", new hinsuperobject(19,100,1,"ृहे सारी पृथ्वी के लोगों यहोवा का जयजयकार करो!"));
hinsuperobject.biblemap.put("b19_100_2", new hinsuperobject(19,100,2,"आनन्द से यहोवा की आराधना करो! जयजयकार के साथ उसके सम्मुख आओ!"));
hinsuperobject.biblemap.put("b19_100_3", new hinsuperobject(19,100,3,"निश्चय जानो, कि यहोवा की परमेश्वर है। उसी ने हम को बनाया, और हम उसी के हैं; हम उसकी प्रजा, और उसकी चराई की भेड़ें हैं।।"));
hinsuperobject.biblemap.put("b19_100_4", new hinsuperobject(19,100,4,"उसके फाटकों से धन्यवाद, और उसके आंगनों में स्तुति करते हुए प्रवेश करो, उसका धन्यवाद करो, और उसके नाम को धन्य कहो!"));
hinsuperobject.biblemap.put("b19_100_5", new hinsuperobject(19,100,5,"क्योंकि यहोवा भला है, उसकी करूणा सदा के लिये, और उसकी सच्चाई पीढ़ी से पीढ़ी तक बनी रहती है।।"));
hinsuperobject.biblemap.put("b19_101_1", new hinsuperobject(19,101,1,"मैं करूणा और न्याय के विषय गाऊंगा; हे यहोवा, मैं तेरा ही भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_101_2", new hinsuperobject(19,101,2,"मैं बुद्धिमानी से खरे मार्ग में चलूंगा। तू मेरे पास कब आएगा! मैं अपने घर में मन की खराई के साथ अपनी चाल चलूंगा;"));
hinsuperobject.biblemap.put("b19_101_3", new hinsuperobject(19,101,3,"मैं किसी आछे काम पर चित्त न लगाऊंगा।। मैं कुमार्ग पर चलनेवालों के काम से घिन रखता हूं; ऐसे काम में मैं न लगूंगा।"));
hinsuperobject.biblemap.put("b19_101_4", new hinsuperobject(19,101,4,"टेढ़ा स्वभाव मुझ से दूर रहेगा; मैं बुराई को जानूंगा भी नहीं।।"));
hinsuperobject.biblemap.put("b19_101_5", new hinsuperobject(19,101,5,"जो छिपकर अपने पड़ोसी की चुगली खाए, उसको मैं सत्यानाश करूंगा; जिसकी आंखें चढ़ी हों और जिसका मन घमण्डी है, उसकी मैं न सहूंगा।।"));
hinsuperobject.biblemap.put("b19_101_6", new hinsuperobject(19,101,6,"मेरी आंखें देश के विश्वासयोग्य लोगों पर लगी रहेंगी कि वे मेरे संग रहें; जो खरे मार्ग पर चलता है वही मेरा टहलुआ होगा।।"));
hinsuperobject.biblemap.put("b19_101_7", new hinsuperobject(19,101,7,"जो छल करता है वह मेरे घर के भीतर न रहने पाएगा; जो झूठ बोलता है वह मेरे साम्हने बना न रहेगा।।"));
hinsuperobject.biblemap.put("b19_101_8", new hinsuperobject(19,101,8,"भोर ही भोर को मैं देश के सब दुष्टों को सत्यानाश किया करूंगा, इसलिये कि यहोवा के नगर के सब अनर्थकारियों को नाश करूं।।"));
hinsuperobject.biblemap.put("b19_102_1", new hinsuperobject(19,102,1,"हे यहोवा, मेरी प्रार्थना सुन; मेरी दोहाई तुझ तक पहुंचे!"));
hinsuperobject.biblemap.put("b19_102_2", new hinsuperobject(19,102,2,"मेरे संकट के दिन अपना मुख मुझ से न छिपा ले; अपना कान मेरी ओर लगा; जिस समय मैं पुकारूं, उसी समय फुर्ती से मेरी सुन ले!"));
hinsuperobject.biblemap.put("b19_102_3", new hinsuperobject(19,102,3,"क्योंकि मेरे दिन धुएं की नाईं उड़े जाते हैं, और मेरी हडि्डयां लुकटी के समान जल गई हैं।"));
hinsuperobject.biblemap.put("b19_102_4", new hinsuperobject(19,102,4,"मेरा मन झुलसी हुई घास की नाईं सूख गया है; और मैं अपनी रोटी खाना भूल जाता हूं।"));
hinsuperobject.biblemap.put("b19_102_5", new hinsuperobject(19,102,5,"कहरते कहरते मेरा चमड़ा हडि्डयों में सट गया है।"));
hinsuperobject.biblemap.put("b19_102_6", new hinsuperobject(19,102,6,"मैं जंगल के धनेश के समान हो गया हूं, मैं उजड़े स्थानों के उल्लू के समान बन गया हूं।"));
hinsuperobject.biblemap.put("b19_102_7", new hinsuperobject(19,102,7,"मैं पड़ा पड़ा जागता रहता हूं और गौरे के समान हो गया हूं जो छत के ऊपर अकेला बैठता है।"));
hinsuperobject.biblemap.put("b19_102_8", new hinsuperobject(19,102,8,"मेरे शत्रु लगातार मेरी नामधराई करते हैं, जो मेरे विराध की धुन में बावले हो रहे हैं, वे मेरा नाम लेकर शपथ खाते हैं।"));
hinsuperobject.biblemap.put("b19_102_9", new hinsuperobject(19,102,9,"क्योंकि मैं ने रोटी की नाईं राख खाईं और आंसू मिलाकर पानी पीता हूं।"));
hinsuperobject.biblemap.put("b19_102_10", new hinsuperobject(19,102,10,"यह तेरे क्रोध और कोप के कारण हुआ है, क्योंकि तू ने मुझे उठाया, और फिर फेंक दिया है।"));
hinsuperobject.biblemap.put("b19_102_11", new hinsuperobject(19,102,11,"मेरी आयु ढलती हुई छाया के समान है; और मैं आप घास की नाईं सूख चला हूं।।"));
hinsuperobject.biblemap.put("b19_102_12", new hinsuperobject(19,102,12,"परन्तु हे यहोवा, तू सदैव विराजमान रहेगा; और जिस नाम से तेरा स्मरण होता है, वह पीढ़ी से पीढ़ी तक बना रहेगा।"));
hinsuperobject.biblemap.put("b19_102_13", new hinsuperobject(19,102,13,"तू उठकर सिरयोन पर दया करेगा; क्योंकि उस पर अनुग्रह करने का ठहराया हुअ समय आ पहुंचा है।"));
hinsuperobject.biblemap.put("b19_102_14", new hinsuperobject(19,102,14,"क्योंकि तेरे दास उसके पत्थरों को चाहते हैं, और उसकी धूलि पर तरस खाते हैं।"));
hinsuperobject.biblemap.put("b19_102_15", new hinsuperobject(19,102,15,"इसलिये अन्यजातियां यहोवा के नाम का भय मानेंगी, और पृथ्वी के सब राजा तेरे प्रताप से डरेंगे।"));
hinsuperobject.biblemap.put("b19_102_16", new hinsuperobject(19,102,16,"क्योंकि यहोवा ने सिरयोन को फिर बसाया है, और वह अपनी महिमा के साथ दिखाई देता है;"));
hinsuperobject.biblemap.put("b19_102_17", new hinsuperobject(19,102,17,"वह लाचार की प्रार्थना की ओर मुंह करता है, और उनकी प्रार्थना को तुच्छ नहीं जानता।"));
hinsuperobject.biblemap.put("b19_102_18", new hinsuperobject(19,102,18,"यह बात आनेवाली पीढ़ी के लिये लिखी जाएगी, और एक जाति जो सिरजी जाएगी वही याह की स्तुति करेगी।"));
hinsuperobject.biblemap.put("b19_102_19", new hinsuperobject(19,102,19,"क्योंकि यहोवा ने अपने ऊंचे और पवित्रा स्थान से दृष्टि करके स्वर्ग से पृथ्वी की ओर देखा है,"));
hinsuperobject.biblemap.put("b19_102_20", new hinsuperobject(19,102,20,"ताकि बन्धुओं का कराहना सुने, और घात होनवालों के बन्धन खोले;"));
hinsuperobject.biblemap.put("b19_102_21", new hinsuperobject(19,102,21,"और सिरयोन में यहोवा के नाम का वर्णन किया जाए, और यरूशलेम में उसकी स्तुति की जाए;"));
hinsuperobject.biblemap.put("b19_102_22", new hinsuperobject(19,102,22,"यह उस समय होगा जब देश देश, और राज्य राज्य के लोग यहोवा की उपासना करने को इकट्ठे होंगे।।"));
hinsuperobject.biblemap.put("b19_102_23", new hinsuperobject(19,102,23,"उस ने मुझे जीवन यात्रा में दु:ख देकर, मेरे बल और आयु को घटाया।"));
hinsuperobject.biblemap.put("b19_102_24", new hinsuperobject(19,102,24,"मैं ने कहा, हे मेरे ईश्वर, मुझे आधी आयु में न उठा ले, मेरे वर्ष पीढ़ी से पीढ़ी तक बने रहेंगे!"));
hinsuperobject.biblemap.put("b19_102_25", new hinsuperobject(19,102,25,"आदि में तू ने पृथ्वी की नेव डाली, और आकाश तेरे हाथों का बनाया हुआ है।"));
hinsuperobject.biblemap.put("b19_102_26", new hinsuperobject(19,102,26,"वह तो नाश होगा, परन्तु तू बना रहेगा; और वह सब कपड़े के समान पुराना हो जाएगा। तू उसको वस्त्रा की नाई बदलेगा, और वह तो बदल जाएगा;"));
hinsuperobject.biblemap.put("b19_102_27", new hinsuperobject(19,102,27,"परन्तु तू वहीं है, और तेरे वर्षों का अन्त नहीं होने का।"));
hinsuperobject.biblemap.put("b19_102_28", new hinsuperobject(19,102,28,"तेरे दासों की सन्तान बनी रहेगी; और उनका वंश तेरे साम्हने स्थिर रहेगा।।"));
hinsuperobject.biblemap.put("b19_103_1", new hinsuperobject(19,103,1,"हे मेरे मन, यहोवा को धन्य कह; और जो कुछ मुझ में है, वह उसके पवित्रा नाम को धन्य कहे!"));
hinsuperobject.biblemap.put("b19_103_2", new hinsuperobject(19,103,2,"हे मेरे मन, यहोवा को धन्य कह, और उसके किसी उपकार को न भूलना।"));
hinsuperobject.biblemap.put("b19_103_3", new hinsuperobject(19,103,3,"वही तो तेरे सब अधर्म को क्षमा करता, और तेरे सब रोगों को चंगा करता है,"));
hinsuperobject.biblemap.put("b19_103_4", new hinsuperobject(19,103,4,"वही तो तेरे प्राण को नाश होने से बचा लेता है, और तेरे सिर पर करूणा और दया का मुकुट बान्धता है,"));
hinsuperobject.biblemap.put("b19_103_5", new hinsuperobject(19,103,5,"वही तो तेरी लालसा को उत्तम पदार्थों से तृप्त करता है, जिस से तेरी जवानी उकाब की नाईं नई हो जाती है।।"));
hinsuperobject.biblemap.put("b19_103_6", new hinsuperobject(19,103,6,"यहोवा सब पिसे हुओं के लिये धर्म और न्याय के काम करता है।"));
hinsuperobject.biblemap.put("b19_103_7", new hinsuperobject(19,103,7,"उस ने मूसा को अपनी गति, और इस्राएलियों पर अपने काम प्रगट किए।"));
hinsuperobject.biblemap.put("b19_103_8", new hinsuperobject(19,103,8,"यहोवा दयालु और अनुग्रहकरी, विलम्ब से कोप करनेवाला और अति करूणामय है।"));
hinsuperobject.biblemap.put("b19_103_9", new hinsuperobject(19,103,9,"वह सर्वदा वादविवाद करता न रहेगा, न उसका क्रोध सदा के लिये भड़का रहेगा।"));
hinsuperobject.biblemap.put("b19_103_10", new hinsuperobject(19,103,10,"उस ने हमारे पापों के अनुसार हम से व्यवहार नहीं किया, और न हमारे अधर्म के कामों के अनुसार हम को बदला दिया है।"));
hinsuperobject.biblemap.put("b19_103_11", new hinsuperobject(19,103,11,"जैसे आकाश पृथ्वी के ऊपर ऊंचा है, वैसे ही उसकी करूणा उसके डरवैयों के ऊपर प्रबल है।"));
hinsuperobject.biblemap.put("b19_103_12", new hinsuperobject(19,103,12,"उदयाचल अस्ताचल से जितनी दूर है, उस ने हमारे अपराधों को हम से उतनी ही दूर कर दिया है।"));
hinsuperobject.biblemap.put("b19_103_13", new hinsuperobject(19,103,13,"जैसे पिता अपने बालकों पर दया करता है, वैसे ही यहोवा अपने डरवैयों पर दया करता है।"));
hinsuperobject.biblemap.put("b19_103_14", new hinsuperobject(19,103,14,"क्योंकि वह हमारी सृष्टि जानता है; और उसको स्मरण रहता है कि मनुष्य मिट्टी ही है।।"));
hinsuperobject.biblemap.put("b19_103_15", new hinsuperobject(19,103,15,"मनुष्य की आयु घास के समान होती है, वह मैदान के फूल की नाईं फूलता है,"));
hinsuperobject.biblemap.put("b19_103_16", new hinsuperobject(19,103,16,"जो पवन लगते ही ठहर नहीं सकता, और न वह अपने स्थान में फिर मिलता है।"));
hinsuperobject.biblemap.put("b19_103_17", new hinsuperobject(19,103,17,"परन्तु यहोवा की करूणा उसके डरवैयों पर युग युग, और उसका धर्म उनके नाती- पोतों पर भी प्रगट होता रहता है,"));
hinsuperobject.biblemap.put("b19_103_18", new hinsuperobject(19,103,18,"अर्थात् उन पर जो उसकी वाचा का पालन करते और उसके उपदेशों को स्मरण करके उन पर चलते हैं।।"));
hinsuperobject.biblemap.put("b19_103_19", new hinsuperobject(19,103,19,"यहोवा ने तो अपना सिंहासन स्वर्ग में स्थिर किया है, और उसका राज्य पूरी सृष्टि पर है।"));
hinsuperobject.biblemap.put("b19_103_20", new hinsuperobject(19,103,20,"हे यहोवा के दूतों, तुम जो बड़े वीर हो, और उसके वचन के मानने से उसको पूरा करते हो उसको धन्य कहो!"));
hinsuperobject.biblemap.put("b19_103_21", new hinsuperobject(19,103,21,"हे यहोवा की सारी सेनाओं, हे उसके टहलुओं, तुम जो उसकी इच्छा पूरी करते हो, उसको धन्य कहो!"));
hinsuperobject.biblemap.put("b19_103_22", new hinsuperobject(19,103,22,"हे यहोवा की सारी सृष्टि, उसके राज्य के सब स्थानों में उसको धन्य कहो। हे मेरे मन, तू यहोवा को धन्य कह!"));
hinsuperobject.biblemap.put("b19_104_1", new hinsuperobject(19,104,1,"हे मेरे मन, तू यहोवा को धन्य कह! हे मेरे परमेश्वर यहोवा, तू अत्यन्त महान है! तू विभव और ऐश्वर्य का वस्त्रा पहिने हुए है,"));
hinsuperobject.biblemap.put("b19_104_2", new hinsuperobject(19,104,2,"जो उजियाले को चादर की नाई ओढ़े रहता है, और आकाश को तम्बू के समान ताने रहता है,"));
hinsuperobject.biblemap.put("b19_104_3", new hinsuperobject(19,104,3,"जो अपनी अटारियों की कड़ियां जल में धरता है, और मेघों को अपना रथ बनाता है, और पवन के पंखों पर चलता है,"));
hinsuperobject.biblemap.put("b19_104_4", new hinsuperobject(19,104,4,"जो पवनों को अपने दूत, और धधकती आग को अपने टहलुए बनाता है।।"));
hinsuperobject.biblemap.put("b19_104_5", new hinsuperobject(19,104,5,"तू ने पृथ्वी को उसकी नीव पर स्थिर किया है, ताकि वह कभी न डगमगाए।"));
hinsuperobject.biblemap.put("b19_104_6", new hinsuperobject(19,104,6,"तू ने उसको गहिरे सागर से ढांप दिया है जैसे वस्त्रा से; जल पहाड़ों के ऊपर ठहर गया।"));
hinsuperobject.biblemap.put("b19_104_7", new hinsuperobject(19,104,7,"तेरी घुड़की से वह भाग गया; तेरे गरजने का शब्द सुनते ही, वह उतावली करके बह गया।"));
hinsuperobject.biblemap.put("b19_104_8", new hinsuperobject(19,104,8,"वह पहाड़ों पर चढ़ गया, और तराईयों के मार्ग से उस स्थान में उतर गया जिसे तू ने उसके लिये तैयार किया था।"));
hinsuperobject.biblemap.put("b19_104_9", new hinsuperobject(19,104,9,"तू ने एक सिवाना ठहराया जिसको वह नहीं लांघ सकता है, और न फिरकर स्थल को ढांप सकता है।।"));
hinsuperobject.biblemap.put("b19_104_10", new hinsuperobject(19,104,10,"तू नालों में सोतों को बहाता है; वे पहाड़ों के बीच से बहते हैं,"));
hinsuperobject.biblemap.put("b19_104_11", new hinsuperobject(19,104,11,"उन से मैदान के सब जीव- जन्तु जल पीते हैं; जंगली गदहे भी अपनी प्यास बुझा लेते हैं।"));
hinsuperobject.biblemap.put("b19_104_12", new hinsuperobject(19,104,12,"उनके पास आकाश के पक्षी बसेरा करते, और डालियों के बीच में से बोलते हैं।"));
hinsuperobject.biblemap.put("b19_104_13", new hinsuperobject(19,104,13,"तू अपनी अटारियों में से पहाड़ों को सींचता है तेरे कामों के फल से पृथ्वी तृप्त रहती है।।"));
hinsuperobject.biblemap.put("b19_104_14", new hinsuperobject(19,104,14,"तू पशुओं के लिये घास, और मनुष्यों के काम के लिये अन्नादि उपजाता है, और इस रीति भूमि से वह भोजन- वस्तुएं उत्पन्न करता है,"));
hinsuperobject.biblemap.put("b19_104_15", new hinsuperobject(19,104,15,"और दाखमधु जिस से मनुष्य का मन आनन्दित होता है, और तेल जिस से उसका मुख चमकता है, और अन्न जिस से वह सम्भल जाता है।"));
hinsuperobject.biblemap.put("b19_104_16", new hinsuperobject(19,104,16,"यहोवा के वृक्ष तृप्त रहते हैं, अर्थात् लबानोन के देवदार जो उसी के लगाए हुए हैं।"));
hinsuperobject.biblemap.put("b19_104_17", new hinsuperobject(19,104,17,"उन में चिड़ियां अपने घोंसले बनाती हैं; लगलग का बसेरा सनौवर के वृक्षों में होता है।"));
hinsuperobject.biblemap.put("b19_104_18", new hinsuperobject(19,104,18,"ऊंचे पहाड़ जंगली बकरों के लिये हैं; और चट्टानें शापानों के शरणस्थान हैं।"));
hinsuperobject.biblemap.put("b19_104_19", new hinsuperobject(19,104,19,"उस ने नियत समयों के लिये चन्द्रमा को बनाया है; सूर्य अपने अस्त होने का समय जानता है।"));
hinsuperobject.biblemap.put("b19_104_20", new hinsuperobject(19,104,20,"तू अन्धकार करता है, तब रात हो जाती है; जिस में वन के सब जीव जन्तु घूमते फिरते हैं।"));
hinsuperobject.biblemap.put("b19_104_21", new hinsuperobject(19,104,21,"जवान सिंह अहेर के लिये गरजते हैं, और ईश्वर से अपना आहार मांगते हैं।"));
hinsuperobject.biblemap.put("b19_104_22", new hinsuperobject(19,104,22,"सूर्य उदय होते ही वे चले जाते हैं और अपनी मांदों में जा बैठते हैं।"));
hinsuperobject.biblemap.put("b19_104_23", new hinsuperobject(19,104,23,"तब मनुष्य अपने काम के लिये और सन्ध्या तक परिश्रम करने के लिये निकलता है।"));
hinsuperobject.biblemap.put("b19_104_24", new hinsuperobject(19,104,24,"हे यहोवा तेरे काम अनगिनित हैं! इन सब वस्तुओं को तू ने बुद्धि से बनाया है; पृथ्वी तेरी सम्पत्ति से परिपूर्ण है।"));
hinsuperobject.biblemap.put("b19_104_25", new hinsuperobject(19,104,25,"इसी प्रकार समुद्र बड़ा और बहुत ही चौड़ा है, और उस में अनगिनित जलचरी जीव- जन्तु, क्या छोटे, क्या बड़े भरे पड़े हैं।"));
hinsuperobject.biblemap.put("b19_104_26", new hinsuperobject(19,104,26,"उस में जहाज भी आते जाते हैं, और लिब्यातान भी जिसे तू ने वहां खेलने के लिये बनाया है।।"));
hinsuperobject.biblemap.put("b19_104_27", new hinsuperobject(19,104,27,"इन सब को तेरा ही आसरा है, कि तू उनका आहार समय पर दिया करे।"));
hinsuperobject.biblemap.put("b19_104_28", new hinsuperobject(19,104,28,"तू उन्हें देता हे, वे चुन लेते हैं; तू अपनी मुट्ठी खोलता है और वे उत्तम पदार्थों से तृप्त होते हैं।"));
hinsuperobject.biblemap.put("b19_104_29", new hinsuperobject(19,104,29,"तू मुख फेर लेता है, और वे घबरा जाते हैं; तू उनकी सांस ले लेता है, और उनके प्राण छूट जाते हैं और मिट्टी में फिर मिल जाते हैं।"));
hinsuperobject.biblemap.put("b19_104_30", new hinsuperobject(19,104,30,"फिर तू अपनी ओर से सांस भेजता है, और वे सिरजे जाते हैं; और तू धरती को नया कर देता है।।"));
hinsuperobject.biblemap.put("b19_104_31", new hinsuperobject(19,104,31,"यहोवा की महिमा सदा काल बनी रहे, यहोवा अपने कामों से आन्दित होवे!"));
hinsuperobject.biblemap.put("b19_104_32", new hinsuperobject(19,104,32,"उसकी दृष्टि ही से पृथ्वी कांप उठती है, और उसके छूते ही पहाड़ों से धुआं निकलता है।"));
hinsuperobject.biblemap.put("b19_104_33", new hinsuperobject(19,104,33,"मैं जीवन भर यहोवा का गीत गाता रहूंगा; जब तक मैं बना रहूंगा तब तक अपने परमेश्वर का भजन गाता रहूंगा।"));
hinsuperobject.biblemap.put("b19_104_34", new hinsuperobject(19,104,34,"मेरा ध्यान करना, उसको प्रिय लगे, क्योंकि मैं तो याहेवा के कारण आनन्दित रहूंगा।"));
hinsuperobject.biblemap.put("b19_104_35", new hinsuperobject(19,104,35,"पापी लोग पृथ्वी पर से मिट जाएं, और दुष्ट लोग आगे को न रहें! हे मेरे मन यहोवा को धन्य कह! याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_105_1", new hinsuperobject(19,105,1,"यहोवा का धन्यवाद करो, उस से प्रार्थना करो, देश देश के लोगों में उसके कामों का प्रचार करो!"));
hinsuperobject.biblemap.put("b19_105_2", new hinsuperobject(19,105,2,"उसके लिये गीत गाओ, उसके लिये भजन गाओ, उसके सब आश्चर्यकर्मों पर ध्यान करो!"));
hinsuperobject.biblemap.put("b19_105_3", new hinsuperobject(19,105,3,"उसके पवित्रा नाम की बढ़ाई करो; यहोवा के खोजियों का हृदय आनन्दित हो!"));
hinsuperobject.biblemap.put("b19_105_4", new hinsuperobject(19,105,4,"यहोवा और उसकी सामर्थ को खोजो, उसके दर्शन के लगातार खोजी बने रहो!"));
hinsuperobject.biblemap.put("b19_105_5", new hinsuperobject(19,105,5,"उसके किए हु आश्चर्यकर्म स्मरण करो, उसके चमत्कार और निर्णय स्मरण करो!"));
hinsuperobject.biblemap.put("b19_105_6", new hinsuperobject(19,105,6,"हे उसके दास इब्राहीम के वंश, हे याकूब की सन्तान, तुम तो उसके चुने हुए हो!"));
hinsuperobject.biblemap.put("b19_105_7", new hinsuperobject(19,105,7,"वही हमारा परमेश्वर यहोवा है; पृथ्वी भर में उसके निर्णय होते हैं।"));
hinsuperobject.biblemap.put("b19_105_8", new hinsuperobject(19,105,8,"वह अपनी वाचा को सदा स्मरण रखता आया है, यह वही वचन है जो उस ने हजार पीढ़ीयों के लिये ठहराया है;"));
hinsuperobject.biblemap.put("b19_105_9", new hinsuperobject(19,105,9,"वही वाचा जो उस ने इब्राहीम के साथ बान्धी, और उसके विषय में उस ने इसहाक से शपथ खाई,"));
hinsuperobject.biblemap.put("b19_105_10", new hinsuperobject(19,105,10,"और उसी को उस ने याकूब के लिये विधि करके, और इस्राएल के लिये यह कहकर सदा की वाचा करके दृढ़ किया,"));
hinsuperobject.biblemap.put("b19_105_11", new hinsuperobject(19,105,11,"कि मैं कनान देश को तुझी को दूंगा, वह बांट में तुम्हारा निज भाग होगा।।"));
hinsuperobject.biblemap.put("b19_105_12", new hinsuperobject(19,105,12,"उस समय तो वे गिनती में थोड़े थे, वरन बहुत ही थोड़े, और उस देश में परदेशी थे।"));
hinsuperobject.biblemap.put("b19_105_13", new hinsuperobject(19,105,13,"वे एक जाति से दूसरी जाति में, और एक राज्य से दूसरे राज्य में फिरते रहे;"));
hinsuperobject.biblemap.put("b19_105_14", new hinsuperobject(19,105,14,"परन्तु उस ने किसी मनुष्य को उन पर अन्धेर करने न दिया; और वह राजाओं को उनके निमित्त यह धमकी देता था,"));
hinsuperobject.biblemap.put("b19_105_15", new hinsuperobject(19,105,15,"कि मेरे अभिषिक्तों को मत छुओं, और न मेरे नबियों की हानि करो!"));
hinsuperobject.biblemap.put("b19_105_16", new hinsuperobject(19,105,16,"फिर उस ने उस देश में अकाल भेजा, और अन्न के सब आधार को दूर कर दिया।"));
hinsuperobject.biblemap.put("b19_105_17", new hinsuperobject(19,105,17,"उस ने यूसुफ नाम एक पुरूष को उन से पहिले भेजा था, जो दास होने के लिये बेचा गया था।"));
hinsuperobject.biblemap.put("b19_105_18", new hinsuperobject(19,105,18,"लोंगों ने उसके पैरों में बेड़ियां डालकर उसे दु:ख दिया; वह लोहे की सांकलों से जकड़ा गया;"));
hinsuperobject.biblemap.put("b19_105_19", new hinsuperobject(19,105,19,"जब तक कि उसकी बात पूरी न हुई तब तक यहोवा का वचन उसे कसौटी पर कसता रहा।"));
hinsuperobject.biblemap.put("b19_105_20", new hinsuperobject(19,105,20,"तब राजा के दूत भेजकर उसे निकलवा लिया, और देश देश के लोगों के स्वामी ने उसके बन्धन खुलवाए;"));
hinsuperobject.biblemap.put("b19_105_21", new hinsuperobject(19,105,21,"उस ने उसको अपने भवन का प्रधान और अपनी पूरी सम्पत्ति का अधिकारी ठहराया,"));
hinsuperobject.biblemap.put("b19_105_22", new hinsuperobject(19,105,22,"कि वह उसके हाकिमों को अपनी इच्छा के अनुसार कैद करे और पुरनियों को ज्ञान सिखाए।।"));
hinsuperobject.biblemap.put("b19_105_23", new hinsuperobject(19,105,23,"फिर इस्राएल मि  में आया; और याकूब हाम के देश में परेदशी रहा।"));
hinsuperobject.biblemap.put("b19_105_24", new hinsuperobject(19,105,24,"तब उस ने अपनी प्रजा को गिनती में बहुत बढ़ाया, और उसके द्रोहियो से अधिक बलवन्त किया।"));
hinsuperobject.biblemap.put("b19_105_25", new hinsuperobject(19,105,25,"उस ने मिस्त्रियों के मन को ऐसा फेर दिया, कि वे उसकी प्रजा से बैर रखने, और उसके दासों से छल करने लगे।।"));
hinsuperobject.biblemap.put("b19_105_26", new hinsuperobject(19,105,26,"उस ने अपने दास मूसा को, और अपने चुने हुए हारून को भेजा।"));
hinsuperobject.biblemap.put("b19_105_27", new hinsuperobject(19,105,27,"उन्हों ने उनके बीच उसकी ओर से भांति भांति के चिन्ह, और हाम के देश में चमत्कार दिखाए।"));
hinsuperobject.biblemap.put("b19_105_28", new hinsuperobject(19,105,28,"उस ने अन्धकार कर दिया, और अन्धियारा हो गया; और उन्हों ने उसकी बातों को न टाला।"));
hinsuperobject.biblemap.put("b19_105_29", new hinsuperobject(19,105,29,"उस ने मिस्त्रियों के जल को लोहू कर डाला, और मछलियों को मार डाला।"));
hinsuperobject.biblemap.put("b19_105_30", new hinsuperobject(19,105,30,"मेंढक उनकी भूमि में वरन उनके राजा की कोठरियों में भी भर गए।"));
hinsuperobject.biblemap.put("b19_105_31", new hinsuperobject(19,105,31,"उस ने आज्ञा दी, तब डांस आ गए, और उनके सारे देश में कुटकियां आ गईं।"));
hinsuperobject.biblemap.put("b19_105_32", new hinsuperobject(19,105,32,"उस ने उनके लिये जलवृष्टि की सन्ती ओले, और उनके देश में धधकती आग बरसाई।"));
hinsuperobject.biblemap.put("b19_105_33", new hinsuperobject(19,105,33,"और उस ने उनकी दाखलताओं और अंजीर के वृक्षों को वरन उनके देश के सब पेड़ों को तोड़ डाला।"));
hinsuperobject.biblemap.put("b19_105_34", new hinsuperobject(19,105,34,"उस ने आज्ञा दी तब अनगिनत टिडि्डयां, और कीड़े आए,"));
hinsuperobject.biblemap.put("b19_105_35", new hinsuperobject(19,105,35,"और उन्हों ने उनके देश के सब अन्नादि को खा डाला; औश्र उनकी भूमि के सब फलों को चट कर गए।"));
hinsuperobject.biblemap.put("b19_105_36", new hinsuperobject(19,105,36,"उस ने उनके देश के सब पहिलौठों को, उनके पौरूष के सब पहिले फल को नाश किया।।"));
hinsuperobject.biblemap.put("b19_105_37", new hinsuperobject(19,105,37,"तब वह अपने गोत्रियों को सोना चांदी दिलाकर निकाल लाया, और उन में से कोई निर्बल न था।"));
hinsuperobject.biblemap.put("b19_105_38", new hinsuperobject(19,105,38,"उनके जाने से मिस्त्रि  आनन्दित हुए, क्योंकि उनका डर उन में समा गया था।"));
hinsuperobject.biblemap.put("b19_105_39", new hinsuperobject(19,105,39,"उस ने छाया के लिये बादल फैलाया, और रात को प्रकाश देने के लिये आग प्रगट की।"));
hinsuperobject.biblemap.put("b19_105_40", new hinsuperobject(19,105,40,"उन्हों ने मांगा तब उस ने बटेरें पहुंचाई, और उनको स्वर्गीय भोजन से तृप्त किया।"));
hinsuperobject.biblemap.put("b19_105_41", new hinsuperobject(19,105,41,"उस ने चट्टान फाड़ी तब पानी बह निकला; और निर्जल भूमि पर नदी बहने लगी।"));
hinsuperobject.biblemap.put("b19_105_42", new hinsuperobject(19,105,42,"क्योंकि उस ने अपने पवित्रा वचन और अपने दास इब्राहीम को स्मरण किया।।"));
hinsuperobject.biblemap.put("b19_105_43", new hinsuperobject(19,105,43,"वह अपनी प्रजा को हर्षित करके और अपने चुने हुओं से जयजयकार करोके निकाल लाया।"));
hinsuperobject.biblemap.put("b19_105_44", new hinsuperobject(19,105,44,"और उनको अन्यजातियों के देश दिए; और वे और लोगों के श्रम के फल के अधिकारी किए गए,"));
hinsuperobject.biblemap.put("b19_105_45", new hinsuperobject(19,105,45,"कि वे उसकी विधियों को मानें, और उसकी व्यवस्था को पूरी करें। याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_106_1", new hinsuperobject(19,106,1,"याह की स्तुति करो! यहोवा का धन्यवाद करो, क्योंकि वह भला है; और उसकी करूणा सदा की है!"));
hinsuperobject.biblemap.put("b19_106_2", new hinsuperobject(19,106,2,"यहोवा के पराक्रम के कामों का वर्णन कौन कर सकता है, न उसका पूरा गुणानुवाद कौन सुना सकता?"));
hinsuperobject.biblemap.put("b19_106_3", new hinsuperobject(19,106,3,"क्या ही धन्य हैं वे जो न्याय पर चलते, और हर समय धर्म के काम करते हैं!"));
hinsuperobject.biblemap.put("b19_106_4", new hinsuperobject(19,106,4,"हे यहोवा, अपनी प्रजा पर की प्रसन्नता के अनुसार मुझे स्मरण कर, मेरे उद्धार के लिये मेरी सुधि ले,"));
hinsuperobject.biblemap.put("b19_106_5", new hinsuperobject(19,106,5,"कि मैं तेरे चुने हुओं का कल्याण देखूं, और तेरी प्रजा के आनन्द में आनन्दित हो जाऊं; और तेरे निज भाग के संग बड़ाई करने पाऊं।।"));
hinsuperobject.biblemap.put("b19_106_6", new hinsuperobject(19,106,6,"हम ने तो अपने पुरखाओं की नाईं पाप किया है; हम ने कुटिलता की, हम ने दुष्टता की है!"));
hinsuperobject.biblemap.put("b19_106_7", new hinsuperobject(19,106,7,"मि  में हमारे पुरखाओं ने तेरे आश्चर्यकर्मों पर मन नहीं लगाया, न तेरी अपार करूणा को स्मरण रखा; उन्हों ने समुद्र के तीर पर, अर्थात् लाल समुद्र के तीर पर बलवा किया।"));
hinsuperobject.biblemap.put("b19_106_8", new hinsuperobject(19,106,8,"तौभी उस ने अपने नाम के निमित्त उनका उद्धार किया, जिस से वह अपने पराक्रम को प्रगट करे।"));
hinsuperobject.biblemap.put("b19_106_9", new hinsuperobject(19,106,9,"तब उस ने लाल समुद्र को घुड़का और वह सूख गया; और वह उन्हें गहिरे जल के बीच से मानों जंगल में से निकाल ले गया।"));
hinsuperobject.biblemap.put("b19_106_10", new hinsuperobject(19,106,10,"उस ने उन्हें बैरी के हाथ से उबारा, और शत्रु के हाथ से छुड़ा लिया।"));
hinsuperobject.biblemap.put("b19_106_11", new hinsuperobject(19,106,11,"और उनके द्रोही जल में डूब गए; उन में से एक भी न बचा।"));
hinsuperobject.biblemap.put("b19_106_12", new hinsuperobject(19,106,12,"तब उनहों ने उसके वचनों का विश्वास किया; और उसकी स्तुति गाने लगे।।"));
hinsuperobject.biblemap.put("b19_106_13", new hinsuperobject(19,106,13,"परन्तु वे झट उसके कामों को भूल गए; और उसकी युक्ति के लिये न ठहरे।"));
hinsuperobject.biblemap.put("b19_106_14", new hinsuperobject(19,106,14,"उन्हों ने जंगल में अति लालसा की और निर्जल स्थान में ईश्वर की परीक्षा की।"));
hinsuperobject.biblemap.put("b19_106_15", new hinsuperobject(19,106,15,"तब उस ने उन्हें मुंह मांगा वर तो दिया, परन्तु उनके प्राण को सुखा दिया।।"));
hinsuperobject.biblemap.put("b19_106_16", new hinsuperobject(19,106,16,"उन्हों ने छावनी में मूसा के, और यहोवा के पवित्रा जन हारून के विषय में डाह की,"));
hinsuperobject.biblemap.put("b19_106_17", new hinsuperobject(19,106,17,"भूमि फट कर दातान को निगल गई, और अबीराम के झुण्ड को ग्रस लिया।"));
hinsuperobject.biblemap.put("b19_106_18", new hinsuperobject(19,106,18,"और उनके झुण्ड में आग भड़क उठी; और दुष्ट लोग लौ से भस्म हो गए।।"));
hinsuperobject.biblemap.put("b19_106_19", new hinsuperobject(19,106,19,"उन्हों ने होरब में बछड़ा बनाया, और ढली हुई मूत्ति को दण्डवत् की।"));
hinsuperobject.biblemap.put("b19_106_20", new hinsuperobject(19,106,20,"यों उन्हों ने अपनी महिमा अर्थात् ईश्वर को घास खानेवाले बैल की प्रतिमा से बदल डाला।"));
hinsuperobject.biblemap.put("b19_106_21", new hinsuperobject(19,106,21,"वे अपने उद्धारकर्ता ईश्वर को भूल गए, जिस ने मि  में बड़े बड़े काम किए थे।"));
hinsuperobject.biblemap.put("b19_106_22", new hinsuperobject(19,106,22,"उस ने तो हाम के देश में आश्चर्यकर्म और लाल समुद्र के तीर पर भयंकर काम किए थे।"));
hinsuperobject.biblemap.put("b19_106_23", new hinsuperobject(19,106,23,"इसलिये उस ने कहा, कि मैं इन्हें सत्यानाश कर डालता यदि मेरा चुना हुआ मूसा जोखिम के स्थान में उनके लिये खड़ा न होता ताकि मेरी जलजलाहट को ठण्डा करे कहीं ऐसा न हो कि मैं उन्हें नाश कर डालूं।।"));
hinsuperobject.biblemap.put("b19_106_24", new hinsuperobject(19,106,24,"उन्हों ने मनभावने देश को निकम्मा जाना, और उसके वचन की प्रतीति न की।"));
hinsuperobject.biblemap.put("b19_106_25", new hinsuperobject(19,106,25,"वे अपने तम्बुओं में कुड़कुड़ाए, और यहोवा का कहा न माना।"));
hinsuperobject.biblemap.put("b19_106_26", new hinsuperobject(19,106,26,"तब उस ने उनके विषय में शपथ खाई कि मैं इनको जंगल में नाश करूंगा,"));
hinsuperobject.biblemap.put("b19_106_27", new hinsuperobject(19,106,27,"और इनके वंश को अन्यजातियों के सम्मुख गिरा दूंगा, और देश देश में तितर बितर करूंगा।।"));
hinsuperobject.biblemap.put("b19_106_28", new hinsuperobject(19,106,28,"वे पोरवाले बाल देवता को पूजने लगे और मुर्दों को चढ़ाए हुए पशुओं का मांस खाने लगे।"));
hinsuperobject.biblemap.put("b19_106_29", new hinsuperobject(19,106,29,"यों उन्हों ने अपने कामों से उसको क्रोध दिलाया और मरी उन में फूट पड़ी।"));
hinsuperobject.biblemap.put("b19_106_30", new hinsuperobject(19,106,30,"तब पीहास ने उठकर न्यायदण्ड दिया, जिस से मरी थम गई।"));
hinsuperobject.biblemap.put("b19_106_31", new hinsuperobject(19,106,31,"और यह उसके लेखे पीढ़ी से पीढ़ी तक सर्वदा के लिये धर्म गिना गया।।"));
hinsuperobject.biblemap.put("b19_106_32", new hinsuperobject(19,106,32,"उन्हों ने मरीबा के सोते के पास भी यहोवा का क्रोध भड़काया, और उनके कारण मूसा की हानि हुई;"));
hinsuperobject.biblemap.put("b19_106_33", new hinsuperobject(19,106,33,"क्योंकि उन्हों ने उसकी आत्मा से बलवा किया, तब मूसा बिन सोचे बोल उठा।"));
hinsuperobject.biblemap.put("b19_106_34", new hinsuperobject(19,106,34,"जिन लोगों के विषय यहोवा ने उन्हें आज्ञा दी थी, उनको उन्हों ने सत्यानाश न किया,"));
hinsuperobject.biblemap.put("b19_106_35", new hinsuperobject(19,106,35,"वरन उन्हीं जातियों से हिलमिल गए और उनके व्यवहारों को सीख लिया;"));
hinsuperobject.biblemap.put("b19_106_36", new hinsuperobject(19,106,36,"और उनकी मूत्तियों की पूजा करने लगे, और वे उनके लिये फन्दा बन गई।"));
hinsuperobject.biblemap.put("b19_106_37", new hinsuperobject(19,106,37,"वरन उन्हों ने अपने बेटे- बेटियों को पिशाचों के लिये बलिदान किया;"));
hinsuperobject.biblemap.put("b19_106_38", new hinsuperobject(19,106,38,"और अपने निर्दोष बेटे- बेटियों का लोहू बहाया जिन्हें उन्हों ने कनान की मूत्तियों पर बलि किया, इसलिये देश खून से अपवित्रा हो गया।"));
hinsuperobject.biblemap.put("b19_106_39", new hinsuperobject(19,106,39,"और वे आप अपने कामों के द्वारा अशुद्ध हो गए, और अपने कार्यों के द्वारा व्यभिचारी भी बन गए।।"));
hinsuperobject.biblemap.put("b19_106_40", new hinsuperobject(19,106,40,"तब यहोवा का क्रोध अपनी प्रजा पर भड़का, और उसको अपने निज भाग से घृणा आई;"));
hinsuperobject.biblemap.put("b19_106_41", new hinsuperobject(19,106,41,"तब उस ने उनको अन्यजातियों के वश में कर दिया, और उनके बैरियो ने उन पर प्रभुता की।"));
hinsuperobject.biblemap.put("b19_106_42", new hinsuperobject(19,106,42,"उनके शत्रुओं ने उन पर अन्धेर किया, और वे उनके हाथ तले दब गए।"));
hinsuperobject.biblemap.put("b19_106_43", new hinsuperobject(19,106,43,"बारम्बार उस ने उन्हें छुड़ाया, परन्तु वे उसके विरूद्ध युक्ति करते गए, और अपने अधर्म के कारण दबते गए।"));
hinsuperobject.biblemap.put("b19_106_44", new hinsuperobject(19,106,44,"तौभी जब जब उनका चिल्लाना उसके कान में पड़ा, तब तब उस ने उनके संकट पर दृष्टि की!"));
hinsuperobject.biblemap.put("b19_106_45", new hinsuperobject(19,106,45,"और उनके हित अपनी वाचा को स्मरण करके अपनी अपार करूणा के अनुसार तरस खाया,"));
hinsuperobject.biblemap.put("b19_106_46", new hinsuperobject(19,106,46,"औश्र जो उन्हें बन्धुए करके ले गए थे उन सब से उन पर दया कराई।।"));
hinsuperobject.biblemap.put("b19_106_47", new hinsuperobject(19,106,47,"हे हमारे परमेश्वर यहोवा, हमारा उद्धार कर, और हमें अन्यजातियों में से इकट्ठा कर ले, कि हम तेरे पवित्रा नाम का धन्यवाद करें, और तेरी स्तुति करते हुए तेरे विषय में बड़ाई करें।।"));
hinsuperobject.biblemap.put("b19_106_48", new hinsuperobject(19,106,48,"इस्राएल का परमेश्वर यहोवा अनादिकाल से अनन्तकाल तक धन्य है! और सारी प्रजा कहे आमीन! याह की स्तुति करो।।"));
hinsuperobject.biblemap.put("b19_107_1", new hinsuperobject(19,107,1,"यहोवा का धन्यवाद करो, क्योंकि वह भला है; और उसकी करूणा सदा की है!"));
hinsuperobject.biblemap.put("b19_107_2", new hinsuperobject(19,107,2,"यहोवा के छुड़ाए हुए ऐसा ही कहें, जिन्हें उस ने द्रोही के हाथ से दाम देकर छुड़ा लिया है,"));
hinsuperobject.biblemap.put("b19_107_3", new hinsuperobject(19,107,3,"और उन्हें देश देश से पूरब- पश्चिम, उत्तर और दक्खिन से इकट्ठा किया है।।"));
hinsuperobject.biblemap.put("b19_107_4", new hinsuperobject(19,107,4,"वे जंगल में मरूभूमि के मार्ग पर भटकते फिरे, और कोई बसा हुआ नगर न पाया;"));
hinsuperobject.biblemap.put("b19_107_5", new hinsuperobject(19,107,5,"भूख और प्यास के मारे, वे विकल हो गए।"));
hinsuperobject.biblemap.put("b19_107_6", new hinsuperobject(19,107,6,"तब उन्हों ने संकट में यहोवा की दोहाई दी, और उस ने उनको सकेती से छुड़ाया;"));
hinsuperobject.biblemap.put("b19_107_7", new hinsuperobject(19,107,7,"और उनको ठीक मार्ग पर चलाया, ताकि वे बसने के लिये किसी नगर को जा पहुंचे।"));
hinsuperobject.biblemap.put("b19_107_8", new hinsuperobject(19,107,8,"लोग यहोवा की करूणा के कारण, और उन आश्चर्यकर्मों के कारण, जो वह मनुष्यों के लिये करता है, उसका धन्यवाद करें!"));
hinsuperobject.biblemap.put("b19_107_9", new hinsuperobject(19,107,9,"क्योंकि वह अभिलाषी जीव को सन्तुष्ट करता है, और भूखे को उत्तम पदार्थों से तृप्त करता है।।"));
hinsuperobject.biblemap.put("b19_107_10", new hinsuperobject(19,107,10,"जो अन्धियारे और मृत्यु की छाया में बैठे, और दु:ख में पड़े और बेड़ियों से जकड़े हुए थे,"));
hinsuperobject.biblemap.put("b19_107_11", new hinsuperobject(19,107,11,"इसलिये कि वे ईश्वर के वचनों के विरूद्ध चले, और परमप्रधान की सम्मति को तुच्छ जाना।"));
hinsuperobject.biblemap.put("b19_107_12", new hinsuperobject(19,107,12,"तब उसने उनको कष्ट के द्वारा दबाया; वे ठोकर खाकर गिर पड़े, और उनको कोई सहायक न मिला।"));
hinsuperobject.biblemap.put("b19_107_13", new hinsuperobject(19,107,13,"तब उन्हों ने संकट में यहोवा की दोहाई दी, और उस न सकेती से उनका उद्धार किया;"));
hinsuperobject.biblemap.put("b19_107_14", new hinsuperobject(19,107,14,"उस ने उनको अन्धियारे और मृत्यु की छाया में से निकाल लिया; और उनके बन्धनों को तोड़ डाला।"));
hinsuperobject.biblemap.put("b19_107_15", new hinsuperobject(19,107,15,"लोग यहोवा की करूणा के कारण, और उन आश्चर्यकर्मों के कारण जो वह मनुष्यों के लिये करता है, उसका धन्यवाद करें!"));
hinsuperobject.biblemap.put("b19_107_16", new hinsuperobject(19,107,16,"क्योंकि उस ने पीतल के फाटकों को तोड़ा, और लोहे के बेण्डों को टुकड़े टुकड़े किया।।"));
hinsuperobject.biblemap.put("b19_107_17", new hinsuperobject(19,107,17,"मूढ़ अपनी कुचाल, और अधर्म के कामों के कारण अति दु:खित होते हैं।"));
hinsuperobject.biblemap.put("b19_107_18", new hinsuperobject(19,107,18,"उनका जी सब भांति के भोजन से मिचलाता है, और वे मृत्यु के फाटक तक पहुंचते हैं।"));
hinsuperobject.biblemap.put("b19_107_19", new hinsuperobject(19,107,19,"तब वे संकट में यहोवा की दोहाई देते हैं, और व सकेती से उनका उद्धार करता है;"));
hinsuperobject.biblemap.put("b19_107_20", new hinsuperobject(19,107,20,"वह अपने वचन के द्वारा उनको चंगा करता और जिस गड़हे में वे पड़े हैं, उस से निकालता है।"));
hinsuperobject.biblemap.put("b19_107_21", new hinsuperobject(19,107,21,"लोग यहोवा की करूणा के कारण और उन आश्चर्यकर्मों के कारण जो वह मनुष्यों के लिये करता है, उसका धन्यवाद करें!"));
hinsuperobject.biblemap.put("b19_107_22", new hinsuperobject(19,107,22,"और वे धन्यवादबलि चढ़ाएं, और जयजयकार करते हुए, उसके कामों का वर्णन करें।।"));
hinsuperobject.biblemap.put("b19_107_23", new hinsuperobject(19,107,23,"जो लोग जहाजों में समुद्र पर चलते हैं, और महासागर पर होकर व्योपार करते हैं;"));
hinsuperobject.biblemap.put("b19_107_24", new hinsuperobject(19,107,24,"वे यहोवा के कामों को, और उन आश्चर्यकर्मों को जो वह गहिरे समुद्र में करता है, देखते हैं।"));
hinsuperobject.biblemap.put("b19_107_25", new hinsuperobject(19,107,25,"क्योंकि वह आज्ञा देता है, वह प्रचण्ड बयार उठकर तरंगों को उठाती है।"));
hinsuperobject.biblemap.put("b19_107_26", new hinsuperobject(19,107,26,"वे आकाश तक चढ़ जाते, फिर गहराई में उतर आते हैं; और क्लेश के मारे उनके जी में जी नहीं रहता;"));
hinsuperobject.biblemap.put("b19_107_27", new hinsuperobject(19,107,27,"वे चक्कर खाते, और मतवाले की नाई लड़खड़ाते हैं, और उनकी सारी बुद्धि मारी जाती है।"));
hinsuperobject.biblemap.put("b19_107_28", new hinsuperobject(19,107,28,"तब वे संकट में यहोवा की दोहाई देते हैं, और वह उनको सकेती से निकालता है।"));
hinsuperobject.biblemap.put("b19_107_29", new hinsuperobject(19,107,29,"वह आंधी को थाम देता है और तरंगें बैठ जाती हैं।"));
hinsuperobject.biblemap.put("b19_107_30", new hinsuperobject(19,107,30,"तब वे उनके बैठने से आनन्दित होते हैं, और वह उनको मन चाहे बन्दर स्थान में पहुंचा देता है।"));
hinsuperobject.biblemap.put("b19_107_31", new hinsuperobject(19,107,31,"लोग यहोवा की करूणा के कारण, और वह उन आश्चर्यकर्मों के कारण जो वह मनुष्यों के लिये करता है, उसका धन्यवाद करें।"));
hinsuperobject.biblemap.put("b19_107_32", new hinsuperobject(19,107,32,"और सभा में उसको सराहें, और पुरतियों के बैठक में उसकी स्तुति करें।।"));
hinsuperobject.biblemap.put("b19_107_33", new hinsuperobject(19,107,33,"वह नदियों को जंगल बना डालता है, और जल के सोतों को सूखी भूमि कर देता है।"));
hinsuperobject.biblemap.put("b19_107_34", new hinsuperobject(19,107,34,"वह फलवन्त भूमि को नोनी करता है, यह वहां के रहनेवालों की दुष्टता के कारण होता है।"));
hinsuperobject.biblemap.put("b19_107_35", new hinsuperobject(19,107,35,"वह जंगल को जल का ताल, और निर्जल देश को जल के सोते कर देता है।"));
hinsuperobject.biblemap.put("b19_107_36", new hinsuperobject(19,107,36,"और वहां वह भूखों को बसाता है, कि वे बसने के लिये नगर तैयार करें;"));
hinsuperobject.biblemap.put("b19_107_37", new hinsuperobject(19,107,37,"और खेती करें, और दाख की बारियां लगाएं, और भांति भांति के फल उपजा लें।"));
hinsuperobject.biblemap.put("b19_107_38", new hinsuperobject(19,107,38,"और वह उनको ऐसी आशीष देता है कि वे बहुत बढ़ जाते हैं, और उनके पशुओं को भी वह घटने नहीं देता।।"));
hinsuperobject.biblemap.put("b19_107_39", new hinsuperobject(19,107,39,"फिर अन्धेर, विपत्ति और शोक के कारण, वे घटते और दब जाते हैं।"));
hinsuperobject.biblemap.put("b19_107_40", new hinsuperobject(19,107,40,"और वह हाकिमों को अपमान से लादकर मार्ग रहित जंगल में भटकाता है;"));
hinsuperobject.biblemap.put("b19_107_41", new hinsuperobject(19,107,41,"वह दरिद्रों को दु:ख से छुड़ाकर ऊंचे पर रखता है, और उनको भेड़ों के झुंड सा परिवार देता है।"));
hinsuperobject.biblemap.put("b19_107_42", new hinsuperobject(19,107,42,"सीधे लोग देखकर आनन्दित होते हैं; और सब कुटिल लोग अपने मुंह बन्द करते हैं।"));
hinsuperobject.biblemap.put("b19_107_43", new hinsuperobject(19,107,43,"जो कोई बुद्धिमान हो, वह इन बातों पर ध्यान करेगा; और यहोवा की करूणा के कामों पर ध्यान करेगा।।"));
hinsuperobject.biblemap.put("b19_108_1", new hinsuperobject(19,108,1,"हे परमेश्वर, मेरा हृदय स्थ्रि है; मैं गाऊंगा, मैं अपनी आत्मा से भी भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_108_2", new hinsuperobject(19,108,2,"हे सारंगी और वीणा जागो! मैं आप पौ फटते जाग उठूंगा!"));
hinsuperobject.biblemap.put("b19_108_3", new hinsuperobject(19,108,3,"हे यहोवा, मैं देश देश के लोगों के मध्य में तेरा धन्यवाद करूंगा, और राज्य राज्य के लोगों के मध्य में तेरा भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_108_4", new hinsuperobject(19,108,4,"क्योंकि तेरी करूणा आकाश से भी ऊंची है, और तेरी सच्चाई आकाशमण्डल तक है।।"));
hinsuperobject.biblemap.put("b19_108_5", new hinsuperobject(19,108,5,"हे परमेश्वर, तू स्वर्ग के ऊपर हो! और तेरी महिमा सारी पृथ्वी के ऊपर हो!"));
hinsuperobject.biblemap.put("b19_108_6", new hinsuperobject(19,108,6,"इसलिये कि तेरे प्रिय छुड़ाए जाएं, तू अपने दहिने हाथ से बचा ले और हमारी बिनती सुन ले!"));
hinsuperobject.biblemap.put("b19_108_7", new hinsuperobject(19,108,7,"परमेश्वर ने अपनी पवित्राता में होकर कहा है, मैं प्रफुल्लित होकर शेकेम को बांट लूंगा, और सुक्कोत की तराई को नपवाऊंगा।"));
hinsuperobject.biblemap.put("b19_108_8", new hinsuperobject(19,108,8,"गिलाद मेरा है, मनश्शे भी मेरा है; और एप्रैम मेरे सिर का टोप है; यहूदा मेरा राजदण्ड है।"));
hinsuperobject.biblemap.put("b19_108_9", new hinsuperobject(19,108,9,"मोआब मेरे धोने का पात्रा है, मैं एदोम पर अपना जूता फेंकूंगा, पलिश्त पर मैं जयजयकार करूंगा।।"));
hinsuperobject.biblemap.put("b19_108_10", new hinsuperobject(19,108,10,"मुझे गढ़वाले नगर में कौन पहुंचाएगा? ऐदोम तक मेरी अगुवाई किस ने की हैं?"));
hinsuperobject.biblemap.put("b19_108_11", new hinsuperobject(19,108,11,"हे परमेश्वर, क्या तू ने हम को नहीं त्याग दिया, और हे परमेश्वर, तू हमारी सेना के साथ पयान नहीं करता।"));
hinsuperobject.biblemap.put("b19_108_12", new hinsuperobject(19,108,12,"द्रोहियों के विरूद्ध हमारी सहायता कर, क्योंकि मनुष्य का किया हुआ छुटकारा व्यर्थ है!"));
hinsuperobject.biblemap.put("b19_108_13", new hinsuperobject(19,108,13,"परमेश्वर की सहायता से हम वीरता दिखाएंगे, हमारे द्रोहियों को वही रौंदेगा।।"));
hinsuperobject.biblemap.put("b19_109_1", new hinsuperobject(19,109,1,"हे परमेश्वर तू जिसकी मैं स्तुति करता हूं, चुप न रह।"));
hinsuperobject.biblemap.put("b19_109_2", new hinsuperobject(19,109,2,"क्योंकि दुष्ट और कपटी मनुष्यों ने मेरे विरूद्ध मुंह खोला है, वे मेरे विषय में झूठ बोलते हैं।"));
hinsuperobject.biblemap.put("b19_109_3", new hinsuperobject(19,109,3,"और उन्हों ने बैर के वचनों से मुझे चारों ओर घेर लिया है, और व्यर्थ मुझ से लड़ते हैं।"));
hinsuperobject.biblemap.put("b19_109_4", new hinsuperobject(19,109,4,"मेरे प्रेम के बदले में वे मुझ से विरोध करते हैं, परन्तु में तो प्रार्थना में लवलीन रहता हूं।"));
hinsuperobject.biblemap.put("b19_109_5", new hinsuperobject(19,109,5,"उन्हों ने भलाई के पलटे में मुझ से बुराई की और मेरे प्रेम के बदले मुझ से बैर किया है।।"));
hinsuperobject.biblemap.put("b19_109_6", new hinsuperobject(19,109,6,"तू उसको किसी दुष्ट के अधिकार में रख, और कोई विरोधी उसकी दहिनी ओर खड़ा रहे।"));
hinsuperobject.biblemap.put("b19_109_7", new hinsuperobject(19,109,7,"जब उसका न्याय किया जाए, तब वह दोषी निकले, और उसकी प्रार्थना पाप गिनी जाए!"));
hinsuperobject.biblemap.put("b19_109_8", new hinsuperobject(19,109,8,"उसके दिन थोड़े हों, और उसके पद को दूसरा ले!"));
hinsuperobject.biblemap.put("b19_109_9", new hinsuperobject(19,109,9,"उसक लड़केबाले अनाथ हो जाएं और उसकी स्त्री विधवा हो जाए!"));
hinsuperobject.biblemap.put("b19_109_10", new hinsuperobject(19,109,10,"और उसके लड़के मारे मारे फिरें, और भीख मांगा करे; उनको उनके उजड़े हुए घर से दूर जाकर टुकड़े मांगना पड़े!"));
hinsuperobject.biblemap.put("b19_109_11", new hinsuperobject(19,109,11,"महाजन फन्दा लगाकर, उसका सर्वस्व ले ले; और परदेशी उसकी कमाई को लूट लें!"));
hinsuperobject.biblemap.put("b19_109_12", new hinsuperobject(19,109,12,"कोई न हो जो उस पर करूणा करता रहे, और उसके अनाथ बालकों पर कोई अनुग्रह न करे!"));
hinsuperobject.biblemap.put("b19_109_13", new hinsuperobject(19,109,13,"उसका वंश नाश हो जाए, दूसरी पीढ़ी में उसका नाम मिट जाए!"));
hinsuperobject.biblemap.put("b19_109_14", new hinsuperobject(19,109,14,"उसके पितरों का अधर्म यहोवा को स्मरण रहे, और उसकी माता का पाप न मिटे!"));
hinsuperobject.biblemap.put("b19_109_15", new hinsuperobject(19,109,15,"वह निरन्तर यहोवा के सम्मुख रहे, कि वह उनका नाम पृथ्वी पर से मिटा डाले!"));
hinsuperobject.biblemap.put("b19_109_16", new hinsuperobject(19,109,16,"क्योंकि वह दुष्ट, कृपा करना भूल गया वरन दी और दरिद्र को सताता था और मार डालने की इच्छा से खेदित मनवालों के पीछे पड़ा रहता था।।"));
hinsuperobject.biblemap.put("b19_109_17", new hinsuperobject(19,109,17,"वह शाप देने में प्रीति रखता था, और शाप उस पर आ पड़ा; वह आशीर्वाद देने से प्रसन्न न होता था, सो आर्शीवाद उस से दूर रहा।"));
hinsuperobject.biblemap.put("b19_109_18", new hinsuperobject(19,109,18,"वह शाप देना वस्त्रा की नाई पहिनता था, और वह उसके पेट में जल की नाई और उसकी हडि्डयों में तेल की नाई समा गया।"));
hinsuperobject.biblemap.put("b19_109_19", new hinsuperobject(19,109,19,"वह उसके लिये ओढ़ने का काम दे, और फेंटे की नाईं उसकी कटि में नित्य कसा रहे।।"));
hinsuperobject.biblemap.put("b19_109_20", new hinsuperobject(19,109,20,"यहोवा की ओर से मेरे विरोधियों को, और मेरे विरूद्ध बुरा कहनेवालों को यही बदला मिले!"));
hinsuperobject.biblemap.put("b19_109_21", new hinsuperobject(19,109,21,"परन्तु मुझ से हे यहोवा प्रभु, तू अपने नाम के निमित्त बर्ताव कर; तेरी करूणा तो बड़ी है, सो तू मुझे छुटकारा दे!"));
hinsuperobject.biblemap.put("b19_109_22", new hinsuperobject(19,109,22,"क्योंकि मैं दी और दरिद्र हूं, और मेरा हृदय घायल हुआ है।"));
hinsuperobject.biblemap.put("b19_109_23", new hinsuperobject(19,109,23,"मैं ढलती हुई छाया की नाई जाता रहा हूं; मैं टिड्डी के समान उड़ा दिया गया हूं।"));
hinsuperobject.biblemap.put("b19_109_24", new hinsuperobject(19,109,24,"उपवास करते करते मेरे घुटने निर्बल हो गए; और मुझ में चर्बी न रहने से मैं सूख गया हूं।"));
hinsuperobject.biblemap.put("b19_109_25", new hinsuperobject(19,109,25,"मेरी तो उन लोगों से नामधराई होती है; जब वे मुझे देखते, तब सिर हिलाते हैं।।"));
hinsuperobject.biblemap.put("b19_109_26", new hinsuperobject(19,109,26,"हे मेरे परमेश्वर यहोवा, मेरी सहायता कर! अपनी करूणा के अनुसार मेरा उद्धार कर!"));
hinsuperobject.biblemap.put("b19_109_27", new hinsuperobject(19,109,27,"जिस से वे जाने कि यह तेरा काम है, और हे यहोवा, तू ही ने यह किया है!"));
hinsuperobject.biblemap.put("b19_109_28", new hinsuperobject(19,109,28,"वे कोसते तो रहें, परन्तु तू आशीष दे! वे तो उठते ही लज्जित हों, परन्तु तेरा दास आनन्दित हो!"));
hinsuperobject.biblemap.put("b19_109_29", new hinsuperobject(19,109,29,"मेरे विरोधियों को अनादररूपी वस्त्रा पहिनाया जाए, और वे अपनी लज्जा को कम्बल की नाईं ओढ़ें!"));
hinsuperobject.biblemap.put("b19_109_30", new hinsuperobject(19,109,30,"मैं यहोवा का बहुत धन्यवाद करूंगा, और बहुत लोगों के बीच में उसकी स्तुति करूंगा।"));
hinsuperobject.biblemap.put("b19_109_31", new hinsuperobject(19,109,31,"क्योंकि वह दरिद्र की दहिनी ओर खड़ा रहेगा, कि उसको घात करनेवाले न्यायियों से बचाए।।"));
hinsuperobject.biblemap.put("b19_110_1", new hinsuperobject(19,110,1,"मेरे प्रभु से यहोवा की वाणी यह है, कि तू मेरे दहिने हाथ बैठ, जब तक मैं तेरे शत्रुओं को तेरे चरणों की चौकी न कर दूं।।"));
hinsuperobject.biblemap.put("b19_110_2", new hinsuperobject(19,110,2,"तेरे पराक्रम का राजदण्ड यहोवा सिरयोन से बढ़ाएगा। तू अपने शत्रुओं के बीच में शासन कर।"));
hinsuperobject.biblemap.put("b19_110_3", new hinsuperobject(19,110,3,"तेरी प्रजा के लोगर तेरे पराक्रम के दिन स्वेच्छाबलि बनते हैं; तेरे जवान लोग पवित्राता से शोभायमान, और भोर के गर्भ से जन्मी हुई ओस के समान तेरे पास हैं।"));
hinsuperobject.biblemap.put("b19_110_4", new hinsuperobject(19,110,4,"यहोवा ने शपथ खाई और न पछताएगा, कि तू मेल्कीसेदेक की रीति पर सर्वदा का याजक है।।"));
hinsuperobject.biblemap.put("b19_110_5", new hinsuperobject(19,110,5,"प्रभु तेरी दहिनी ओर होकर अपने क्रोध के दिन राजाओं को चूर कर देगा।"));
hinsuperobject.biblemap.put("b19_110_6", new hinsuperobject(19,110,6,"वह जाति जाति में न्याय चुकाएगा, रणभूमि लोथों से भर जाएगी; वह लम्बे चौड़े देश के प्रधान को चूर चूर कर देगा।"));
hinsuperobject.biblemap.put("b19_110_7", new hinsuperobject(19,110,7,"वह मार्ग में चलता हुआ नदी का जल पीएगा इस कारण वह सिर को ऊंचा करेगा।।"));
hinsuperobject.biblemap.put("b19_111_1", new hinsuperobject(19,111,1,"याह की स्तुति करो। मैं सीधे लोगों की गोष्ठी में और मण्डली में भी सम्पूर्ण मन से यहोवा का धन्यवाद करूंगा।"));
hinsuperobject.biblemap.put("b19_111_2", new hinsuperobject(19,111,2,"यहोवा के काम बड़े हैं, जितने उन से प्रसन्न रहते हैं, वे उन पर ध्यान लगाते हैं।"));
hinsuperobject.biblemap.put("b19_111_3", new hinsuperobject(19,111,3,"उसके काम का विभवमय और ऐश्वरर्यमय होते हैं, और उसका धन सदा तक बना रहेगा।"));
hinsuperobject.biblemap.put("b19_111_4", new hinsuperobject(19,111,4,"उस ने अपने आश्चर्यकर्मों का स्मरण कराया है; यहोवा अनुग्रहकारी और दयावन्त है।"));
hinsuperobject.biblemap.put("b19_111_5", new hinsuperobject(19,111,5,"उस ने अपने डरवैयों को आहार दिया है; वह अपनी वाचा को सदा तक स्मरण रखेगा।"));
hinsuperobject.biblemap.put("b19_111_6", new hinsuperobject(19,111,6,"उस ने अपनी प्रजा को अन्यजातियों का भाग देने के लिये, अपने कामों का प्रताप दिखाया है।"));
hinsuperobject.biblemap.put("b19_111_7", new hinsuperobject(19,111,7,"सच्चाई और न्याय उसके हाथों के काम हैं; उसके सब उपदेश विश्वासयोग्य हैं,"));
hinsuperobject.biblemap.put("b19_111_8", new hinsuperobject(19,111,8,"वे सदा सर्वदा अटल रहेंगे, वे सच्चाई और सिधाई से किए हुए हैं।"));
hinsuperobject.biblemap.put("b19_111_9", new hinsuperobject(19,111,9,"उस ने अपनी प्रजा का उद्धार किया है; उस ने अपनी वाचा को सदा के लिये ठहराया है। उसका नाम पवित्रा और भययोग्य है।"));
hinsuperobject.biblemap.put("b19_111_10", new hinsuperobject(19,111,10,"बुद्धि का मूल यहोवा का भय है; जितने उसकी आज्ञाओं को मानते हैं, उनकी बुद्धि अच्छी होती है। उसकी स्तुति सदा बनी रहेगी।।"));
hinsuperobject.biblemap.put("b19_112_1", new hinsuperobject(19,112,1,"याह की स्तुति करो। क्या ही धन्य है वह पुरूष जो यहोवा का भय मानता है, और उसकी आज्ञाओं से अति प्रसन्न रहता है!"));
hinsuperobject.biblemap.put("b19_112_2", new hinsuperobject(19,112,2,"उसका वंश पृथ्वी पर पराक्रमी होगा; सीधे लोगों की सन्तान आशीष पाएगी।"));
hinsuperobject.biblemap.put("b19_112_3", new hinsuperobject(19,112,3,"उसके घर में धन सम्पत्ति रहती है; और उसका धर्म सदा बना रहेगा।"));
hinsuperobject.biblemap.put("b19_112_4", new hinsuperobject(19,112,4,"सीधे लोगों के लिये अन्धकार के बीच में ज्योति उदय होती है; वह अनुग्रहकारी, दयावन्त और धर्मी होता है।"));
hinsuperobject.biblemap.put("b19_112_5", new hinsuperobject(19,112,5,"जो पुरूष अनुग्रह करता और उधार देता है, उसका कल्याण होता है, वह न्याय में अपने मुक में को जीतेगा।"));
hinsuperobject.biblemap.put("b19_112_6", new hinsuperobject(19,112,6,"वह तो सदा तक अटल रहेगा; धर्मी का स्मरण सदा तक बना रहेगा।"));
hinsuperobject.biblemap.put("b19_112_7", new hinsuperobject(19,112,7,"वह बुरे समाचार से नहीं डरता; उसका हृदय यहोवा पर भरोसा रखने से स्थिर रहता है।"));
hinsuperobject.biblemap.put("b19_112_8", new hinsuperobject(19,112,8,"उसका हृदय सम्भला हुआ है, इसलिये वह न डरेगा, वरन अपने द्रोहियों पर दृष्टि करके सन्तुष्ट होगा।"));
hinsuperobject.biblemap.put("b19_112_9", new hinsuperobject(19,112,9,"उस ने उदारता से दरिद्रों को दान दिया, उसका धर्म सदा बना रहेगा और उसका सींग महिमा के साथ ऊंचा किया जाएगा।"));
hinsuperobject.biblemap.put("b19_112_10", new hinsuperobject(19,112,10,"दुष्ट उसे देखकर कुढेगा; वह दांत पीस- पीसकर गल जाएगा; दुष्टों की लालसा पूरी न होगी।।"));
hinsuperobject.biblemap.put("b19_113_1", new hinsuperobject(19,113,1,"याह की स्तुति करो हे यहोवा के दासों स्तुति करो, यहोवा के नाम की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_113_2", new hinsuperobject(19,113,2,"यहोवा का नाम अब से लेकर सर्वदा तक धन्य कहा जाय!"));
hinsuperobject.biblemap.put("b19_113_3", new hinsuperobject(19,113,3,"उदयाचल से लेकर अस्ताचल तक, यहोवा का नाम स्तुति के योग्य है।"));
hinsuperobject.biblemap.put("b19_113_4", new hinsuperobject(19,113,4,"यहोवा सारी जातियों के ऊपर महान है, और उसकी महिमा आकाश से भी ऊंची है।।"));
hinsuperobject.biblemap.put("b19_113_5", new hinsuperobject(19,113,5,"हमारे परमेश्वर यहोवा के तुल्य कौन है? वह तो ऊंचे पर विराजमान है,"));
hinsuperobject.biblemap.put("b19_113_6", new hinsuperobject(19,113,6,"और आकाश और पृथ्वी पर भी, दृष्टि करने के लिये झुकता है।"));
hinsuperobject.biblemap.put("b19_113_7", new hinsuperobject(19,113,7,"वह कंगाल को मिट्टी पर से, और दरिद्र को घूरे पर से उठाकर ऊंचा करता है,"));
hinsuperobject.biblemap.put("b19_113_8", new hinsuperobject(19,113,8,"कि उसको प्रधानों के संग, अर्थात् अपनी प्रजा के प्रधानों के संग बैठाए।"));
hinsuperobject.biblemap.put("b19_113_9", new hinsuperobject(19,113,9,"वह बांझ को घर में लड़कों की आनन्द करनेवाली माता बनाता है। याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_114_1", new hinsuperobject(19,114,1,"जब इस्राएल ने मि  से, अर्थात् याकूब के घराने ने अन्य भाषावालों के बीच में कूच किया,"));
hinsuperobject.biblemap.put("b19_114_2", new hinsuperobject(19,114,2,"तब यहूदा यहोवा का पवित्रास्थान और इस्राएल उसके राज्य के लोग हो गए।।"));
hinsuperobject.biblemap.put("b19_114_3", new hinsuperobject(19,114,3,"समुद्र देखकर भागा, यर्दन नदी उलटी बही।"));
hinsuperobject.biblemap.put("b19_114_4", new hinsuperobject(19,114,4,"पहाड़ मेढ़ों की नाईं उछलने लगे, और पहाड़ियां भेड़- बकरियों के बच्चों की नाईं उछलने लगीं।।"));
hinsuperobject.biblemap.put("b19_114_5", new hinsuperobject(19,114,5,"हे समुद्र, तुझे क्या हुआ, कि तू भागा? और हे यर्दन तुझे क्या हुआ, कि तू उलठी बही?"));
hinsuperobject.biblemap.put("b19_114_6", new hinsuperobject(19,114,6,"हे पहाड़ों तुम्हें क्या हुआ, कि तुम भेड़ों की नाईं, और हे पहाड़ियों तुम्हें क्या हुआ, कि तुम भेड़- बकरियों के बच्चों की नाईं उछलीं?"));
hinsuperobject.biblemap.put("b19_114_7", new hinsuperobject(19,114,7,"हे पृथ्वी प्रभु के साम्हने, हां याकूब के परमेश्वर के साम्हने थरथरा।"));
hinsuperobject.biblemap.put("b19_114_8", new hinsuperobject(19,114,8,"वह चट्टान को जल का ताल, चकमक के पत्थर को जल का सोता बना डालता है।।"));
hinsuperobject.biblemap.put("b19_115_1", new hinsuperobject(19,115,1,"हे यहोवा, हमारी नहीं, हमारी नहीं, वरन अपने ही नाम की महिमा, अपनी करूणा और सच्चाई के निमित्त कर।"));
hinsuperobject.biblemap.put("b19_115_2", new hinsuperobject(19,115,2,"जाति जाति के लोग क्यों कहने पांए, कि उनका परमेश्वर कहां रहा?"));
hinsuperobject.biblemap.put("b19_115_3", new hinsuperobject(19,115,3,"हमारा परमेश्वर तो स्वर्ग में हैं; उस ने जो चाहा वही किया है।"));
hinsuperobject.biblemap.put("b19_115_4", new hinsuperobject(19,115,4,"उन लोगों की मूरतें सोने चान्दी ही की तो हैं, वे मनुष्यों के हाथ की बनाई हुई हैं।"));
hinsuperobject.biblemap.put("b19_115_5", new hinsuperobject(19,115,5,"उनक मुंह तो रहता है परन्तु वे बोल नहीं सकती; उनके आंखें तो रहती हैं परन्तु वे देख नहीं सकतीं।"));
hinsuperobject.biblemap.put("b19_115_6", new hinsuperobject(19,115,6,"उनके कान तो रहते हैं, परन्तु वे सुन नहीं सकतीं; उनके नाक तो रहती हैं, परन्तु वे सूंघ नहीं सकतीं।"));
hinsuperobject.biblemap.put("b19_115_7", new hinsuperobject(19,115,7,"उनके हाथ तो रहते हैं, परन्तु वे स्पर्श नहीं कर सकतीं; उनके पांव तो रहते हैं, परन्तु वे चल नहीं सकतीं; और उनके कण्ठ से कुछ भी शब्द नहीं निकाल सकतीं।"));
hinsuperobject.biblemap.put("b19_115_8", new hinsuperobject(19,115,8,"जैसी वे हैं वैसे ही उनके बनानेवाले हैं; और उन पर भरोसा रखनेवाले भी वैसे ही हो जाएंगे।।"));
hinsuperobject.biblemap.put("b19_115_9", new hinsuperobject(19,115,9,"हे इस्राएल यहोवा पर भरोसा रख! तेरा सहायक और ढाल वही है।"));
hinsuperobject.biblemap.put("b19_115_10", new hinsuperobject(19,115,10,"हे हारून के घराने यहोवा पर भरोसा रख! तेरा सहायक और ढाल वही है।"));
hinsuperobject.biblemap.put("b19_115_11", new hinsuperobject(19,115,11,"हे यहोवा के डरवैयो, यहोवा पर भरोसा रखो! तुम्हारा सहायक और ढाल वही है।।"));
hinsuperobject.biblemap.put("b19_115_12", new hinsuperobject(19,115,12,"यहोवा ने हम को स्मरण किया है; वह आशीष देगा; वह इस्राएल के घराने को आशीष देगा; वह हारून के घराने को आशीष देगा।"));
hinsuperobject.biblemap.put("b19_115_13", new hinsuperobject(19,115,13,"क्या छोटे क्या बड़े जितने यहोवा के डरवैये हैं, वह उन्हें आशीष देगा।।"));
hinsuperobject.biblemap.put("b19_115_14", new hinsuperobject(19,115,14,"यहोवा तुम को और तुम्हारे लड़कों को भी अधिक बढ़ाता जाए!"));
hinsuperobject.biblemap.put("b19_115_15", new hinsuperobject(19,115,15,"यहोवा जो आकाश और पृथ्वी का कर्ता है, उसकी ओर से तुम अशीष पाए हो।।"));
hinsuperobject.biblemap.put("b19_115_16", new hinsuperobject(19,115,16,"स्वर्ग तो यहोवा का है, परन्तु पृथ्वी उस ने मनुष्यों को दी है।"));
hinsuperobject.biblemap.put("b19_115_17", new hinsuperobject(19,115,17,"मृतक जितने चुपचाप पड़े हैं, वे तो याह की स्तुति नहीं कर सकते,"));
hinsuperobject.biblemap.put("b19_115_18", new hinsuperobject(19,115,18,"परन्तु हम लोग याह को अब से लेकर सर्वदा तक धन्य कहते रहेंगे। याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_116_1", new hinsuperobject(19,116,1,"मैं प्रेम रखता हूं, इसलिये कि यहोवा ने मेरे गिड़गिड़ाने को सुना है।"));
hinsuperobject.biblemap.put("b19_116_2", new hinsuperobject(19,116,2,"उस ने जो मेरी ओर कान लगाया है, इसलिये मैं जीवन भर उसको पुकारा करूंगा।"));
hinsuperobject.biblemap.put("b19_116_3", new hinsuperobject(19,116,3,"मृत्यु की रस्सियां मेरे चारों ओर थीं; मैं अधोलोक की सकेती में पड़ा था; मुझे संकट और शोक भोगना पड़ा।"));
hinsuperobject.biblemap.put("b19_116_4", new hinsuperobject(19,116,4,"तब मैं ने यहोवा से प्रार्थना की, कि हे यहोवा बिनती सुनकर मेरे प्राण को बचा ले!"));
hinsuperobject.biblemap.put("b19_116_5", new hinsuperobject(19,116,5,"यहोवा अनुग्रहकारी और धर्मी है; और हमारा परमेश्वर दया करनेवाला है।"));
hinsuperobject.biblemap.put("b19_116_6", new hinsuperobject(19,116,6,"यहोवा भोलों की रक्षा करता है; जब मैं बलहीन हो गया था, उस ने मेरा उद्धार किया।"));
hinsuperobject.biblemap.put("b19_116_7", new hinsuperobject(19,116,7,"हे मेरे प्राण तू अपने विश्रामस्थान में लौट आ; क्योंकि यहोवा ने तेरा उपकार किया है।।"));
hinsuperobject.biblemap.put("b19_116_8", new hinsuperobject(19,116,8,"तू ने तो मेरे प्राण को मृत्यु से, मेरी आंख को आंसू बहाने से, और मेरे पांव को ठोकर खाने से बचाया है।"));
hinsuperobject.biblemap.put("b19_116_9", new hinsuperobject(19,116,9,"मैं जीवित रहते हुए, अपने को यहोवा के साम्हने जानकर नित चलता रहूंगा।"));
hinsuperobject.biblemap.put("b19_116_10", new hinsuperobject(19,116,10,"मैं ने जो ऐसा कहा है, इसे विश्वास की कसौटी पर कस कर कहा है, कि मैं तो बहुत की दु:खित हुआ;"));
hinsuperobject.biblemap.put("b19_116_11", new hinsuperobject(19,116,11,"मैं ने उतावली से कहा, कि सब मनुष्य झूठें हैं।।"));
hinsuperobject.biblemap.put("b19_116_12", new hinsuperobject(19,116,12,"यहोवा ने मेरे जितने उपकार किए हैं, उनका बदला मैं उसको क्या दूं?"));
hinsuperobject.biblemap.put("b19_116_13", new hinsuperobject(19,116,13,"मैं उद्धार का कटोरा उठाकर, यहोवा से प्रार्थना करूंगा,"));
hinsuperobject.biblemap.put("b19_116_14", new hinsuperobject(19,116,14,"मैं यहोवा के लिये अपनी मन्नतें सभों की दृष्टि में प्रगट रूप में उसकी सारी प्रजा के साम्हने पूरी करूंगा।"));
hinsuperobject.biblemap.put("b19_116_15", new hinsuperobject(19,116,15,"यहोवा के भक्तों की मृत्यु, उसकी दृष्टि में अनमोल है।"));
hinsuperobject.biblemap.put("b19_116_16", new hinsuperobject(19,116,16,"हे यहोवा, सुन, मैं तो तेरा दास हूं; मैं तेरा दास, और तेरी दासी का पुत्रा हूं। तू ने मेरे बन्धन खोल दिए हैं।"));
hinsuperobject.biblemap.put("b19_116_17", new hinsuperobject(19,116,17,"मैं तुझ को धन्यवादबलि चढ़ाऊंगा, और यहोवा से प्रार्थना करूंगा।"));
hinsuperobject.biblemap.put("b19_116_18", new hinsuperobject(19,116,18,"मैं यहोवा के लिये अपनी मन्नतें, प्रगट में उसकी सारी प्रजा के साम्हने"));
hinsuperobject.biblemap.put("b19_116_19", new hinsuperobject(19,116,19,"यहोवा के भवन के आंगनों में, हे यरूशलेम, तेरे भीतर पूरी करूंगा। याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_117_1", new hinsuperobject(19,117,1,"हे जाति जाति के सब लोगों यहोवा की स्तुति करो! हे राज्य राज्य के सब लोगो, उसकी प्रशंसा करो!"));
hinsuperobject.biblemap.put("b19_117_2", new hinsuperobject(19,117,2,"क्योंकि उसकी करूणा हमारे ऊपर प्रबल हुई है; और यहोवा की सच्चाई सदा की है याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_118_1", new hinsuperobject(19,118,1,"यहोवा का धन्यवाद करो, क्योंकि वह भला है; और उसकी करूणा सदा की है!"));
hinsuperobject.biblemap.put("b19_118_2", new hinsuperobject(19,118,2,"इस्राएल कहे, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_118_3", new hinsuperobject(19,118,3,"हारून का घराना कहे, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_118_4", new hinsuperobject(19,118,4,"यहोवा के डरवैये कहे, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_118_5", new hinsuperobject(19,118,5,"मैं ने सकेती में परमेश्वर को पुकारा, परमेश्वर ने मेरी सुनकर, मुझे चौड़े स्थान में पहुंचाया।"));
hinsuperobject.biblemap.put("b19_118_6", new hinsuperobject(19,118,6,"यहोवा मेरी ओर है, मैं न डरूंगा। मनुष्य मेरा क्या कर सकता है?"));
hinsuperobject.biblemap.put("b19_118_7", new hinsuperobject(19,118,7,"यहोवा मेरी ओर मेरे सहायकों में है; मैं अपने बैरियों पर दृष्टि कर सन्तुष्ट हूंगा।"));
hinsuperobject.biblemap.put("b19_118_8", new hinsuperobject(19,118,8,"यहोवा की शरण लेनी, मनुष्य पर भरोसा रखने से उत्तम है।"));
hinsuperobject.biblemap.put("b19_118_9", new hinsuperobject(19,118,9,"यहोवा की शरण लेनी, प्रधानों पर भी भरोसा रखने से उत्तम है।।"));
hinsuperobject.biblemap.put("b19_118_10", new hinsuperobject(19,118,10,"सब जातियों ने मुझ को घेर लिया है; परन्तु यहोवा के नाम से मैं निश्चय उन्हें नाश कर डालूंगा!"));
hinsuperobject.biblemap.put("b19_118_11", new hinsuperobject(19,118,11,"उन्हों ने मुझ को घेर लिया है, नि:सन्देह घेर लिया है; परन्तु यहोवा के नाम से मैं निश्चय उन्हें नाश कर डालूंगा!"));
hinsuperobject.biblemap.put("b19_118_12", new hinsuperobject(19,118,12,"उन्हों ने मुझे मधुमक्खियों की नाईं घेर लिया है, परन्तु कांटों की आग की नाईं वे बुझ गए; यहोवा के नाम से मैं निश्चय उन्हें नाश कर डालूंगा!"));
hinsuperobject.biblemap.put("b19_118_13", new hinsuperobject(19,118,13,"तू ने मुझे बड़ा धक्का दिया तो था, कि मैं गिर पडूं परन्तु यहोवा ने मेरी सहायता की।"));
hinsuperobject.biblemap.put("b19_118_14", new hinsuperobject(19,118,14,"परमेश्वर मेरा बल और भजन का विषय है; वह मेरा उद्धार ठहरा है।।"));
hinsuperobject.biblemap.put("b19_118_15", new hinsuperobject(19,118,15,"धर्मियों के तम्बुओं में जयजयकार और उद्धार की ध्वनि हो रही है, यहोवा के दहिने हाथ से पराक्रम का काम होता है,"));
hinsuperobject.biblemap.put("b19_118_16", new hinsuperobject(19,118,16,"यहोवा का दहिना हाथ महान हुआ है, यहोवा के दहिने हाथ से पराक्रम का काम होता है!"));
hinsuperobject.biblemap.put("b19_118_17", new hinsuperobject(19,118,17,"मैं न मरूंगा वरन जीवित रहूंगा, और परमेश्वर परमेश्वर के कामों का वर्णन करता रहूंगा।"));
hinsuperobject.biblemap.put("b19_118_18", new hinsuperobject(19,118,18,"परमेश्वर ने मेरी बड़ी ताड़ना तो की है परन्तु मुझे मृत्यु के वश में नहीं किया।।"));
hinsuperobject.biblemap.put("b19_118_19", new hinsuperobject(19,118,19,"मेरे लिये धर्म के द्वार खोलो, मैं उन से प्रवेश करके याह का धन्यवाद करूंगा।।"));
hinsuperobject.biblemap.put("b19_118_20", new hinsuperobject(19,118,20,"यहोवा का द्वार यही है, इस से धर्मी प्रवेश करने पाएंगे।।"));
hinsuperobject.biblemap.put("b19_118_21", new hinsuperobject(19,118,21,"हे यहोवा मैं तेरा धन्यवाद करूंगा, क्योंकि तू ने मेरी सुन ली है और मेरा उद्धार ठहर गया है।"));
hinsuperobject.biblemap.put("b19_118_22", new hinsuperobject(19,118,22,"राजमिस्त्रियों ने जिस पत्थर को निकम्मा ठहराया था वही कोने का सिरा हो गया है।"));
hinsuperobject.biblemap.put("b19_118_23", new hinsuperobject(19,118,23,"यह तो यहोवा की ओर से हुआ है, यह हमारी दृष्टि में अद्भुत है।"));
hinsuperobject.biblemap.put("b19_118_24", new hinsuperobject(19,118,24,"आज वह दिन है जो यहोवा ने बनाया है; हम इस में मगन और आनन्दित हों।"));
hinsuperobject.biblemap.put("b19_118_25", new hinsuperobject(19,118,25,"हे यहोवा, बिनती सुन, उद्धार कर! हे यहोवा, बिनती सुन, सफलता दे!"));
hinsuperobject.biblemap.put("b19_118_26", new hinsuperobject(19,118,26,"धन्य है वह जो यहोवा के नाम से आता है! हम ने तुम को यहोवा के घर से आशीर्वाद दिया है।"));
hinsuperobject.biblemap.put("b19_118_27", new hinsuperobject(19,118,27,"यहोवा ईश्वर है, और उस ने हम को प्रकाश दिया है। यज्ञपशु को वेदी के सींगों से रस्सियों से बान्धो!"));
hinsuperobject.biblemap.put("b19_118_28", new hinsuperobject(19,118,28,"हे यहोवा, तू मेरा ईश्वर है, मैं तेरा धन्यवाद करूंगा; तू मेरा परमेश्वर है, मैं तुझ को सराहूंगा।।"));
hinsuperobject.biblemap.put("b19_118_29", new hinsuperobject(19,118,29,"यहोवा का धन्यवाद करो, क्योंकि वह भला है; और उसकी करूणा सदा बनी रहेगी!"));
hinsuperobject.biblemap.put("b19_119_1", new hinsuperobject(19,119,1,"क्या ही धन्य हैं वे जो चाल के खरे हैं, और यहोवा की व्यवस्था पर चलते हैं!"));
hinsuperobject.biblemap.put("b19_119_2", new hinsuperobject(19,119,2,"क्या ही धन्य हैं वे जो उसकी चितौनियों को मानते हैं, और पूर्ण मन से उसके पास आते हैं!"));
hinsuperobject.biblemap.put("b19_119_3", new hinsuperobject(19,119,3,"फिर वे कुटिलता का काम नहीं करते, वे उसके मार्गों में चलते हैं।"));
hinsuperobject.biblemap.put("b19_119_4", new hinsuperobject(19,119,4,"तू ने अपने उपदेश इसलिये दिए हैं, कि वे यत्न से माने जाएं।"));
hinsuperobject.biblemap.put("b19_119_5", new hinsuperobject(19,119,5,"भला होता कि तेरी विधियों के मानने के लिये मेरी चालचलन दृढ़ हो जाए!"));
hinsuperobject.biblemap.put("b19_119_6", new hinsuperobject(19,119,6,"तब मैं तेरी सब आज्ञाओं की ओर चित्त लगाए रहूंगा, और मेरी आशा न टूटेगी।"));
hinsuperobject.biblemap.put("b19_119_7", new hinsuperobject(19,119,7,"जब मैं तेरे धर्ममय नियमों को सीखूंगा, तब तेरा धन्यवाद सीधे मन से करूंगा।"));
hinsuperobject.biblemap.put("b19_119_8", new hinsuperobject(19,119,8,"मैं तेरी विधियों को मानूंगा: मुझे पूरी रीति से न तज!"));
hinsuperobject.biblemap.put("b19_119_9", new hinsuperobject(19,119,9,"जवान अपनी चाल को किस उपाय से शुद्ध रखे? तेरे वचन के अनुसार सावधान रहने से।"));
hinsuperobject.biblemap.put("b19_119_10", new hinsuperobject(19,119,10,"मैं पूरे मन से तेरी खोज मे लगा हूं; मुझे तेरी आज्ञाओं की बाट से भटकने न दे!"));
hinsuperobject.biblemap.put("b19_119_11", new hinsuperobject(19,119,11,"मैं ने तेरे वचन को अपने हृदय में रख छोड़ा है, कि तेरे विरूद्ध पाप न करूं।"));
hinsuperobject.biblemap.put("b19_119_12", new hinsuperobject(19,119,12,"हे यहोवा, तू धन्य है; मुझे अपनी विधियां सिखा!"));
hinsuperobject.biblemap.put("b19_119_13", new hinsuperobject(19,119,13,"तेरे सब कहे हुए नियमों का वर्णन, मैं ने अपने मुंह से किया है।"));
hinsuperobject.biblemap.put("b19_119_14", new hinsuperobject(19,119,14,"मैं तेरी चितौनियों के मार्ग से, मानों सब प्रकार के धन से हर्षित हुआ हूं।"));
hinsuperobject.biblemap.put("b19_119_15", new hinsuperobject(19,119,15,"मैं तेरे उपदेशों पर ध्यान करूंगा, और तेरे मार्गों की ओर दृष्टि रखूंगा।"));
hinsuperobject.biblemap.put("b19_119_16", new hinsuperobject(19,119,16,"मैं तेरी विधियों से सुख पाऊंगा; और तेरे वचन को न भूलूंगा।।"));
hinsuperobject.biblemap.put("b19_119_17", new hinsuperobject(19,119,17,"अपने दास का उपकार कर, कि मैं जीवित रहूं, और तेरे वचन पर चलता रहूं।"));
hinsuperobject.biblemap.put("b19_119_18", new hinsuperobject(19,119,18,"मेरी आंखें खोल दे, कि मैं तेरी व्यवस्था की अद्भुत बातें देख सकूं।"));
hinsuperobject.biblemap.put("b19_119_19", new hinsuperobject(19,119,19,"मैं तो पृथ्वी पर परदेशी हूं; अपनी आज्ञाओं को मुझ से छिपाए न रख!"));
hinsuperobject.biblemap.put("b19_119_20", new hinsuperobject(19,119,20,"मेरा मन तेरे नियमों की अभिलाषा के कारण हर समय खेदित रहता है।"));
hinsuperobject.biblemap.put("b19_119_21", new hinsuperobject(19,119,21,"तू ने अभिमानियों को, जो शापित हैं, घुड़का है, वे तेरी आज्ञाओं की बाट से भटके हुए हैं।"));
hinsuperobject.biblemap.put("b19_119_22", new hinsuperobject(19,119,22,"मेरी नामधराई और अपमान दूर कर, क्योंकि मैं तेरी चितौनियों को पकड़े हूं।"));
hinsuperobject.biblemap.put("b19_119_23", new hinsuperobject(19,119,23,"हाकिम भी बैठे हुए आपास में मेरे विरूद्ध बातें करते थे, परन्तु तेरा दास तेरी विधियों पर ध्यान करता रहा।"));
hinsuperobject.biblemap.put("b19_119_24", new hinsuperobject(19,119,24,"तेरी चितौनियां मेरा सुखमूल और मेरे मन्त्री हैं।।"));
hinsuperobject.biblemap.put("b19_119_25", new hinsuperobject(19,119,25,"मैं धूल में पड़ा हूं; तू अपने वचन के अनुसार मुझ को जिला!"));
hinsuperobject.biblemap.put("b19_119_26", new hinsuperobject(19,119,26,"मैं ने अपनी चालचलन का तुझ से वर्णन किया है और तू ने मेरी बात मान ली है; तू मुझ को अपनी विधियां सिखा!"));
hinsuperobject.biblemap.put("b19_119_27", new hinsuperobject(19,119,27,"अपने उपदेशों का मार्ग मुझे बता, तब मैं तेरे आश्यर्चकर्मों पर ध्यान करूंगा।"));
hinsuperobject.biblemap.put("b19_119_28", new hinsuperobject(19,119,28,"मेरा जीवन उदासी के मारे गल चला है; तू अपने वचन के अनुसार मुझे सम्भल!"));
hinsuperobject.biblemap.put("b19_119_29", new hinsuperobject(19,119,29,"मुझ को झूठ के मार्ग से दूर कर; और करूणा करके अपनी व्यवस्था मुझे दे।"));
hinsuperobject.biblemap.put("b19_119_30", new hinsuperobject(19,119,30,"मैं ने सच्चाई का मार्ग चुन लिया है, तेरे नियमों की ओर मैं चित्त लगाए रहता हूं।"));
hinsuperobject.biblemap.put("b19_119_31", new hinsuperobject(19,119,31,"मैं तेरी चितौनियों में लवलीन हूं, हे यहोवा, मेरी आशा न तोड़!"));
hinsuperobject.biblemap.put("b19_119_32", new hinsuperobject(19,119,32,"जब तू मेरा हियाव बढ़ाएगा, तब मैं तेरी आज्ञाओ के मार्ग में दौडूंगा।।"));
hinsuperobject.biblemap.put("b19_119_33", new hinsuperobject(19,119,33,"हे यहोवा, मुझे अपनी विधियों का मार्ग दिखा दे; तब मैं उसे अन्त तक पकड़े रहूंगा।"));
hinsuperobject.biblemap.put("b19_119_34", new hinsuperobject(19,119,34,"मुझे समझ दे, तब मैं तेरी व्यवस्था को पकड़े रहूंगा और पूर्ण मन से उस पर चलूंगा।"));
hinsuperobject.biblemap.put("b19_119_35", new hinsuperobject(19,119,35,"अपनी आज्ञाओं के पथ में मुझ को चला, क्योंकि मैं उसी से प्रसन्न हूं।"));
hinsuperobject.biblemap.put("b19_119_36", new hinsuperobject(19,119,36,"मेरे मन को लोभ की ओर नहीं, अपनी चितौनियों ही की ओर फेर दे।"));
hinsuperobject.biblemap.put("b19_119_37", new hinsuperobject(19,119,37,"मेरी आंखों को व्यर्थ वस्तुओं की ओर से फेर दे; तू अपने मार्ग में मुझे जिला।"));
hinsuperobject.biblemap.put("b19_119_38", new hinsuperobject(19,119,38,"तेरा वचन जो तेरे भय माननेवालों के लिये है, उसको अपने दास के निमित्त भी पूरा कर।"));
hinsuperobject.biblemap.put("b19_119_39", new hinsuperobject(19,119,39,"जिस नामधराई से मैं डरता हूं, उसे दूर कर; क्योंकि तेरे नियम उत्तम हैं।"));
hinsuperobject.biblemap.put("b19_119_40", new hinsuperobject(19,119,40,"देख, मैं तेरे उपदेशों का अभिलाषी हूं; अपने धर्म के कारण मुझ को जिला।"));
hinsuperobject.biblemap.put("b19_119_41", new hinsuperobject(19,119,41,"हे यहोवा, तेरी करूणा और तेरा किया हुआ उद्धार, तेरे वचन के अनुसार, मुझ को भी मिले;"));
hinsuperobject.biblemap.put("b19_119_42", new hinsuperobject(19,119,42,"तब मैं अपनी नामधराई करनेवालों को कुछ उत्तर दे सकूंगा, क्योंकि मेरा भरोसा, तेरे वचन पर है।"));
hinsuperobject.biblemap.put("b19_119_43", new hinsuperobject(19,119,43,"मुझे अपने सत्य वचन कहने से न रोक क्योंकि मेरी आशा तेरे नियमों पर है।"));
hinsuperobject.biblemap.put("b19_119_44", new hinsuperobject(19,119,44,"तब मैं तेरी व्यवस्था पर लगातार, सदा सर्वदा चलता रहूंगा;"));
hinsuperobject.biblemap.put("b19_119_45", new hinsuperobject(19,119,45,"और मैं चोड़े स्थान में चला फिरा करूंगा, क्योंकि मैं ने तेरे उपदेशों की सुधि रखी है।"));
hinsuperobject.biblemap.put("b19_119_46", new hinsuperobject(19,119,46,"और मैं तेरी चितौनियों की चर्चा राजाओं के साम्हने भी करूंगा, और संकोच न करूंगा;"));
hinsuperobject.biblemap.put("b19_119_47", new hinsuperobject(19,119,47,"क्योंकि मैं तेरी आज्ञाओं के कारण सुखी हूं, और मैं उन से प्रीति रखता हूं।"));
hinsuperobject.biblemap.put("b19_119_48", new hinsuperobject(19,119,48,"मैं तेरी आज्ञाओं की ओर जिन में मैं प्रीति रखता हूं, हाथ फैलाऊंगा और तेरी विधियों पर ध्यान करूंगा।।"));
hinsuperobject.biblemap.put("b19_119_49", new hinsuperobject(19,119,49,"जो वचन तू ने अपने दास को दिया है, उसे स्मरण कर, क्योंकि तू ने मुझे आशा दी है।"));
hinsuperobject.biblemap.put("b19_119_50", new hinsuperobject(19,119,50,"मेरे दु:ख में मुझे शान्ति उसी से हुई है, क्योंकि तेरे वचन के द्वारा मैं ने जीवन पाया है।"));
hinsuperobject.biblemap.put("b19_119_51", new hinsuperobject(19,119,51,"अभिमानियों ने मुझे अत्यन्त ठट्ठे में उड़ाया है, तौभी मैं तेरी व्यवस्था से नहीं हटा।"));
hinsuperobject.biblemap.put("b19_119_52", new hinsuperobject(19,119,52,"हे यहोवा, मैं ने तेरे प्राचीन नियमों को स्मरण करके शान्ति पाई है।"));
hinsuperobject.biblemap.put("b19_119_53", new hinsuperobject(19,119,53,"जो दुष्ट तेरी व्यवस्था को छोड़े हुए हैं, उनके कारण मैं सन्ताप से जलता हूं।"));
hinsuperobject.biblemap.put("b19_119_54", new hinsuperobject(19,119,54,"जहां मैं परदेशी होकर रहता हूं, वहां तेरी विधियां, मेरे गीत गाने का विषय बनी हैं।"));
hinsuperobject.biblemap.put("b19_119_55", new hinsuperobject(19,119,55,"हे यहोवा, मैं ने रात को तेरा नाम स्मरण किया और तेरी व्यवस्था पर चला हूं।"));
hinsuperobject.biblemap.put("b19_119_56", new hinsuperobject(19,119,56,"यह मुझ से इस कारण हुआ, कि मैं तेरे उपदेशों को पकड़े हुए था।।"));
hinsuperobject.biblemap.put("b19_119_57", new hinsuperobject(19,119,57,"यहोवा मेरा भाग है; मैं ने तेरे वचनों के अनुसार चलने का निश्चय किया है।"));
hinsuperobject.biblemap.put("b19_119_58", new hinsuperobject(19,119,58,"मैं ने पूरे मन से तुझे मनाया है; इसलिये अपने वचन के अनुसार मुझ पर अनुग्रह कर।"));
hinsuperobject.biblemap.put("b19_119_59", new hinsuperobject(19,119,59,"मैं ने अपनी चालचलन को सोचा, और तेरी चितौनियों का मार्ग लिया।"));
hinsuperobject.biblemap.put("b19_119_60", new hinsuperobject(19,119,60,"मैं ने तेरी आज्ञाओं के मानने में विलम्ब नहीं, फुर्ती की है।"));
hinsuperobject.biblemap.put("b19_119_61", new hinsuperobject(19,119,61,"मैं दुष्टों की रस्सियों से बन्ध गया हूं, तौभी मैं तेरी व्यवस्था को नहीं भूला।"));
hinsuperobject.biblemap.put("b19_119_62", new hinsuperobject(19,119,62,"तेरे धर्ममय नियमों के कारण मैं आधी रात को तेरा धन्यवाद करने को उठूंगा।"));
hinsuperobject.biblemap.put("b19_119_63", new hinsuperobject(19,119,63,"जितने तेरा भय मानते और तेरे उपदेशों पर चलते हैं, उनका मैं संगी हूं।"));
hinsuperobject.biblemap.put("b19_119_64", new hinsuperobject(19,119,64,"हे यहोवा, तेरी करूणा पृथ्वी में भरी हुई है; तू मुझे अपनी विधियां सिखा!"));
hinsuperobject.biblemap.put("b19_119_65", new hinsuperobject(19,119,65,"हे यहोवा, तू ने अपने वचन के अनुसार अपने दास के संग भलाई की है।"));
hinsuperobject.biblemap.put("b19_119_66", new hinsuperobject(19,119,66,"मुझे भली विवेक- शक्ति और ज्ञान दे, क्योंकि मैं ने तेरी आज्ञाओं का विश्वास किया है।"));
hinsuperobject.biblemap.put("b19_119_67", new hinsuperobject(19,119,67,"उस से पहिले कि मैं दु:खित हुआ, मैं भटकता था; परन्तु अब मैं तेरे वचन को मानता हूं।"));
hinsuperobject.biblemap.put("b19_119_68", new hinsuperobject(19,119,68,"तू भला है, और भला करता भी है; मुझे अपनी विधियां सिखा।"));
hinsuperobject.biblemap.put("b19_119_69", new hinsuperobject(19,119,69,"अभिमानियों ने तो मेरे विरूद्ध झूठ बात गढ़ी है, परन्तु मैं तेरे उपदेशों को पूरे मन से पकड़े रहूंगा।"));
hinsuperobject.biblemap.put("b19_119_70", new hinsuperobject(19,119,70,"उनका मन मोटा हो गया है, परन्तु मैं तेरी व्यवस्था के कारण सुखी हूं।"));
hinsuperobject.biblemap.put("b19_119_71", new hinsuperobject(19,119,71,"मुझे जो दु:ख हुआ वह मेरे लिये भला ही हुआ है, जिस से मैं तेरी विधियों को सीख सकूं।"));
hinsuperobject.biblemap.put("b19_119_72", new hinsuperobject(19,119,72,"तेरी दी हुई व्यवस्था मेरे लिये हजारों रूपयों और मुहरों से भी उत्तम है।।"));
hinsuperobject.biblemap.put("b19_119_73", new hinsuperobject(19,119,73,"तेरे हाथों से मैं बनाया और रचा गया हूं; मुझे समझ दे कि मैं तेरी आज्ञाओं को सीखूं।"));
hinsuperobject.biblemap.put("b19_119_74", new hinsuperobject(19,119,74,"तेरे डरवैये मुझे देखकर आनन्दित होंगे, क्योंकि मैं ने तेरे वचन पर आशा लगाई है।"));
hinsuperobject.biblemap.put("b19_119_75", new hinsuperobject(19,119,75,"हे यहोवा, मैं जान गया कि तेरे नियम धर्ममय हैं, और तू ने अपने सच्चाई के अनुसार मुझे दु:ख दिया है।"));
hinsuperobject.biblemap.put("b19_119_76", new hinsuperobject(19,119,76,"मुझे अपनी करूणा से शान्ति दे, क्योंकि तू ने अपने दास को ऐसा ही वचन दिया है।"));
hinsuperobject.biblemap.put("b19_119_77", new hinsuperobject(19,119,77,"तेरी दया मुझ पर हो, तब मैं जीवित रहूंगा; क्योंकि मैं तेरी व्यवस्था से सुखी हूं।"));
hinsuperobject.biblemap.put("b19_119_78", new hinsuperobject(19,119,78,"अभिमानियों की आशा टूटे, क्योंकि उन्हों ने मुझे झूठ के द्वारा गिरा दिया है; परन्तु मैं तेरे उपदेशों पर ध्यान करूंगा।"));
hinsuperobject.biblemap.put("b19_119_79", new hinsuperobject(19,119,79,"जो तेरा भय मानते हैं, वह मेरी ओर फिरें, तब वे तेरी चितौनियों को समझ लेंगे।"));
hinsuperobject.biblemap.put("b19_119_80", new hinsuperobject(19,119,80,"मेरा मन तेरी विधियों के मानने में सिद्ध हो, ऐसा न हो कि मुझे लज्जित होना पड़े।।"));
hinsuperobject.biblemap.put("b19_119_81", new hinsuperobject(19,119,81,"मेरा प्राण तेरे उद्धार के लिये बैचेन है; परन्तु मुझे तेरे वचन पर आशा रहती है।"));
hinsuperobject.biblemap.put("b19_119_82", new hinsuperobject(19,119,82,"मेरी आंखें तेरे वचन के पूरे होने की बाट जोहते जोहते रह गईं है; और मैं कहता हूं कि तू मुझे कब शान्ति देगा?"));
hinsuperobject.biblemap.put("b19_119_83", new hinsuperobject(19,119,83,"क्योंकि मैं धूएं में की कुप्पी के समान हो गया हूं, तौभी तेरी विधियों को नहीं भूला।"));
hinsuperobject.biblemap.put("b19_119_84", new hinsuperobject(19,119,84,"तेरे दास के कितने दिन रह गए हैं? तू मेरे पीछे पड़े हुओं को दण्ड कब देगा?"));
hinsuperobject.biblemap.put("b19_119_85", new hinsuperobject(19,119,85,"अभिमानी जो तरी व्यवस्था के अनुसार नहीं चलते, उन्हों ने मेरे लिये गड़हे खोदे हैं।"));
hinsuperobject.biblemap.put("b19_119_86", new hinsuperobject(19,119,86,"तेरी सब आज्ञाएं विश्वासयोग्य हैं; वे लोग झूठ बोलते हुए मेरे पीछे पड़े हैं; तू मेरी सहायता कर!"));
hinsuperobject.biblemap.put("b19_119_87", new hinsuperobject(19,119,87,"वे मुझ को पृथ्वी पर से मिटा डालने ही पर थे, परन्तु मैं ने तेरे उपदेशों को नहीं छोड़ा।"));
hinsuperobject.biblemap.put("b19_119_88", new hinsuperobject(19,119,88,"अपनी करूणा के अनुसार मुझ को जिला, तब मैं तेरी दी हुई चितौनी को मानूंगा।।"));
hinsuperobject.biblemap.put("b19_119_89", new hinsuperobject(19,119,89,"हे यहोवा, तेरा वचन, आकाश में सदा तक स्थिर रहता है।"));
hinsuperobject.biblemap.put("b19_119_90", new hinsuperobject(19,119,90,"तेरी सच्चाई पीढ़ी से पीढ़ी तक बनी रहती है; तू ने पृथ्वी को स्थिर किया, इसलिये वह बनी है।"));
hinsuperobject.biblemap.put("b19_119_91", new hinsuperobject(19,119,91,"वे आज के दिन तक तेरे नियमों के अनुसार ठहरे हैं; क्योंकि सारी सृष्टि तेरे अधीन है।"));
hinsuperobject.biblemap.put("b19_119_92", new hinsuperobject(19,119,92,"यदि मैं तेरी व्यवस्था से सुखी न होता, तो मैं दु:ख के समय नाश हो जाता।"));
hinsuperobject.biblemap.put("b19_119_93", new hinsuperobject(19,119,93,"मैं तेरे उपदेशों को कभी न भूलूंगा; क्योंकि उन्हीं के द्वारा तू ने मुझे जिलाया है।"));
hinsuperobject.biblemap.put("b19_119_94", new hinsuperobject(19,119,94,"मैं तेरा ही हूं, तू मेरा उद्धार कर; क्योंकि मैं तेरे उपदेशों की सुधि रखता हूं।"));
hinsuperobject.biblemap.put("b19_119_95", new hinsuperobject(19,119,95,"दुष्ट मेरा नाश करने के लिये मेरी घात में लगे हैं; परन्तु मैं तेरी चितौनियों पर ध्यान करता हूं।"));
hinsuperobject.biblemap.put("b19_119_96", new hinsuperobject(19,119,96,"जितनी बातें पूरी जान पड़ती हैं, उन सब को तो मैं ने अधूरी पाया है, परन्तु तेरी आज्ञा का विस्तार बड़ा है।।"));
hinsuperobject.biblemap.put("b19_119_97", new hinsuperobject(19,119,97,"अहा! मैं तेरी व्यवस्था में कैसी प्रीति रखता हूं! दिन भर मेरा ध्यान उसी पर लगा रहता है।"));
hinsuperobject.biblemap.put("b19_119_98", new hinsuperobject(19,119,98,"तू अपनी आज्ञाओं के द्वारा मुझे अपने शत्रुओं से अधिक बुद्धिमान करता है, क्योंकि वे सदा मेरे मन में रहती हैं।"));
hinsuperobject.biblemap.put("b19_119_99", new hinsuperobject(19,119,99,"मैं अपने सब शिक्षकों से भी अधिक समझ रखता हूं, क्योंकि मेरा ध्यान तेरी चितौनियों पर लगा है।"));
hinsuperobject.biblemap.put("b19_119_100", new hinsuperobject(19,119,100,"मैं पुरनियों से भी समझदार हूं, क्योंकि मैं तेरे उपदेशों को पकड़े हुए हूं।"));
hinsuperobject.biblemap.put("b19_119_101", new hinsuperobject(19,119,101,"मैं ने अपने पांवों को हर एक बुरे रास्ते से रोक रखा है, जिस से मैं तेरे वचन के अनुसार चलूं।"));
hinsuperobject.biblemap.put("b19_119_102", new hinsuperobject(19,119,102,"मैं तेरे नियमों से नहीं हटा, क्योंकि तू ही ने मुझे शिक्षा दी है।"));
hinsuperobject.biblemap.put("b19_119_103", new hinsuperobject(19,119,103,"तेरे वचन मुझ को कैसे मीठे लगते हैं, वे मेरे मुंह में मधु से भी मीठे हैं!"));
hinsuperobject.biblemap.put("b19_119_104", new hinsuperobject(19,119,104,"तेरे उपदेशों के कारण मैं समझदार हो जाता हूं, इसलिये मैं सब मिथ्या मार्गों से बैर रखता हूं।।"));
hinsuperobject.biblemap.put("b19_119_105", new hinsuperobject(19,119,105,"तेरा वचन मेरे पांव के लिये दीपक, और मेरे मार्ग के लिये उजियाला है।"));
hinsuperobject.biblemap.put("b19_119_106", new hinsuperobject(19,119,106,"मैं ने शपथ खाई, और ठाना भी है कि मैं तेरे धर्मपय नियमों के अनुसार चलूंगा।"));
hinsuperobject.biblemap.put("b19_119_107", new hinsuperobject(19,119,107,"मैं अत्यन्त दु:ख में पड़ा हूं; हे यहोवा, अपने वचन के अनुसार मुझे जिला।"));
hinsuperobject.biblemap.put("b19_119_108", new hinsuperobject(19,119,108,"हे यहोवा, मेरे वचनों को स्वेच्छाबलि जानकर ग्रहण कर, और अपने नियमों को मुझे सिखा।"));
hinsuperobject.biblemap.put("b19_119_109", new hinsuperobject(19,119,109,"मेरा प्राण निरन्तर मेरी हथेली पर रहता है, तौभी मैं तेरी व्यवस्था को भूल नहीं गया।"));
hinsuperobject.biblemap.put("b19_119_110", new hinsuperobject(19,119,110,"दुष्टों ने मेरे लिये फन्दा लगाया है, परन्तु मैं तेरे उपदेशों के मार्ग से नहीं भटका।"));
hinsuperobject.biblemap.put("b19_119_111", new hinsuperobject(19,119,111,"मैं ने तेरी चितौनियों को सदा के लिये अपना निज भाग कर लिया है, क्योंकि वे मेरे हृदय के हर्ष का कारण है।"));
hinsuperobject.biblemap.put("b19_119_112", new hinsuperobject(19,119,112,"मैं ने अपने मन को इस बात पर लगाया है, कि अन्त तक तेरी विधियों पर सदा चलता रहूं।"));
hinsuperobject.biblemap.put("b19_119_113", new hinsuperobject(19,119,113,"मैं दुचित्तों से तो बैर रखता हूं, परन्तु तेरी व्यवस्था से प्रीति रखता हूं।"));
hinsuperobject.biblemap.put("b19_119_114", new hinsuperobject(19,119,114,"तू मेरी आड़ और ढ़ाल है; मेरी आशा तेरे वचन पर है।"));
hinsuperobject.biblemap.put("b19_119_115", new hinsuperobject(19,119,115,"हे कुकर्मियों, मुझ से दूर हो जाओ, कि मैं अपने परमेश्वर की आज्ञाओं को पकड़े रहूं।"));
hinsuperobject.biblemap.put("b19_119_116", new hinsuperobject(19,119,116,"हे यहोवा, अपने वचन के अनुसार मुझे सम्भाल, कि मैं जीवित रहूं, और मेरी आशा को न तोड़!"));
hinsuperobject.biblemap.put("b19_119_117", new hinsuperobject(19,119,117,"मुझे थांभ रख, तब मैं बचा रहूंगा, और निरन्तर तेरी विधियों की ओर चित्त लगाए रहूंगा!"));
hinsuperobject.biblemap.put("b19_119_118", new hinsuperobject(19,119,118,"जितने तेरी विधियों के मार्ग से भटक जाते हैं, उन सब को तू तुच्छ जानता है, क्योंकि उनकी चतुराई झूठ है।"));
hinsuperobject.biblemap.put("b19_119_119", new hinsuperobject(19,119,119,"तू ने पृथ्वी के सब दुष्टों को धातु के मैल के समान दूर किया है; इस कारण मैं तेरी चितौनियों में प्रीति रखता हूं।"));
hinsuperobject.biblemap.put("b19_119_120", new hinsuperobject(19,119,120,"तेरे भय से मेरा शरीर कांप उठता है, और मैं तेरे नियमों से डरता हूं।।"));
hinsuperobject.biblemap.put("b19_119_121", new hinsuperobject(19,119,121,"मैं ने तो न्याय और धर्म का काम किया है; तू मुझे अन्धेर करनेवालों के हाथ में न छोड़।"));
hinsuperobject.biblemap.put("b19_119_122", new hinsuperobject(19,119,122,"अपने दास की भलाई के लिये जामिन हो, ताकि अभिमानी मुझ पर अन्धेर न करने पांए।"));
hinsuperobject.biblemap.put("b19_119_123", new hinsuperobject(19,119,123,"मेरी आंखें तुझ से उद्धार पाने, और तेरे धर्ममय वचन के पूरे होने की बाट जोहते जोहते रह गई हैं।"));
hinsuperobject.biblemap.put("b19_119_124", new hinsuperobject(19,119,124,"अपने दास के संग अपनी करूणा के अनुसार बर्ताव कर, और अपनी विधियां मुझे सिखा।"));
hinsuperobject.biblemap.put("b19_119_125", new hinsuperobject(19,119,125,"मैं तेरा दास हूं, तू मुझे समझ दे कि मैं तेरी चितौनियों को समझूं।"));
hinsuperobject.biblemap.put("b19_119_126", new hinsuperobject(19,119,126,"वह समय आया है, कि यहोवा काम करे, क्योंकि लोगों ने तेरी व्यवस्था को तोड़ दिया है।"));
hinsuperobject.biblemap.put("b19_119_127", new hinsuperobject(19,119,127,"इस कारण मैं तेरी आज्ञाओं को सोने से वरन कुन्दन से भी अधिक प्रिय मानता हूं।"));
hinsuperobject.biblemap.put("b19_119_128", new hinsuperobject(19,119,128,"इसी कारण मैं तेरे सब उपदेशों को सब विषयों में ठीक जानता हूं; और सब मिथ्या मार्गों से बैर रखता हूं।।"));
hinsuperobject.biblemap.put("b19_119_129", new hinsuperobject(19,119,129,"तेरी चितौनियां अनूप हैं, इस कारण मैं उन्हें अपने जी से पकड़े हुए हूं।"));
hinsuperobject.biblemap.put("b19_119_130", new hinsuperobject(19,119,130,"तेरी बातों के खुलने से प्राकाश होता है; उस से भोले लोग समझ प्राप्त करते हैं।"));
hinsuperobject.biblemap.put("b19_119_131", new hinsuperobject(19,119,131,"मैं मुंह खोलकर हांफने लगा, क्योंकि मैं तेरी आज्ञाओं का प्यासा था।"));
hinsuperobject.biblemap.put("b19_119_132", new hinsuperobject(19,119,132,"जैसी तेरी रीति अपने नाम की प्रीति रखनेवालों से है, वैसे ही मेरी ओर भी फिरकर मुझ पर अनुग्रह कर।"));
hinsuperobject.biblemap.put("b19_119_133", new hinsuperobject(19,119,133,"मेरे पैरों को अपने वचन के मार्ग पर स्थिर कर, और किसी अनर्थ बात को मुझ पर प्रभुता न करने दे।"));
hinsuperobject.biblemap.put("b19_119_134", new hinsuperobject(19,119,134,"मुझे मनुष्यों के अन्धेर से छुड़ा ले, तब मैं तेरे उपदेशों को मानूंगा।"));
hinsuperobject.biblemap.put("b19_119_135", new hinsuperobject(19,119,135,"अपने दास पर अपने मुख का प्रकाश चमका दे, और अपनी विधियां मुझे सिखा।"));
hinsuperobject.biblemap.put("b19_119_136", new hinsuperobject(19,119,136,"मेरी आंखों से जल की धारा बहती रहती है, क्योंकि लोग तेरी व्यवस्था को नहीं मानते।।"));
hinsuperobject.biblemap.put("b19_119_137", new hinsuperobject(19,119,137,"हे यहोवा तू धर्मी है, और तेरे नियम सीधे हैं।"));
hinsuperobject.biblemap.put("b19_119_138", new hinsuperobject(19,119,138,"तू ने अपनी चितौनियों को धर्म और पूरी सत्यता से कहा है।"));
hinsuperobject.biblemap.put("b19_119_139", new hinsuperobject(19,119,139,"मैं तेरी धुन में भस्म हो रहा हूं, क्योंकि मेरे सतानेवाले तेरे वचनों को भूल गए हैं।"));
hinsuperobject.biblemap.put("b19_119_140", new hinsuperobject(19,119,140,"तेरा वचन पूरी रीति से ताया हुआ है, इसलिये तेरा दास उस में प्रीति रखता है।"));
hinsuperobject.biblemap.put("b19_119_141", new hinsuperobject(19,119,141,"मैं छोटा और तुच्छ हूं, तौभी मैं तेरे उपदेशों को नही भूलता।"));
hinsuperobject.biblemap.put("b19_119_142", new hinsuperobject(19,119,142,"तेरा धर्म सदा का धर्म है, और तेरी व्यवस्था सत्य है।"));
hinsuperobject.biblemap.put("b19_119_143", new hinsuperobject(19,119,143,"मैं संकट और सकेती में फंसा हूं, परन्तु मैं तेरी आज्ञाओं से सुखी हूं।"));
hinsuperobject.biblemap.put("b19_119_144", new hinsuperobject(19,119,144,"तेरी चितौनियां सदा धर्ममय हैं; तू मुझ को समझ दे कि मैं जीवित रहूं।।"));
hinsuperobject.biblemap.put("b19_119_145", new hinsuperobject(19,119,145,"मैं ने सारे मन से प्रार्थना की है, हे यहोवा मेरी सुन लेना! मैं तेरी विधियों को पकड़े रहूंगा।"));
hinsuperobject.biblemap.put("b19_119_146", new hinsuperobject(19,119,146,"मैं ने तुझ से प्रार्थना की है, तू मेरा उद्धार कर, और मैं तेरी चितौनियों को माना करूंगा।"));
hinsuperobject.biblemap.put("b19_119_147", new hinsuperobject(19,119,147,"मैं ने पौ फटने से पहिले दोहाई दी; मेरी आशा तेरे वचनों पर थी।"));
hinsuperobject.biblemap.put("b19_119_148", new hinsuperobject(19,119,148,"मेरी आंखें रात के एक एक पहर से पहिले खुल गईं, कि मैं तेरे वचन पर ध्यान करूं।"));
hinsuperobject.biblemap.put("b19_119_149", new hinsuperobject(19,119,149,"अपनी करूणा के अनुसार मेरी सुन ले; हे यहोवा, अपनी रीति के अनुसार मुझे जीवित कर।"));
hinsuperobject.biblemap.put("b19_119_150", new hinsuperobject(19,119,150,"जो दुष्टता में धुन लगाते हैं, वे निकट आ गए हैं; वे तेरी व्यवस्था से दूर हैं।"));
hinsuperobject.biblemap.put("b19_119_151", new hinsuperobject(19,119,151,"हे यहोवा, तू निकट है, और तेरी सब आज्ञाएं सत्य हैं।"));
hinsuperobject.biblemap.put("b19_119_152", new hinsuperobject(19,119,152,"बहुत काल से मैं तेरी चितौनियों को जानता हूं, कि तू ने उनकी नेव सदा के लिये डाली है।।"));
hinsuperobject.biblemap.put("b19_119_153", new hinsuperobject(19,119,153,"मेरे दु:ख को देखकर मुझे छुड़ा ले, क्योंकि मैं तेरी व्यवस्था को भूल नहीं गया।"));
hinsuperobject.biblemap.put("b19_119_154", new hinsuperobject(19,119,154,"मेरा मुक मा लड़, और मुझे छुड़ा ले; अपने वचन के अनुसार मुझ को जिला।"));
hinsuperobject.biblemap.put("b19_119_155", new hinsuperobject(19,119,155,"दुष्टों को उद्धार मिलना कठिन है, क्योंकि वे तेरी विधियों की सुधि नहीं रखते।"));
hinsuperobject.biblemap.put("b19_119_156", new hinsuperobject(19,119,156,"हे यहोवा, तेरी दया तो बड़ी है; इसलिये अपने नियमों के अनुसार मुझे जिला।"));
hinsuperobject.biblemap.put("b19_119_157", new hinsuperobject(19,119,157,"मेरा पीछा करनेवाले और मेरे सतानेवाले बहुत हैं, परन्तु मैं तेरी चितौनियों से नहीं हटता।"));
hinsuperobject.biblemap.put("b19_119_158", new hinsuperobject(19,119,158,"मैं विश्वासघातियों को देखकर उदास हुआ, क्योंकि वे तेरे वचन को नहीं मानते।"));
hinsuperobject.biblemap.put("b19_119_159", new hinsuperobject(19,119,159,"देख, मैं तेरे नियमों से कैसी प्रीति रखता हूं! हे यहोवा, अपनी करूणा के अनुसार मुझ को जिला।"));
hinsuperobject.biblemap.put("b19_119_160", new hinsuperobject(19,119,160,"तेरा सारा वचन सत्य ही है; और तेरा एक एक धर्ममय नियम सदा काल तक अटल है।।"));
hinsuperobject.biblemap.put("b19_119_161", new hinsuperobject(19,119,161,"हाकिम व्यर्थ मेरे पीछे पड़े हैं, परन्तु मेरा हृदय तेरे वचनों का भय मानता है।"));
hinsuperobject.biblemap.put("b19_119_162", new hinsuperobject(19,119,162,"जैसे कोई बड़ी लूट पाकर हर्षित होता है, वैसे ही मैं तेरे वचन के कारण हर्षित हूं।"));
hinsuperobject.biblemap.put("b19_119_163", new hinsuperobject(19,119,163,"झूठ से तो मैं बैर और घृणा रखता हूं, परन्तु तेरी व्यवस्था से प्रीति रखता हूं।"));
hinsuperobject.biblemap.put("b19_119_164", new hinsuperobject(19,119,164,"तेरे धर्ममय नियमों के कारण मैं प्रतिदिन सात बेर तेरी स्तुति करता हूं।"));
hinsuperobject.biblemap.put("b19_119_165", new hinsuperobject(19,119,165,"तेरी व्यवस्था से प्रीति रखनेवालों को बड़ी शान्ति होती है; और उनको कुछ ठोकर नहीं लगती।"));
hinsuperobject.biblemap.put("b19_119_166", new hinsuperobject(19,119,166,"हे यहोवा, मैं तुझ से उद्धार पाने की आशा रखता हूं; और तेरी आज्ञाओं पर चलता आया हूं।"));
hinsuperobject.biblemap.put("b19_119_167", new hinsuperobject(19,119,167,"मैं तेरी चितौनियों को जी से मानता हूं, और उन से बहुत प्रीति रखता आया हूं।"));
hinsuperobject.biblemap.put("b19_119_168", new hinsuperobject(19,119,168,"मैं तेरे उपदेशों और चितौनियों को मानता आया हूं, क्योंकि मेरी सारी चालचलन तेरे सम्मुख प्रगट है।।"));
hinsuperobject.biblemap.put("b19_119_169", new hinsuperobject(19,119,169,"हे यहोवा, मेरी दोहाई तुझ तक पहुंचे; तू अपने वचन के अनुसार मुझे समझ दे!"));
hinsuperobject.biblemap.put("b19_119_170", new hinsuperobject(19,119,170,"मेरा गिड़गिड़ाना तुझ तक पहुंचे; तू अपने वचन के अनुसार मुझे छुड़ा ले।"));
hinsuperobject.biblemap.put("b19_119_171", new hinsuperobject(19,119,171,"मेरे मुंह से स्तुति निकला करे, क्योंकि तू मुझे अपनी विधियां सिखाता है।"));
hinsuperobject.biblemap.put("b19_119_172", new hinsuperobject(19,119,172,"मैं तेरे वचन का गीत गाऊंगा, क्योंकि तेरी सब आज्ञाएं धर्ममय हैं।"));
hinsuperobject.biblemap.put("b19_119_173", new hinsuperobject(19,119,173,"तेरा हाथ मेरी सहायता करने को तैयार रहता है, क्योंकि मैं ने तेरे उपदेशों को अपनाया है।"));
hinsuperobject.biblemap.put("b19_119_174", new hinsuperobject(19,119,174,"हे यहोवा, मैं तुझ से उद्धार पाने की अभिलाषा करता हूं, मैं तेरी व्यवस्था से सुखी हूं।"));
hinsuperobject.biblemap.put("b19_119_175", new hinsuperobject(19,119,175,"मुझे जिला, और मैं तेरी स्तुति करूंगा, तेरे नियमों से मेरी सहायता हो।"));
hinsuperobject.biblemap.put("b19_119_176", new hinsuperobject(19,119,176,"मैं खोई हुई भेड़ की नाईं भटका हूं; तू अपने दास को ढूंढ़ ले, क्योंकि मैं तेरी आज्ञाओं को भूल नहीं गया।।"));
hinsuperobject.biblemap.put("b19_120_1", new hinsuperobject(19,120,1,"संकट के समय मैं ने यहोवा को पुकारा, और उस ने मेरी सुन ली।"));
hinsuperobject.biblemap.put("b19_120_2", new hinsuperobject(19,120,2,"हे यहोवा, झूठ बोलनेवाले मुंह से और छली जीभ से मेरी रक्षा कर।।"));
hinsuperobject.biblemap.put("b19_120_3", new hinsuperobject(19,120,3,"हे छली जीभ, तुझ को क्या मिले? और तेरे साथ और क्या अधिक किया जाए?"));
hinsuperobject.biblemap.put("b19_120_4", new hinsuperobject(19,120,4,"वीर के नोकीले तीर और झाऊ के अंगारे!"));
hinsuperobject.biblemap.put("b19_120_5", new hinsuperobject(19,120,5,"हाय, हाय, क्योंकि मुझे मेशेक में परदेशी होकर रहना पड़ा और केदार के तम्बुओं में बसना पड़ा है!"));
hinsuperobject.biblemap.put("b19_120_6", new hinsuperobject(19,120,6,"बहुत काल से मुझ को मेल के बैरियों के साथ बसना पड़ा है।"));
hinsuperobject.biblemap.put("b19_120_7", new hinsuperobject(19,120,7,"मैं तो मेल चाहता हूं; परन्तु मेरे बोलते ही, वे लड़ना चाहते हैं!"));
hinsuperobject.biblemap.put("b19_121_1", new hinsuperobject(19,121,1,"मैं अपनी आंखें पर्वतों की ओर लगाऊंगा। मुझे सहायता कहां से मिलेगी?"));
hinsuperobject.biblemap.put("b19_121_2", new hinsuperobject(19,121,2,"मुझे सहायता यहोवा की ओर से मिलती है, जो आकाश और पृथ्वी का कर्त्ता है।।"));
hinsuperobject.biblemap.put("b19_121_3", new hinsuperobject(19,121,3,"वह तेरे पांव को टलने न देगा, तेरा रक्षक कभी न ऊंघे।"));
hinsuperobject.biblemap.put("b19_121_4", new hinsuperobject(19,121,4,"सुन, इस्राएल का रक्षक, न ऊंघेगा और न सोएगा।।"));
hinsuperobject.biblemap.put("b19_121_5", new hinsuperobject(19,121,5,"यहोवा तेरा रक्षक है; यहोवा तेरी दहिनी ओर तेरी आड़ है।"));
hinsuperobject.biblemap.put("b19_121_6", new hinsuperobject(19,121,6,"न तो दिन को धूप से, और न रात को चांदनी से तेरी कुछ हाति होगी।।"));
hinsuperobject.biblemap.put("b19_121_7", new hinsuperobject(19,121,7,"यहोवा सारी विपत्ति से तेरी रक्षा करेगा; यह तेरे प्राण की रक्षा करेगा।"));
hinsuperobject.biblemap.put("b19_121_8", new hinsuperobject(19,121,8,"यहोवा तेरे आने जाने में तेरी रक्षा अब से लेकर सदा तक करता रहेगा।।"));
hinsuperobject.biblemap.put("b19_122_1", new hinsuperobject(19,122,1,"जब लोगों ने मुझ से कहा, कि हम यहोवा के भवन को चलें, तब मैं आनन्दित हुआ।"));
hinsuperobject.biblemap.put("b19_122_2", new hinsuperobject(19,122,2,"हे यरूशलेम, तेरे फाटकों के भीतर, हम खड़े हो गए हैं!"));
hinsuperobject.biblemap.put("b19_122_3", new hinsuperobject(19,122,3,"हे यरूशलेम, तू ऐसे नगर के समान बना है, जिसके घर एक दूसरे से मिले हुए हैं।"));
hinsuperobject.biblemap.put("b19_122_4", new hinsuperobject(19,122,4,"वहां याह के गोत्रा गोत्रा के लोग यहोवा के नाम का धन्यवाद करने को जाते हैं; यह इस्राएल के लिये साक्षी है।"));
hinsuperobject.biblemap.put("b19_122_5", new hinsuperobject(19,122,5,"वहां तो न्याय के सिंहासन, दाऊद के घराने के लिये धरे हुए हैं।।"));
hinsuperobject.biblemap.put("b19_122_6", new hinsuperobject(19,122,6,"यरूशलेम की शान्ति का वरदान मांगो, तेरे प्रेमी कुशल से रहें!"));
hinsuperobject.biblemap.put("b19_122_7", new hinsuperobject(19,122,7,"तेरी शहरपनाह के भीतर शान्ति, और तेरे महलों में कुशल होवे!"));
hinsuperobject.biblemap.put("b19_122_8", new hinsuperobject(19,122,8,"अपने भाइयों और संगियों के निमित्त, मैं कहूंगा कि तुझ में शान्ति होवे!"));
hinsuperobject.biblemap.put("b19_122_9", new hinsuperobject(19,122,9,"अपने परमेश्वर यहोवा के भवन के निमित्त, मैं तेरी भलाई का यत्न करूंगा।।"));
hinsuperobject.biblemap.put("b19_123_1", new hinsuperobject(19,123,1,"हे स्वर्ग में विराजमान मैं अपनी आंखें तेरी ओर लगाता हूं!"));
hinsuperobject.biblemap.put("b19_123_2", new hinsuperobject(19,123,2,"देख, जैसे दासों की आंखें अपने स्वामियों के हाथ की ओर, और जैसे दासियों की आंखें अपनी स्वामिनी के हाथ की ओर लगी रहती है, वैसे ही हमारी आंखें हमारे परमेश्वर यहोवा की ओर उस समय तक लगी रहेंगी, जब तक वह हम पर अनुग्रह न करे।।"));
hinsuperobject.biblemap.put("b19_123_3", new hinsuperobject(19,123,3,"हम पर अनुग्रह कर, हे यहोवा, हम पर अनुग्रह कर, क्योंकि हम अपमान से बहुत ही भर गए हैं।"));
hinsuperobject.biblemap.put("b19_123_4", new hinsuperobject(19,123,4,"हमारा जीव सुखी लोगों के ठट्ठों से, और अहंकारियों के अपमान से बहुत ही भर गया है।।"));
hinsuperobject.biblemap.put("b19_124_1", new hinsuperobject(19,124,1,"इस्राएल यह कहे, कि यदि हमारी ओर यहोवा न होता,"));
hinsuperobject.biblemap.put("b19_124_2", new hinsuperobject(19,124,2,"यदि यहोवा उस समय हमारी ओर न होता जब मनुष्यों ने हम पर चढ़ाई की,"));
hinsuperobject.biblemap.put("b19_124_3", new hinsuperobject(19,124,3,"तो वे हम को उसी समय जीवित निगल जाते, जब उनका क्रोध हम पर भड़का था,"));
hinsuperobject.biblemap.put("b19_124_4", new hinsuperobject(19,124,4,"हम उसी समय जल में डूब जाते और धारा में बह जाते;"));
hinsuperobject.biblemap.put("b19_124_5", new hinsuperobject(19,124,5,"उमड़ते जल में हम उसी समय ही बह जाते।।"));
hinsuperobject.biblemap.put("b19_124_6", new hinsuperobject(19,124,6,"धन्य है यहोवा, जिस ने हम को उनके दातों तले जाने न दिया!"));
hinsuperobject.biblemap.put("b19_124_7", new hinsuperobject(19,124,7,"हमार जीव पक्षी की नाईं चिड़ीमार के जाल से छूट गया; जाल फट गया, हम बच निकले!"));
hinsuperobject.biblemap.put("b19_124_8", new hinsuperobject(19,124,8,"यहोवा जो आकाश और पृथ्वी का कर्त्ता है, हमारी सहायता उसी के नाम से होती है।"));
hinsuperobject.biblemap.put("b19_125_1", new hinsuperobject(19,125,1,"जो यहोवा पर भरोसा रखते हैं, वे सिरयोन पर्वत के समान हैं, जो टलता नहीं, वरन सदा बना रहता है।"));
hinsuperobject.biblemap.put("b19_125_2", new hinsuperobject(19,125,2,"जिस प्रकार यरूशलेम के चारों ओर पहाड़ हैं, उसी प्रकार यहोवा अपनी प्रजा के चारों ओर अब से लेकर सर्वदा तक बना रहेगा।"));
hinsuperobject.biblemap.put("b19_125_3", new hinsuperobject(19,125,3,"क्योंकि दुष्टों का राजदण्ड धर्मियों के भाग पर बना न रहेगा, ऐसा न हो कि धर्मी अपने हाथ कुटिल काम की ओर बढ़ाएं।।"));
hinsuperobject.biblemap.put("b19_125_4", new hinsuperobject(19,125,4,"हे यहोवा, भलों का, और सीधे मनवालों का भला कर!"));
hinsuperobject.biblemap.put("b19_125_5", new hinsuperobject(19,125,5,"परन्तु जो मुड़कर टेढ़े मार्गों में चलते हैं, उनको यहोवा अनर्थकारियों के संग निकाल देगा! इस्राएल को शान्ति मिले!"));
hinsuperobject.biblemap.put("b19_126_1", new hinsuperobject(19,126,1,"जब यहोवा सिरयोन से लौअनेवालों को लौटा ले आया, तब हम स्वप्त देखनेवाले से हो गए।"));
hinsuperobject.biblemap.put("b19_126_2", new hinsuperobject(19,126,2,"तब हम आनन्द से हंसने और जयजयकार करने लगे; तब जाति जाति के बीच में कहा जाता था, कि यहोवा ने, इनके साथ बड़े बड़े काम किए हैं।"));
hinsuperobject.biblemap.put("b19_126_3", new hinsuperobject(19,126,3,"यहोवा ने हमारे साथ बड़े बड़े काम किए हैं; और इस से हम आनन्दित हैं।।"));
hinsuperobject.biblemap.put("b19_126_4", new hinsuperobject(19,126,4,"हे यहोवा, दक्खिन देश के नालों की नाईं, हमारे बन्धुओं को लौटा ले आ!"));
hinsuperobject.biblemap.put("b19_126_5", new hinsuperobject(19,126,5,"जो आंसू बहाते हुए बोते हैं, वे जयजयकार करते हुए लवने पाएंगे।"));
hinsuperobject.biblemap.put("b19_126_6", new hinsuperobject(19,126,6,"चाहे बोनेवाला बीज लेकर रोता हुआ चला जाए, परन्तु वह फिर पूलियां लिए जयजयकार करता हुआ निश्चय लौट आएगा।।"));
hinsuperobject.biblemap.put("b19_127_1", new hinsuperobject(19,127,1,"यदि घर को यहोवा न बानाए, तो उसके बनानेवालों को परिश्रम व्यर्थ होगा। यदि नगर की रक्षा यहोवा न करे, तो रखवाले का जागना व्यर्थ ही होगा।"));
hinsuperobject.biblemap.put("b19_127_2", new hinsuperobject(19,127,2,"तुम जो सवेरे उठते और देर करके विश्राम करते और दु:ख भरी रोटी खाते हो, यह सब तुम्हारे लिये व्यर्थ ही है; क्योंकि वह अपने प्रियों को योंही नींद दान करता है।।"));
hinsuperobject.biblemap.put("b19_127_3", new hinsuperobject(19,127,3,"देखे, लड़के यहोवा के दिए हुए भाग हैं, गर्भ का फल उसकी ओर से प्रतिफल है।"));
hinsuperobject.biblemap.put("b19_127_4", new hinsuperobject(19,127,4,"जैसे वीर के हाथ में तीर, वैसे ही जवानी के लड़के होते हैं।"));
hinsuperobject.biblemap.put("b19_127_5", new hinsuperobject(19,127,5,"क्या ही धन्य है वह पुरूष जिस ने अपने तर्कश को उन से भर लिया हो! वह फाटक के पास शत्रुओं से बातें करते संकोच न करेगा।।"));
hinsuperobject.biblemap.put("b19_128_1", new hinsuperobject(19,128,1,"क्या ही धन्य है हर एक जो यहोवा का भय मानता है, और उसके मार्गों पर चलता है!"));
hinsuperobject.biblemap.put("b19_128_2", new hinsuperobject(19,128,2,"तू अपनी कमाई को निश्चय खाने पाएगा; तू धन्य होगा, और तेरा भला ही होगा।।"));
hinsuperobject.biblemap.put("b19_128_3", new hinsuperobject(19,128,3,"तेरे घर के भीतर तेरी स्त्री फलवन्त दाखलता सी होगी; तेरी मेज के चारों ओर तेरे बालक जलपाई के पौधे से होंगे।"));
hinsuperobject.biblemap.put("b19_128_4", new hinsuperobject(19,128,4,"सुन, जो पुरूष यहोवा का भय मानता हो, वह ऐसी ही आशीष पाएगा।।"));
hinsuperobject.biblemap.put("b19_128_5", new hinsuperobject(19,128,5,"यहोवा तुझे सिरयोन से आशीष देवे, और तू जीवन भर यरूशलेम का कुशल देखता रहे!"));
hinsuperobject.biblemap.put("b19_128_6", new hinsuperobject(19,128,6,"वरन तू अपने नाती- पोतों को भी देखने पाए! इस्राएल को शान्ति मिले!"));
hinsuperobject.biblemap.put("b19_129_1", new hinsuperobject(19,129,1,"इस्राएल अब यह कहे, कि मेरे बचपन से लोग मुझे बार बार क्लेश देते आए हैं,"));
hinsuperobject.biblemap.put("b19_129_2", new hinsuperobject(19,129,2,"मेरे बचपन से वे मुझ को बार बार क्लेश देते तो आए हैं, परन्तु मुझ पर प्रबल नहीं हुए।"));
hinsuperobject.biblemap.put("b19_129_3", new hinsuperobject(19,129,3,"हलवाहों ने मेरी पीठ के ऊपर हल चलाया, और लम्बी लम्बी रेखाएं कीं।"));
hinsuperobject.biblemap.put("b19_129_4", new hinsuperobject(19,129,4,"यहोवा धर्मी है; उस ने दुष्टों के फन्दों को काट डाला है।"));
hinsuperobject.biblemap.put("b19_129_5", new hinsuperobject(19,129,5,"जितने सिरयोन से बैर रखते हैं, उन सभों की आशा टूटे, ओर उनको पीछे हटना पड़े!"));
hinsuperobject.biblemap.put("b19_129_6", new hinsuperobject(19,129,6,"वे छत पर की घास के समान हों, जो बढ़ने से पहिले सूख जाती है;"));
hinsuperobject.biblemap.put("b19_129_7", new hinsuperobject(19,129,7,"जिस से कोई लवैया अपनी मुट्ठी नहीं भरता, न पूलियों का कोई बान्धनेवाला अपनी अंकवार भर पाता है,"));
hinsuperobject.biblemap.put("b19_129_8", new hinsuperobject(19,129,8,"और न आने जानेवाले यह कहते हैं, कि यहोवा की आशीष तुम पर होवे! हम तुम को यहोवा के नाम से आशीर्वाद देते हैं!"));
hinsuperobject.biblemap.put("b19_130_1", new hinsuperobject(19,130,1,"हे यहोवा, मैं ने गहिरे स्थानों में से तुझ को पुकारा है!"));
hinsuperobject.biblemap.put("b19_130_2", new hinsuperobject(19,130,2,"हे प्रभु, मेरी सुन! तेरे कान मेरे गिड़गिड़ाने की ओर ध्यान से लगे रहें!"));
hinsuperobject.biblemap.put("b19_130_3", new hinsuperobject(19,130,3,"हे याह, यदि तू अधर्म के कामों का लेखा ले, तो हे प्रभु कौन खड़ा रह सकेगा?"));
hinsuperobject.biblemap.put("b19_130_4", new hinsuperobject(19,130,4,"परन्तु तू क्षमा करनेवाला है? जिस से तेरा भय माना जाए।"));
hinsuperobject.biblemap.put("b19_130_5", new hinsuperobject(19,130,5,"मैं यहोवा की बाट जोहता हूं, मैं जी से उसकी बाट जोहता हूं, और मेरी आशा उसके वचन पर है;"));
hinsuperobject.biblemap.put("b19_130_6", new hinsuperobject(19,130,6,"पहरूए जितना भोर को चाहते हैं, हां, पहरूए जितना भोर को चाहते हैं, उस से भी अधिक मैं यहोवा को अपने प्राणों से चाहता हूं।।"));
hinsuperobject.biblemap.put("b19_130_7", new hinsuperobject(19,130,7,"इस्राएल यहोवा पर आशा लगाए रहे! क्योंकि यहोवा करूणा करनेवाला और पूरा छुटकारा देनेवाला है।"));
hinsuperobject.biblemap.put("b19_130_8", new hinsuperobject(19,130,8,"इस्राएल को उसके सारे अधर्म के कामों से वही छुटकारा देगा।।"));
hinsuperobject.biblemap.put("b19_131_1", new hinsuperobject(19,131,1,"हे यहोवा, न तो मेरा मन गर्व से और न मेरी दृष्टि घमण्ड से भरी है; और जो बातें बड़ी और मेरे लिये अधिक कठिन हैं, उन से मैं काम नहीं रखता।"));
hinsuperobject.biblemap.put("b19_131_2", new hinsuperobject(19,131,2,"निश्चय मैं ने अपने मन को शान्त और चुप कर दिया है, जैसे दूध छुड़ाया हुआ लड़का अपनी मां की गोद में रहता है, वैसे ही दूध छुड़ाए हुए लड़के के समान मेरा मन भी रहता है।।"));
hinsuperobject.biblemap.put("b19_131_3", new hinsuperobject(19,131,3,"हे इस्राएल, अब से लेकर सदा सर्वदा यहोवा ही पर आशा लगाए रह!"));
hinsuperobject.biblemap.put("b19_132_1", new hinsuperobject(19,132,1,"हे यहोवा, दाऊद के लिये उसकी सारी दुर्दशा को स्मरण कर;"));
hinsuperobject.biblemap.put("b19_132_2", new hinsuperobject(19,132,2,"उस ने यहोवा से शपथ खाई, और याकूब के सर्वशक्तिमान की मन्नत मानी है,"));
hinsuperobject.biblemap.put("b19_132_3", new hinsuperobject(19,132,3,"कि निश्चय मैं उस समय तक अपने घर में प्रवेश न करूंगा, और ने अपने पलंग पर चढूंगा;"));
hinsuperobject.biblemap.put("b19_132_4", new hinsuperobject(19,132,4,"न अपनी आंखों में नींद, और न अपनी पलकों में झपकी आने दूंगा,"));
hinsuperobject.biblemap.put("b19_132_5", new hinsuperobject(19,132,5,"जब तक मैं यहोवा के लिये एक स्थान, अर्थात् याकूब के सर्वशक्तिमान के लिये निवास स्थान न पाऊं।।"));
hinsuperobject.biblemap.put("b19_132_6", new hinsuperobject(19,132,6,"देखो, हम ने एप्राता में इसकी चर्चा सुनी है, हम ने इसको वन के खेतों में पाया है।"));
hinsuperobject.biblemap.put("b19_132_7", new hinsuperobject(19,132,7,"आओ, हम उसके निवास में प्रवेश करें, हम उसके चरणों की चौकी के आगे दण्डवत् करें!"));
hinsuperobject.biblemap.put("b19_132_8", new hinsuperobject(19,132,8,"हे यहोवा, उठकर अपने विश्रामस्थान में अपनी सामर्थ्य के सन्दूक समेत आ।"));
hinsuperobject.biblemap.put("b19_132_9", new hinsuperobject(19,132,9,"तेरे याजक धर्म के वस्त्रा पहिने रहें, और तेरे भक्त लोग जयजयकार करें।"));
hinsuperobject.biblemap.put("b19_132_10", new hinsuperobject(19,132,10,"अपने दास दाऊद के लिये अपने अभिषिक्त की प्रार्थना की अनसुनी न कर।।"));
hinsuperobject.biblemap.put("b19_132_11", new hinsuperobject(19,132,11,"यहोवा ने दाऊद से सच्ची शपथ खाई है और वह उस से न मुकरेगा: कि मैं तेरी गद्दी पर तेरे एक निज पुत्रा को बैठाऊंगा।"));
hinsuperobject.biblemap.put("b19_132_12", new hinsuperobject(19,132,12,"यदि तेरे वंश के लोग मेरी वाचा का पालन करें और जो चितौनी मैं उन्हें सिखाऊंगा, उस पर चलें, तो उनके वंश के लोग भी तेरी गद्दी पर युग युग बैठते चले जाएंगे।"));
hinsuperobject.biblemap.put("b19_132_13", new hinsuperobject(19,132,13,"क्योंकि यहोवा ने सिरयोन को अपनाया है, और उसे अपने निवास के लिये चाहा है।।"));
hinsuperobject.biblemap.put("b19_132_14", new hinsuperobject(19,132,14,"यह तो युग युग के लिये मेरा विश्रामस्थान हैं; यहीं मैं रहूंगा, क्योंकि मैं ने इसका चाहा है।"));
hinsuperobject.biblemap.put("b19_132_15", new hinsuperobject(19,132,15,"मैं इस में की भोजनवस्तुओं पर अति आशीष दूंगा; और इसके दरिद्रों को रोटी से तृप्त करूंगा।"));
hinsuperobject.biblemap.put("b19_132_16", new hinsuperobject(19,132,16,"इसके याजकों को मैं उद्धार का वस्त्रा पहिनाऊंगा, और इसके भक्त लोग ऊंचे स्वर से जयजयकार करेंगे।"));
hinsuperobject.biblemap.put("b19_132_17", new hinsuperobject(19,132,17,"वहां मैं दाऊद के एक सींग उगाऊंगा; मैं ने अपने अभिषिक्त के लिये एक दीपक तैयार कर रखा है।"));
hinsuperobject.biblemap.put("b19_132_18", new hinsuperobject(19,132,18,"मैं उसे शत्रुओं को तो लज्जा का वस्त्रा पहिनाऊंगा, परन्तु उसी के सिर पर उसका मुकुट शोभायमान रहेगा।।"));
hinsuperobject.biblemap.put("b19_133_1", new hinsuperobject(19,133,1,"देखो, यह क्या ही भली और मनोहर बात है कि भाई लोग आपस में मिले रहें!"));
hinsuperobject.biblemap.put("b19_133_2", new hinsuperobject(19,133,2,"यह तो उस उत्तम तेल के समान है, जो हारून के सिर पर डाला गया था, और उसकी दाढ़ी पर बहकर, उसके वस्त्रा की छोर तक पहुंच गया।"));
hinsuperobject.biblemap.put("b19_133_3", new hinsuperobject(19,133,3,"वह हेर्मोन् की उस ओस के समान है, जो सिरयोन के पहाड़ों पर गिरती है! यहोवा ने तो वहीं सदा के जीवन की आशीष ठहराई है।।"));
hinsuperobject.biblemap.put("b19_134_1", new hinsuperobject(19,134,1,"हे यहोवा के सब सेवको, सुनो, तुम जो रात रात को यहोवा के भवन में खड़े रहते हो, यहोवा को धन्य कहो।"));
hinsuperobject.biblemap.put("b19_134_2", new hinsuperobject(19,134,2,"अपने हाथ पवित्रास्थान में उठाकर, यहोवा को धन्य कहो।"));
hinsuperobject.biblemap.put("b19_134_3", new hinsuperobject(19,134,3,"यहोवा जो आकाश और पृथ्वी का कर्ता है, वह सिरयोन में से तुझे आशीष देवे।।"));
hinsuperobject.biblemap.put("b19_135_1", new hinsuperobject(19,135,1,"याह की स्तुति करो, यहोवा के नाम की स्तुति करो, हे यहोवा के सेवको तुम स्तुति करो,"));
hinsuperobject.biblemap.put("b19_135_2", new hinsuperobject(19,135,2,"तुम जो यहोवा के भवन में, अर्थात् हमारे परमेश्वर के भवन के आंगनों में खड़े रहते हो!"));
hinsuperobject.biblemap.put("b19_135_3", new hinsuperobject(19,135,3,"याह की स्तुति करो, क्योंकि यहोवा भला है; उसके नाम का भजन गाओ, क्योंकि यह मनभाऊ है!"));
hinsuperobject.biblemap.put("b19_135_4", new hinsuperobject(19,135,4,"याह ने तो याकूब को अपने लिये चुना है, अर्थात् इस्राएल को अपने निज धन होने के लिये चुन लिया है।"));
hinsuperobject.biblemap.put("b19_135_5", new hinsuperobject(19,135,5,"मैं तो जानता हूं कि हमारा प्रभु यहोवा सब देवताओं से महान है।"));
hinsuperobject.biblemap.put("b19_135_6", new hinsuperobject(19,135,6,"जो कुछ यहोवा ने चाहा उसे उस ने आकाश और पृथ्वी और समुद्र और सब गहिरे स्थानों में किया है।"));
hinsuperobject.biblemap.put("b19_135_7", new hinsuperobject(19,135,7,"वह पृथ्वी की छोर से कुहरे उठाता है, और वर्षा के लिये बिजली बनाता है, और पवन को अपने भण्डार में से निकालता है।"));
hinsuperobject.biblemap.put("b19_135_8", new hinsuperobject(19,135,8,"उस ने मि  में क्या मनुष्य क्या पशु, सब के पहिलौठों को मार डाला!"));
hinsuperobject.biblemap.put("b19_135_9", new hinsuperobject(19,135,9,"हे मि , उस ने तेरे बीच में फिरौन और उसके सब कर्मचारियों के बीच चिन्ह और चमत्कार किए।"));
hinsuperobject.biblemap.put("b19_135_10", new hinsuperobject(19,135,10,"उस ने बहुत सी जातियां नाश की, और सामर्थी राजाओं को,"));
hinsuperobject.biblemap.put("b19_135_11", new hinsuperobject(19,135,11,"अर्थात् एमोरियों के राजा सीहोन को, और बाशान के राजा ओग को, और कनान के सब राजाओं को घात किया;"));
hinsuperobject.biblemap.put("b19_135_12", new hinsuperobject(19,135,12,"और उनके देश को बांटकर, अपनी प्रजा इस्राएल के भाग होने के लिये दे दिया।।"));
hinsuperobject.biblemap.put("b19_135_13", new hinsuperobject(19,135,13,"हे यहोवा, तेरा नाम सदा स्थिर है, हे यहोवा जिस नाम से तेरा स्मरण होता है, वह पीढ़ी- पीढ़ी बना रहेगा।"));
hinsuperobject.biblemap.put("b19_135_14", new hinsuperobject(19,135,14,"यहोवा तो अपनी प्रजा का न्याय चुकाएगा, और अपने दासों की दुर्दशा देखकर तरस खाएगा।"));
hinsuperobject.biblemap.put("b19_135_15", new hinsuperobject(19,135,15,"अन्यजातियों की मूरतें सोना- चान्दी ही हैं, वे मनुष्यों की बनाई हुई हैं।"));
hinsuperobject.biblemap.put("b19_135_16", new hinsuperobject(19,135,16,"उनके मुंह तो रहता है, परन्तु वे बोल नहीं सकतीं, उनके आंखें तो रहती हैं, परन्तु वे देख नहीं सकतीं,"));
hinsuperobject.biblemap.put("b19_135_17", new hinsuperobject(19,135,17,"उनके कान तो रहते हैं, परन्तु वे सुन नहीं सकतीं, न उनके कुछ भी सांस चलती है।"));
hinsuperobject.biblemap.put("b19_135_18", new hinsuperobject(19,135,18,"जैसी वे हैं वैसे ही उनके बनानेवाले भी हैं; और उन पर सब भरोसा रखनेवाले भी वैसे ही हो जाएंगे!"));
hinsuperobject.biblemap.put("b19_135_19", new hinsuperobject(19,135,19,"हे इस्राएल के घराने यहोवा को धन्य कह! हे हारून के घराने यहोवा को धन्य कह!"));
hinsuperobject.biblemap.put("b19_135_20", new hinsuperobject(19,135,20,"हे लेवी के घराने यहोवा को धन्य कह! हे यहोवा के डरवैयो यहोवा को धन्य कहो!"));
hinsuperobject.biblemap.put("b19_135_21", new hinsuperobject(19,135,21,"यहोवा जो यरूशलेम में वास करता है, उसे सिरयोन में धन्य कहा जावे! याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_136_1", new hinsuperobject(19,136,1,"यहोवा का धन्यवाद करो, क्योंकि वह भला है, और उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_2", new hinsuperobject(19,136,2,"जो ईश्वरों का परमेश्वर है, उसका धन्यवाद करो, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_3", new hinsuperobject(19,136,3,"जो प्रभुओं का प्रभु है, उसका धन्यवाद करो, उसकी करूणा सदा की है।।"));
hinsuperobject.biblemap.put("b19_136_4", new hinsuperobject(19,136,4,"उसको छोड़कर कोई बड़े बड़े अशचर्यकर्म नहीं करता, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_5", new hinsuperobject(19,136,5,"उस ने अपनी बुद्धि से आकाश बनाया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_6", new hinsuperobject(19,136,6,"उस ने पृथ्वी को जल के ऊपर फैलाया है, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_7", new hinsuperobject(19,136,7,"उस ने बड़ी बड़ी ज्योतियों बनाईं, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_8", new hinsuperobject(19,136,8,"दिन पर प्रभुता करने के लिये सूर्य को बनाया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_9", new hinsuperobject(19,136,9,"और रात पर प्रभुता करने के लिये चन्द्रमा और तारागण को बनाया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_10", new hinsuperobject(19,136,10,"उस ने मिस्त्रियों के पहिलौठों को मारा, उसकी करूणा सदा की है।।"));
hinsuperobject.biblemap.put("b19_136_11", new hinsuperobject(19,136,11,"और उनके बीच से इस्राएलियों को निकाला, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_12", new hinsuperobject(19,136,12,"बलवन्त हाथ और बढ़ाई हुई भुजा से निकाल लाया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_13", new hinsuperobject(19,136,13,"उस ने लाल समुद्र को खण्ड खण्ड कर दिया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_14", new hinsuperobject(19,136,14,"और इस्राएल को उसके बीच से पार कर दिया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_15", new hinsuperobject(19,136,15,"और फिरौन को सेना समेत लाल समुद्र में डाल दिया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_16", new hinsuperobject(19,136,16,"वह अपनी प्रजा को जंगल में ले चला, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_17", new hinsuperobject(19,136,17,"उस ने बड़े बड़े राजा मारे, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_18", new hinsuperobject(19,136,18,"उस ने प्रतापी राजाओं को भी मारा, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_19", new hinsuperobject(19,136,19,"एमोरियों के राजा सीहोन को, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_20", new hinsuperobject(19,136,20,"और बाशान के राजा ओग को घात किया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_21", new hinsuperobject(19,136,21,"और उनके देश को भाग होने के लिये, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_22", new hinsuperobject(19,136,22,"अपने दास इस्राएलियों के भाग होने के लिये दे दिया, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_23", new hinsuperobject(19,136,23,"उस ने हमारी दुर्दशा में हमारी सुधि ली, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_24", new hinsuperobject(19,136,24,"और हम को द्रोहियों से छुड़ाया है, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_25", new hinsuperobject(19,136,25,"वह सब प्राणियों को आहार देता है, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_136_26", new hinsuperobject(19,136,26,"स्वर्ग के परमेश्वर का धन्यवाद करो, उसकी करूणा सदा की है।"));
hinsuperobject.biblemap.put("b19_137_1", new hinsuperobject(19,137,1,"बाबुल की नहरों के किनारे हम लोग बैठ गए, और सिरयोन को स्मरण करके रो पड़े!"));
hinsuperobject.biblemap.put("b19_137_2", new hinsuperobject(19,137,2,"उसके बीच के मजनू वर्क्षों पर हम ने अपनी वीणाओं को टांग दिया;"));
hinsuperobject.biblemap.put("b19_137_3", new hinsuperobject(19,137,3,"क्योंकि जो हम को बन्धुए करके ले गए थे, उन्हों ने वहां हम से गीत गवाना चाहा, और हमारे रूलानेवलों ने हम से आनन्द चाहकर कहा, सिरयोन के गीतों में से हमारे लिये कोई गीत गाओ!"));
hinsuperobject.biblemap.put("b19_137_4", new hinsuperobject(19,137,4,"हम यहोवा के गीत को, पराए देश में क्योंकर गाएं?"));
hinsuperobject.biblemap.put("b19_137_5", new hinsuperobject(19,137,5,"हे यरूशलेम, यदि मैं तुझे भूल जाऊं, तो मेरा दहिना हाथ झूठा हो जाए!"));
hinsuperobject.biblemap.put("b19_137_6", new hinsuperobject(19,137,6,"यदि मैं तुझे स्मरण न रखूं, यदि मैं यरूशलेम को अपने सब आनन्द से श्रेष्ठ न जानूं, तो मेरी जीभ तालू से चिपट जाए!"));
hinsuperobject.biblemap.put("b19_137_7", new hinsuperobject(19,137,7,"हे यहोवा, यरूशलेम के दिन को एदोमियों के विरूद्ध स्मरण कर, कि वे क्योंकर कहते थे, ढाओ! उसको नेव से ढा दो।"));
hinsuperobject.biblemap.put("b19_137_8", new hinsuperobject(19,137,8,"हे बाबुल तू जो उजड़नेवाली है, क्या ही धन्य वह होगा, जो तुझ से ऐसा बर्ताव करेगा जैसा तू ने हम से किया है!"));
hinsuperobject.biblemap.put("b19_137_9", new hinsuperobject(19,137,9,"क्या ही धन्य वह होगा, जो तेरे बच्चों को पकड़कर, चट्टान पर पटक देगा!"));
hinsuperobject.biblemap.put("b19_138_1", new hinsuperobject(19,138,1,"मैं पूरे मन से तेरा धन्यवाद करूंगा; देवताओं के साम्हने भी मैं तेरा भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_138_2", new hinsuperobject(19,138,2,"मैं तेरे पवित्रा मन्दिर की ओर दण्डवत् करूंगा, और तेरी करूणा और सच्चाई के कारण तेरे नाम का धन्यवाद करूंगा; क्योंकि तू ने अपने वचन को अपने बड़े नाम से अधिक महत्व दिया है।"));
hinsuperobject.biblemap.put("b19_138_3", new hinsuperobject(19,138,3,"जिस दिन मैं ने पुकारा, उसी दिन तू ने मेरी सुन ली, और मुझ में बल देकर हियाव बन्धाया।।"));
hinsuperobject.biblemap.put("b19_138_4", new hinsuperobject(19,138,4,"हे यहोवा, पृथ्वी के सब राजा तेरा धन्यवाद करेंगे, क्योंकि उन्होंने तेरे वचन सुने हैं;"));
hinsuperobject.biblemap.put("b19_138_5", new hinsuperobject(19,138,5,"और वे यहोवा की गति के विषय में गाएंगे, क्योंकि यहोवा की महिमा बड़ी है।"));
hinsuperobject.biblemap.put("b19_138_6", new hinsuperobject(19,138,6,"यद्यपि यहोवा महान है, तौभी वह नम्र मनुष्य की ओर दृष्टि करता है; परन्तु अहंकारी को दूर ही से पहिचानता है।।"));
hinsuperobject.biblemap.put("b19_138_7", new hinsuperobject(19,138,7,"चाहे मैं संकट के बीच में रहूं तौभी तू मुझे जिलाएगा, तू मेरे क्रोधित शत्रुओं के विरूद्ध हाथ बढ़ाएगा, और अपने दहिने हाथ से मेरा उद्धार करेगा।"));
hinsuperobject.biblemap.put("b19_138_8", new hinsuperobject(19,138,8,"यहोवा मेरे लिये सब कुछ पूरा करेगा; हे यहोवा, तेरी करूणा सदा की है। तू अपने हाथों के कार्यों को त्याग न दे।।"));
hinsuperobject.biblemap.put("b19_139_1", new hinsuperobject(19,139,1,"हे यहोवा, तू ने मुझे जांच कर जान लिया है।।"));
hinsuperobject.biblemap.put("b19_139_2", new hinsuperobject(19,139,2,"तू मेरा उठना बैठना जानता है; और मेरे विचारों को दूर ही से समझ लेता है।"));
hinsuperobject.biblemap.put("b19_139_3", new hinsuperobject(19,139,3,"मेरे चलने और लेटने की तू भली भांति छानबीन करता है, और मेरी पूरी चालचलन का भेद जानता है।"));
hinsuperobject.biblemap.put("b19_139_4", new hinsuperobject(19,139,4,"हे यहोवा, मेरे मुंह में ऐसी कोई बात नहीं जिसे तू पूरी रीति से न जानता हो।"));
hinsuperobject.biblemap.put("b19_139_5", new hinsuperobject(19,139,5,"तू ने मुझे आगे पीछे घेर रखा है, और अपना हाथ मुझ पर रखे रहता है।"));
hinsuperobject.biblemap.put("b19_139_6", new hinsuperobject(19,139,6,"यह ज्ञान मेरे लिये बहुत कठिन है; यह गम्भीर और मेरी समझ से बाहर है।।"));
hinsuperobject.biblemap.put("b19_139_7", new hinsuperobject(19,139,7,"मैं तेरे आत्मा से भागकर किधर जाऊं? वा तेरे साम्हने से किधर भागूं?"));
hinsuperobject.biblemap.put("b19_139_8", new hinsuperobject(19,139,8,"यदि मैं आकाश पर चढूं, तो तू वहां है! यदि मैं अपना बिछौना अधोलोक में बिछाऊं तो वहां भी तू है!"));
hinsuperobject.biblemap.put("b19_139_9", new hinsuperobject(19,139,9,"यदि मैं भोर की किरणों पर चढ़कर समुद्र के पार जा बसूं,"));
hinsuperobject.biblemap.put("b19_139_10", new hinsuperobject(19,139,10,"तो वहां भी तू अपने हाथ से मेरी अगुवाई करेगा, और अपने दहिने हाथ से मुझे पकड़े रहेगा।"));
hinsuperobject.biblemap.put("b19_139_11", new hinsuperobject(19,139,11,"यदि मैं कहूं कि अन्धकार में तो मैं छिप जाऊंगा, और मेरे चारों ओर का उजियाला रात का अन्धेरा हो जाएगा,"));
hinsuperobject.biblemap.put("b19_139_12", new hinsuperobject(19,139,12,"तौभी अन्धकार तुझ से न छिपाएगा, रात तो दिन के तुल्य प्रकाश देगी; क्योंकि तेरे लिये अन्धियारा और उजियाला दोनों एक समान हैं।।"));
hinsuperobject.biblemap.put("b19_139_13", new hinsuperobject(19,139,13,"मेरे मन का स्वामी तो तू है; तू ने मुझे माता के गर्भ में रचा।"));
hinsuperobject.biblemap.put("b19_139_14", new hinsuperobject(19,139,14,"मैं तेरा धन्यवाद करूंगा, इसलिये कि मैं भयानक और अद्भुत रीति से रचा गया हूं। तेरे काम तो आश्चर्य के हैं, और मैं इसे भली भांति जानता हूं।"));
hinsuperobject.biblemap.put("b19_139_15", new hinsuperobject(19,139,15,"जब मैं गुप्त में बनाया जाता, और पृथ्वी के नीचे स्थानों में रचा जाता था, तब मेरी हडि्डयां तुझ से छिपी न थीं।"));
hinsuperobject.biblemap.put("b19_139_16", new hinsuperobject(19,139,16,"तेरी आंखों ने मेरे बेड़ौल तत्व को देखा; और मेरे सब अंग जो दिन दिन बनते जाते थे वे रचे जाने से पहिले तेरी पुस्तक में लिखे हुए थे।"));
hinsuperobject.biblemap.put("b19_139_17", new hinsuperobject(19,139,17,"और मेरे लिये तो हे ईश्वर, तेरे विचार क्या ही बहुमूल्य हैं! उनकी संख्या का जोड़ कैसा बड़ा है।।"));
hinsuperobject.biblemap.put("b19_139_18", new hinsuperobject(19,139,18,"यदि मैं उनको गिनता तो वे बालू के किनकों से भी अधिक ठहरते। जब मैं जाग उठता हूं, तब भी तेरे संग रहता हूं।।"));
hinsuperobject.biblemap.put("b19_139_19", new hinsuperobject(19,139,19,"हे ईश्वर निश्चय तू दुष्ट को घात करेगा! हे हत्यारों, मुझ से दूर हो जाओ।"));
hinsuperobject.biblemap.put("b19_139_20", new hinsuperobject(19,139,20,"क्योंकि वे तेरी चर्चा चतुराई से करते हैं; तेरे द्रोही तेरा नाम झूठी बात पर लेते हैं।"));
hinsuperobject.biblemap.put("b19_139_21", new hinsuperobject(19,139,21,"हे यहोवा, क्या मैं तेरे बैरियों से बैर न रखूं, और तेरे विरोधियों से रूठ न जाऊं?"));
hinsuperobject.biblemap.put("b19_139_22", new hinsuperobject(19,139,22,"हां, मैं उन से पूर्ण बैर रखता हूं; मैं उनको अपना शत्रु समझता हूं।"));
hinsuperobject.biblemap.put("b19_139_23", new hinsuperobject(19,139,23,"हे ईश्वर, मुझे जांचकर जान ले! मुझे परखकर मेरी चिन्ताओं को जान ले!"));
hinsuperobject.biblemap.put("b19_139_24", new hinsuperobject(19,139,24,"और देख कि मुझ में कोई बुरी चाल है कि नहीं, और अनन्त के मार्ग में मेरी अगुवाई कर!"));
hinsuperobject.biblemap.put("b19_140_1", new hinsuperobject(19,140,1,"हे यहोवा, मुझ को बुरे मनुष्य से बचा ले; उपद्रवी पुरूष से मेरी रक्षा कर,"));
hinsuperobject.biblemap.put("b19_140_2", new hinsuperobject(19,140,2,"क्योंकि उन्हों ने मन में बुरी कल्पनाएं की हैं; वे लगातार लड़ाइयां मचाते हैं।"));
hinsuperobject.biblemap.put("b19_140_3", new hinsuperobject(19,140,3,"उनका बोलना सांप का काटना सा है, उनके मुंह में नाग का सा विष रहता है।।"));
hinsuperobject.biblemap.put("b19_140_4", new hinsuperobject(19,140,4,"हे यहोवा, मुझे दुष्ट के हाथों से बचा ले; उपद्रवी पुरूष से मेरी रक्षा कर, क्योंकि उन्हों ने मेरे पैरों के उखाड़ने की युक्ति की है।"));
hinsuperobject.biblemap.put("b19_140_5", new hinsuperobject(19,140,5,"घमण्डियों ने मेरे लिये फन्दा और पासे लगाए, और पथ के किनारे जाल बिछाया है; उन्हों ने मेरे लिये फन्दे लगा रखे हैं।।"));
hinsuperobject.biblemap.put("b19_140_6", new hinsuperobject(19,140,6,"हे यहोवा, मैं ने तुझ से कहा है कि तू मेरा ईश्वर है; हे यहोवा, मेरे गिड़गड़ाने की ओर कान लगा!"));
hinsuperobject.biblemap.put("b19_140_7", new hinsuperobject(19,140,7,"हे यहोवा प्रभु, हे मेरे सामर्थी उद्धारकर्ता, तू ने युद्ध के दिन मेरे सिर की रक्षा की है।"));
hinsuperobject.biblemap.put("b19_140_8", new hinsuperobject(19,140,8,"हे यहोवा दुष्ट की इच्छा को पूरी न होने दे, उसकी बुरी युक्ति को सफल न कर, नहीं तो वह घमण्ड करेगा।।"));
hinsuperobject.biblemap.put("b19_140_9", new hinsuperobject(19,140,9,"मेरे घेरनेवालों के सिर पर उन्हीं का विचारा हुआ उत्पात पड़े!"));
hinsuperobject.biblemap.put("b19_140_10", new hinsuperobject(19,140,10,"उन पर अंगारे डाले जाएं! वे आग में गिरा दिए जाएं! और ऐसे गड़हों में गिरें, कि वे फिर उठ न सकें!"));
hinsuperobject.biblemap.put("b19_140_11", new hinsuperobject(19,140,11,"बकवादी पृथ्वी पर स्थिर नहीं होने का; उपद्रवी पुरूष को गिराने के लिये बुराई उसका पीछा करेगी।।"));
hinsuperobject.biblemap.put("b19_140_12", new hinsuperobject(19,140,12,"हे यहोवा, मुझे निश्चय है कि तू दीन जन का और दरिद्रों का न्याय चुकाएगा।"));
hinsuperobject.biblemap.put("b19_140_13", new hinsuperobject(19,140,13,"नि:सन्देह धर्मी तेरे नाम का धन्यवाद करने पाएंगे; सीधे लोग तेरे सम्मुख वास करेंगे।।"));
hinsuperobject.biblemap.put("b19_141_1", new hinsuperobject(19,141,1,"हे यहोवा, मैं ने तुझे पुकारा है; मेरे लिये फुर्ती कर! जब मैं तुझ को पुकारूं, तब मेरी ओर कान लगा!"));
hinsuperobject.biblemap.put("b19_141_2", new hinsuperobject(19,141,2,"मेरी प्रार्थना तेरे साम्हने सुगन्ध धूप, और मेरा हाथ फैलाना, संध्याकाल का अन्नबलि ठहरे!"));
hinsuperobject.biblemap.put("b19_141_3", new hinsuperobject(19,141,3,"हे हयोवा, मेरे मुख का पहरा बैठा, मेरे हाठों के द्वार पर रखवाली कर!"));
hinsuperobject.biblemap.put("b19_141_4", new hinsuperobject(19,141,4,"मेरा मन किसी बुरी बात की ओर फिरने न दे; मैं अनर्थकारी पुरूषों के संग, दुष्ट कामों में न लगूं, और मै उनके स्वादिष्ट भोजनवस्तुओं में से कुछ न खाऊं!"));
hinsuperobject.biblemap.put("b19_141_5", new hinsuperobject(19,141,5,"धर्मी मुझ को मारे तो यह कुपा मानी जाएगी, और वह मुझे ताड़ना दे, तो यह मेरे सिर पर का लेत ठहरेगा; मेरा सिर उस से इन्कार न करेगा।। लोगों के बुरे काम करने पर भी मैं प्रार्थना में लवलीन रहूंगा।"));
hinsuperobject.biblemap.put("b19_141_6", new hinsuperobject(19,141,6,"जब उनके न्यायी चट्टान के पास गिराए गए, तब उन्हों ने मेरे वचन सुन लिए; क्योंकि वे मधुर हैं।"));
hinsuperobject.biblemap.put("b19_141_7", new hinsuperobject(19,141,7,"जैसे भूमि में हल चलने से ढेले फूटते हैं, वैसे ही हमारी हडि्डयां अधोलोक के मुंह पर छितराई हुई हैं।।"));
hinsuperobject.biblemap.put("b19_141_8", new hinsuperobject(19,141,8,"परन्तु हे यहोवा, प्रभु, मेरी आंखे तेरी ही ओर लगी हैं; मैं तेरा शरणागत हूं; तू मेरे प्राण जाने न दे!"));
hinsuperobject.biblemap.put("b19_141_9", new hinsuperobject(19,141,9,"मुझे उस फन्दे से, जो उन्हों ने मेरे लिये लगाया है, और अनर्थकारियों के जाल से मेरी रक्षा कर!"));
hinsuperobject.biblemap.put("b19_141_10", new hinsuperobject(19,141,10,"दुष्ट लोग अपने जालों में आप ही फंसें, और मैं बच निकलूं।।"));
hinsuperobject.biblemap.put("b19_142_1", new hinsuperobject(19,142,1,"मैं यहोवा की दोहाई देता, मैं यहोवा से गिड़गिड़ाता हूं,"));
hinsuperobject.biblemap.put("b19_142_2", new hinsuperobject(19,142,2,"मैं अपने शोक की बातें उस से खोलकर कहता, मैं अपना संकट उसके आगे प्रगट करता हूं।"));
hinsuperobject.biblemap.put("b19_142_3", new hinsuperobject(19,142,3,"जब मेरी आत्मा मेरे भीतर से व्याकुल हो रही थी, तब तू मेरी दशा को जानता था! जिस रास्ते से मैं जानेवाला था, उसी में उन्हों ने मेरे लिये फन्दा लगाया।"));
hinsuperobject.biblemap.put("b19_142_4", new hinsuperobject(19,142,4,"मैं ने दहिनी ओर देखा, परन्तु कोई मुझे नहीं देखता है। मेरे लिये शरण कहीं नहीं रही, न मुझ को कोई पूछता है।।"));
hinsuperobject.biblemap.put("b19_142_5", new hinsuperobject(19,142,5,"हे यहोवा, मैं ने तेरी दोहाई दी है; मैं ने कहा, तू मेरा शरणस्थान है, मेरे जीते ही तू मेरा भाग है।"));
hinsuperobject.biblemap.put("b19_142_6", new hinsuperobject(19,142,6,"मेरी चिल्लाहट को ध्यान देकर सुन, क्योंकि मेरी बड़ी दुर्दशा हो गई है! जो मेरे पीछे पड़े हैं, उन से मुझे बचा ले; क्योंकि वे मुझ से अधिक सामर्थी हैं।"));
hinsuperobject.biblemap.put("b19_142_7", new hinsuperobject(19,142,7,"मुझ को बन्दीगृह से निकाल कि मैं तेरे नाम का धन्यवाद करूं! धर्मी लोग मेरे चारों ओर आएंगे; क्योंकि तू मेरा उपकार करेगा।।"));
hinsuperobject.biblemap.put("b19_143_1", new hinsuperobject(19,143,1,"हे यहोवा मेरी प्रार्थना सुन; मेरे गिड़गिड़ाने की ओर कान लगा! तू जो सच्चा और धर्मी है, सो मेरी सुन ले,"));
hinsuperobject.biblemap.put("b19_143_2", new hinsuperobject(19,143,2,"और अपने दास से मुक मा न चला! क्योंकि कोई प्राणी तेरी दृष्टि में निर्दोष नहीं ठहर सकता।।"));
hinsuperobject.biblemap.put("b19_143_3", new hinsuperobject(19,143,3,"शत्रु तो मेरे प्राण का गाहक हुआ है; उस ने मुझे चूर करके मिट्टी में मिलाया है, और मुझे ढेर दिन के मरे हुओं के समान अन्धेरे स्थान में डाल दिया है।"));
hinsuperobject.biblemap.put("b19_143_4", new hinsuperobject(19,143,4,"मेरी आत्मा भीतर से व्याकुल हो रही है मेरा मन विकल है।।"));
hinsuperobject.biblemap.put("b19_143_5", new hinsuperobject(19,143,5,"मुझे प्राचीनकाल के दिन स्मरण आते हैं, मैं तेरे सब अद्भुत कामों पर ध्यान करता हूं, और तेरे काम को सोचता हूं।"));
hinsuperobject.biblemap.put("b19_143_6", new hinsuperobject(19,143,6,"मैं तेरी ओर अपने हाथ फैलाए हूए हूं; सूखी भूमि की नाईं मैं तेरा प्यासा हूं।।"));
hinsuperobject.biblemap.put("b19_143_7", new hinsuperobject(19,143,7,"हे यहोवा, फुर्ती करके मेरी सुन ले; क्योंकि मेरे प्राण निकलने ही पर हैं मुझ से अपना मुंह न छिपा, ऐसा न हो कि मैं कबर में पड़े हुओं के समान हो जाऊं।"));
hinsuperobject.biblemap.put("b19_143_8", new hinsuperobject(19,143,8,"अपनी करूणा की बात मुझे शीघ्र सुना, क्योंकि मैं ने तुझी पर भरोसा रखा है। जिस मार्ग से मुझे चलना है, वह मुझ को बता दे, क्योंकि मैं अपना मन तेरी ही ओर लगाता हूं।।"));
hinsuperobject.biblemap.put("b19_143_9", new hinsuperobject(19,143,9,"हे हयोवा, मुझे शत्रुओं से बचा ले; मैं तेरी ही आड़ में आ छिपा हूं।"));
hinsuperobject.biblemap.put("b19_143_10", new hinsuperobject(19,143,10,"मुझ को यह सिखा, कि मैं तेरी इच्छा क्योंकर पूरी करूं, क्योंकि मेरा परमेश्वर तू ही है! तेरा भला आत्मा मुझ को धर्म के मार्ग में ले चले!"));
hinsuperobject.biblemap.put("b19_143_11", new hinsuperobject(19,143,11,"हे यहोवा, मुझे अपने नाम के निमित्त जिला! तू जो धर्मी है, मुझ को संकट से छुड़ा ले!"));
hinsuperobject.biblemap.put("b19_143_12", new hinsuperobject(19,143,12,"और करूणा करके मेरे शत्रुओं को सत्यानाश कर, और मेरे सब सतानेवालों का नाश कर डाल, क्योंकि मैं तेरा दास हूं।।"));
hinsuperobject.biblemap.put("b19_144_1", new hinsuperobject(19,144,1,"धन्य है यहोवा, जो मेरी चट्टान है, वह मेरे हाथों को लड़ने, और युद्ध करने के लिये तैयार करता है।"));
hinsuperobject.biblemap.put("b19_144_2", new hinsuperobject(19,144,2,"वह मेरे लिये करूणानिधान और गढ़, ऊंचा स्थान और छुड़ानेवाला है, वह मेरी ढ़ाल और शरणस्थान है, जो मेरी प्रजा को मेरे वश में कर देता है।।"));
hinsuperobject.biblemap.put("b19_144_3", new hinsuperobject(19,144,3,"हे यहोवा, मनुष्य क्या है कि तू उसकी सुधि लेता है, या आदमी क्या है, कि तू उसकी कुछ चिन्ता करता है?"));
hinsuperobject.biblemap.put("b19_144_4", new hinsuperobject(19,144,4,"मनुष्य तो सांस के समान है; उसके दिन ढलती हुई छाया के समान हैं।।"));
hinsuperobject.biblemap.put("b19_144_5", new hinsuperobject(19,144,5,"हे यहोवा, अपने स्वर्ग को नीचा करके उतर आ! पहाड़ों को छू तब उन से धुंआं उठेंगा!"));
hinsuperobject.biblemap.put("b19_144_6", new hinsuperobject(19,144,6,"बिजली कड़काकर उनके तितर बितर कर दे, अपने तीर चलाकर उनको घबरा दे!"));
hinsuperobject.biblemap.put("b19_144_7", new hinsuperobject(19,144,7,"अपने हाथ ऊपर से बढ़ाकर मुझे महासागर से उबार, अर्थात् परदेशियों के वश से छुड़ा।"));
hinsuperobject.biblemap.put("b19_144_8", new hinsuperobject(19,144,8,"उनके मुंह से तो व्यर्थ बातें निकलती हैं, और उनके दहिने हाथ से धोखे के काम होते हैं।।"));
hinsuperobject.biblemap.put("b19_144_9", new hinsuperobject(19,144,9,"हे परमेश्वर, मैं तेरी स्तुति का नया गीत गाऊंगा; मैं दस तारवाली सारंगी बजाकर तेरा भजन गाऊंगा।"));
hinsuperobject.biblemap.put("b19_144_10", new hinsuperobject(19,144,10,"तू राजाओं का उद्धार करता है, और अपने दास दाऊद को तलवार की मार से बचाता है।"));
hinsuperobject.biblemap.put("b19_144_11", new hinsuperobject(19,144,11,"तू मुझ को उबार और परदेशियों के वश से छुड़ा ले, जिन के मुंह से व्यर्थ बातें निकलती हैं, और जिनका दहिना हाथ झूठ का दहिना हाथ है।।"));
hinsuperobject.biblemap.put("b19_144_12", new hinsuperobject(19,144,12,"जब हमारे बेटे जवानी के समय पौधों की नाईं बढ़े हुए हों, और हमारी बेटियां उन कोनेवाले पत्थरों के समान हों, जो मन्दिर के पत्थरों की नाईं बनाए जाएं;"));
hinsuperobject.biblemap.put("b19_144_13", new hinsuperobject(19,144,13,"जब हमारे खत्ते भरे रहें, और उन में भांति भांति का अन्न धरा जाए, और हमारी भेड़- बकरियों हमारे मैदानों में हजारों हजार बच्चे जनें;"));
hinsuperobject.biblemap.put("b19_144_14", new hinsuperobject(19,144,14,"जब हमारे बैल खूब लदे हुए हों; जब हमें न विध्न हो और न हमारा कहीं जाना हो, और न हमारे चौकों में रोना- पीटना हो,"));
hinsuperobject.biblemap.put("b19_144_15", new hinsuperobject(19,144,15,"तो इस दशा में जो राज्य हो वह क्या ही धन्य होगा! जिस राज्य का परमेश्वर यहोवा है, वह क्या ही धन्य है!"));
hinsuperobject.biblemap.put("b19_145_1", new hinsuperobject(19,145,1,"हे मेरे परमेश्वर, हे राजा, मैं तुझे सराहूंगा, और तेरे नाम को सदा सर्वदा धन्य कहता रहूंगा।"));
hinsuperobject.biblemap.put("b19_145_2", new hinsuperobject(19,145,2,"प्रति दिन मैं तुझ को धन्य कहा करूंगा, और तेरे नाम की स्तुति सदा सर्वदा करता रहूंगा।"));
hinsuperobject.biblemap.put("b19_145_3", new hinsuperobject(19,145,3,"यहोवा महान और अति स्तुति के योग्य है, और उसकी बड़ाई अगम है।।"));
hinsuperobject.biblemap.put("b19_145_4", new hinsuperobject(19,145,4,"तेरे कामों की प्रशंसा और तेरे पराक्रम के कामों का वर्णन, पीढ़ी पीढ़ी होता चला जाएगा।"));
hinsuperobject.biblemap.put("b19_145_5", new hinsuperobject(19,145,5,"मैं तेरे ऐश्वर्य की महिमा के प्रताप पर और तेरे भांति भांति के आश्चर्यकर्मों पर ध्यान करूंगा।"));
hinsuperobject.biblemap.put("b19_145_6", new hinsuperobject(19,145,6,"लोग तेरे भयानक कामों की शक्ति की चर्चा करेंगे, और मैं तेरे बड़े बड़े कामों का वर्णन करूंगा।"));
hinsuperobject.biblemap.put("b19_145_7", new hinsuperobject(19,145,7,"लोग तेरी बड़ी भलाई का स्मरण करके उसकी चर्चा करेंगे, और तेरे धर्म का जयजयकार करेंगे।।"));
hinsuperobject.biblemap.put("b19_145_8", new hinsuperobject(19,145,8,"यहोवा अनुग्रहकारी और दयालु, विलम्ब से क्रोध करनेवाला और अति करूणामय है।"));
hinsuperobject.biblemap.put("b19_145_9", new hinsuperobject(19,145,9,"यहोवा सभों के लिये भला है, और उसकी दया उसकी सारी सृष्टि पर है।।"));
hinsuperobject.biblemap.put("b19_145_10", new hinsuperobject(19,145,10,"हे यहोवा, तेरी सारी सृष्टि तेरा धन्यवाद करेगी, और तेरे भक्त लाग तुझे धन्य कहा करेंगे!"));
hinsuperobject.biblemap.put("b19_145_11", new hinsuperobject(19,145,11,"वे तेरे राज्य की महिमा की चर्चा करेंगे, और तेरे पराक्रम के विषय में बातें करेंगे;"));
hinsuperobject.biblemap.put("b19_145_12", new hinsuperobject(19,145,12,"कि वे आदमियों पर तेरे पराक्रम के काम और तेरे राज्य के प्रताप की महिमा प्रगट करें।"));
hinsuperobject.biblemap.put("b19_145_13", new hinsuperobject(19,145,13,"तेरा राज्य युग युग का और तेरी प्रभुता सब पीढ़ियों तक बनी रहेगी।।"));
hinsuperobject.biblemap.put("b19_145_14", new hinsuperobject(19,145,14,"यहोवा सब गिरते हुओं को संभालता है, और सब झुके हुओं को सीधा खड़ा करता है।"));
hinsuperobject.biblemap.put("b19_145_15", new hinsuperobject(19,145,15,"सभों की आंखें तेरी ओर लगी रहती हैं, और तू उनको आहार समय पर देता है।"));
hinsuperobject.biblemap.put("b19_145_16", new hinsuperobject(19,145,16,"तू अपनी मुट्ठी खोलकर, सब प्राणियों को आहार से तृप्त करता है।"));
hinsuperobject.biblemap.put("b19_145_17", new hinsuperobject(19,145,17,"यहोवा अपनी सब गति में धर्मी और अपने सब कामों मे करूणामय है।"));
hinsuperobject.biblemap.put("b19_145_18", new hinsuperobject(19,145,18,"जिनते यहोवा को पुकारते हैं, अर्थात् जितने उसको सच्चाई से पुकारते हें; उन सभों के वह निकट रहता है।"));
hinsuperobject.biblemap.put("b19_145_19", new hinsuperobject(19,145,19,"वह अपने डरवैयों की इच्छा पूरी करता है, ओर उनकी दोहाई सुनकर उनका उद्धार करता है।"));
hinsuperobject.biblemap.put("b19_145_20", new hinsuperobject(19,145,20,"यहोवा अपने सब प्रेमियों की तो रक्षा करता, परन्तु सब दुष्टों को सत्यानाश करता है।।"));
hinsuperobject.biblemap.put("b19_145_21", new hinsuperobject(19,145,21,"मैं यहोवा की स्तुति करूंगा, और सारे प्राणी उसके पवित्रा नाम को सदा सर्वदा धन्य कहते रहें।।"));
hinsuperobject.biblemap.put("b19_146_1", new hinsuperobject(19,146,1,"याह की स्तुति करो। हे मेरे मन यहोवा की स्तुति कर!"));
hinsuperobject.biblemap.put("b19_146_2", new hinsuperobject(19,146,2,"मैं जीवन भर यहोवा की स्तुति करता रहूंगा; जब तक मैं बना रहूंगा, तब तक मैं अपने परमेश्वर का भजन गाता रहूंगा।।"));
hinsuperobject.biblemap.put("b19_146_3", new hinsuperobject(19,146,3,"तुम प्रधानों पर भरोसा न रखना, न किसी आदमी पर, क्योंकि उस में उद्धार करने की भी शक्ति नहीं।"));
hinsuperobject.biblemap.put("b19_146_4", new hinsuperobject(19,146,4,"उसका भी प्राण निकलेगा, वही भी मिट्टी में मिल जाएगा; उसी दिन उसकी सब कल्पनाएं नाश हो जाएंगी।।"));
hinsuperobject.biblemap.put("b19_146_5", new hinsuperobject(19,146,5,"क्या ही धन्य वह है, जिसका सहायक याकूब का ईश्वर है, और जिसका भरोसा अपने परमेश्वर यहोवा पर है।"));
hinsuperobject.biblemap.put("b19_146_6", new hinsuperobject(19,146,6,"वह आकाश और पृथ्वी और समुद्र और उन में जो कुछ है, सब का कर्ता है; और वह अपना वचन सदा के लिये पूरा करता रहेगा।"));
hinsuperobject.biblemap.put("b19_146_7", new hinsuperobject(19,146,7,"वह पिसे हुओं का न्याय चुकाता है; और भूखों को रोटी देता है।। यहोवा बन्धुओं को छुड़ाता है;"));
hinsuperobject.biblemap.put("b19_146_8", new hinsuperobject(19,146,8,"यहोवा अन्धों को आंखें देता है। यहोवा झुके हुओं को सीधा खड़ा करता है; यहोवा धर्मियों से प्रेम रखता है।"));
hinsuperobject.biblemap.put("b19_146_9", new hinsuperobject(19,146,9,"यहोवा परदेशियों की रक्षा करता है; और अनाथों और विधवा को तो सम्भालता है; परन्तु दुष्टों के मार्ग को टेढ़ा मेढ़ा करता है।।"));
hinsuperobject.biblemap.put("b19_146_10", new hinsuperobject(19,146,10,"हे सिरयोन, यहोवा सदा के लिये, तेरा परमेश्वर पीढ़ी पीढ़ी राज्य करता रहेगा। याह की स्तुति करो!"));
hinsuperobject.biblemap.put("b19_147_1", new hinsuperobject(19,147,1,"याह की स्तुति करो! क्योंकि अपने परमेश्वर का भजन गाना अच्छा है; क्योंकि वह मनभावना है, उसकी स्तुति करनी मनभावनी है।"));
hinsuperobject.biblemap.put("b19_147_2", new hinsuperobject(19,147,2,"यहोवा यरूशलेम को बसा रहा है; वह निकाले हुए इस्राएलियों को इकट्ठा कर रहा है।"));
hinsuperobject.biblemap.put("b19_147_3", new hinsuperobject(19,147,3,"वह खेदित मनवालों को चंगा करता है, और उनके शोक पर मरहम- पट्टी बान्धता है।"));
hinsuperobject.biblemap.put("b19_147_4", new hinsuperobject(19,147,4,"वह तारों को गिनता, और उन में से एक एक का नाम रखता है।"));
hinsuperobject.biblemap.put("b19_147_5", new hinsuperobject(19,147,5,"हमारा प्रभु महान और अति सामर्थी है; उसकी बुद्धि अपरम्पार है।"));
hinsuperobject.biblemap.put("b19_147_6", new hinsuperobject(19,147,6,"यहोवा नम्र लोगों को सम्भलता है, और दुष्टों को भूमि पर गिरा देता है।।"));
hinsuperobject.biblemap.put("b19_147_7", new hinsuperobject(19,147,7,"धन्यवाद करते हुए यहोवा का गीत गाओ; वीणा बजाते हुए हमारे परमेश्वर का भजन गाओ।"));
hinsuperobject.biblemap.put("b19_147_8", new hinsuperobject(19,147,8,"वह आकाश को मेघों से छा देता है, और पृथ्वी के लिये मेंह की तैयारी करता है, और पहाड़ों पर घास उगाता है।"));
hinsuperobject.biblemap.put("b19_147_9", new hinsuperobject(19,147,9,"वह पशुओं को और कौवे के बच्चों को जो पुकारते हैं, आहार देता है।"));
hinsuperobject.biblemap.put("b19_147_10", new hinsuperobject(19,147,10,"न तो वह घोड़े के बल को चाहता है, और न पुरूष के पैरों से प्रसन्न होता है;"));
hinsuperobject.biblemap.put("b19_147_11", new hinsuperobject(19,147,11,"यहोवा अपने डरवैयों ही से प्रसन्न होता है, अर्थात् उन से जो उसकी करूणा की आशा लगाए रहते हैं।।"));
hinsuperobject.biblemap.put("b19_147_12", new hinsuperobject(19,147,12,"हे यरूशलेम, यहोवा की प्रशंसा कर! हे सिरयोन, अपने परमेश्वर की स्तुति कर!"));
hinsuperobject.biblemap.put("b19_147_13", new hinsuperobject(19,147,13,"क्योंकि उस ने तेरे फाटकों के खम्भों को दृढ़ किया है; और तेरे लड़के बालों को आशीष दी है।"));
hinsuperobject.biblemap.put("b19_147_14", new hinsuperobject(19,147,14,"और तेरे सिवानों में शान्ति देता है, और तुझ को उत्तम से उत्तम गेहूं से तृप्त करता है।"));
hinsuperobject.biblemap.put("b19_147_15", new hinsuperobject(19,147,15,"वह पृथ्वी पर अपनी आज्ञा का प्रचार करता है, उसका वचन अति वेग से दौड़ता है।"));
hinsuperobject.biblemap.put("b19_147_16", new hinsuperobject(19,147,16,"वह ऊन के समान हिम को गिराता है, और राख की नाईं पाला बिखेरता है।"));
hinsuperobject.biblemap.put("b19_147_17", new hinsuperobject(19,147,17,"वह बर्फ के टुकड़े गिराता है, उसकी की हुई ठण्ड को कौन सह सकता है?"));
hinsuperobject.biblemap.put("b19_147_18", new hinsuperobject(19,147,18,"वह आज्ञा देकर उन्हें गलाता है; वह वायु बहाता है, तब जल बहने लगता है।"));
hinsuperobject.biblemap.put("b19_147_19", new hinsuperobject(19,147,19,"वह याकूब को अपना वचन, और इस्राएल को अपनी विधियां और नियम बताता है।"));
hinsuperobject.biblemap.put("b19_147_20", new hinsuperobject(19,147,20,"किसी और जाति से उस ने ऐसा बर्ताव नहीं किया; और उसके नियमों को औरों ने नहीं जाता।। याह की स्तुति करो।"));
hinsuperobject.biblemap.put("b19_148_1", new hinsuperobject(19,148,1,"याह की स्तुति करो! यहोवा की स्तुति स्वर्ग में से करो, उसकी स्तुति ऊंचे स्थानों में करो!"));
hinsuperobject.biblemap.put("b19_148_2", new hinsuperobject(19,148,2,"हे उसके सब दूतों, उसकी स्तुति करो: हे उसकी सब सेना उसकी स्तुति कर!"));
hinsuperobject.biblemap.put("b19_148_3", new hinsuperobject(19,148,3,"हे सूर्य और चन्द्रमा उसकी स्तुति करो, हे सब ज्योतिमय तारागण उसकी स्तुति करो!"));
hinsuperobject.biblemap.put("b19_148_4", new hinsuperobject(19,148,4,"हे सब से ऊंचे आकाश, और हे आकाश के ऊपरवाले जल, तुम दोनों उसकी स्तुति करो।"));
hinsuperobject.biblemap.put("b19_148_5", new hinsuperobject(19,148,5,"वे यहोवा के नाम की स्तुति करें, क्योंकि उसी ने आज्ञा दी और ये सिरजे गए।"));
hinsuperobject.biblemap.put("b19_148_6", new hinsuperobject(19,148,6,"और उस ने उनको सदा सर्वदा के लिये स्थिर किया है; और ऐसी विधि ठहराई है, जो टलने की नहीं।।"));
hinsuperobject.biblemap.put("b19_148_7", new hinsuperobject(19,148,7,"पृथ्वी में से यहोवा की स्तुति करो, हे मगरमच्छों और गहिरे सागर,"));
hinsuperobject.biblemap.put("b19_148_8", new hinsuperobject(19,148,8,"हे अग्नि और ओलो, हे हिम और कुहरे, हे उसका वचन माननेवाली प्रचण्ड बयार!"));
hinsuperobject.biblemap.put("b19_148_9", new hinsuperobject(19,148,9,"हे पहाड़ों और सब टीलो, हे फलदाई वृक्षों और सब देवदारों!"));
hinsuperobject.biblemap.put("b19_148_10", new hinsuperobject(19,148,10,"हे वन- पशुओं और सब घरैलू पशुओं, हे रेंगनेवाले जन्तुओं और हे पक्षियों!"));
hinsuperobject.biblemap.put("b19_148_11", new hinsuperobject(19,148,11,"हे पृथ्वी के राजाओं, और राज्य राज्य के सब लोगों, हे हाकिमों और पृथ्वी के सब न्यायियों!"));
hinsuperobject.biblemap.put("b19_148_12", new hinsuperobject(19,148,12,"हे जवनों और कुमारियों, हे पुरनियों और बालकों!"));
hinsuperobject.biblemap.put("b19_148_13", new hinsuperobject(19,148,13,"यहोवा के नाम की स्तुति करो, क्योंकि केवल उसकी का नाम महान है; उसका ऐश्वर्य पृथ्वी और आकाश के ऊपर है।"));
hinsuperobject.biblemap.put("b19_148_14", new hinsuperobject(19,148,14,"और उस ने अपनी प्रजा के लिये एक सींग ऊंचा किया है; यह उसके सब भक्तों के लिये अर्थात् इस्राएलियों के लिये और उसके समीप रहनेवाली प्रजा के लिये स्तुति करने का विषय है। याह की स्तुति करो।"));
